INSERT INTO `he_thong` VALUES (1, 'Trang Thông tin điện tử Hội khuyến học tỉnh Sơn La', '', '', 'ICT', '', '', 'https://khuyenhoc.sonla.gov.vn', 'Số 06, ngõ 2A, phố Xuân Thủy, tổ 8, phường Chiềng Lề, thành phố Sơn La, tỉnh Sơn La', '', 'khuyenhocsonla@gmail.com', '', '0212.3857.602', '12/GP-TTĐT', '<p>Qu&yacute; bạn đọc th&acirc;n mến!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Điền đầy đủ v&agrave; ch&iacute;nh x&aacute;c c&aacute;c th&ocirc;ng tin c&aacute; nh&acirc;n: họ v&agrave; t&ecirc;n, ghi r&otilde; địa chỉ nơi cư tr&uacute; (số nh&agrave;, t&ecirc;n đường, khu phố hoặc th&ocirc;n, ấp, phường hoặc x&atilde;, huyện hoặc th&agrave;nh phố), địa chỉ thư điện tử...v&agrave;o c&aacute;c &ocirc; ph&iacute;a dưới theo mẫu hướng dẫn. Nội dung thư phản &aacute;nh, kiến nghị v&agrave; giải đ&aacute;p về ch&iacute;nh s&aacute;ch, ph&aacute;p luật c&ugrave;ng với c&aacute;c th&ocirc;ng tin c&aacute; nh&acirc;n phải sử dụng tiếng việt c&oacute; dấu. Khi phản &aacute;nh một vấn đề phải n&ecirc;u r&otilde; vấn đề, t&igrave;nh h&igrave;nh hoặc tr&igrave;nh b&agrave;y r&otilde; sự việc vướng mắc cần giải đ&aacute;p, giải quyết. Những bức thư kh&ocirc;ng đ&aacute;p ứng được c&aacute;c nhu cầu n&ecirc;u tr&ecirc;n sẽ kh&ocirc;ng được cổng th&ocirc;ng tin điện tử tỉnh . Thư hợp lệ sẽ được ch&uacute;ng t&ocirc;i chuyển đến c&aacute;c cơ quan c&oacute; chức năng c&oacute; thẩm quyền để xem x&eacute;t v&agrave; c&oacute; phản hồi trong thời gian sớm nhất. Nội dung phản hồi được gửi đến hộp thư của qu&yacute; bạn đọc hoặc đăng tải tr&ecirc;n chuy&ecirc;n mục Hỏi đ&aacute;p trực tuyến.</p>\r\n\r\n<p><em>Xin cảm ơn sự tin cậy v&agrave; hợp t&aacute;c của bạn đọc!</em></p>\r\n', '02123.857.571', 'https://www.google.com/maps?ll=21.32752,103.9095&z=15&t=m&hl=vi&gl=US&mapclient=embed&cid=1570758637398736261', '', '');
