INSERT INTO `hoc_tap` VALUES (22, 'Hướng dẫn cơ bản tách tóc với Plugin Mask PRO 4 trong Photoshop', '/data/images/hoc-tap/photoshop/photoshop-maskpro4.jpg', 1, 'Video sau Dũng sẽ chia sẻ cho các bạn sử dụng cơ bản Plugin Mask PRO 4 để tách đối tượng ta khỏi phông xanh dễ dàng, nhanh chóng & hiệu quả. Plugin Mask PRO 4 sử dụng thuật toán loại màu & giữ màu, giúp cho chúng ta có thể loại bỏ bất cứ màu sắc nào trên hình hoặc đối với đối tượng nhiều chi tiết thì chúng ta sẽ giữ lại những mảng màu gần nhau nhất. Chắc hẳn sau khi xem Video chia sẻ này các bạn sẽ không còn khó chịu khi gặp phải những trường hợp tách đối tượng ra khỏi nền phức tạp.', '', '12:02:53', 20, 'http://www.youtube.com/watch?v=xQird01ZovU&feature=youtu.be', 18);
INSERT INTO `hoc_tap` VALUES (23, 'Làm nét và sáng ảnh trong photoshop', '/data/images/hoc-tap/photoshop/lam-net-anh.jpg', 1, 'Một phương pháp làm nét ảnh đơn giản bằng "highpass"\r\n', '<p style="text-align: center;">\r\n	<a href="http://dohoavn.net/wp-content/uploads/2014/01/Untitled-2-4-1.jpg" target="_blank"><img alt="Làm nét ảnh trong photoshop" class="aligncenter  wp-image-7291" height="465" src="http://dohoavn.net/wp-content/uploads/2014/01/Untitled-2-4-1.jpg" style="border: 0px;" title="Làm nét ảnh trong photoshop" width="620" /></a></p>\r\n<p style="text-align: left;">\r\n	ch&agrave;o mọi người,đầu năm cũng n&ecirc;n c&oacute; g&igrave; đ&oacute; mời b&agrave; con chứ nhỉ.<br />\r\n	D&aacute;m chắc rằng chi&ecirc;u n&agrave;y rất &iacute;t người biết<br />\r\n	L&acirc;u nay muốn l&agrave;m n&eacute;t một tấm h&igrave;nh ,c&oacute; rất nhiều c&aacute;ch,đ&agrave;nh rằng kh&ocirc;ng phải c&aacute;ch n&agrave;o cũng giống c&aacute;ch n&agrave;o,kh&ocirc;ng phải c&aacute;ch n&agrave;o cũng đều cho kết quả giống nhau:<br />\r\n	Tuy nhi&ecirc;n ,chung quy cũng chỉ l&agrave; sử dụng c&aacute;c bộ lọc<br />\r\n	-Của Nik th&igrave; c&oacute; Shapener Pro<br />\r\n	-cũng l&agrave;m net được tr&ecirc;n Neat Image<br />\r\n	-Đặc biệt ,c&oacute; Focut Magic,rất hiệu quả<br />\r\n	T&iacute;ch hợp sẵn th&igrave; c&oacute; Sharpen<br />\r\n	Nhưng m&igrave;nh kh&ocirc;ng hướng dẫn những c&aacute;i đ&oacute;,m&agrave; h&ocirc;m nay sẽ chỉ c&aacute;ch l&agrave;m cực kỳ đơn giản v&agrave; hi&ecirc;uh quả,lại nhanh một c&aacute;ch bất ngờ<br />\r\n	lấy một tấm ảnh n&agrave;o đ&oacute;,<br />\r\n	bạn h&atilde;y l&agrave;m mịn da bằng neat Image ,hoặc Portraiture&hellip;hay một tầm ảnh chụp bị mờ&hellip;<br />\r\n	N&oacute;i t&oacute;m lại l&agrave; &ldquo;MỜ&rdquo;<br />\r\n	h&atilde;y l&agrave;m n&eacute;t :<br />\r\n	1/nh&acirc;n đ&ocirc;i&nbsp;<acronym title="Trong Photoshop, moi hinh nam tren 1 layer, layer duoc hieu la 1 Trang giay chua hinh` anh?">layer</acronym>&nbsp;đ&oacute; l&ecirc;n<br />\r\n	2/chuyển th&agrave;nh đen trắng(shif+ctrl+u)<br />\r\n	3/Filter&gt;other&gt;other high Pass&hellip;chỉ cần nh&igrave;n thấy n&eacute;t viền khu&ocirc;n mặt<br />\r\n	4/chế độ h&ograve;a trộn Overlay<br />\r\n	Ch&uacute; &yacute;:Nếu thấy chưa n&eacute;t,h&atilde;y copy th&ecirc;m v&agrave;i lần cho đến khi ok</p>\r\n<p style="text-align: left;">\r\n	<a href="http://dohoavn.net/wp-content/uploads/2014/01/Untitled-2-4.jpg" target="_blank"><img alt="Làm nét ảnh trong photoshop" class="aligncenter  wp-image-7292" height="465" src="http://dohoavn.net/wp-content/uploads/2014/01/Untitled-2-4.jpg" style="border: 0px;" title="Làm nét ảnh trong photoshop" width="620" /></a></p>\r\n<p>\r\n	cực kỳ đơn giản nh&eacute;</p>\r\n<div style="overflow: hidden; color: rgb(0, 0, 0); background-color: rgb(255, 255, 255); text-decoration: none; border: medium none; text-align: right;">\r\n	<strong>dohoavn.net</strong></div>\r\n', '12:53:21', 22, '', 18);
