INSERT INTO `thu_tuc` VALUES (44, 'FILE PDF: Bộ Thủ tục hành chính thuộc thẩm quyền giải quyết của Sở Thông tin và Truyền thông ', '', '', '', '/data/files/Tai%20lieu/2478_QD_UBND_20161126035353340340_01_01_H52.pdf', '2017-10-11 09:55:43', 1, 1, 0, 0, '', '', '', '', '', '', '', '', '', '');
INSERT INTO `thu_tuc` VALUES (76, 'Cho phép họp báo (nước ngoài)', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/27_%20Cho%20phep%20hop%20bao%20nuoc%20ngoai.doc', '2017-04-20 16:06:14', 1, 1, 3, 5, 'Bước 1: Cơ quan, tổ chức nước ngoài tại Việt Nam, đoàn khách nước ngoài thăm Việt Nam theo lời mời của các cơ quan Bộ, ngành địa phương hoặc cơ quan nhà nước khác của Việt Nam có nhu cầu tổ chức họp báo tại địa bàn tỉnh Sơn La gửi đơn đề nghị đến bộ phận tiếp nhận và trả kết quả Sở Thông tin và Truyền thông tỉnh Sơn La (đường Hoàng Quốc Việt, phường Chiềng Cơi, thành phố Sơn La, tỉnh Sơn La) ít nhất 02 ngày làm việc trước khi tiến hành họp báo. \r\nBước 2: Sau 02 ngày kể từ ngày nhận được đơn đề nghị, nếu không có ý kiến của Sở Thông tin và Truyền thông thì cuộc họp báo coi như được chấp thuận. Cơ quan đại diện nước ngoài tổ chức họp báo tại địa bàn tỉnh Sơn La đồng gửi đơn đề nghị gửi Bộ Ngoại giao để thông báo.\r\n', '<p>\r\n	&nbsp;Nộp trực tiếp hoặc qua đường bưu ch&iacute;nh đến bộ phận tiếp nhận v&agrave; trả kết quả Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Sơn La.</p>\r\n', '', '<p>\r\n	+ Luật B&aacute;o ch&iacute;; Luật sửa đổi, bổ sung một số điều của Luật B&aacute;o ch&iacute;;</p>\r\n<p>\r\n	+ Nghị định 51/2002/NĐ-CP ng&agrave;y 26/4/2002 của Ch&iacute;nh phủ quy định chi tiết thi h&agrave;nh Luật B&aacute;o ch&iacute;; Luật sửa đổi, bổ sung một số điều của Luật B&aacute;o ch&iacute;;</p>\r\n<p>\r\n	+ Nghị định số 88/2012/NĐ-CP ng&agrave;y 23/10/2012 của Ch&iacute;nh phủ về hoạt động th&ocirc;ng tin, b&aacute;o ch&iacute; của b&aacute;o ch&iacute; nước ngo&agrave;i, cơ quan đại diện nước ngo&agrave;i, tổ chức nước ngo&agrave;i tại Việt Nam;</p>\r\n<p>\r\n	<strong>+ </strong>Th&ocirc;ng tư số 04/2014/TT-BTTTT ng&agrave;y 19/3/2014 của Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định chi tiết v&agrave; hướng dẫn thực hiện một số Điều của Nghị định số 88/2012/ NĐ-CP ng&agrave;y 23/10/2012 của Ch&iacute;nh phủ về hoạt động th&ocirc;ng tin, b&aacute;o ch&iacute; của b&aacute;o ch&iacute; nước ngo&agrave;i, cơ quan đại diện nước ngo&agrave;i, tổ chức nước ngo&agrave;i tại Việt Nam./.</p>\r\n', '<p>\r\n	- Th&agrave;nh phần hồ sơ: Đơn đề nghị (theo mẫu).</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ.</p>\r\n', '02 ngày kể từ ngày nhận được hồ sơ hợp lệ.', 'Tổ chức.', '', 'Không', '<p>\r\n	+ Nội dung họp b&aacute;o phải ph&ugrave; hợp với chức năng, nhiệm vụ v&agrave; mục đ&iacute;ch của tổ chức đ&oacute;.</p>\r\n<p>\r\n	+ Kh&ocirc;ng vi phạm c&aacute;c quy định tại Điều 10 Luật B&aacute;o ch&iacute;.</p>\r\n<p>\r\n	+ Phải đăng k&yacute; bằng văn bản chậm nhất trước 48 giờ.</p>\r\n');
INSERT INTO `thu_tuc` VALUES (77, 'Phát hành thông cáo báo chí', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/28_%20Phat%20hanh%20thong%20cao%20bao%20chi.doc', '2017-04-20 16:07:58', 1, 1, 3, 5, 'Bước 1: Cơ quan, tổ chức nước ngoài tại Việt Nam có trụ sở tại tỉnh Sơn La không tổ chức họp báo nhưng có nhu cầu phát hành thông cáo báo chí gửi hồ sơ đến bộ phận tiếp nhận và trả kết quả Sở Thông tin và Truyền thông tỉnh Sơn La (đường Hoàng Quốc Việt, phường Chiềng Cơi, thành phố Sơn La, tỉnh Sơn La). \r\nBước 2: Sau 24 tiếng đồng hồ kể từ khi nhận được hồ sơ hợp lệ đề nghị cấp phép phát hành thông cáo báo chí, Sở thông tin và Truyền thông không có văn bản trả lời thì cơ quan, tổ chức đó được phép phát hành thông cáo báo chí.\r\n', '<p>\r\n	Nộp trực tiếp hoặc qua đường bưu ch&iacute;nh đến Bộ phận tiếp nhận v&agrave; trả kết quả Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Sơn La.</p>\r\n', '', '<p>\r\n	+ Luật B&aacute;o ch&iacute;; Luật sửa đổi, bổ sung một số điều của Luật B&aacute;o ch&iacute;;</p>\r\n<p>\r\n	+ Nghị định 51/2002/NĐ-CP ng&agrave;y 26/4/2002 của Ch&iacute;nh phủ quy định chi tiết thi h&agrave;nh Luật B&aacute;o ch&iacute;; Luật sửa đổi, bổ sung một số điều của Luật B&aacute;o ch&iacute;;</p>\r\n<p>\r\n	+ Nghị định số 88/2012/NĐ-CP ng&agrave;y 23/10/2012 của Ch&iacute;nh phủ về hoạt động th&ocirc;ng tin, b&aacute;o ch&iacute; của b&aacute;o ch&iacute; nước ngo&agrave;i, cơ quan đại diện nước ngo&agrave;i, tổ chức nước ngo&agrave;i tại Việt Nam;</p>\r\n<p>\r\n	+ Th&ocirc;ng tư số 04/2014/TT-BTTTT ng&agrave;y 19/3/2014 của Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định chi tiết v&agrave; hướng dẫn thực hiện một số Điều của Nghị định số 88/2012/ NĐ-CP ng&agrave;y 23/10/2012 của Ch&iacute;nh phủ về hoạt động th&ocirc;ng tin, b&aacute;o ch&iacute; của b&aacute;o ch&iacute; nước ngo&agrave;i, cơ quan đại diện nước ngo&agrave;i, tổ chức nước ngo&agrave;i tại Việt Nam;</p>\r\n<p>\r\n	+ Th&ocirc;ng tư số 21/2011/TT-BTTTT ng&agrave;y 13/7/2011 của Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng sửa đổi, bổ sung một số quy định của Th&ocirc;ng tư số 13/2011/TT-BTTTT ng&agrave;y 31/12/2008 hướng dẫn việc th&agrave;nh lập v&agrave; hoạt động của cơ quan đại diện, ph&oacute;ng vi&ecirc;n thường tr&uacute; ở trong nước của c&aacute;c cơ quan b&aacute;o ch&iacute; v&agrave; Quyết định số 28/2002/QĐ-BVHTT ng&agrave;y 21/11/2002 của Bộ trưởng Bộ Văn h&oacute;a - Th&ocirc;ng tin ban h&agrave;nh quy chế xuất bản bản tin, t&agrave;i liệu, tờ rơi; ph&aacute;t h&agrave;nh th&ocirc;ng c&aacute;o b&aacute;o ch&iacute;; đăng, ph&aacute;t bản tin tr&ecirc;n m&agrave;n h&igrave;nh điện tử của c&aacute;c cơ quan, tổ chức nước ngo&agrave;i, ph&aacute;p nh&acirc;n c&oacute; yếu tố nước ngo&agrave;i tại Việt Nam;</p>\r\n<p>\r\n	+ Quyết định số 20/2007/QĐ-BVHTT ng&agrave;y 09/7/2007 của Bộ trưởng Bộ Văn h&oacute;a Th&ocirc;ng tin về việc b&atilde;i bỏ một số quy định về thủ tục trong hồ sơ xin cấp giấy ph&eacute;p để thực hiện thủ tục một cửa;</p>\r\n<p>\r\n	+ Quyết định số 28/2002/QĐ-BVHTT ng&agrave;y 21/11/2002 của Bộ trưởng Bộ Văn h&oacute;a &ndash; Th&ocirc;ng tin ban h&agrave;nh Quy chế Xuất bản bản tin, t&agrave;i liệu, tờ rơi; ph&aacute;t h&agrave;nh th&ocirc;ng c&aacute;o b&aacute;o ch&iacute;; đăng, ph&aacute;t bản tin tr&ecirc;n m&agrave;n h&igrave;nh điện tử của c&aacute;c cơ quan, tổ chức nước ngo&agrave;i, ph&aacute;p nh&acirc;n c&oacute; yếu tố nước ngo&agrave;i tại Việt Nam./.</p>\r\n<p>\r\n	&nbsp;</p>\r\n', '<p>\r\n	- Th&agrave;nh phần hồ sơ: Đơn đề nghị (theo mẫu);</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ (bản ch&iacute;nh).</p>\r\n', 'Sau 24 tiếng đồng hồ kể từ khi nhận được hồ sơ hợp lệ.', 'Tổ chức.', '', 'Không', '<p>\r\n	+ Đối với cơ quan đại diện nước ngo&agrave;i:</p>\r\n<p>\r\n	(1). C&oacute; người chịu tr&aacute;ch nhiệm về việc ph&aacute;t h&agrave;nh th&ocirc;ng c&aacute;o b&aacute;o ch&iacute;;</p>\r\n<p>\r\n	(2). X&aacute;c định r&otilde; nội dung th&ocirc;ng tin, mục đ&iacute;ch th&ocirc;ng tin; đối tượng phục vụ của th&ocirc;ng c&aacute;o b&aacute;o ch&iacute;;</p>\r\n<p>\r\n	(3). C&oacute; trụ sở ch&iacute;nh thức v&agrave; c&aacute;c điều kiện cần thiết bảo đảm cho việc ph&aacute;t h&agrave;nh th&ocirc;ng c&aacute;o b&aacute;o ch&iacute;.</p>\r\n<p>\r\n	+ Đối với cơ quan, tổ chức nước ngo&agrave;i:</p>\r\n<p>\r\n	(1). Hoạt động hợp ph&aacute;p tại Việt Nam;</p>\r\n<p>\r\n	(2). C&oacute; người chịu tr&aacute;ch nhiệm về việc ph&aacute;t h&agrave;nh th&ocirc;ng c&aacute;o b&aacute;o ch&iacute;;</p>\r\n<p>\r\n	(3). X&aacute;c định r&otilde; nội dung th&ocirc;ng tin, mục đ&iacute;ch th&ocirc;ng tin; đối tượng phục vụ của th&ocirc;ng c&aacute;o b&aacute;o ch&iacute;;</p>\r\n<p>\r\n	(4). C&oacute; trụ sở ch&iacute;nh thức v&agrave; c&aacute;c điều kiện cần thiết bảo đảm cho việc ph&aacute;t h&agrave;nh th&ocirc;ng c&aacute;o b&aacute;o ch&iacute;.</p>\r\n');
INSERT INTO `thu_tuc` VALUES (78, 'Cấp đăng ký thu tín hiệu truyền hình nước ngoài trực tiếp từ vệ tinh', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/29_%20Cap%20dang%20ky%20thu%20tin%20hieu%20truyen%20hinh%20nuoc%20ngoai.doc', '2017-04-20 16:10:12', 1, 1, 3, 8, 'Bước 1: Tổ chức, doanh nghiệp có nhu cầu thu tín hiệu truyền hình nước ngoài trực tiếp từ vệ tinh gửi hồ sơ đến bộ phận tiếp nhận và trả kết quả Sở Thông tin và Truyền thông tỉnh Sơn La (đường Hoàng Quốc Việt, phường Chiềng Cơi, thành phố Sơn La, tỉnh Sơn La). \r\nBước 2: Trong thời hạn 15 ngày làm việc kể từ ngày nhận được hồ sơ hợp lệ, Sở Thông tin và Truyền thông tỉnh Sơn La xem xét, cấp đăng ký thu tín hiệu truyền hình nước ngoài trực tiếp từ vệ tinh. Trường hợp không cấp đăng ký, Sở Thông tin và Truyền thông có văn bản trả lời và nêu rõ lý do. \r\n', '<p>\r\n	Nộp trực tiếp hoặc qua đường bưu ch&iacute;nh đến bộ phận tiếp nhận v&agrave; trả kết quả Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Sơn La.</p>\r\n', '', '<p>\r\n	+ Luật B&aacute;o ch&iacute; ng&agrave;y 28 th&aacute;ng 12 năm 1989; Luật sửa đổi, bổ sung một số điều của Luật B&aacute;o ch&iacute; ng&agrave;y 12 th&aacute;ng 6 năm 1999;</p>\r\n<p>\r\n	+ Luật Viễn th&ocirc;ng ng&agrave;y 23 th&aacute;ng 11 năm 2009;</p>\r\n<p>\r\n	+ Nghị định số 06/2016/NĐ-CP ng&agrave;y 18/01/2016 của Ch&iacute;nh phủ về quản l&yacute;, cung cấp v&agrave; sử dụng dịch vụ ph&aacute;t thanh, truyền h&igrave;nh.</p>\r\n<p>\r\n	+ Th&ocirc;ng tư số 19/2016/TT-BTTTT ng&agrave;y 30/6/2016 của Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định c&aacute;c biểu mẫu Tờ khai đăng k&yacute;, Đơn đề nghị cấp Giấy ph&eacute;p, Giấy Chứng nhận, Giấy ph&eacute;p v&agrave; B&aacute;o c&aacute;o nghiệp vụ theo quy định tại Nghị định số 06/2016/NĐ-CP ng&agrave;y 18/01/2016 của Ch&iacute;nh phủ về quản l&yacute;, cung cấp v&agrave; sử dụng dịch vụ ph&aacute;t thanh, truyền h&igrave;nh./.</p>\r\n', '<p>\r\n	- Th&agrave;nh phần hồ sơ:</p>\r\n<p>\r\n	+ Tờ khai đăng k&yacute; thu t&iacute;n hiệu truyền h&igrave;nh nước ngo&agrave;i trực tiếp từ vệ tinh;</p>\r\n<p>\r\n	+ Bản sao c&oacute; chứng thực hoặc bản sao k&egrave;m bản gốc để đối chiếu c&aacute;c loại giấy tờ sau: Giấy ph&eacute;p th&agrave;nh lập hoặc văn bản ph&aacute;p l&yacute; tương đương đối với c&aacute;c tổ chức quy định tại Khoản 2 Điều 29 Nghị định 06/2016/NĐ-CP ng&agrave;y 18/01/2016 của Ch&iacute;nh phủ; Giấy chứng nhận đăng k&yacute; doanh nghiệp, Giấy chứng nhận đăng k&yacute; đầu tư (nếu c&oacute;)&nbsp; đối với c&aacute;c tổ chức, Thẻ thường tr&uacute; hoặc giấy tờ ph&aacute;p l&yacute; tương đương đối với hộ gia đ&igrave;nh, c&aacute; nh&acirc;n người nước ngo&agrave;i quy định tại Khoản 3 Điều 29 Nghị định 06/2016/NĐ-CP ng&agrave;y 18/01/2016 của Ch&iacute;nh phủ; văn bản đăng k&yacute; hoạt động của kh&aacute;ch sạn quy định tại điểm b Khoản 4 Điều 29 Nghị định 06/2016/NĐ-CP ng&agrave;y 18/01/2016 của Ch&iacute;nh phủ; Giấy ph&eacute;p cung cấp dịch vụ ph&aacute;t thanh, truyền h&igrave;nh trả tiền đối với doanh nghiệp quy định tại Khoản 6 Điều 29 Nghị định 06/2016/NĐ-CP ng&agrave;y 18/01/2016 của Ch&iacute;nh phủ;&nbsp;</p>\r\n<p>\r\n	+ Bản sao c&oacute; chứng thực hoặc bản sao k&egrave;m bản gốc để đối chiếu văn bản thu&ecirc;, mượn địa điểm lắp đặt thiết bị trong trường hợp thiết bị kh&ocirc;ng được lắp đặt tại địa điểm ghi trong Giấy ph&eacute;p th&agrave;nh lập, Giấy chứng nhận đăng k&yacute; đầu tư, Giấy chứng nhận đăng k&yacute; doanh nghiệp, Thẻ thường tr&uacute;, Giấy ph&eacute;p cung cấp dịch vụ ph&aacute;t thanh, truyền h&igrave;nh trả tiền.</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ (bản ch&iacute;nh).</p>\r\n', '15 ngày kể từ ngày nhận được đủ hồ sơ hợp lệ', 'Tổ chức, doanh nghiệp.', 'Giấy chứng nhận.', 'Không', '<p>\r\n	Kh&ocirc;ng.</p>\r\n');
INSERT INTO `thu_tuc` VALUES (79, 'Đăng ký sửa đổi, bổ sung giấy chứng nhận đăng ký thu tín hiệu truyền hình nước ngoài trực tiếp từ vệ tinh', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/30_%20Dang%20ky%20s%C6%B0a%20doi%20bo%20sung%20GCN%20dang%20ky%20thu%20tin%20hieu%20truyen%20hinh%20nuoc%20ngoai.doc', '2017-04-20 16:12:06', 1, 1, 3, 8, 'Bước 1: Tổ chức, doanh nghiệp có nhu cầu sửa đổi, bổ sung giấy chứng nhận đăng ký thu tín hiệu truyền hình nước ngoài trực tiếp từ vệ tinh gửi hồ sơ về bộ phận tiếp nhận và trả kết quả Sở Thông tin và Truyền thông tỉnh Sơn La (đường Hoàng Quốc Việt, phường Chiềng Cơi, thành phố Sơn La, tỉnh Sơn La). \r\nBước 2: Trong thời hạn 10 ngày làm việc kể từ ngày nhận được hồ sơ hợp lệ, Sở Thông tin và Truyền thông tỉnh Sơn La xem xét, cấp đăng ký thu tín hiệu truyền hình nước ngoài trực tiếp từ vệ tinh. Trường hợp không cấp đăng ký, Sở Thông tin và Truyền thông có văn bản trả lời và nêu rõ lý do. \r\n', '<p>\r\n	Nộp trực tiếp hoặc qua đường bưu ch&iacute;nh đến bộ phận tiếp nhận v&agrave; trả kết quả Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Sơn La.</p>\r\n', '', '<p>\r\n	+ Luật B&aacute;o ch&iacute; ng&agrave;y 28 th&aacute;ng 12 năm 1989; Luật sửa đổi, bổ sung một số điều của Luật B&aacute;o ch&iacute; ng&agrave;y 12 th&aacute;ng 6 năm 1999;</p>\r\n<p>\r\n	+ Luật Viễn th&ocirc;ng ng&agrave;y 23 th&aacute;ng 11 năm 2009;</p>\r\n<p>\r\n	+ Nghị định số 06/2016/NĐ-CP ng&agrave;y 18/01/2016 của Ch&iacute;nh phủ về quản l&yacute;, cung cấp v&agrave; sử dụng dịch vụ ph&aacute;t thanh, truyền h&igrave;nh.</p>\r\n<p>\r\n	+ Th&ocirc;ng tư số 19/2016/TT-BTTTT ng&agrave;y 30/6/2016 của Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định c&aacute;c biểu mẫu Tờ khai đăng k&yacute;, Đơn đề nghị cấp Giấy ph&eacute;p, Giấy Chứng nhận, Giấy ph&eacute;p v&agrave; B&aacute;o c&aacute;o nghiệp vụ theo quy định tại Nghị định số 06/2016/NĐ-CP ng&agrave;y 18/01/2016 của Ch&iacute;nh phủ về quản l&yacute;, cung cấp v&agrave; sử dụng dịch vụ ph&aacute;t thanh, truyền h&igrave;nh./.</p>\r\n', '<p>\r\n	Văn bản đề nghị n&ecirc;u r&otilde; nội dung cần sửa đổi, bổ sung; đối với trường hợp thay đổi địa điểm lắp đặt, cần c&oacute; bản sao c&oacute; chứng thực hoặc bản sao k&egrave;m bản gốc để đối chiếu văn bản chứng minh quyền sở hữu, thu&ecirc;, mượn địa điểm mới.</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ (bản ch&iacute;nh).</p>\r\n', '10 ngày làm việc kể từ ngày nhận đủ hồ sơ theo quy định. ', 'Tổ chức, doanh nghiệp.', 'Giấy chứng nhận.', 'Không', '<p>\r\n	Kh&ocirc;ng.</p>\r\n');
INSERT INTO `thu_tuc` VALUES (80, 'Cấp giấy phép thiết lập trang thông tin điện tử tổng hợp', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/31_%20Cap%20giay%20phep%20thiet%20lap%20trang%20TTDT%20tong%20hop.doc', '2017-04-20 16:13:58', 1, 1, 3, 8, 'Bước 1: Tổ chức, doanh nghiệp đề nghị cấp giấy phép thiết lập trang thông tin điện tử tổng hợp gửi hồ sơ đề nghị đến bộ phận tiếp nhận và trả kết quả Sở Thông tin và Truyền thông tỉnh Sơn La (đường Hoàng Quốc Việt, phường Chiềng Cơi, thành phố Sơn La, tỉnh Sơn La). \r\nBước 2: \r\n+ Trong thời hạn 15 ngày làm việc kể từ ngày nhận được hồ sơ hợp lệ, Sở Thông tin và Truyền thông xem xét cấp giấy phép thiết lập trang thông tin điện tử tổng hợp. Trường hợp từ chối, Sở Thông tin và Truyền thông có văn bản trả lời nêu rõ lý do.\r\n+ Trong thời hạn 05 ngày làm việc kể từ khi nhận được hồ sơ hợp lệ của các cơ quan báo chí địa phương, Sở Thông tin và Truyền thông thực hiện thẩm định và chuyển hồ sơ kèm theo văn bản đề nghị cấp giấy phép đến Cục Phát thanh, truyền hình và Thông tin điện tử. Trường hợp hồ sơ không hợp lệ, Sở Thông tin và Truyền thông có văn bản trả lời nêu rõ lý do. \r\n', '<p>\r\n	Nộp trực tiếp hoặc qua đường bưu ch&iacute;nh đến Bộ phận tiếp nhận v&agrave; trả kết quả Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Sơn La.</p>\r\n', '', '<p>\r\n	+ Nghị định số 72/2013/NĐ-CP ng&agrave;y 15/7/2013 của Ch&iacute;nh phủ về quản l&yacute;, cung cấp, sử dụng dịch vụ Internet v&agrave; th&ocirc;ng tin tr&ecirc;n mạng;</p>\r\n<p>\r\n	+ Th&ocirc;ng tư số 09/2014/TT-BTTTT ng&agrave;y 19/8/2014 của Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định chi tiết về hoạt động quản l&yacute;, cung cấp, sử dụng th&ocirc;ng tin tr&ecirc;n trang th&ocirc;ng tin điện tử v&agrave; mạng x&atilde; hội./.</p>\r\n', '<p>\r\n	+ Đơn đề nghị cấp giấy ph&eacute;p thiết lập trang th&ocirc;ng tin điện tử tổng hợp (theo mẫu);</p>\r\n<p>\r\n	+ Bản sao c&oacute; chứng thực một trong c&aacute;c loại giấy tờ: Giấy chứng nhận đăng k&yacute; doanh nghiệp (đối với doanh nghiệp th&agrave;nh lập theo Luật Doanh nghiệp); Giấy chứng nhận đầu tư (đối với doanh nghiệp th&agrave;nh lập theo Luật Đầu tư); Quyết định th&agrave;nh lập (đối với tổ chức kh&ocirc;ng phải doanh nghiệp); Giấy ph&eacute;p hoạt động b&aacute;o ch&iacute; (đối với cơ quan b&aacute;o ch&iacute;); Điều lệ hoạt động (đối với c&aacute;c tổ chức hội, đo&agrave;n thể).</p>\r\n<p>\r\n	+ Giấy chứng nhận đăng k&yacute; doanh nghiệp, giấy chứng nhận đầu tư, Quyết định th&agrave;nh lập phải c&oacute; ng&agrave;nh, nghề, lĩnh vực kinh doanh hoặc chức năng nhiệm vụ ph&ugrave; hợp với nội dung th&ocirc;ng tin cung cấp tr&ecirc;n trang th&ocirc;ng tin điện tử tổng hợp;</p>\r\n<p>\r\n	+ Bản sao c&oacute; chứng thực bằng tốt nghiệp đại học hoặc tương đương trở l&ecirc;n v&agrave; sơ yếu l&yacute; lịch của người chịu tr&aacute;ch nhiệm quản l&yacute; nội dung c&oacute; x&aacute;c nhận của cơ quan nh&agrave; nước c&oacute; thẩm quyền, c&oacute; ảnh v&agrave; dấu gi&aacute;p lai;</p>\r\n<p>\r\n	+ Đề &aacute;n hoạt động c&oacute; chữ k&yacute;, dấu của người đứng đầu tổ chức, doanh nghiệp đề nghị cấp giấy ph&eacute;p. Đề &aacute;n bao gồm c&aacute;c nội dung ch&iacute;nh sau:</p>\r\n<p>\r\n	(1). Về nội dung th&ocirc;ng tin: Mục đ&iacute;ch cung cấp th&ocirc;ng tin; nội dung th&ocirc;ng tin, c&aacute;c chuy&ecirc;n mục dự kiến; nguồn tin ch&iacute;nh thức, bản in trang chủ v&agrave; c&aacute;c trang chuy&ecirc;n mục ch&iacute;nh;</p>\r\n<p>\r\n	(2). Phương &aacute;n tổ chức, nh&acirc;n sự, kỹ thuật, quản l&yacute; th&ocirc;ng tin, t&agrave;i ch&iacute;nh nhằm bảo đảm hoạt động của trang th&ocirc;ng tin điện tử tổng hợp ph&ugrave; thuộc với c&aacute;c quy định tại khoản 5 Điều 23 Nghị định số 72/2013/NĐ-CP v&agrave; đ&aacute;p ứng c&aacute;c điều kiện quy định tại c&aacute;c Điều 3, 4, 5 Th&ocirc;ng tư số 09/2014/TT-BTTTT;</p>\r\n<p>\r\n	(3). Địa điểm đặt m&aacute;y chủ tại Việt Nam;</p>\r\n<p>\r\n	+ Văn bản chấp thuận của c&aacute;c tổ chức cung cấp nguồn tin đảm bảo t&iacute;nh hợp ph&aacute;p của nguồn tin.</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ.</p>\r\n', '15 ngày kể từ ngày nhận được đủ hồ sơ hợp lệ', 'Tổ chức.', 'Giấy phép.', 'Không', '<p>\r\n	Tổ chức, doanh nghiệp được cấp giấy ph&eacute;p thiết lập trang th&ocirc;ng tin điện tử tổng hợp khi c&oacute; đủ c&aacute;c điều kiện sau đ&acirc;y:</p>\r\n<p>\r\n	1. L&agrave; tổ chức, doanh nghiệp được th&agrave;nh lập theo ph&aacute;p luật Việt Nam c&oacute; chức năng, nhiệm vụ hoặc đăng k&yacute; kinh doanh ng&agrave;nh nghề ph&ugrave; hợp với dịch vụ v&agrave; nội dung th&ocirc;ng tin cung cấp;</p>\r\n<p>\r\n	2. C&oacute; nh&acirc;n sự quản l&yacute; đ&aacute;p ứng c&aacute;c y&ecirc;u cầu sau:</p>\r\n<p>\r\n	2.1. Điều kiện về nh&acirc;n sự chịu tr&aacute;ch nhiệm quản l&yacute; nội dung th&ocirc;ng tin:</p>\r\n<p>\r\n	+ Người đứng đầu tổ chức, doanh nghiệp l&agrave; người chịu tr&aacute;ch nhiệm trước ph&aacute;p luật về quản l&yacute; nội dung cung cấp tr&ecirc;n trang th&ocirc;ng tin điện tử tổng hợp;</p>\r\n<p>\r\n	+ Người chịu tr&aacute;ch nhiệm quản l&yacute; nội dung phải tốt nghiệp đại học hoặc tương đương trở l&ecirc;n, c&oacute; quốc tịch Việt Nam. Đối với người nước ngo&agrave;i, c&oacute; địa chỉ tạm tr&uacute; &iacute;t nhất 6 th&aacute;ng tại Việt Nam;</p>\r\n<p>\r\n	+ Người chịu tr&aacute;ch nhiệm quản l&yacute; nội dung c&oacute; tr&aacute;ch nhiệm cung cấp số điện thoại li&ecirc;n lạc thường xuy&ecirc;n, địa chỉ email cho cơ quan quản l&yacute; nh&agrave; nước ở trung ương v&agrave; địa phương để phối hợp xử l&yacute; ngay khi cần thiết;</p>\r\n<p>\r\n	+ Người đứng đầu tổ chức, doanh nghiệp c&oacute; thể giao nhiệm vụ cho cấp ph&oacute; chịu tr&aacute;ch nhiệm quản l&yacute; nội dung th&ocirc;ng tin;</p>\r\n<p>\r\n	+ Tổ chức, doanh nghiệp thiết lập trang th&ocirc;ng tin điện tử tổng hợp phải th&agrave;nh lập bộ phận quản l&yacute; nội dung th&ocirc;ng tin.</p>\r\n<p>\r\n	2.2.&nbsp; Điều kiện về nh&acirc;n sự bộ phận kỹ thuật: Bộ phận quản l&yacute; kỹ thuật tối thiểu c&oacute; 01 người đ&aacute;p ứng quy định tại điểm g, điểm h khoản 2 Điều 2 Th&ocirc;ng tư số 02/3014/TT-BTTTT ng&agrave;y 11/3/2013 của Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng ban h&agrave;nh quy chuẩn kỹ năng sử dụng c&ocirc;ng nghệ th&ocirc;ng tin.</p>\r\n<p>\r\n	3. Đ&atilde; đăng k&yacute; t&ecirc;n miền sử dụng để thiết lập trang th&ocirc;ng tin điện tử tổng hợp:</p>\r\n<p>\r\n	+ Đối với tổ chức, doanh nghiệp kh&ocirc;ng phải l&agrave; cơ quan b&aacute;o ch&iacute;, d&atilde;y k&yacute; tự tạo n&ecirc;n t&ecirc;n miền kh&ocirc;ng được giống hoặc tr&ugrave;ng với t&ecirc;n cơ quan b&aacute;o ch&iacute;;</p>\r\n<p>\r\n	+ Trang th&ocirc;ng tin điện tử tổng hợp v&agrave; mạng x&atilde; hội của c&ugrave;ng một tổ chức, doanh nghiệp kh&ocirc;ng được sử dụng c&ugrave;ng một t&ecirc;n miền;</p>\r\n<p>\r\n	+ T&ecirc;n miền phải c&ograve;n thời hạn sử dụng &iacute;t nhất 06 th&aacute;ng tại thời điểm đề nghị cấp ph&eacute;p v&agrave; phải tu&acirc;n thủ theo đ&uacute;ng quy định của Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định về quản l&yacute; v&agrave; sử dụng t&agrave;i nguy&ecirc;n Internet.</p>\r\n<p>\r\n	4. C&oacute; đủ khả năng t&agrave;i ch&iacute;nh, kỹ thuật, tổ chức, nh&acirc;n sự ph&ugrave; hợp với quy m&ocirc; hoạt động:</p>\r\n<p>\r\n	4.1. Tổ chức, doanh nghiệp đề nghị cấp giấy ph&eacute;p trang th&ocirc;ng tin điện tử tổng hợp phải c&oacute; phương &aacute;n t&agrave;i ch&iacute;nh bảo đảm thiết lập hệ thống thiết bị kỹ thuật để duy tr&igrave; hoạt động trong thời gian giấy ph&eacute;p c&oacute; hiệu lực.</p>\r\n<p>\r\n	4.2. Điều kiện về kỹ thuật: thiết lập hệ thống thiết bị kỹ thuật c&oacute; khả năng đ&aacute;p ứng c&aacute;c y&ecirc;u cầu sau:</p>\r\n<p>\r\n	+ Lưu trữ tối thiểu 90 (ch&iacute;n mươi) ng&agrave;y đối với nội dung th&ocirc;ng tin tổng hợp kể từ thời điểm đăng tải; lưu trữ tối thiểu 02 (hai) năm đối với nhật k&yacute; xử l&yacute; th&ocirc;ng tin được đăng tải;</p>\r\n<p>\r\n	+ Tiếp nhận v&agrave; xử l&yacute; cảnh b&aacute;o th&ocirc;ng tin vi phạm từ người sử dụng;</p>\r\n<p>\r\n	+ Ph&aacute;t hiện, cảnh b&aacute;o v&agrave; ngăn chặn truy nhập bất hợp ph&aacute;p, c&aacute;c h&igrave;nh thức tấn c&ocirc;ng tr&ecirc;n m&ocirc;i trường mạng v&agrave; tu&acirc;n theo những ti&ecirc;u chuẩn đảm bảo an to&agrave;n th&ocirc;ng tin;</p>\r\n<p>\r\n	+ C&oacute; phương &aacute;n dự ph&ograve;ng bảo đảm duy tr&igrave; hoạt động an to&agrave;n, li&ecirc;n tục v&agrave; khắc phục khi c&oacute; sự cố xảy ra, trừ những trường hợp bất khả kh&aacute;ng theo quy định của ph&aacute;p luật;</p>\r\n<p>\r\n	+ Đảm bảo phải c&oacute; &iacute;t nhất 01 (một) hệ thống m&aacute;y chủ đặt tại Việt Nam, cho ph&eacute;p tại thời điểm bất kỳ cũng c&oacute; thể đ&aacute;p ứng việc thanh tra, kiểm tra, lưu trữ, cung cấp th&ocirc;ng tin&nbsp; tr&ecirc;n to&agrave;n bộ c&aacute;c trang th&ocirc;ng tin điện tử do tổ chức, doanh nghiệp sở hữu.</p>\r\n<p>\r\n	5. C&oacute; biện ph&aacute;p bảo đảm an to&agrave;n th&ocirc;ng tin v&agrave; an ninh th&ocirc;ng tin:</p>\r\n<p>\r\n	+ C&oacute; quy tr&igrave;nh quản l&yacute; th&ocirc;ng tin c&ocirc;ng cộng: X&aacute;c định phạm vi nguồn th&ocirc;ng tin khai th&aacute;c, cơ chế quản l&yacute;, kiểm tra th&ocirc;ng tin trước v&agrave; sau khi đăng tải;</p>\r\n<p>\r\n	+ C&oacute; cơ chế kiểm so&aacute;t nguồn tin, đảm bảo th&ocirc;ng tin tổng hợp đăng tải phải ch&iacute;nh x&aacute;c theo đ&uacute;ng th&ocirc;ng tin nguồn;</p>\r\n<p>\r\n	+ C&oacute; cơ chế phối hợp để c&oacute; thể loại bỏ ngay nội dung vi phạm khoản 1 Điều 5 Nghị định số 72/2013/NĐ-CP chậm nhất sau 03 (ba) giờ kể từ khi tự ph&aacute;t hiện hoặc c&oacute; y&ecirc;u cầu của cơ quan nh&agrave; nước c&oacute; thẩm quyền (bằng văn bản, điện thoại, email).</p>\r\n');
INSERT INTO `thu_tuc` VALUES (81, 'Sửa đổi, bổ sung giấy phép thiết lập trang thông tin điện tử tổng hợp', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/32_%20Sua%20doi%2C%20bo%20sung%20GP%20thiet%20lap%20trang%20TTDT%20tong%20hop.doc', '2017-04-20 16:16:01', 1, 1, 3, 8, 'Bước 1: Tổ chức, doanh nghiệp có giấy phép thiết lập trang thông tin điện tử tổng hợp phải thực hiện đề nghị sửa đổi, bổ sung giấy phép trong những trường hợp sau:\r\n+ Thay đổi tên, địa chỉ trụ sở chính của tổ chức, doanh nghiệp;\r\n+ Thay đổi tên miền;\r\n+ Thay đổi địa điểm đặt máy chủ tại Việt Nam;\r\n+ Thay đổi phạm vi cung cấp thông tin, dịch vụ;\r\n+ Thay đổi nhân sự chịu trách nhiệm chính;\r\n+ Thay đổi, bổ sung lĩnh vực thông tin cung cấp đối với trang thông tin điện tử tổng hợp.\r\nHồ sơ của tổ chức, doanh nghiệp đề nghị sửa đổi, bổ sung gửi đến bộ phận tiếp nhận và trả kết quả Sở Thông tin và Truyền thông tỉnh Sơn La (đường Hoàng Quốc Việt, phường Chiềng Cơi, thành phố Sơn La, tỉnh Sơn La). \r\nBước 2: Trong thời hạn 15 ngày Sở Thông tin và Truyền thông xem xét, cấp giấy phép; trường hợp từ chối Sở Thông tin và Truyền thông sẽ có văn bản trả lời nêu rõ lý do.\r\n', '<p>\r\n	Nộp trực tiếp hoặc qua đường bưu ch&iacute;nh đến bộ phận tiếp nhận v&agrave; trả kết quả Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Sơn La.</p>\r\n', '', '<p>\r\n	+ Nghị định số 72/2013/NĐ-CP ng&agrave;y 15/7/2013 của Ch&iacute;nh phủ về quản l&yacute;, cung cấp, sử dụng dịch vụ Internet v&agrave; th&ocirc;ng tin tr&ecirc;n mạng;</p>\r\n<p>\r\n	+ Th&ocirc;ng tư số 09/2014/TT-BTTTT ng&agrave;y 19/8/2014 của Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định chi tiết về hoạt động quản l&yacute;, cung cấp, sử dụng th&ocirc;ng tin tr&ecirc;n trang th&ocirc;ng tin điện tử v&agrave; mạng x&atilde; hội./</p>\r\n', '<p>\r\n	- Th&agrave;nh phần hồ sơ:</p>\r\n<p>\r\n	+ Văn bản đề nghị (n&ecirc;u r&otilde; nội dung, l&yacute; do cần sửa đổi, bổ sung);</p>\r\n<p>\r\n	+ C&aacute;c t&agrave;i liệu chứng minh c&oacute; li&ecirc;n quan đến cơ quan cấp giấy ph&eacute;p.</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ.</p>\r\n', '15 ngày kể từ ngày nhận được đủ hồ sơ hợp lệ', 'Tổ chức.', 'Giấy phép.', 'Không', '<p>\r\n	Kh&ocirc;ng</p>\r\n');
INSERT INTO `thu_tuc` VALUES (82, 'Gia hạn giấy phép thiết lập trang thông tin điện tử tổng hợp', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/33_%20Gia%20han%20GP%20thiet%20lap%20trang%20TTDT%20tong%20hop.doc', '2017-04-20 16:17:35', 1, 1, 3, 8, 'Bước 1: 30 ngày trước khi hết hạn giấy phép thiết lập trang thông tin điện tử tổng hợp, tổ chức, doanh nghiệp muốn gia hạn giấy phép đã được cấp gửi hồ sơ đề nghị gia hạn giấy phép đến bộ phận tiếp nhận và trả kết quả Sở Thông tin và Truyền thông tỉnh Sơn La (đường Hoàng Quốc Việt, phường Chiềng Cơi, thành phố Sơn La, tỉnh Sơn La). \r\nBước 2: Trong thời hạn 15 ngày Sở Thông tin và Truyền thông xem xét, cấp giấy phép. Trong trường hợp từ chối Sở Thông tin và Truyền thông sẽ có văn bản trả lời nêu rõ lý do.\r\n', '<p>\r\n	Nộp trực tiếp hoặc qua đường bưu ch&iacute;nh đến bộ phận tiếp nhận v&agrave; trả kết quả Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Sơn La.</p>\r\n', '', '<p>\r\n	<strong>+ </strong>Nghị định số 72/2013/NĐ-CP ng&agrave;y 15/7/2013 của Ch&iacute;nh phủ về quản l&yacute;, cung cấp, sử dụng dịch vụ Internet v&agrave; th&ocirc;ng tin tr&ecirc;n mạng;</p>\r\n<p>\r\n	+ Th&ocirc;ng tư số 09/2014/TT-BTTTT ng&agrave;y 19/8/2014 của Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định chi tiết về hoạt động quản l&yacute;, cung cấp, sử dụng th&ocirc;ng tin tr&ecirc;n trang th&ocirc;ng tin điện tử v&agrave; mạng x&atilde; hội./.</p>\r\n', '<p>\r\n	- Th&agrave;nh phần hồ sơ:</p>\r\n<p>\r\n	<strong>+ </strong>Văn bản đề nghị gia hạn (n&ecirc;u r&otilde; thời gian gia hạn);</p>\r\n<p>\r\n	+ Bản sao giấy ph&eacute;p.</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ.</p>\r\n', '15 ngày kể từ ngày nhận được đủ hồ sơ hợp lệ', 'Tổ chức.', 'Giấy phép.', 'Không', '<p>\r\n	- Th&agrave;nh phần hồ sơ:</p>\r\n<p>\r\n	<strong>+ </strong>Văn bản đề nghị gia hạn (n&ecirc;u r&otilde; thời gian gia hạn);</p>\r\n<p>\r\n	+ Bản sao giấy ph&eacute;p.</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ.</p>\r\n');
INSERT INTO `thu_tuc` VALUES (83, 'Cấp lại giấy phép thiết lập trang thông tin điện tử tổng hợp', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/34_%20Cap%20lai%20GP%20thiet%20lap%20trang%20TTDT%20Tong%20hop.doc', '2017-04-20 16:19:41', 1, 1, 3, 8, 'Bước 1: Trường hợp giấy phép thiết lập trang thông tin điện tử tổng hợp bị mất hoặc bị hư hỏng không còn sử dụng được, tổ chức, doanh nghiệp có giấy phép gửi hồ sơ đề nghị cấp lại giấy phép đến bộ phận tiếp nhận và trả kết quả Sở Thông tin và Truyền thông tỉnh Sơn La (đường Hoàng Quốc Việt, phường Chiềng Cơi, thành phố Sơn La, tỉnh Sơn La). \r\nBước 2: Trong thời hạn 10 ngày, Sở Thông tin và Truyền thông xem xét, cấp giấy phép; trường hợp từ chối Sở Thông tin và Truyền thông có văn bản trả lời nêu rõ lý do.\r\n', '<p>\r\n	Nộp trực tiếp hoặc qua đường bưu ch&iacute;nh đến bộ phận tiếp nhận v&agrave; trả kết quả Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Sơn La</p>\r\n', '', '<p>\r\n	+ Nghị định số 72/2013/NĐ-CP ng&agrave;y 15/7/2013 của Ch&iacute;nh phủ về quản l&yacute;, cung cấp, sử dụng dịch vụ Internet v&agrave; th&ocirc;ng tin tr&ecirc;n mạng;</p>\r\n<p>\r\n	+ Th&ocirc;ng tư số 09/2014/TT-BTTTT ng&agrave;y 19/8/2014 của Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định chi tiết về hoạt động quản l&yacute;, cung cấp, sử dụng th&ocirc;ng tin tr&ecirc;n trang th&ocirc;ng tin điện tử v&agrave; mạng x&atilde; hội./.</p>\r\n', '<p>\r\n	- Th&agrave;nh phần hồ sơ:</p>\r\n<p>\r\n	+ Văn bản đề nghị cấp lại giấy ph&eacute;p (n&ecirc;u r&otilde; số giấy ph&eacute;p, ng&agrave;y cấp của giấy ph&eacute;p đ&atilde; cấp v&agrave; l&yacute; do đề nghị cấp lại giấy ph&eacute;p);</p>\r\n<p>\r\n	+ Trường hợp giấy ph&eacute;p bị hư hỏng th&igrave; gửi k&egrave;m theo bản giấy ph&eacute;p bị hư hỏng.</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ.</p>\r\n', '10 ngày làm việc kể từ ngày nhận được hồ sơ hợp lệ.', 'Tổ chức.', 'Giấy phép.', 'Không', '<p>\r\n	Kh&ocirc;ng</p>\r\n');
INSERT INTO `thu_tuc` VALUES (84, 'Báo cáo tình hình thực hiện giấy phép thiết lập trang thông tin điện tử tổng hợp trên mạng', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/35_%20Bao%20cao%20tinh%20hinh%20thuc%20hien%20GP%20thiet%20lap%20trang%20TTDT.doc', '2017-04-20 16:21:49', 1, 1, 3, 8, '\r\nCác tổ chức, doanh nghiệp thiết lập trang thông tin điện tử tổng hợp thực hiện chế độ báo cáo định kỳ mỗi năm một lần và báo cáo đột xuất theo yêu cầu của Sở Thông tin và Truyền thông.\r\nTổ chức, doanh nghiệp thiết lập trang thông tin điện tử tổng hợp có trách nhiệm gửi báo cáo trước ngày 15 tháng 01 hàng năm.\r\n', '<p>\r\n	Nộp trực tiếp hoặc qua đường bưu ch&iacute;nh đến Bộ phận tiếp nhận v&agrave; trả kết quả Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Sơn La.</p>\r\n', '', '<p>\r\n	+ Nghị định số 72/2013/NĐ-CP ng&agrave;y 15/7/2013 của Ch&iacute;nh phủ về quản l&yacute;, cung cấp, sử dụng dịch vụ Internet v&agrave; th&ocirc;ng tin tr&ecirc;n mạng;</p>\r\n<p>\r\n	+ Th&ocirc;ng tư số 09/2014/TT-BTTTT ng&agrave;y 19/8/2014 của Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định chi tiết về hoạt động quản l&yacute;, cung cấp, sử dụng th&ocirc;ng tin tr&ecirc;n trang th&ocirc;ng tin điện tử v&agrave; mạng x&atilde; hội./.</p>\r\n', '<p>\r\n	- Th&agrave;nh phần hồ sơ:&nbsp; B&aacute;o c&aacute;o t&igrave;nh h&igrave;nh thực hiện giấy ph&eacute;p thiết lập trang th&ocirc;ng tin điện tử tổng hợp tr&ecirc;n mạng.</p>\r\n<p>\r\n	- Số lượng: 01 bản.</p>\r\n', 'Không', 'Tổ chức.', '', 'Không', '<p>\r\n	Kh&ocirc;ng</p>\r\n');
INSERT INTO `thu_tuc` VALUES (85, 'Thông báo thời gian chính thức cung cấp dịch vụ trò chơi điện tử G1 trên mạng', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/36_%20Thong%20bao%20thoi%20gian%20chinh%20thuc%20cung%20cap%20tro%20choi%20dien%20tu%20G1.doc', '2017-04-20 16:23:25', 1, 1, 3, 8, 'Bước 1: Trước khi chính thức cung cấp dịch vụ trò chơi điện tử G1 10 ngày, doanh nghiệp phải gửi thông báo cụ thể về thời gian chính thức cung cấp dịch vụ đến bộ phận tiếp nhận và trả kết quả Sở Thông tin và Truyền thông tỉnh Sơn La (đường Hoàng Quốc Việt, phường Chiềng Cơi, thành phố Sơn La, tỉnh Sơn La). \r\nBước 2: Trong thời hạn 07 ngày kể từ ngày nhận được thông báo, Sở Thông tin và Truyền thông có trách nhiệm gửi giấy biên nhận cho doanh nghiệp.\r\n', '<p>\r\n	Nộp trực tiếp hoặc qua đường bưu ch&iacute;nh đến bộ phận tiếp nhận v&agrave; trả kết quả Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Sơn La.</p>\r\n', '', '<p>\r\n	+ Nghị định số 72/2013/NĐ-CP ng&agrave;y 15/7/2013 của Ch&iacute;nh phủ về quản l&yacute;, cung cấp, sử dụng dịch vụ Internet v&agrave; th&ocirc;ng tin tr&ecirc;n mạng;</p>\r\n<p>\r\n	+ Th&ocirc;ng tư số 24/2014/TT-BTTTT ng&agrave;y 29/12/2014 của Bộ trưởng Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định chi tiết về hoạt động quản l&yacute;, cung cấp v&agrave; sử dụng dịch vụ tr&ograve; chơi điện tử tr&ecirc;n mạng</p>\r\n', '<p>\r\n	- Th&agrave;nh phần hồ sơ: Th&ocirc;ng b&aacute;o cụ thể về thời gian ch&iacute;nh thức cung cấp dịch vụ tr&ograve; chơi điện tử G1.</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ.</p>\r\n', '07 ngày làm việc kể từ ngày nhận được thông báo.', 'Tổ chức.', 'Giấy biên nhận.', 'Không', '<p>\r\n	Kh&ocirc;ng</p>\r\n');
INSERT INTO `thu_tuc` VALUES (86, 'Thông báo thay đổi trụ sở chính, văn phòng giao dịch, địa chỉ cho thuê máy chủ của doanh nghiệp cung cấp dịch vụ trò chơi điện tử G1 trên mạng', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/37_%20Thong%20bao%20thay%20doi%20tru%20so%20dia%20chi%20thue%20may%20chu%20cua%20doanh%20nghiep%20cung%20cap%20dv%20tro%20choi%20dien%20tu%20G1.doc', '2017-04-20 16:26:11', 1, 1, 3, 8, 'Bước 1: Trong thời hạn 05 ngày làm việc trước khi thay đổi địa chỉ trụ sở chính, văn phòng giao dịch, địa chỉ cho thuê máy chủ thì doanh nghiệp không phải thực hiện thủ tục sửa đổi, bổ sung giấy phép cung cấp dịch vụ trò chơi điện tử G1 nhưng phải thông báo bằng văn bản đến bộ phận tiếp nhận và trả kết quả Sở Thông tin và Truyền thông tỉnh Sơn La (đường Hoàng Quốc Việt, phường Chiềng Cơi, thành phố Sơn La, tỉnh Sơn La). \r\nBước 2: Trong thời hạn 07 ngày kể từ ngày nhận được thông báo, Sở Thông tin và Truyền thông có trách nhiệm gửi giấy biên nhận cho doanh nghiệp\r\n', '<p>\r\n	Nộp trực tiếp hoặc qua đường bưu ch&iacute;nh đến bộ phận tiếp nhận v&agrave; trả kết quả Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Sơn La.</p>\r\n', '', '<p>\r\n	+ Nghị định số 72/2013/NĐ-CP ng&agrave;y 15/7/2013 của Ch&iacute;nh phủ về quản l&yacute;, cung cấp, sử dụng dịch vụ Internet v&agrave; th&ocirc;ng tin tr&ecirc;n mạng;</p>\r\n<p>\r\n	+ Th&ocirc;ng tư số 24/2014/TT-BTTTT ng&agrave;y 29/12/2014 của Bộ trưởng Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định chi tiết về hoạt động quản l&yacute;, cung cấp v&agrave; sử dụng dịch vụ tr&ograve; chơi điện tử tr&ecirc;n mạng./.</p>\r\n', '<p>\r\n	<strong>- </strong>Th&agrave;nh phần hồ sơ: Th&ocirc;ng b&aacute;o thay đổi trụ sở ch&iacute;nh, văn ph&ograve;ng giao dịch, địa chỉ cho thu&ecirc; m&aacute;y chủ.</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ</p>\r\n', '07 ngày làm việc kể từ ngày nhận được thông báo', 'Tổ chức.', 'Giấy biên nhận.', 'Không', '<p>\r\n	Kh&ocirc;ng</p>\r\n');
INSERT INTO `thu_tuc` VALUES (87, 'Thông báo thay đổi phần vốn góp dẫn đến thay đổi thành viên góp vốn (hoặc cổ đông) có phần vốn góp từ 30% vốn điều lệ trở lên của doanh nghiệp cung cấp dịch vụ trò chơi điện tử G1 trên mạng', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/38_%20Thong%20bao%20thay%20doi%20phan%20von%20gop%20dan%20den%20thay%20doi%20thanh%20vien%20cua%20doanh%20nghiep%20cung%20cap%20G1.doc', '2017-04-20 16:29:08', 1, 1, 3, 8, 'Bước 1: Các doanh nghiệp đã được cấp giấy phép cung cấp dịch vụ trò chơi điện tử G1 khi có sự thay đổi phần vốn góp dẫn đến thay đổi thành viên góp vốn (hoặc cổ đông) có phần vốn góp từ 30% vốn điều lệ trở lên phải thông báo bằng văn bản tới bộ phận tiếp nhận và trả kết quả Sở Thông tin và Truyền thông tỉnh Sơn La (đường Hoàng Quốc Việt, phường Chiềng Cơi, thành phố Sơn La, tỉnh Sơn La) trong thời hạn 15 ngày kể từ ngày có quyết định thay đổi.\r\nBước 2: Trong thời hạn 07 ngày kể từ ngày nhận được thông báo, Sở Thông tin và Truyền thông gửi giấy biên nhận cho doanh nghiệp. \r\n', '<p>\r\n	Nộp trực tiếp hoặc qua đường bưu ch&iacute;nh đến bộ phận tiếp nhận v&agrave; trả kết quả Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Sơn La.</p>\r\n', '', '<p>\r\n	+ Nghị định số 72/2013/NĐ-CP ng&agrave;y 15/7/2013 của Ch&iacute;nh phủ về quản l&yacute;, cung cấp, sử dụng dịch vụ Internet v&agrave; th&ocirc;ng tin tr&ecirc;n mạng;</p>\r\n<p>\r\n	+ Th&ocirc;ng tư số 24/2014/TT-BTTTT ng&agrave;y 29/12/2014 của Bộ trưởng Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định chi tiết về hoạt động quản l&yacute;, cung cấp v&agrave; sử dụng dịch vụ tr&ograve; chơi điện tử tr&ecirc;n mạng</p>\r\n', '<p>\r\n	- Th&agrave;nh phần hồ sơ:&nbsp; Th&ocirc;ng b&aacute;o thay đổi phần vốn g&oacute;p dẫn đến thay đổi th&agrave;nh vi&ecirc;n g&oacute;p vốn (hoặc cổ đ&ocirc;ng) c&oacute; phần vốn g&oacute;p từ 30% vốn điều lệ trở l&ecirc;n của doanh nghiệp cung cấp dịch vụ tr&ograve; chơi điện tử G1.</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ.</p>\r\n', '07 ngày làm việc kể từ ngày nhận được thông báo', 'Tổ chức.', 'Giấy biên nhận.', 'Không', '<p>\r\n	Kh&ocirc;ng</p>\r\n');
INSERT INTO `thu_tuc` VALUES (88, 'Thông báo thời gian chính thức cung cấp trò chơi điện tử trên mạng', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/39_%20Thong%20bao%20thay%20doi%20thoi%20gian%20cung%20cap%20tro%20choi%20dien%20tu.doc', '2017-04-20 16:30:27', 1, 1, 3, 8, 'Bước 1: Trước khi doanh nghiệp chính thức bắt đầu cung cấp trò chơi công cộng 10 ngày làm việc, doanh nghiệp phải gửi thông báo cụ thể về thời gian chính thức cung cấp trò chơi tới bộ phận tiếp nhận và trả kết quả Sở Thông tin và Truyền thông tỉnh Sơn La (đường Hoàng Quốc Việt, phường Chiềng Cơi, thành phố Sơn La, tỉnh Sơn La). \r\nBước 2: Trong thời hạn 07 ngày kể từ ngày nhận được thông báo, Sở Thông tin và Truyền thông gửi giấy biên nhận cho doanh nghiệp. \r\n', '<p>\r\n	Nộp trực tiếp hoặc qua đường bưu ch&iacute;nh đến Bộ phận tiếp nhận v&agrave; trả kết quả Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Sơn La.</p>\r\n', '', '<p>\r\n	+ Nghị định số 72/2013/NĐ-CP ng&agrave;y 15/7/2013 của Ch&iacute;nh phủ về quản l&yacute;, cung cấp, sử dụng dịch vụ Internet v&agrave; th&ocirc;ng tin tr&ecirc;n mạng;</p>\r\n<p>\r\n	+ Th&ocirc;ng tư số 24/2014/TT-BTTTT ng&agrave;y 29/12/2014 của Bộ trưởng Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định chi tiết về hoạt động quản l&yacute;, cung cấp v&agrave; sử dụng dịch vụ tr&ograve; chơi điện tử tr&ecirc;n mạng./.</p>\r\n', '<p>\r\n	- Th&agrave;nh phần hồ sơ: Th&ocirc;ng b&aacute;o thời gian ch&iacute;nh thức cung cấp tr&ograve; chơi điện tử.</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ.</p>\r\n', '07 ngày làm việc kể từ ngày nhận được thông báo', 'Tổ chức.', 'Giấy biên nhận.', 'Không', '<p>\r\n	Kh&ocirc;ng</p>\r\n');
INSERT INTO `thu_tuc` VALUES (89, 'Thông báo thay đổi phương thức, phạm vi cung cấp dịch vụ trò chơi điện tử G1 trên mạng đã được phê duyệt', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/40_%20Thong%20bao%20thay%20doi%20phuong%20thuc%20cung%20cap%20G1.doc', '2017-04-20 16:31:53', 1, 1, 3, 8, 'Bước 1: Trong thời hạn 05 ngày làm việc trước khi thay đổi phương thức, phạm vi cung cấp dịch vụ trò chơi điện tử G1 đã được phê duyệt (tên miền đối với trò chơi cung cấp trên internet, kênh phân phối đối với trò chơi cung cấp trên mạng viễn thông di động) thì doanh nghiệp không phải thực hiện thủ tục sửa đổi, bổ sung quyết định nhưng phải thông báo bằng văn bản tới bộ phận tiếp nhận và trả kết quả Sở Thông tin và Truyền thông tỉnh Sơn La (đường Hoàng Quốc Việt, phường Chiềng Cơi, thành phố Sơn La, tỉnh Sơn La). \r\nBước 2: Trong thời hạn 07 ngày kể từ ngày nhận được thông báo, Sở Thông tin và Truyền thông gửi giấy biên nhận cho doanh nghiệp.\r\n', '<p>\r\n	Nộp trực tiếp hoặc qua đường bưu ch&iacute;nh đến bộ phận tiếp nhận v&agrave; trả kết quả Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Sơn La.</p>\r\n', '', '<p>\r\n	+ Nghị định số 72/2013/NĐ-CP ng&agrave;y 15/7/2013 của Ch&iacute;nh phủ về quản l&yacute;, cung cấp, sử dụng dịch vụ Internet v&agrave; th&ocirc;ng tin tr&ecirc;n mạng;</p>\r\n<p>\r\n	+ Th&ocirc;ng tư số 24/2014/TT-BTTTT ng&agrave;y 29/12/2014 của Bộ trưởng Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định chi tiết về hoạt động quản l&yacute;, cung cấp v&agrave; sử dụng dịch vụ tr&ograve; chơi điện tử tr&ecirc;n mạng./.</p>\r\n', '<p>\r\n	- Th&agrave;nh phần hồ sơ:&nbsp; Th&ocirc;ng b&aacute;o thay đổi phương thức, phạm vi cung cấp dịch vụ tr&ograve; chơi điện tử G1 đ&atilde; được ph&ecirc; duyệt.</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ.</p>\r\n', '07 ngày làm việc kể từ ngày nhận được thông báo', 'Tổ chức.', 'Giấy biên nhận.', 'Không', '<p>\r\n	Kh&ocirc;ng</p>\r\n');
INSERT INTO `thu_tuc` VALUES (90, 'Thông báo thời gian chính thức bắt đầu cung cấp trò chơi G2, G3, G4 trên mạng cho công cộng', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/41_%20Thong%20bao%20thoi%20gian%20chinh%20thuc%20cung%20cap%20G2%2C%20G3%2C%20G4.doc', '2017-04-20 16:33:25', 1, 1, 3, 8, 'Bước 1: Trước khi doanh nghiệp chính thức bắt đầu cung cấp trò chơi cho công cộng 10 ngày làm việc, doanh nghiệp phải gửi thông báo cụ thể về thời gian chính thức cung cấp dịch vụ tới gửi về bộ phận tiếp nhận và trả kết quả Sở Thông tin và Truyền thông tỉnh Sơn La (đường Hoàng Quốc Việt, phường Chiềng Cơi, thành phố Sơn La, tỉnh Sơn La). \r\nBước 2: Trong thời hạn 07 ngày kể từ ngày nhận được thông báo, Sở Thông tin và Truyền thông gửi giấy biên nhận cho doanh nghiệp. \r\n', '<p>\r\n	Nộp trực tiếp hoặc qua đường bưu ch&iacute;nh đến bộ phận tiếp nhận v&agrave; trả kết quả Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Sơn La.</p>\r\n', '', '<p>\r\n	+ Nghị định số 72/2013/NĐ-CP ng&agrave;y 15/7/2013 của Ch&iacute;nh phủ về quản l&yacute;, cung cấp, sử dụng dịch vụ Internet v&agrave; th&ocirc;ng tin tr&ecirc;n mạng;</p>\r\n<p>\r\n	+ Th&ocirc;ng tư số 24/2014/TT-BTTTT ng&agrave;y 29/12/2014 của Bộ trưởng Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định chi tiết về hoạt động quản l&yacute;, cung cấp v&agrave; sử dụng dịch vụ tr&ograve; chơi điện tử tr&ecirc;n mạng./.</p>\r\n', '<p>\r\n	- Th&agrave;nh phần hồ sơ:&nbsp; Th&ocirc;ng b&aacute;o thời gian ch&iacute;nh thức bắt đầu cung cấp tr&ograve; chơi G2, G3, G4 cho c&ocirc;ng cộng.</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ.</p>\r\n', '07 ngày làm việc kể từ ngày nhận được thông báo', 'Tổ chức.', 'Giấy biên nhận.', 'Không', '<p>\r\n	Kh&ocirc;ng</p>\r\n');
INSERT INTO `thu_tuc` VALUES (91, 'Thông báo thay đổi tên miền trang thông tin điện tử (trên internet), kênh phân phối trò chơi (trên mạng viễn thông di động), thể loại trò chơi (G2, G3, G4)', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/42_%20Thong%20bao%20thay%20doi%20ten%20mien%20trang%20TTDT%2C%20kenh%20phan%20phoi%20tro%20choi%2C%20the%20loai%20tro%20choi.doc', '2017-04-20 16:35:01', 1, 1, 3, 8, 'Bước 1: Trong thời hạn 05 ngày làm việc trước khi thay đổi tên miền trang thông tin điện tử (trên internet), kênh phân phối trò chơi (trên mạng viễn thông di động), thể loại trò chơi (G2, G3, G4) thì doanh nghiệp không phải thực hiện thủ tục sửa đổi, bổ sung giấy chứng nhận đăng ký cung cấp dịch vụ trò chơi điện tử nhưng phải thông báo bằng văn bản tới bộ phận tiếp nhận và trả kết quả Sở Thông tin và Truyền thông tỉnh Sơn La (đường Hoàng Quốc Việt, phường Chiềng Cơi, thành phố Sơn La, tỉnh Sơn La). \r\nBước 2: Trong thời hạn 07 ngày kể từ ngày nhận được thông báo, Sở Thông tin và Truyền thông gửi giấy biên nhận cho doanh nghiệp. \r\n', '<p>\r\n	Nộp trực tiếp hoặc qua đường bưu ch&iacute;nh đến bộ phận tiếp nhận v&agrave; trả kết quả Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Sơn La.</p>\r\n', '', '<p>\r\n	+ Nghị định số 72/2013/NĐ-CP ng&agrave;y 15/7/2013 của Ch&iacute;nh phủ về quản l&yacute;, cung cấp, sử dụng dịch vụ Internet v&agrave; th&ocirc;ng tin tr&ecirc;n mạng;</p>\r\n<p>\r\n	+ Th&ocirc;ng tư số 24/2014/TT-BTTTT ng&agrave;y 29/12/2014 của Bộ trưởng Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định chi tiết về hoạt động quản l&yacute;, cung cấp v&agrave; sử dụng dịch vụ tr&ograve; chơi điện tử tr&ecirc;n mạng./.</p>\r\n', '<p>\r\n	- Th&agrave;nh phần hồ sơ:&nbsp; Th&ocirc;ng b&aacute;o thay đổi t&ecirc;n miền trang th&ocirc;ng tin điện tử (tr&ecirc;n internet), k&ecirc;nh ph&acirc;n phối tr&ograve; chơi (tr&ecirc;n mạng viễn th&ocirc;ng di động), thể loại tr&ograve; chơi (G2, G3, G4).</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ.</p>\r\n', '07 ngày làm việc kể từ ngày nhận được thông báo', 'Tổ chức.', 'Giấy biên nhận.', 'Không', '<p>\r\n	Kh&ocirc;ng</p>\r\n');
INSERT INTO `thu_tuc` VALUES (92, 'Thông báo thay đổi phần vốn góp dẫn đến thay đổi thành viên góp vốn (hoặc cổ đông) có phần vốn góp từ 30% vốn điều lệ trở lên của doanh nghiệp đã được cấp giấy chứng nhận đăng ký cung cấp dịch vụ trò chơi điện tử G2, G3, G4 trên mạng', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/43_%20Thong%20bao%20thay%20doi%20phan%20von%20dan%20den%20thay%20doi%20thanh%20vien%20cua%20doanh%20nghiep%20cung%20cap%20G234.doc', '2017-04-20 16:36:35', 1, 1, 3, 8, 'Bước 1: Các doanh nghiệp đã được cấp giấy chứng nhận đăng ký cung cấp dịch vụ trò chơi điện tử G2, G3, G4 khi có sự thay đổi phần vốn góp dẫn đến thay đổi thành viên góp vốn (hoặc cổ đông) có phần vốn góp từ 30% vốn điều lệ trở lên phải thông báo bằng văn bản tới Sở Thông tin và Truyền trong thời hạn 05 ngày làm việc kể từ ngày có quyết định thay đổi. Thông báo gửi đến bộ phận tiếp nhận và trả kết quả Sở Thông tin và Truyền thông tỉnh Sơn La (đường Hoàng Quốc Việt, phường Chiềng Cơi, thành phố Sơn La, tỉnh Sơn La). \r\nBước 2: Trong thời hạn 07 ngày kể từ ngày nhận được thông báo, Sở Thông tin và Truyền thông gửi giấy biên nhận cho doanh nghiệp. \r\n', '<p>\r\n	Nộp trực tiếp hoặc qua đường bưu ch&iacute;nh đến bộ phận tiếp nhận v&agrave; trả kết quả Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Sơn La.</p>\r\n', '', '<p>\r\n	+ Nghị định số 72/2013/NĐ-CP ng&agrave;y 15/7/2013 của Ch&iacute;nh phủ về quản l&yacute;, cung cấp, sử dụng dịch vụ Internet v&agrave; th&ocirc;ng tin tr&ecirc;n mạng;</p>\r\n<p>\r\n	+ Th&ocirc;ng tư số 24/2014/TT-BTTTT ng&agrave;y 29/12/2014 của Bộ trưởng Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng quy định chi tiết về hoạt động quản l&yacute;, cung cấp v&agrave; sử dụng dịch vụ tr&ograve; chơi điện tử tr&ecirc;n mạng./.</p>\r\n', '<p>\r\n	- Th&agrave;nh phần hồ sơ:&nbsp; Th&ocirc;ng b&aacute;o thay đổi phần vốn g&oacute;p dẫn đến thay đổi th&agrave;nh vi&ecirc;n g&oacute;p vốn (hoặc cổ đ&ocirc;ng) c&oacute; phần vốn g&oacute;p từ 30% vốn điều lệ trở l&ecirc;n của doanh nghiệp đ&atilde; được cấp giấy chứng nhận đăng k&yacute; cung cấp dịch vụ tr&ograve; chơi điện tử G2, G3, G4.</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ.</p>\r\n', '07 ngày làm việc kể từ ngày nhận được thông báo', 'Tổ chức.', 'Giấy biên nhận.', 'Không', '<p>\r\n	Kh&ocirc;ng</p>\r\n');
INSERT INTO `thu_tuc` VALUES (93, 'Cấp giấy chứng nhận đủ điều kiện hoạt động điểm cung cấp dịch vụ trò chơi điện tử công cộng', '', '', '', '/data/files/Thu%20tuc%20hanh%20chinh/H1_%20Cap%20GCN%20du%20dieu%20kien%20HD%20diem%20cung%20cap%20dich%20vu%20tro%20choi%20dien%20tu.doc', '2017-04-20 16:39:19', 1, 1, 5, 10, 'Bước 1: Tổ chức, cá nhân nộp hồ sơ đề nghị cấp giấy chứng nhận đủ điều kiện hoạt động điểm cung cấp dịch vụ trò chơi điện tử công cộng gửi một bộ hồ sơ tới Bộ phận tiếp nhận và trả kết quả của cơ quan cấp giấy chứng nhận (Ủy ban nhân dân huyện, thành phố).\r\nBước 2: Trong thời hạn 10 ngày làm việc kể từ ngày nhận được hồ sơ theo quy định, cơ quan cấp giấy chứng nhận thẩm định hồ sơ, kiểm tra thực tế và cấp giấy chứng nhận đủ điều kiện hoạt động điểm cung cấp dịch vụ trò chơi điện tử công cộng.\r\nTrường hợp từ chối, cơ quan cấp giấy chứng nhận có trách nhiệm thông báo bằng văn bản trong đó nêu rõ lý do từ chối cho tổ chức, cá nhân và cơ quan cấp giấy đăng ký kinh doanh biết.', '<p>\r\n	Nộp trực tiếp hoặc qua hệ thống bưu ch&iacute;nh đến bộ phận tiếp nhận v&agrave; trả kết quả của cơ quan cấp giấy chứng nhận.</p>\r\n', '', '<p>\r\n	+ Luật Viễn th&ocirc;ng ng&agrave;y 23 th&aacute;ng 11 năm 2009;</p>\r\n<p>\r\n	+ Nghị định số 72/2013/NĐ-CP ng&agrave;y 15/7/2013 về Quản l&yacute;, cung cấp, sử dụng dịch vụ Internet v&agrave; th&ocirc;ng tin tr&ecirc;n mạng;</p>\r\n<p>\r\n	+ Th&ocirc;ng tư 23/2013/TT-BTTTT ng&agrave;y 24/12/2013 quy định về quản l&yacute; điểm truy nhập Internet c&ocirc;ng cộng v&agrave; điểm cung cấp dịch vụ tr&ograve; chơi điện tử c&ocirc;ng cộng;</p>\r\n<p>\r\n	+ Quyết định số 33/2014/QĐ-UBND, ng&agrave;y 26 th&aacute;ng 11 năm 2014 của Ủy ban nh&acirc;n d&acirc;n tỉnh Sơn La ban h&agrave;nh Quy định quy tr&igrave;nh thẩm định v&agrave; thẩm quyền cấp, sửa đổi,bổ sung, gia hạn, cấp lại, thu hồi Giấy chứng nhận đủ điều kiện hoạt động điểm cung cấp dịch vụ tr&ograve; chơi điện tử c&ocirc;ng cộng tr&ecirc;n địa b&agrave;n tỉnh Sơn La;</p>\r\n<p>\r\n	+ Quyết định số 24/2015/QĐ-UBND ng&agrave;y 30 th&aacute;ng 7 năm 2015 sửa đổi, bổ sung một số điều của Quy định quy tr&igrave;nh thẩm định v&agrave; thẩm quyền cấp, sửa đổi, bổ sung, gia hạn, cấp lại, thu hồi Giấy chứng nhận đủ điều kiện hoạt động điểm cung cấp dịch vụ tr&ograve; chơi điện tử c&ocirc;ng cộng tr&ecirc;n địa b&agrave;n tỉnh Sơn La ban h&agrave;nh k&egrave;m theo Quyết định số 33/2014/QĐ-UBND ng&agrave;y 24/11/2014 của UBND tỉnh Sơn La./.</p>\r\n', '<p>\r\n	- Th&agrave;nh phần hồ sơ:</p>\r\n<p>\r\n	+ Đơn đề nghị cấp giấy chứng nhận;</p>\r\n<p>\r\n	+ Bản sao c&oacute; chứng thực giấy đăng k&yacute; kinh doanh điểm cung cấp dịch vụ tr&ograve; chơi điện tử c&ocirc;ng cộng;</p>\r\n<p>\r\n	+ Bản sao chứng thực chứng minh nh&acirc;n d&acirc;n của chủ điểm cấp dịch vụ tr&ograve; chơi điện tử c&ocirc;ng cộng đối với trường hợp chủ điểm l&agrave; c&aacute; nh&acirc;n;</p>\r\n<p>\r\n	+ Bản sao chứng thực chứng minh nh&acirc;n d&acirc;n của người quản l&yacute; trực tiếp điểm cấp dịch vụ tr&ograve; chơi điện tử c&ocirc;ng cộng đối với trường hợp chủ điểm l&agrave; tổ chức, doanh nghiệp.</p>\r\n<p>\r\n	- Số lượng hồ sơ: 01 bộ.</p>\r\n', '10 ngày làm việc kể từ ngày nhận đủ hồ sơ theo quy định. ', 'Chủ điểm cung cấp dịch vụ trò chơi điện tử công cộng.', 'Giấy chứng nhận đủ điều kiện hoạt động điểm cung cấp dịch vụ trò chơi điện tử công cộng.', 'Không.', '<p>\r\n	Tổ chức, c&aacute; nh&acirc;n được cấp Giấy chứng nhận đủ điều kiện hoạt động điểm cung cấp dịch vụ tr&ograve; chơi điện tử c&ocirc;ng cộng khi đ&aacute;p ứng c&aacute;c điều kiện sau đ&acirc;y (Khoản 2 Điều 35 Nghị định 72/2013/NĐ-CP v&agrave; Điều 5 Th&ocirc;ng tư 23/2013/TT-BTTTT):</p>\r\n<p>\r\n	+ C&oacute; đăng k&yacute; kinh doanh điểm cung cấp dịch vụ tr&ograve; chơi điện tử c&ocirc;ng cộng.</p>\r\n<p>\r\n	+ Địa điểm cung cấp dịch vụ tr&ograve; chơi điện tử c&ocirc;ng cộng c&aacute;ch cổng trường tiểu học, trung học cơ sở, trung học phổ th&ocirc;ng từ 200 m trở l&ecirc;n. Quy định về khoảng c&aacute;ch từ địa điểm cung cấp dịch vụ tr&ograve; chơi điện tử c&ocirc;ng cộng tới cổng c&aacute;c trường học được &aacute;p dụng v&agrave; được hiểu như sau:</p>\r\n<p>\r\n	(1). &Aacute;p dụng đối với trường tiểu học, trường trung học cơ sở, trường trung học phổ th&ocirc;ng, trường phổ th&ocirc;ng c&oacute; nhiều cấp học, trung t&acirc;m gi&aacute;o dục thường xuy&ecirc;n, trường phổ th&ocirc;ng d&acirc;n tộc nội tr&uacute;, trường phổ th&ocirc;ng d&acirc;n tộc b&aacute;n tr&uacute;;</p>\r\n<p>\r\n	&nbsp;(2). Được hiểu l&agrave; chiều d&agrave;i đường bộ ngắn nhất từ cửa ch&iacute;nh hoặc cửa phụ của điểm cung cấp dịch vụ tr&ograve; chơi điện tử c&ocirc;ng cộng tới cổng ch&iacute;nh hoặc cổng phụ của trường v&agrave; trung t&acirc;m n&ecirc;u tại Điểm a Khoản 2 Điều 5 Th&ocirc;ng tư 23/2013/TT-BTTTT.&nbsp;</p>\r\n<p>\r\n	+&nbsp; C&oacute; biển hiệu &ldquo;Điểm cung cấp dịch vụ tr&ograve; chơi điện tử c&ocirc;ng cộng&rdquo; bao gồm t&ecirc;n điểm, địa chỉ, số điện thoại li&ecirc;n hệ, số đăng k&yacute; kinh doanh. Ngo&agrave;i ra, cần th&ecirc;m c&aacute;c th&ocirc;ng tin kh&aacute;c như sau:</p>\r\n<p>\r\n	(1). Trường hợp điểm cung cấp dịch vụ tr&ograve; chơi điện tử c&ocirc;ng cộng đồng thời l&agrave; đại l&yacute; Internet, th&ecirc;m c&aacute;c th&ocirc;ng tin quy định tại điểm b Khoản 1 Điều 9 Nghị định 72/2013/NĐ-CP.</p>\r\n<p>\r\n	(2). Trường hợp điểm cung cấp dịch vụ tr&ograve; chơi điện tử c&ocirc;ng cộng đồng thời l&agrave; điểm truy nhập Internet c&ocirc;ng cộng của doanh nghiệp, th&ecirc;m c&aacute;c th&ocirc;ng tin quy định tại điểm a Khoản 2 Điều 9 Nghị định 72/2013/NĐ-CP&rdquo;.</p>\r\n<p>\r\n	+&nbsp; Tổng diện t&iacute;ch c&aacute;c ph&ograve;ng m&aacute;y của điểm cung cấp dịch vụ tr&ograve; chơi điện tử c&ocirc;ng cộng tối thiểu 50 m<sup>2 </sup>tại khu vực th&agrave;nh phố Sơn La; tối thiểu l&agrave; 40 m<sup>2</sup> tại khu vực thị trấn thuộc c&aacute;c huyện; tối thiểu 30 m<sup>2</sup> tại c&aacute;c khu vực kh&aacute;c tr&ecirc;n địa b&agrave;n tỉnh.</p>\r\n<p>\r\n	+ Bảo đảm đủ &aacute;nh s&aacute;ng, độ chiếu s&aacute;ng đồng đều trong ph&ograve;ng m&aacute;y.</p>\r\n<p>\r\n	+ C&oacute; thiết bị v&agrave; nội quy ph&ograve;ng ch&aacute;y, chữa ch&aacute;y theo quy định về ph&ograve;ng, chống ch&aacute;y, nổ của Bộ C&ocirc;ng an.</p>\r\n<p>\r\n	+ Nộp lệ ph&iacute; cấp Giấy chứng nhận đủ điều kiện hoạt động điểm cung cấp dịch vụ tr&ograve; chơi điện tử c&ocirc;ng cộng.</p>\r\n');
