INSERT INTO `tin_tuc` VALUES (1428, 'VỀ VỚI ĐẤT TỔ', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><strong><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; VỀ VỚI ĐẤT TỔ</em></strong></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><strong><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bảo Nguy&ecirc;n, Hội KH Sơn la</em></strong></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Từ Sơn la, con về thăm Đất Tổ </em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em>D&acirc;ng hương thơm tưởng nhớ c&aacute;c Vua H&ugrave;ng,</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em>Nơi cội nguồn của đất nước, non s&ocirc;ng</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em>Tỏa linh kh&iacute; h&ugrave;ng thi&ecirc;ng s&ocirc;ng n&uacute;i!</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14p