INSERT INTO `tin_tuc` VALUES (1418, 'Hội Khuyến học tỉnh Sơn La phối hợp với Quỹ học bổng ISSHIN – ASAHI tổ chức sơ tuyển chương trình học bổng', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp;Ng&agrave;y 03/4/2023, Hội Khuyến học tỉnh Sơn La đ&atilde; tham gia phối hợp với đo&agrave;n c&ocirc;ng t&aacute;c <span style="background-color:white">Quỹ học bổng ISSHIN &ndash; ASAHI </span>thực hiện sơ tuyển <span style="background-color:white">chương tr&igrave;nh học bổng ISSHIN &ndash; ASAHI</span> tại 3 Trường: Trường THPT Chuy&ecirc;n Sơn La, Trường THCS&amp;THPT Nguyễn Du, Trường PTDT Nội Tr&uacute; tỉnh. Th&agrave;nh phần đo&agrave;n c&ocirc;ng t&aacute;c đại diện <span style="background-color:white">Quỹ học bổng ISSHIN - ASAHI gồm c&oacute;: &Ocirc;ng Hiroshi Hikawa to&agrave;n quyền quỹ học bổng&nbsp; ASAHI tại Việt Nam, &ocirc;ng Dương Trọng Cường, Ph&oacute; gi&aacute;m đốc C&ocirc;ng ty cổ phần ISSHIN, b&agrave; L&ecirc; Thị Nhung, trưởng bộ phận Nhật Bản C&ocirc;ng ty cổ phần ISSHIN. Về ph&iacute;a Hội Khuyến học tỉnh c&oacute; NGND Nguyễn Văn Bao Ph&oacute; Chủ tịch Hội Khuyến học tỉnh.&nbsp;</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp;S&aacute;ng ng&agrave;y 03/4/2023, đo&agrave;n c&ocirc;ng t&aacute;c đại diện <span style="background-color:white">Quỹ học bổng ISSHIN - ASAHI đ&atilde; sơ tuyển </span>tại Trường THPT Chuy&ecirc;n Sơn La. &nbsp;Kết quả c&oacute; 48/48 em học sinh tham gia sơ tuyển v&agrave; tất cả đều đạt theo y&ecirc;u cầu.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="color:black"><img id="Picture_x0020_1" src="/data/images/tin-tuc/Capture13.JPG" /> </span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:black">&nbsp;48 học sinh Trường THPT Chuy&ecirc;n Sơn La đang tham gia l&agrave;m b&agrave;i sơ tuyển</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img id="Picture_x0020_3" src="/data/images/tin-tuc/Capture14.JPG" style="color:black" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="color:black"><img id="Picture_x0020_4" src="/data/images/tin-tuc/Capture15(1).JPG" style="height:314px; width:601px" /> </span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="color:black"><img id="Picture_x0020_9" src="/data/images/tin-tuc/Capture16(1).JPG" /> </span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:black">Kết quả 48 em tham gia thi sơ tuyển đều c&oacute; số điểm đạt theo y&ecirc;u cầu</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp;C&ugrave;ng ng&agrave;y, đo&agrave;n c&ocirc;ng t&aacute;c đại diện </span><span style="color:black">Quỹ học bổng ISSHIN - ASAHI đ&atilde; sơ tuyển </span><span style="color:black">tại Trường PTDT Nội Tr&uacute; tỉnh. Kết quả 18/22 em học sinh tham gia sơ tuyển đều đạt theo y&ecirc;u cầu (4 em l&agrave;m b&agrave;i kh&ocirc;ng đạt sẽ cho th&ecirc;m cơ hội thi lại nếu c&oacute; quyết t&acirc;m d&agrave;nh học bổng).</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="color:black"><img id="Picture_x0020_11" src="/data/images/tin-tuc/Capture3(1).JPG" /> </span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:black">&nbsp;22 học sinh Trường PTDT Nội Tr&uacute; tỉnh đang tham gia l&agrave;m b&agrave;i sơ tuyển</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="color:black"><img id="Picture_x0020_12" src="/data/images/tin-tuc/Capture33.JPG" /> </span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:black">18/22 học sinh </span></em><em><span style="color:black">tham gia thi sơ tuyển c&oacute; số điểm đạt theo y&ecirc;u cầu</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp;S<span style="background-color:white">ơ tuyển </span>tại Trường THCS&amp;THPT Nguyễn Du. Kết quả 6/9 em học sinh tham gia sơ tuyển đạt theo y&ecirc;u cầu.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="color:black"><img id="Picture_x0020_13" src="/data/images/tin-tuc/Capture17(1).JPG" /> </span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:black">9 học sinh Trường THCS&amp;THPT Nguyễn Du đang tham gia l&agrave;m b&agrave;i sơ tuyển</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="color:black"><img id="Picture_x0020_14" src="/data/images/tin-tuc/Capture18(1).JPG" /> </span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:black">6/9 học sinh </span></em><em><span style="color:black">tham gia thi sơ tuyển c&oacute; số điểm đạt theo y&ecirc;u cầu</span></em></span></p>\r\n', '2023-04-10 04:10:27', 20, '/data/files/hkh/Capture14.JPG', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1424, 'Trao 15 suất học học bổng Bùi Kim Đỉnh cho học sinh nghèo hiếu học tại huyện Vĩnh Linh', '', '<p><a href="http://vinhlinh.quangtri.gov.vn/chi-tiet/-/view-article/1/1647698310522/1682049742124">http://vinhlinh.quangtri.gov.vn/chi-tiet/-/view-article/1/1647698310522/1682049742124</a></p>\r\n', '2023-04-21 08:59:58', 11, '/data/files/anh%20tinh%20ban/Captureqt.JPG', '', 0, 55, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1419, 'Tấm gương sáng trong công tác tự học, tự rèn luyện', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white">&nbsp; &nbsp; &nbsp; Hơn 15 năm gắn b&oacute; với ng&agrave;nh gi&aacute;o dục của huyện Y&ecirc;n Ch&acirc;u chưa hẳn phải l&agrave; một qu&atilde;ng thời gian d&agrave;i, song với &ocirc;ng Nguyễn Thế Sang-Ph&oacute; Chủ tịch Hội đồng nh&acirc;n d&acirc;n huyệnY&ecirc;n Ch&acirc;u th&igrave; đ&acirc;y l&agrave; khoảng thời gian m&agrave; &ocirc;ng đ&atilde; được bồi dưỡng, trưởng th&agrave;nh v&agrave; ph&aacute;t triển. Ch&iacute;nh v&igrave; l&yacute; do đ&oacute;, d&ugrave; kh&ocirc;ng c&ograve;n c&ocirc;ng t&aacute;c trong ng&agrave;nh gi&aacute;o dục, nh<span style="color:red">ư</span>ng bản th&acirc;n &ocirc;ng vẫn lu&ocirc;n cố gắng kh&ocirc;ng ngừng trong việc học tập, n&acirc;ng cao tr&igrave;nh độ bản th&acirc;n, đồng thời d&agrave;nh nhiều t&acirc;m huyết cho c&ocirc;ng t&aacute;c gi&aacute;o dục, khuyến học, khuyến t&agrave;i của huyện nh&agrave;.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><img src="/data/images/anh%20co%20so/Captureyc.JPG" style="height:385px; width:647px" /></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="background-color:white"><em>&Ocirc;ng Nguyễn Thế Sang &ndash; Đang nghi&ecirc;n cứu văn bản, nghị quyết tại ph&ograve;ng l&agrave;m việc</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white">&nbsp; &nbsp; &nbsp; &nbsp; Sinh năm 1972, năm 1994 sau khi học xong lớp Trung cấp sư phạm cấp 1 tại Trường Trung cấp sư phạm Sơn La, &ocirc;ng được tuyển l&agrave;m Gi&aacute;o vi&ecirc;n tại Trường tiểu học Mường Lựm x&atilde; Mường Lựm huyện Y&ecirc;n Ch&acirc;u.Trong qu&aacute; tr&igrave;nh giảng dạy tại trường, &ocirc;ng chủ động vừa cố gắng n&acirc;ng cao chất lượng chuy&ecirc;n m&ocirc;n v&agrave; tự học tập để thi đỗ đại học, đến th&aacute;ng 6/1999 ho&agrave;n th&agrave;nh chương tr&igrave;nh, được nhận tấm bằng Cử nh&acirc;n sư phạm Gi&aacute;o dục tiểu học v&agrave; được chuyển c&ocirc;ng t&aacute;c tại ph&ograve;ng Gi&aacute;o dục v&agrave; Đ&agrave;o tạo huyện từ th&aacute;ng 8/1999 đến th&aacute;ng 3/2009. Trong thời gian c&ocirc;ng t&aacute;c ở ng&agrave;nh gi&aacute;o dục, &ocirc;ng tiếp tục đi học cao học, th&aacute;ng 10/2005,&nbsp; bảo vệ th&agrave;nh c&ocirc;ng luận văn Thạc sĩ gi&aacute;o dục học, chuy&ecirc;n ng&agrave;nh gi&aacute;o dục tiểu học. Từ th&aacute;ng 4/2009 đến th&aacute;ng 9/2020 &ocirc;ng được lu&acirc;n chuyển c&ocirc;ng t&aacute;c sang một số cơ quan chuy&ecirc;n tr&aacute;ch tham mưu, gi&uacute;p việc huyện ủy, đảm nhiệm nhiều vị tr&iacute; như: Ph&oacute; Ch&aacute;nh <span style="color:red">V</span>ăn ph&ograve;ng Huyện ủy; Ch&aacute;nh <span style="color:red">V</span>ăn ph&ograve;ng Huyện ủy; Trưởng ban Tuy&ecirc;n gi&aacute;o huyện ủy, Gi&aacute;m đốc Trung t&acirc;m Ch&iacute;nh trị huyện. Từ th&aacute;ng 10/2020 đến nay, &ocirc;ng Nguyễn Thế Sang được chuyển c&ocirc;ng t&aacute;c sang khối cơ quan ch&iacute;nh quyền huyện, đảm nhiệm chức vụ Ph&oacute; Chủ tịch Hội đồng nh&acirc;n d&acirc;n huyện. Trong thời gian c&ocirc;ng t&aacute;c ở khối huyện ủy, khối ch&iacute;nh quyền huyện, &ocirc;ng tiếp tục theo học để c&oacute; th&ecirc;m bằng Cao cấp l&yacute; luận ch&iacute;nh trị - h&agrave;nh ch&iacute;nh, bằng Cử nh&acirc;n ng&agrave;nh Luật Kinh tế, bằng Thạc sĩ luật học chuy&ecirc;n ng&agrave;nh Luật Kinh tế; c&ugrave;ng nhiều chứng chỉ chuy&ecirc;n m&ocirc;n kh&aacute;c. &Ocirc;ng Sang t&acirc;m sự: <em>Bởi bản th&acirc;n được trưởng th&agrave;nh từ m&ocirc;i trường gi&aacute;o dục, n&ecirc;n d&ugrave; đ&atilde; chuyển c&ocirc;ng t&aacute;c khỏi ng&agrave;nh nhiều năm nhưng bản th&acirc;n vẫn lu&ocirc;n nỗ lực học tập, tự học, tự r&egrave;n luyện để l&agrave;m tốt hơn nữa c&aacute;c c&ocirc;ng việc được giao</em><em>.</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="background-color:white">&nbsp; &nbsp; &nbsp; &nbsp; Trong c&ocirc;ng việc, </span><span style="background-color:white">&ocirc;ng </span><span style="background-color:white">lu&ocirc;n đề cao tr&aacute;ch nhiệm trong c&ocirc;ng việc, lu&ocirc;n tự học hỏi, phấn đấu đ&atilde; tạo n&ecirc;n cho </span><span style="background-color:white">&ocirc;ng</span><span style="background-color:white"> nhiều th&agrave;nh t&iacute;ch nổi trội trong c&ocirc;ng t&aacute;c</span><span style="background-color:white">: chủ nhiệm 1 đề t&agrave;i nghi&ecirc;n cứu khoa học x&atilde; hội v&agrave; nh&acirc;n văn cấp tỉnh, c&oacute; 2 s&aacute;ng kiến cấp tỉnh, nhiều s&aacute;ng kiến cấp cơ sở</span><span style="background-color:white">. Với vai tr&ograve; </span><span style="background-color:white">Ủy vi&ecirc;n Ban Thường vụ Huyện ủy, Ph&oacute; Chủ tịch Hội đồng nh&acirc;n d&acirc;n huyện, &ocirc;ng đ&atilde; ch&uacute; trọng l&agrave;m tốt </span><span style="background-color:white">c&ocirc;ng t&aacute;c chỉ đạo điều h&agrave;nh, n&ecirc;u cao &yacute; thức tr&aacute;ch nhiệm, gương mẫu trong &ldquo;Học tập v&agrave; l&agrave;m theo tư tưởng, đạo đức, phong c&aacute;ch Hồ Ch&iacute; Minh&rdquo; theo Chỉ thị 05-CT/TW của Bộ Ch&iacute;nh trị gắn với thực hiện c&aacute;c phong tr&agrave;o thi đua do </span><span style="background-color:white">tỉnh v&agrave; huyện</span><span style="background-color:white"> tổ chức. Đặc biệt, &ocirc;ng rất quan t&acirc;m đến c&ocirc;ng t&aacute;c tự học tự r&egrave;n luyện của c&aacute;c c&aacute;n bộ, </span><span style="background-color:white">c&ocirc;ng chức</span><span style="background-color:white">, nhất l&agrave; c&aacute;n bộ trẻ mới v&agrave;o </span><span style="background-color:white">c&ocirc;ng t&aacute;c</span><span style="background-color:white">.</span> <span style="background-color:white">&Ocirc;ng lu&ocirc;n tự nhủ </span><span style="background-color:white">kh&ocirc;ng c&oacute; ai tự giỏi v&agrave; cũng kh&ocirc;ng c&oacute; ai m&atilde;i giỏi nếu kh&ocirc;ng chịu kh&oacute; học tập, r&egrave;n luyện</span><span style="background-color:white">. D</span><span style="background-color:white">o vậy</span><span style="background-color:white">,</span><span style="background-color:white"> trong c&aacute;c buổi sinh hoạt chi bộ, c&aacute;c buổi họp </span><span style="background-color:white">cơ quan, &ocirc;ng</span><span style="background-color:white"> đều nhắc nhở v&agrave; kiểm tra đ&ocirc;n đốc c&ocirc;ng việc, triển khai c&aacute;c c&ocirc;ng văn, t&agrave;i liệu mới</span><span style="background-color:white">,</span><span style="background-color:white"> đồng thời kiểm tra c&ocirc;ng t&aacute;c tự nghi&ecirc;n cứu của từng c&aacute; nh&acirc;n, nắm được </span><span style="background-color:white">sở trường, năng lực để c&oacute; &yacute; kiến </span><span style="background-color:white">ph&acirc;n c&ocirc;ng c&ocirc;ng việc ph&ugrave; hợp với từng người, </span><span style="background-color:white">tạo điều kiện </span><span style="background-color:white">c&oacute; sự hỗ trợ, gi&uacute;p đỡ lẫn nhau để đảm bảo c&aacute;c c&ocirc;ng việc đều được thực hiện đ&uacute;ng tiến độ.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="background-color:white">&nbsp; &nbsp; &nbsp; &nbsp; Với khối lượng c&ocirc;ng việc </span><span style="background-color:white">trong lĩnh vực thuộc nhiệm vụ, quyền hạn của Hội đồng nh&acirc;n d&acirc;n, của Thường trực Hội đồng nh&acirc;n d&acirc;n huyện, &ocirc;ng</span><span style="background-color:white"> lu&ocirc;n </span><span style="background-color:white">chủ động </span><span style="background-color:white">bố tr&iacute; c&ocirc;ng việc một c&aacute;ch khoa học, giao việc </span><span style="background-color:white">theo chức tr&aacute;ch, nhiệm vụ của</span><span style="background-color:white"> c&aacute;c Ban của Hội đồng nh&acirc;n d&acirc;n huyện, </span><span style="background-color:white">nhiệm vụ của </span><span style="background-color:white">l&atilde;nh đạo v&agrave; chuy&ecirc;n vi&ecirc;n</span> <span style="background-color:white">gi&uacute;p việc Hội</span><span style="background-color:white"> đồng nh&acirc;n d&acirc;n huyện; b&aacute;o c&aacute;o Thường trực Hội đồng nh&acirc;n d&acirc;n huyện cho &yacute; kiến, tr&igrave;nh kỳ họp Hội đồng nh&acirc;n d&acirc;n huyện xem x&eacute;t, quyết nghị theo thẩm quyền. Đồng thời, &ocirc;ng d&agrave;nh nhiều thời gian xuống cơ sở n<span style="color:red">ắ</span>m bắt t&igrave;nh h&igrave;nh, khảo s&aacute;t hoạt động của ch&iacute;nh quyền địa phương cấp x&atilde;, kịp thời nhắc nhở những tồn tại, hạn chế để khắc phục; chỉ đạo l&agrave;m tốt c&ocirc;ng t&aacute;c gi&aacute;m s&aacute;t, lắng nghe, phản ảnh t&acirc;m tư nguyện vọng của người d&acirc;n, của cử tri đến với c&aacute;c cơ quan chức năng xem x&eacute;t, giải quyết theo thẩm quyển.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="background-color:white">&nbsp; &nbsp; &nbsp; &nbsp;C&ocirc;ng việc l&agrave; thế, </span><span style="background-color:white">&ocirc;ng</span><span style="background-color:white"> lu&ocirc;n bận rộn, tận t&acirc;m </span><span style="background-color:white">tr&aacute;ch nhiệm </span><span style="background-color:white">với c&ocirc;ng việc, trong sinh hoạt, </span><span style="background-color:white">&ocirc;ng</span><span style="background-color:white"> l&agrave; người rất h&ograve;a đồng v&agrave; dễ gần n&ecirc;n lu&ocirc;n được sự t&iacute;n nhiệm v&agrave; y&ecirc;u mến từ đồng ch&iacute;, đồng nghiệp v&agrave; c&aacute;c cơ quan c&oacute; li&ecirc;n quan. Với sự nỗ lực phấn đấu của bản th&acirc;n, </span><span style="background-color:white">&ocirc;ng</span><span style="background-color:white"> được tặng thưởng</span><span style="background-color:white"> nhiều</span><span style="background-color:white"> giấy khen, bằng khen của c&aacute;c cấp, c&aacute;c ng&agrave;nh</span><span style="background-color:white">, 3 lần li&ecirc;n tiếp được c&ocirc;ng nhận l&agrave; chiến sĩ thi đua cấp tỉnh</span><span style="background-color:white">. </span><span style="background-color:white">Điển h&igrave;nh v&agrave;o năm 2019, &ocirc;ng đ&atilde; vinh dự được nhận bằng khen của Thủ tướng Ch&iacute;nh phủ v&igrave; đ&atilde; c&oacute; th&agrave;nh t&iacute;ch trong c&ocirc;ng t&aacute;c g&oacute;p phần v&agrave;o sự nghiệp x&acirc;y dựng chủ nghĩa x&atilde; hội v&agrave; bảo vệ Tổ quốc</span><span style="background-color:white">. </span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="background-color:white"><img src="/data/images/anh%20co%20so/Captureyc2.JPG" style="height:479px; width:642px" /></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="background-color:white">&nbsp; &nbsp; &nbsp; &nbsp; Sau gần 30 năm c&ocirc;ng t&aacute;c</span><span style="background-color:white">, d&ugrave; ở vị tr&iacute; n&agrave;o </span><span style="background-color:white">&ocirc;ng</span><span style="background-color:white"> lu&ocirc;n thể hiện bản lĩnh vững v&agrave;ng của người c&aacute;n bộ, xứng đ&aacute;ng l&agrave; tấm gương để lớp trẻ học tập./.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="background-color:white"><strong><span style="background-color:white">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trần Sơn</span></strong></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm">&nbsp;</p>\r\n', '2023-04-17 02:58:50', 39, '/data/files/anh%20co%20so/Captureyc.JPG', '', 1, 36, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1420, 'Hội Khuyến học tỉnh Sơn La tổ chức Hội nghị Ban Thường vụ đánh giá kết quả công tác Hội quí 1, bàn và thống nhất 1 số nhiệm vụ trọng tâm quí 2.', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; Ng&agrave;y 19/4, HKH tỉnh Sơn La tổ chức HN BTV đ&aacute;nh gi&aacute; kết quả c&ocirc;ng t&aacute;c Hội qu&iacute; 1, b&agrave;n v&agrave; thống nhất 1 số nhiệm vụ trọng t&acirc;m qu&iacute; 2.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img src="/data/images/tin-tuc/z4280143369308_4881928cd8a37f55fbd0b06dd5813427.jpg" style="height:468px; width:608px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp; Trong qu&iacute; 1, c&ocirc;ng t&aacute;c KH, KT, XDXHHT to&agrave;n tỉnh đ&atilde; đạt một số kết quả quan trọng, trong đ&oacute; c&oacute; 1 số hoạt động nổi bật:</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; 1. Hội đ&atilde; phối hợp với Đảng ủy khối c&aacute;c cơ quan v&agrave; doanh nghiệp tỉnh kiện to&agrave;n 91 Ban khuyến học tại c&aacute;c cơ quan, doanh nghiệp. Ph&aacute;t triển th&ecirc;m 9.652 hội vi&ecirc;n, n&acirc;ng tổng số hội vi&ecirc;n hội khuyến học to&agrave;n tỉnh l&ecirc;n 355.688 hội vi&ecirc;n, chiến tỷ lệ 27% d&acirc;n số to&agrave;n tỉnh.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; 2. Hội đ&atilde; tham mưu với UBND tỉnh tổ chức Đ&ecirc;m nhạc &ldquo;M&Agrave;U HOA C&Ograve;N M&Atilde;I&rdquo; nh&acirc;n ng&agrave;y sinh v&agrave; ng&agrave;y mất của liệt sỹ - Nh&agrave; c&aacute;ch mạng T&ocirc; Hiệu nhằm vận động ủng hộ Quĩ Học bổng T&ocirc; Hiệu. Kết quả tại đ&ecirc;m nhạc, c&aacute;c tập thể c&aacute; nh&acirc;n, c&aacute;c nh&agrave; hảo t&acirc;m, c&aacute;c doanh nghiệp trong v&agrave; ngo&agrave;i tỉnh đ&atilde; ủng hộ quĩ số tiền 4.385.000.000đ (bốn tỷ ba trăm t&aacute;m mươi lăm triệu đồng) n&acirc;ng tổng số Quĩ học bổng T&ocirc; hiệu do Hội quản l&yacute; l&ecirc;n gần 13 tỷ đồng.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; 3. Tổ chức đo&agrave;n c&aacute;n bộ Hội gồm 19 người (tỉnh hội v&agrave; 12 huyện th&agrave;nh phố) đi thăm v&agrave; học tập kinh nghiệm của Hội khuyến học 6 tỉnh th&agrave;nh c&oacute; th&agrave;nh t&iacute;ch v&agrave; c&aacute;c m&ocirc; h&igrave;nh ti&ecirc;u biểu trong cả nước (gồm: Ph&uacute; Thọ, Y&ecirc;n B&aacute;i, Th&aacute;i Nguy&ecirc;n, Thanh Ho&aacute;, Quảng Trị, Đ&agrave; Nẵng). Sau chuyến đi c&oacute; b&aacute;o c&aacute;o tham mưu, đề xuất với Tỉnh ủy, HĐND, UBND tỉnh một số vấn đề về cơ chế ch&iacute;nh s&aacute;ch v&agrave; c&aacute;c nd hoạt động của Hội cần quan t&acirc;m th&aacute;o gỡ, đồng thời tham khảo c&aacute;ch l&agrave;m hay, hiệu quả của c&aacute;c tỉnh để &aacute;p dụng v&agrave;o t&igrave;nh h&igrave;nh thực tiễn c&ocirc;ng t&aacute;c Hội của Sơn La.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; 4. Trong qu&iacute; 1, Hội KH tỉnh đ&atilde; trao học bổng v&agrave; c&aacute;c nd khen khuyến học khuyến t&agrave;i cho 326 lượt trị gi&aacute; 203 triệu đồng. HKH cấp huyện, TP trao 12.734 lượt trị gi&aacute; 1.090 triệu đồng. </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;Tại hội nghị, BTV hội cũng cho &yacute; kiến v&agrave;o Kế hoạch tổ chức Lễ trao học bổng &ldquo;Học kh&ocirc;ng bao giờ c&ugrave;ng&rdquo; v&agrave;o dịp 19/5 theo chỉ đạo của Trung ương Hội KHVN v&agrave; cho chủ trương kiện to&agrave;n, bầu bổ sung BCH, BTV Hội nhiệm kỳ 2021-2026.</span></p>\r\n', '2023-04-20 09:58:03', 22, '/data/files/hkh/z4280143369308_4881928cd8a37f55fbd0b06dd5813427.jpg', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
