INSERT INTO `tin_tuc` VALUES (1405, 'Quỹ học bổng Tô Hiệu tiếp nhận ủng hộ từ Đêm nhạc “Khát vọng sống” ', 'Tối 27/8, tại Nhà hát Lớn Hà Nội đã diễn ra đêm nhạc “Khát vọng sống” – chương trình nghệ thuật ủng hộ Quỹ học bổng Tô Hiệu.', '<p><a href="https://cand.com.vn/Tieu-diem-van-hoa/dem-nhac-khat-vong-song-ung-ho-quy-hoc-bong-to-hieu-i665575/">https://cand.com.vn/Tieu-diem-van-hoa/dem-nhac-khat-vong-song-ung-ho-quy-hoc-bong-to-hieu-i665575/</a></p>\r\n', '2023-02-22 09:46:29', 44, '/data/files/hkh/Capture12.JPG', '', 0, 61, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1406, 'Tấm gương “Học không bao giờ cùng” ', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp;Sinh ra v&agrave; lớn l&ecirc;n ở tiểu khu I thị trấn &Iacute;t Ong huyện Mường La v&agrave;o năm 1972, chị Lương Mỹ Hạnh đến với văn chương kh&aacute; muộn. V&igrave; điều kiện ho&agrave;n cảnh phải bươn chải kiếm sống lo cho bản th&acirc;n v&agrave; gia đ&igrave;nh, chị chỉ được học tới lớp 10 phổ th&ocirc;ng v&agrave; nghề nghiệp ch&iacute;nh của chị l&agrave; thợ may. Năm 2016, chị mới thử sức với những vần thơ đầu ti&ecirc;n, nhưng chỉ một năm sau chị đ&atilde; c&oacute; thơ in tr&ecirc;n tạp ch&iacute; Suối Reo của Hội văn học nghệ thuật tỉnh Sơn La v&agrave; một số tờ b&aacute;o tỉnh bạn. Đến th&aacute;ng 7 năm 2018, chị được kết nạp l&agrave; hội vi&ecirc;n chuy&ecirc;n ng&agrave;nh văn học của Hội li&ecirc;n hiệp văn học nghệ thuật tỉnh Sơn La. </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; Chị được tham gia tập huấn nghiệp vụ l&yacute; luận ph&ecirc; b&igrave;nh văn học nghệ thuật kh&oacute;a V của li&ecirc;n hiệp c&aacute;c Hội văn học nghệ thuật năm 2019; Ho&agrave;n th&agrave;nh chương tr&igrave;nh bồi dưỡng viết văn kh&oacute;a XIII năm 2019 do Trung t&acirc;m bồi dưỡng viết văn Nguyễn Du tổ chức tại th&agrave;nh phố H&agrave; Nội. Điều n&agrave;y đ&atilde; kh&iacute;ch lệ chị tiếp tục s&aacute;ng t&aacute;c những vần thơ giầu cảm x&uacute;c về t&igrave;nh y&ecirc;u, cuộc sống, con người, thi&ecirc;n nhi&ecirc;n v&ugrave;ng T&acirc;y Bắc. </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; Năm 2019 chị s&aacute;ng t&aacute;c tập thơ &ldquo;Đ&aacute; h&aacute;t&rdquo; gồm 63 b&agrave;i thơ được Nh&agrave; xuất bản Hội Nh&agrave; văn in đ&atilde; mang đến cho bạn đọc nhiều cảm x&uacute;c đẹp, ri&ecirc;ng biệt. Tập thơ thứ hai &ldquo;M&ugrave;a l&aacute; thức&rdquo; với khoảng 65 b&agrave;i của chị cũng được Nh&agrave; xuất bản Hội Nh&agrave; văn in v&agrave;o th&aacute;ng 5 năm 2022 v&agrave; vinh dự được nhận giải khuyến kh&iacute;ch của Trung ương c&aacute;c Hội Li&ecirc;n hiệp Văn học Nghệ thuật Việt Nam. Thơ chị mang kh&aacute;t vọng đổi đời, mang t&igrave;nh y&ecirc;u m&aacute;u thịt với mảnh đất T&acirc;y Bắc nơi chị sinh ra v&agrave; lớn l&ecirc;n, m&agrave; chị lu&ocirc;n coi l&agrave; qu&ecirc; hương của m&igrave;nh.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; Thơ chị c&oacute; mặt thường xuy&ecirc;n ở Tạp ch&iacute; Suối Reo tỉnh Sơn La v&agrave; c&aacute;c B&aacute;o, Tạp ch&iacute; kh&aacute;c như B&aacute;o Văn Nghệ của Hội Nh&agrave; văn Việt nam, tạp ch&iacute; Văn nghệ Qu&acirc;n đội, tạp chỉ Văn nghệ th&agrave;nh phố Hồ Ch&iacute; Minh,&nbsp; tạp ch&iacute; Diễn Đ&agrave;n Văn Nghệ Việt Nam. Văn nghệ Điện Bi&ecirc;n, Văn Nghệ Lai Ch&acirc;u, Văn Nghệ Vĩnh Ph&uacute;c, Văn Nghệ Cần Thơ, Văn Nghệ C&agrave; Mau...</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; Ngo&agrave;i viết thơ chị c&ograve;n viết tản văn, tản văn của chị được đăng ở b&aacute;o Đại Biểu Nh&acirc;n D&acirc;n, Tạp ch&iacute; Văn nghệ H&ograve;a B&igrave;nh, B&aacute;o Điện Bi&ecirc;n Phủ, Tạp ch&iacute; Diễn Đ&agrave;n Văn Nghệ Việt Nam. Đầu năm 2023 chị vinh dự được kết nạp v&agrave;o Hội Văn Học Nghệ Thuật c&aacute;c D&acirc;n tộc thiểu số Việt Nam tại tỉnh Sơn La.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; Vần thơ kh&ocirc;ng chỉ thỏa m&atilde;n niềm đam m&ecirc; m&agrave; c&ograve;n mang lại rất nhiều giải thưởng cho chị như:</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; - Đạt giải Tư cuộc thi thơ T&acirc;m vọng cố đ&ocirc; &ndash; 2018 </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; - Đạt giải A v&agrave; giải B Giải thưởng Văn học nghệ thuật (VHNT) lần thứ nhất, lần thứ 2 (2015 &ndash; 2018; 2019 &ndash; 2021).</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; - Giải Nh&igrave; cuộc thi viết về D&acirc;n tộc v&agrave; Miền n&uacute;i &ndash; Qu&aacute;n Chi&ecirc;u Văn 2019</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; - Giải Nh&igrave; giải thưởng thi thơ Đại Nam &ndash; Nguyễn Bỉnh Khi&ecirc;m (2018 &ndash; 2019) chủ đề: Tổ quốc &ndash; Qu&ecirc; hương &ndash; Bi&ecirc;n giới &ndash; Hải đảo.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;- Giải Nh&igrave; cuộc thi thơ &ldquo;Sống v&agrave; hy vọng&rdquo;</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;-&nbsp; Giải Khuyến kh&iacute;ch cuộc thi thơ &ldquo;Việt Nam đất nước lời ru&rdquo; lần thứ nhất năm 2022.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;-&nbsp; Bằng khen của UBND tỉnh Sơn La đạt giải A Giải thưởng VHNT tỉnh Sơn La lần thứ 2 (giai đoạn 2019 &ndash; 2021).</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; -&nbsp; Giải Nh&igrave; cuộc thi thơ: Đất nước v&agrave;o xu&acirc;n năm 2022 (Hội người y&ecirc;u thơ Việt).</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; Với tinh thần tự học vươn l&ecirc;n, niềm đam m&ecirc; với thơ ca, chị Lương Mỹ Hạnh l&agrave; c&acirc;y b&uacute;t kh&ocirc;ng c&ograve;n xa lạ với giới văn nghệ sĩ v&agrave; bạn đọc của tỉnh Sơn La. Tấm gương tự học của chị sẽ được lan tỏa trong cộng đồng.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:right"><span style="font-size:14px"><strong><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Hội Khuyến học huyện Mường La</em></strong></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:right">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14pt">Một số h&igrave;nh ảnh phần thưởng được c&aacute;c tổ chức trao tặng chị Lương Mỹ Hạnh</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14pt"><img alt="Description: C:UsersAdministratorPicturesz4131712725958_101fd8da27fad77eb7b29f10d963023f.jpg" id="Picture_x0020_1" src="/data/images/anh%20co%20so/z4131712725958_101fd8da27fad77eb7b29f10d963023f.jpg" /> </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14pt"><img alt="Description: C:UsersAdministratorPicturesz4131712726431_d0af4e6868dbbc2ff655fe437a1f5d54.jpg" id="Picture_x0020_2" src="/data/images/anh%20co%20so/z4131712707376_0e917ebdf876134fcc18fa3e8ec56b9a.jpg" /> </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14pt"><img alt="Description: C:UsersAdministratorPicturesz413171