INSERT INTO `tin_tuc` VALUES (1407, 'Ngày 18.2.2023, Hội Khuyến học huyện Mường La đã trao thưởng cho 30 giáo viên đạt danh hiệu Giáo viên Chủ nhiệm giỏi cấp  TH-THCS.', '', '', '2023-02-24 02:15:32', 22, '/data/files/anh%20co%20so/Capture13.JPG', '', 0, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1408, 'CÔNG TY CỔ PHẦN ĐẦU TƯ &amp; XÂY DỰNG  ĐƠN VỊ ĐI ĐẦU PHONG TRÀO KHUYẾN HỌC HUYỆN MỘC CHÂU', '                                                                           Giàng Khánh Ly\r\n                                                       Chủ tịch Hội Khuyến học huyện Mộc Châu\r\n', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp; &nbsp; &nbsp; &nbsp; C&ocirc;ng ty cổ phần Đầu tư &amp; X&acirc;y dựng Mộc Ch&acirc;u l&agrave; đơn vị khuyến học trực thuộc Hội Khuyến học huyện. C&ocirc;ng ty kh&ocirc;ng chỉ c&oacute; nhiều s&aacute;ng kiến, s&aacute;ng tạo trong hoạt động x&acirc;y dựng x&atilde; hội học tập tại đơn vị, m&agrave; c&ograve;n thường xuy&ecirc;n chia sẻ, gi&uacute;p đỡ, hỗ trợ c&aacute;c đối tượng học sinh ngh&egrave;o, học sinh c&oacute; ho&agrave;n cảnh đặc biệt kh&oacute; khăn tr&ecirc;n địa b&agrave;n. </span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đồng ch&iacute; Phan Kỳ Sơn, l&agrave; l&atilde;nh đạo, đồng thời cũng l&agrave; Trưởng Ban Khuyến học của C&ocirc;ng ty, người lu&ocirc;n gương mẫu đi đầu trong mọi phong tr&agrave;o do tỉnh, huyện v&agrave; cơ quan ph&aacute;t động. Con em, c&aacute;n bộ c&ocirc;ng chức, vi&ecirc;n chức, người lao động trong đơn vị thường xuy&ecirc;n được Ban Khuyến học chăm lo, tạo điều kiện tốt nhất để được học thường xuy&ecirc;n, học suốt đời nhằm n&acirc;ng cao tr&igrave;nh độ, chất lượng lao động, học tập v&agrave; kinh doanh.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&ocirc;ng ty cổ phần Đầu tư &amp; X&acirc;y dựng Mộc Ch&acirc;u l&agrave; đơn vị khuyến học hiện nay đang nhận đỡ đầu 04 ch&aacute;u học sinh c&oacute; ho&agrave;n cảnh đặc biệt kh&oacute; khăn với mức hỗ trợ 500.000/ch&aacute;u/th&aacute;ng, trị gi&aacute; 24.000.000 đồng/năm v&agrave; thực hiện từ năm 2015, đến nay C&ocirc;ng ty vẫn đang tiếp tục đỡ đầu cho c&aacute;c ch&aacute;u. C&aacute;c ch&aacute;u được C&ocirc;ng ty đỡ đầu đều chăm ngoan v&agrave; học tập tiến bộ.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&ocirc;ng ty được huyện ph&acirc;n c&ocirc;ng phụ tr&aacute;ch x&atilde; Hua Păng, l&agrave; một trong những x&atilde; trong kế hoạch x&acirc;y dựng n&ocirc;ng th&ocirc;n mới của huyện&nbsp; nhưng&nbsp; x&atilde; vẫn c&ograve;n nhiều hộ ngh&egrave;o n&ecirc;n l&atilde;nh đạo c&ocirc;ng ty đ&atilde; giao nhiệm vụ cho Ban Khuyến học h&agrave;ng năm phải r&agrave; so&aacute;t, tham mưu, đề xuất phương &aacute;n hỗ trợ số học sinh v&agrave; c&aacute;c hộ ngh&egrave;o để C&ocirc;ng ty c&oacute; phương &aacute;n hỗ trợ. X&aacute;c định r&otilde; tr&aacute;ch nhiệm, nhiệm vụ x&acirc;y dựng x&atilde; hội học tập theo Đề &aacute;n của Thủ tướng Ch&iacute;nh phủ, L&atilde;nh đạo C&ocirc;ng ty lu&ocirc;n trăn trở, t&igrave;m t&ograve;i những c&aacute;ch l&agrave;m hay, kinh nghiệm tốt để l&agrave;m khuyến học giỏi trong hệ thống doanh nghiệp. Từ đ&oacute;, hằng năm C&ocirc;ng ty đều tổ chức tặng qu&agrave;, tặng học bổng cho nhiều học sinh kh&oacute; khăn trong x&atilde; cũng như c&aacute;c x&atilde; kh&aacute;c trong huyện. Đặc biệt, ng&agrave;y 27/2/2023, C&ocirc;ng ty đ&atilde; tổ chức tặng qu&agrave; cho học sinh tại bản Suối Ba, x&atilde; Hua Păng với 96 xuất trong đ&oacute; c&oacute; nhiều đồ d&ugrave;ng học tập v&agrave; đồ chơi cho 96 ch&aacute;u học sinh của bản với tổng trị gi&aacute; khoảng 37.000.000 đồng.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Những th&agrave;nh quả m&agrave; phong tr&agrave;o khuyến học huyện Mộc Ch&acirc;u đạt được, trong đ&oacute; c&oacute; sự đ&oacute;ng g&oacute;p quan trọng, kh&ocirc;ng nhỏ của C&ocirc;ng ty cổ phần Đầu tư &amp; X&acirc;y dựng, m&agrave; chủ lực l&agrave; Ban Khuyến học v&agrave; sự nỗ lực kh&ocirc;ng mệt mỏi của c&aacute; nh&acirc;n đồng ch&iacute; Trưởng Ban Phan Kỳ Sơn. Ban Khuyến học C&ocirc;ng ty cổ phần Đầu tư &amp; X&acirc;y dựng Mộc Ch&acirc;u - một trong những &ldquo;<strong><em>B&ocirc;ng hoa khuyến học</em></strong>&rdquo; đ&atilde;, đang v&agrave; lu&ocirc;n g&oacute;p phần khoe sắc thắm, tỏa ng&aacute;t hương thơm trong &ldquo;<strong><em>Vườn hoa khuyến học</em></strong>&rdquo; huyện Mộc Ch&acirc;u./.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center">&nbsp;</p>\r\n', '2023-03-01 09:57:41', 40, '/data/files/anh%20co%20so/z4144287058086_94b73eb45f5177d7c3af3fbb1828e31f.jpg', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
