INSERT INTO `tin_tuc` VALUES (1410, 'Chương trình ''Màu hoa còn mãi'' tri ân những cống hiến của đồng chí Tô Hiệu', 'Nhân kỷ niệm 111 năm Ngày sinh (1912 - 2023) và 79 năm Ngày mất đồng chí Tô Hiệu (1944 - 2023), tối 7/3, tại Quảng trường Tây Bắc, Ủy ban nhân dân tỉnh Sơn La tổ chức Chương trình nghệ thuật “Màu hoa còn mãi” vận động xây dựng Quỹ học bổng Tô Hiệu năm 2023.', '<p><a href="https://baotintuc.vn/thoi-su/chuong-trinh-mau-hoa-con-mai-tri-an-nhung-cong-hien-cua-dong-chi-to-hieu-20230307224414598.htm">https://baotintuc.vn/thoi-su/chuong-trinh-mau-hoa-con-mai-tri-an-nhung-cong-hien-cua-dong-chi-to-hieu-20230307224414598.htm</a></p>\r\n', '2023-03-09 02:52:20', 39, '/data/files/hkh/Capture0(1).JPG', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1411, 'Màu hoa còn mãi – Đêm nhạc gây “Quỹ học bổng Tô Hiệu”', 'VOV.VN - Nhân dịp Kỷ niệm 111 năm ngày sinh (1912 - 2023) và 79 năm ngày mất (7/3/1944 - 7/3/2023) của Nhà cách mạng kiên trung, liệt sĩ Tô Hiệu, tối 7/3, tại Quảng trường Tây Bắc, UBND tỉnh Sơn La đã tổ chức Chương trình nghệ thuật “Màu hoa còn mãi” để vận động quyên góp “Quỹ học bổng Tô Hiệu” tỉnh Sơn La năm 2023.', '<p><a href="https://vov.vn/xa-hoi/mau-hoa-con-mai-dem-nhac-gay-quy-hoc-bong-to-hieu-post1006043.vov">https://vov.vn/xa-hoi/mau-hoa-con-mai-dem-nhac-gay-quy-hoc-bong-to-hieu-post1006043.vov</a></p>\r\n', '2023-03-10 02:35:41', 33, '/data/files/anh%20video/Capture00.JPG', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1412, '“Màu hoa còn mãi” - Vận động xây dựng Quỹ học bổng Tô Hiệu', 'Nhân dịp kỷ niệm 111 năm Ngày sinh (1912-2023) và 79 năm Ngày mất của đồng chí Tô Hiệu (7/3/1944 - 7/3/2023), tối ngày 7/3, tại Quảng trường Tây Bắc, UBND tỉnh Sơn La tổ chức Chương trình nghệ thuật “Màu hoa còn mãi” vận động xây dựng Quỹ học bổng Tô Hiệu năm 2023.', '<p><a href="https://baotainguyenmoitruong.vn/mau-hoa-con-mai-van-dong-xay-dung-quy-hoc-bong-to-hieu-351291.html">https://baotainguyenmoitruong.vn/mau-hoa-con-mai-van-dong-xay-dung-quy-hoc-bong-to-hieu-351291.html</a></p>\r\n', '2023-03-10 03:31:18', 39, '/data/files/anh%20video/Capture000.JPG', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1413, 'Sơn La tổ chức đêm nhạc xây dựng “Quỹ học bổng Tô Hiệu”', 'Tối 7/3, tại Quảng trường Tây Bắc, thành phố Sơn La, Ủy ban nhân dân tỉnh Sơn La tổ chức chương trình nghệ thuật “Màu hoa còn mãi” nhằm tiếp tục vận động xây dựng “Quỹ học bổng Tô Hiệu”. Đây là hoạt động nhân dịp kỷ niệm 111 năm ngày sinh (1912-2023) và 79 năm ngày mất của đồng chí Tô Hiệu (7/3/1944-7/3/2023).', '<p><a href="https://nhandan.vn/tag/m%C3%A0u%20hoa%20c%C3%B2n%20m%C3%A3i.html">https://nhandan.vn/tag/m%C3%A0u%20hoa%20c%C3%B2n%20m%C3%A3i.html</a></p>\r\n', '2023-03-10 03:35:20', 57, '/data/files/anh%20video/Capture0000.JPG', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1414, 'Hội khuyến học tỉnh vinh dự đc nhận cờ thi đua x.sắc của UBND Tỉnh YB Về thành tích xuât sắc năm 2022', '', '', '2023-03-10 03:46:13', 36, '/data/files/anh%20tinh%20ban/Capture00000(1).JPG', '', 0, 55, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1415, 'Khuyến học Phú Thọ: Sáng nay 8 tháng 3 năm 2023 hội khuyến học huyện Tân Sơn Gặp mặt  , trao thưởng cho học sinh huyện miền núi nghèo Tân Sơn đạt giải cao trong cuộc thi hs giỏi văn hoá lớp 9 của tỉnh ', '', '', '2023-03-10 03:47:50', 30, '/data/files/anh%20tinh%20ban/Capture000000.JPG', '', 0, 55, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1416, 'Hội Khuyến học tỉnh Sơn La phối hợp chuẩn bị kỳ thi tốt nghiệp trung học phổ thông năm 2023', 'Ngày 13/3, Sở Giáo dục và Đào tạo Sơn La phối hợp với Hội Khuyến học tỉnh Sơn La, Hội Nông dân, Hội Liên hiệp phụ nữ và Đoàn Thanh niên Cộng sản Hồ Chí Minh tỉnh Sơn La tổ chức Hội nghị triển khai nhiệm vụ phối hợp chuẩn bị cho kỳ thi tốt nghiệp trung học phổ thông năm học 2022-2023.', '<p><a href="https://congdankhuyenhoc.vn/hoi-khuyen-hoc-tinh-son-la-phoi-hop-chuan-bi-ky-thi-tot-nghiep-trung-hoc-pho-thong-nam-2023-179230314194420085.htm">https://congdankhuyenhoc.vn/hoi-khuyen-hoc-tinh-son-la-phoi-hop-chuan-bi-ky-thi-tot-nghiep-trung-hoc-pho-thong-nam-2023-179230314194420085.htm</a></p>\r\n', '2023-03-19 09:44:03', 40, '/data/files/hkh/Capture102.JPG', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1417, 'Hội Khuyến học tỉnh Sơn La giao lưu, trao đổi, học tập kinh nghiệm về công tác khuyến học, khuyến tài, xây dựng xã hội học tập tại 6 tỉnh.', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;Thực hiện Kế hoạch hoạt động năm 2023, được sự nhất tr&iacute; của UBND tỉnh Sơn La, từ ng&agrave;y 20/3/2023 đến ng&agrave;y 29/3/2023, Hội Khuyến học tỉnh Sơn La đ&atilde; tổ chức 2 đo&agrave;n c&ocirc;ng t&aacute;c đi giao lưu, trao đổi, học tập kinh nghiệm về c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập tại 6 tỉnh.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;Ng&agrave;y 20/3/2023 Đo&agrave;n giao lưu, trao đổi kinh nghiệm với Hội Khuyến học tỉnh Ph&uacute; Thọ v&agrave; Hội Khuyến học tỉnh Hải Dương.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img src="/data/images/tin-tuc/Capture0.JPG" style="height:439px; width:585px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em>Hội Khuyến học tỉnh Sơn La trao tặng bức tranh Quảng trường T&acirc;y Bắc c&oacute; v&ograve;ng x&ograve;e Th&aacute;i cho Hội Khuyến học tỉnh Ph&uacute; Thọ</em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp;Tại buổi giao lưu, đồng ch&iacute; Nguyễn Thị Kim Hải, Chủ tịch Hội Khuyến học Ph&uacute; Thọ chia sẻ về kinh nghiệm v&agrave; c&aacute;ch thức tham mưu với Tỉnh ủy, UBND tỉnh, Trung ương Hội Khuyến học Việt Nam để ban h&agrave;nh c&aacute;c văn bản chỉ đạo về c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, XDXHHT, về n&acirc;ng cao chất lượng c&ocirc;ng t&aacute;c tuy&ecirc;n truyền tr&ecirc;n c&aacute;c phương tiện th&ocirc;ng tin đại ch&uacute;ng theo hướng thường xuy&ecirc;n, s&acirc;u rộng, lan tỏa, như ch&uacute; trọng, tập trung tuy&ecirc;n truyền ch&agrave;o mừng th&aacute;ng khuyến học, Ng&agrave;y Khuyến học Việt Nam, Tuần lễ hưởng ứng Học tập suốt đời; Đẩy mạnh việc ph&aacute;t&nbsp; triển c&aacute;c tổ chức Hội v&agrave; hội vi&ecirc;n theo hướng tiếp tục tăng về số lượng v&agrave; n&acirc;ng cao năng lực vận động nh&acirc;n d&acirc;n thực hiện tốt c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập theo y&ecirc;u cầu chuyển đổi số quốc gia, trọng t&acirc;m l&agrave; khối c&aacute;c doanh nghiệp, khu c&ocirc;ng nghiệp, đơn vị sản xuất kinh doanh; Ph&aacute;t triển quỹ học bổng khuyến học theo hướng đa dạng, mang lại hiệu quả cao của c&aacute;c suất học bổng, c&aacute;c phần thưởng d&agrave;nh cho học sinh, sinh vi&ecirc;n v&agrave; người lớn c&oacute; th&agrave;nh t&iacute;ch học tập tốt, hỗ trợ gi&aacute;o vi&ecirc;n c&oacute; ho&agrave;n cảnh kh&oacute; khăn ho&agrave;n th&agrave;nh tốt nhiệm vụ; T&iacute;ch cực vận động nguồn đầu tư x&acirc;y dựng cơ sở vật chất phục vụ dạy, học; Đẩy mạnh c&ocirc;ng t&aacute;c phối hợp với c&aacute;c cấp, c&aacute;c ng&agrave;nh, c&aacute;c đo&agrave;n thể v&agrave; hội quần ch&uacute;ng, cộng đồng doanh nghiệp v&agrave; c&aacute;c tổ chức, c&aacute; nh&acirc;n hảo t&acirc;m tạo ra li&ecirc;n minh c&aacute;c lực lượng&nbsp; gắn kết, chia sẻ v&agrave; th&uacute;c đẩy c&aacute;c hoạt động khuyến học, khuyến t&agrave;i, hỗ trợ gi&aacute;o dục trong v&agrave; ngo&agrave;i nh&agrave; trường nhất l&agrave; gi&aacute;o dục v&ugrave;ng x&atilde; hội học tập, HTSĐ; l&agrave;m tốt c&ocirc;ng t&aacute;c tư vấn, phản biện x&atilde; hội c&aacute;c vấn đề c&oacute; li&ecirc;n quan...</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img src="/data/images/Capture00.JPG" style="height:377px; width:563px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><em>Đo&agrave;n c&ocirc;ng t&aacute;c đến thăm v&agrave; thắp hương tại đền H&ugrave;ng, đền Lạc Long Qu&acirc;n&nbsp;</em><span style="font-size:14px"><em>v&agrave; đền Mẫu &Acirc;u Cơ tại tỉnh Ph&uacute; Thọ</em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; Ng&agrave;y 21/3/2023 Đo&agrave;n giao lưu, học tập kinh nghiệm tại Hội Khuyến học tỉnh Y&ecirc;n B&aacute;i.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Hội Khuyến học tỉnh Y&ecirc;n B&aacute;i ngo&agrave;i việc trao đổi về c&aacute;ch thức thực hiện c&aacute;c nhiệm vụ khuyến học, khuyến t&agrave;i đ&atilde; trao đổi trọng t&acirc;m, chuy&ecirc;n s&acirc;u về c&aacute;ch thức bảo vệ đề t&agrave;i v&agrave; c&aacute;ch tham mưu để c&oacute; kinh ph&iacute; thực hiện Đề t&agrave;i &ldquo;Nghi&ecirc;n cứu bi&ecirc;n soạn t&agrave;i liệu, x&acirc;y dựng chương tr&igrave;nh dạy v&agrave; học chữ N&ocirc;m Dao ph&ugrave; hợp với bản sắc người Dao tỉnh Y&ecirc;n B&aacute;i&rdquo; c&aacute;ch thức tổ chức Hội thảo bộ t&agrave;i liệu về &ldquo;Chương tr&igrave;nh dạy v&agrave; học chữ N&ocirc;m Dao ph&ugrave; hợp với bản sắc người Dao tỉnh Y&ecirc;n B&aacute;i&rdquo;, đồng thời tập huấn nội dung về c&aacute;c bộ t&agrave;i liệu cho những người trực tiếp giảng dạy chữ N&ocirc;m Dao phục vụ Đề t&agrave;i. Đ&acirc;y l&agrave; b&agrave;i học tốt cho Hội Khuyến học Sơn La v&igrave; Sơn La c&oacute; nhiều n&eacute;t tương đồng do cả hai tỉnh đều l&agrave; tỉnh miền n&uacute;i c&oacute; nhiều đồng b&agrave;o d&acirc;n tộc thiểu số m&agrave; việc viết chữ, đọc chữ cổ của c&aacute;c d&acirc;n tộc đang c&oacute; nguy cơ bị mai một.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img src="/data/images/Capture000.JPG" style="height:409px; width:544px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img src="/data/images/Capture0000.JPG" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em>Hội Khuyến học Sơn La v&agrave; Hội Khuyến học tỉnh Y&ecirc;n B&aacute;i sau khi trao đổi kinh nghiệm đ&atilde; c&ugrave;ng trao tặng qu&agrave; lưu niệm.</em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm">&nbsp; &nbsp; &nbsp; &nbsp;<span style="font-size:14px">Ng&agrave;y 22/3/2023 Đo&agrave;n giao lưu, trao đổi kinh nghiệm tại Hội Khuyến học tỉnh Th&aacute;i Nguy&ecirc;n với chủ đề chuyển đổi số trong c&ocirc;ng t&aacute;c x&acirc;y dựng x&atilde; hội học tập.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white">&nbsp; &nbsp; &nbsp; &nbsp; Với số hội vi&ecirc;n gần 400 ng&agrave;n người tr&ecirc;n to&agrave;n tỉnh, Hội Khuyến học Th&aacute;i Nguy&ecirc;n đ&atilde; triển khai t&iacute;ch cực c&ocirc;ng t&aacute;c chuyển đổi số với việc b&aacute;m s&aacute;t y&ecirc;u cầu của chuyển đổi số l&agrave; tr&ecirc;n ba trụ cột: Ch&iacute;nh quyền số, kinh tế số v&agrave; x&atilde; hội số để triển khai những việc cụ thể ở tất cả c&aacute;c cấp Hội<strong> </strong>như:<strong> </strong>Tham&nbsp;gia t&iacute;ch cực v&agrave;o x&acirc;y dựng&nbsp;trang th&ocirc;ng tin điện tử của Hội khuyenhocthainguyen.edu.vn. Trang th&ocirc;ng tin được thiết kế với giao diện th&acirc;n thiện, hiện đại với c&aacute;c c&ocirc;ng cụ để giới thiệu, trao đổi, tuy&ecirc;n truyền, lưu trữ c&aacute;c tư liệu cũng như hoạt động từ cơ sở đến tỉnh Hội. Trang th&ocirc;ng tin c&ograve;n li&ecirc;n kết với c&aacute;c trang th&ocirc;ng tin của Trung ương v&agrave; địa phương như: Hội Khuyến học Việt Nam, Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo, Cổng dịch c&ocirc;ng Quốc gia, Cơ sở dữ liệu Quốc gia, C&ocirc;ng b&aacute;o Th&aacute;i Nguy&ecirc;n, Sở Gi&aacute;o dục Đ&agrave;o tạo, VNPT Th&aacute;i Nguy&ecirc;n&hellip;. Trang th&ocirc;ng tin của Hội được giới thiệu đến tất cả c&aacute;c cấp hội để c&aacute;c hội vi&ecirc;n biết v&agrave; quan t&acirc;m chia sẻ. Từ ng&agrave;y ra mắt trang th&ocirc;ng tin, c&aacute;c hoạt động của Hội từ tỉnh đến cơ sở được cập nhật thường xuy&ecirc;n, tạo sự quan t&acirc;m ch&uacute; &yacute; của hội vi&ecirc;n v&agrave; n&acirc;ng cao nhận thức cũng như hiệu quả c&ocirc;ng t&aacute;c tuy&ecirc;n truyền của hội khuyến học c&aacute;c cấp trong tỉnh.Lập nh&oacute;m điều h&agrave;nh c&ocirc;ng t&aacute;c h&agrave;ng ng&agrave;y th&ocirc;ng qua mạng x&atilde; hội Zalo gồm: Nh&oacute;m Thường trực Hội; nh&oacute;m Ban Chấp h&agrave;nh; nh&oacute;m Chủ tịch Hội c&aacute;c huyện, th&agrave;nh phố, thị x&atilde;; nh&oacute;m c&aacute;c huyện th&agrave;nh thực hiện th&iacute; điểm x&acirc;y dựng m&ocirc; h&igrave;nh &ldquo;C&ocirc;ng d&acirc;n học tập&rdquo;. Chủ tịch v&agrave; Ph&oacute; Chủ tịch Thường trực tỉnh Hội tham gia nh&oacute;m Zalo&nbsp;của&nbsp;Hội&nbsp;Khuyến học Việt Nam lập. Mọi th&ocirc;ng tin điều h&agrave;nh về c&ocirc;ng t&aacute;c khuyến học được phản &aacute;nh tức th&igrave; hai chiều qua hệ thống tin nhắn tiện dụng của Zalo. Thường trực tỉnh Hội đ&atilde; thống nhất với c&aacute;n bộ, hội vi&ecirc;n quy định về sử dụng th&ocirc;ng tin tr&ecirc;n c&aacute;c nh&oacute;m Zalo để phục vụ c&ocirc;ng t&aacute;c chuy&ecirc;n m&ocirc;n đảm bảo nhanh nhạy, lại tiết kiệm về thời gian cũng như kinh ph&iacute; gửi nhận văn bản qua Bưu điện (trừ c&aacute;c văn bản cần sử dụng dấu đỏ v&agrave; c&aacute;c văn bản mật).</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white">&nbsp; &nbsp; &nbsp; &nbsp; Tỉnh Hội đ&atilde; chủ động đăng k&yacute; v&agrave; sử dụng phần mềm li&ecirc;n th&ocirc;ng văn bản th&ocirc;ng qua hệ thống mailthainguyen.gov.vn v&agrave; chữ k&yacute; số khi giao dịch với c&aacute;c cơ quan trong tỉnh th&ocirc;ng qua xử l&yacute; tr&ecirc;n m&ocirc;i trường mạng.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img src="/data/images/Capture00000.JPG" style="height:432px; width:575px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em>Đ/c Nguyễn Quang Thuấn &ndash; Ph&oacute; Chủ tịch Thường trực Hội Khuyến học tỉnh Th&aacute;i Nguy&ecirc;n trao đổi kinh nghiệm với chủ đề chuyển đổi số</em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img src="/data/images/Capture000000.JPG" style="height:434px; width:579px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em>Hội Khuyến học 2 tỉnh Th&aacute;i Nguy&ecirc;n v&agrave; Sơn La chụp ảnh lưu niệm</em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm">&nbsp; &nbsp; &nbsp; &nbsp;Ng&agrave;y 24/3/2023 Đo&agrave;n giao lưu, trao đổi kinh nghiệm tại Hội Khuyến học tỉnh Thanh H&oacute;a.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;Thanh H&oacute;a l&agrave; miền đất c&oacute; truyền thống hiếu học, c&oacute; nhiều người con th&agrave;nh t&agrave;i t&igrave;nh nguyện đ&oacute;ng g&oacute;p chung tay c&ugrave;ng Hội Khuyến học tỉnh l&agrave;m tốt c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập. Hiện HKH tỉnh Thanh H&oacute;a đang quản l&yacute; 12 loại Quỹ khuyến học với số tiền tr&ecirc;n 300 tỷ đồng. Việc x&acirc;y dựng ph&aacute;t triển Quỹ KH lu&ocirc;n tr&ecirc;n tinh thần năm sau cao hơn hoặc bằng năm trước. Việc ph&aacute;t động x&acirc;y dựng quỹ thường th&ocirc;ng qua tổ chức lễ vinh danh, trao thưởng cho Gi&aacute;o sư, Ph&oacute; gi&aacute;o sư, tiến sỹ&hellip;trong buổi vinh danh đ&oacute; c&oacute; sự tham gia của Thường trực tỉnh ủy, UBND, c&aacute;c Sở ban ng&agrave;nh c&ugrave;ng ph&aacute;t động đ&oacute;ng g&oacute;p x&acirc;y dựng quỹ, qua đ&oacute; thường xuy&ecirc;n hỗ trợ x&acirc;y dựng điểm trường, hỗ trợ trang thiết bị phục vụ cho học tập; ph&aacute;t triển hội vi&ecirc;n v&agrave; tổ chức hội cho 2 khu vực (khu vực c&oacute; đồng b&agrave;o d&acirc;n tộc thiểu số, khu vực c&oacute; người d&acirc;n theo đạo)&hellip;</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img src="/data/images/Capture0000000.JPG" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="background-color:white">NGƯT Vương Văn Việt, Chủ tịch Hội Khuyến học tỉnh Thanh H&oacute;a ph&aacute;t biểu </span></em></span><span style="font-size:14px"><em><span style="background-color:white">v&agrave; trao đổi kinh nghiệm</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img src="/data/images/Capture1.JPG" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Đ/c Mai Thu Hương, Chủ tịch Hội Khuyến học tỉnh Sơn La ph&aacute;t biểu trao đổi v&agrave; tặng qu&agrave; lưu niệm cho Hội Khuyến học tỉnh Thanh H&oacute;a.</em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; Ng&agrave;y 27/3/2023, Đo&agrave;n giao lưu, trao đổi kinh nghiệm với Hội Khuyến học th&agrave;nh phố Đ&agrave; Nẵng.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;Trong buổi trao đổi, Hội Khuyến học Th&agrave;nh phố Đ&agrave; Nẵng đ&atilde; cho thấy c&aacute;c chương tr&igrave;nh, giải thưởng khuyến học khuyến t&agrave;i rất đa dạng như: Chương tr&igrave;nh học bổng do tổ chức phi ch&iacute;nh phủ nước ngo&agrave;i t&agrave;i trợ (Care the people, Passerelles numeriques Viet Nam, V&ograve;ng tay Th&aacute;i B&igrave;nh &ldquo;Pacific links foundation&rdquo;, Học bổng khuyến học, Chương tr&igrave;nh HB &ldquo;nu&ocirc;i dưỡng ước mơ&rdquo;, HB Ng&agrave;nh Ng&acirc;n h&agrave;ng, c&aacute; nh&acirc;n, đơn vị t&agrave;i trợ); Trao c&aacute;c giải thưởng khuyến t&agrave;i như (Giải thưởng học sinh xuất sắc to&agrave;n diện ti&ecirc;u biểu, giải thưởng khuyến t&agrave;i Huỳnh Ph&uacute;c Kh&aacute;ng, giải thưởng học sinh, sinh vi&ecirc;n vượt kh&oacute; học giỏi, giải thưởng HS l&agrave; c&aacute;n bộ Đo&agrave;n đội 2 giỏi, giải thưởng HS Cơ Tu vượt kh&oacute; hiếu học)&hellip;</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; Đặc biệt Hội Khuyến học Th&agrave;nh phố Đ&agrave; Nẵng đ&atilde; tổ <span style="background-color:white">chức Hội nghị hướng dẫn đ&aacute;nh gi&aacute;, c&ocirc;ng nhận danh hiệu C&ocirc;ng d&acirc;n học tập; đ&aacute;nh gi&aacute;, xếp loại Đơn vị học tập trong c&aacute;c cơ quan, đơn vị tr&ecirc;n địa b&agrave;n th&agrave;nh phố Đ&agrave; Nẵng.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img src="/data/images/Capture11.JPG" style="height:521px; width:749px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em>Thầy Nguyễn Hữu Xạ, Ph&oacute; Chủ tịch Thường trực Hội Khuyến học th&agrave;nh phố Đ&agrave; Nẵng trao đổi kinh nghiệm l&agrave;m c&ocirc;ng t&aacute;c khuyến học</em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;<img src="/data/images/Capture111.JPG" style="height:534px; width:627px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em>Thăm v&agrave; t&igrave;m hiểu phương thức hoạt động của Trung t&acirc;m học tập cộng đồng ở quận Cẩm Lệ, phường Khu&ecirc; Trung, tp Đ&agrave; Nẵng</em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp;Trung t&acirc;m học tập cộng đồng ở quận Cẩm Lệ, phường Khu&ecirc; Trung, tp Đ&agrave; Nẵng hoạt động dưới h&igrave;nh thức tạo c&aacute;c ph&ograve;ng tập thể h&igrave;nh, thể dục thẩm mỹ thu học ph&iacute; từ học vi&ecirc;n từ đ&oacute; lấy nguồn phục vụ cho tổ chức học tập cộng đồng.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><img alt="" src="/data/images/tin-tuc/Capture1111.JPG" style="height:489px; width:745px" />Thăm v&agrave; t&igrave;m hiểu phương thức hoạt động của Trung t&acirc;m học tập cộng đồng ở x&atilde; H&ograve;a Phước, huyện H&ograve;a Vang, tp Đ&agrave; Nẵng</em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; Trung t&acirc;m học tập cộng đồng ở x&atilde; H&ograve;a Phước, huyện H&ograve;a Vang, tp Đ&agrave; Nẵng hoạt động dưới phương thức mở c&aacute;c lớp học buổi tối 6,7,CN học kỹ thuật chăn nu&ocirc;i, trồng trọt cho người d&acirc;n ph&ugrave; hợp với đặc th&ugrave; từng địa phương theo nhu cầu đăng k&yacute; học (hướng cần g&igrave; học lấy) ph&iacute; mở lớp d&ugrave;ng quỹ khuyến học, huy động x&atilde; hội h&oacute;a.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; Ng&agrave;y 28/3/2023, Đo&agrave;n giao lưu, học tập, trao đổi kinh nghiệm với Hội Khuyến học tỉnh Quảng Trị;</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;Trong buổi trao đổi, Hội Khuyến học tỉnh Quảng Trị đ&atilde; cho thấy c&oacute; rất nhiều giải thưởng, học bổng khuyến học, khuyến t&agrave;i như: HB mầm xanh, HB tiếp sức đến trường, HB hoa t&igrave;nh thương, HB v&ograve;ng tay đồng đội, giải thưởng Trần H&agrave;nh, GT B&ugrave;i Dục T&agrave;i; 3 học bổng của tổ chức phi ch&iacute;nh phủ (Tổ chức v&ograve;ng tay Th&aacute;i B&igrave;nh, tổ chức C&acirc;y H&ograve;a B&igrave;nh, tổ chức Passerelles numeriques Viet Nam); c&oacute; 2 quỹ học bổng (Quỹ khuyến học Nguyễn Thanh Li&ecirc;m, Quỹ KH &ldquo;S&aacute;ng m&atilde;i niềm tin&rdquo;)</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px">&nbsp;</span><span style="font-size:13pt"><span style="font-size:14.0pt">&nbsp;<img src="/data/images/Capture11111.JPG" style="height:430px; width:575px" /></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14.0pt"><img src="/data/images/Capture2(2).JPG" style="height:476px; width:635px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em>Trao qu&agrave; lưu niệm giữa Hội Khuyến học hai tỉnh</em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img src="/data/images/Capture22(1).JPG" style="height:443px; width:625px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em>Đo&agrave;n c&ocirc;ng t&aacute;c của Hội Khuyến học tỉnh Sơn La viếng nghĩa trang Th&agrave;nh Cổ Quảng Trị</em></span></p>\r\n', '2023-04-10 03:12:34', 45, '/data/files/hkh/Capture0(2).JPG', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1373, 'Ký kết phối hợp tuyên truyền khuyến học, khuyến tài giai đoạn 2022-2030', 'Ngày 25/11, Hội Khuyến học tỉnh đã tổ chức Lễ ký kết với Báo Sơn La và Đài Phát thanh – Truyền hình tỉnh về Chương trình phối hợp “Đẩy mạnh tuyên truyền khuyến học, khuyến tài, xây dựng xã hội học tập” giai đoạn 2022-2030.', '<p style="text-align:center"><img alt="" src="/data/images/tin-tuc/30-copy.jpg" style="height:400px; width:600px" /><br />\r\nLễ K&yacute; kết&nbsp;Chương tr&igrave;nh phối hợp &ldquo;Đẩy mạnh tuy&ecirc;n truyền khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập&rdquo; giai đoạn 2022-2030</p>\r\n\r\n<p>Trong giai đoạn 2015-2021,&nbsp;c&aacute;c&nbsp;đơn vị&nbsp;đ&atilde; b&aacute;m s&aacute;t sự chỉ đạo của tỉnh, nội dung hoạt động của Hội&nbsp;x&acirc;y dựng&nbsp;nội dung tuy&ecirc;n truyền thường xuy&ecirc;n, s&acirc;u rộng v&agrave; thiết thực. Trong đ&oacute;,&nbsp;tập trung tuy&ecirc;n truyền&nbsp;c&aacute;c phong tr&agrave;o thi đua, cuộc vận động khuyến học, khuyến t&agrave;i, m&ocirc; h&igrave;nh học tập, tấm gương điển h&igrave;nh ti&ecirc;n tiến.&nbsp;Nhiều b&agrave;i viết, bản tin, ph&oacute;ng sự đ&atilde;&nbsp;tạo sự&nbsp;lan tỏa trong&nbsp;nh&acirc;n d&acirc;n. Trong giai đoạn n&agrave;y,&nbsp;B&aacute;o Sơn La đ&atilde; đăng tải 500 tin, b&agrave;i, ảnh tr&ecirc;n&nbsp;3 ấn phẩn: B&aacute;o Sơn La thường kỳ, B&aacute;o điện tử Sơn La v&agrave; Tin ảnh Sơn La. Đồng thời,&nbsp;x&acirc;y dựng c&aacute;c trang chuy&ecirc;n đề tuy&ecirc;n truyền đậm n&eacute;t về&nbsp;Ng&agrave;y Khuyến học Việt Nam (2/10), Ng&agrave;y th&agrave;nh lập Hội khuyến học tỉnh v&agrave; c&aacute;c hoạt động trước, trong v&agrave; sau Đại hội Khuyến học Sơn La, nhiệm kỳ 2021-2026. Đ&agrave;i Ph&aacute;t thanh -&nbsp;Truyền h&igrave;nh tỉnh ph&aacute;t s&oacute;ng gần 500 tin, b&agrave;i, ph&oacute;ng sự bằng tiếng phổ th&ocirc;ng, tiếng Th&aacute;i v&agrave; tiếng M&ocirc;ng; phối hợp với Trung t&acirc;m TT-VH c&aacute;c huyện, th&agrave;nh phố x&acirc;y dựng v&agrave; ph&aacute;t s&oacute;ng 260 số chuy&ecirc;n mục &ldquo;Khuyến học Sơn La&rdquo; v&agrave;o thứ 6 h&agrave;ng tuần&hellip; C&ocirc;ng t&aacute;c tuy&ecirc;n truyền&nbsp;đ&atilde; g&oacute;p phần&nbsp;n&acirc;ng cao nhận thức v&agrave; tr&aacute;ch nhiệm của c&aacute;c cấp, c&aacute;c ng&agrave;nh, c&aacute;n bộ v&agrave; nh&acirc;n d&acirc;n trong&nbsp;tỉnh về c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i. T&iacute;nh đến cuối năm 2021, to&agrave;n tỉnh c&oacute;&nbsp;73,6%&nbsp;gia đ&igrave;nh được c&ocirc;ng nhận gia đ&igrave;nh học tập; 85,5% tổ, bản tiểu khu đạt&nbsp;cộng đồng học tập; 80,7% đơn vị, cơ quan, doanh nghiệp học tập v&agrave; 70,2% d&ograve;ng họ&nbsp;học tập.</p>\r\n\r\n<p>Tại Lễ k&yacute; kết, c&aacute;c đại biểu đ&atilde; thảo luận, trao đổi một số nội dung về c&ocirc;ng t&aacute;c phối hợp tuy&ecirc;n truyền&nbsp;như: C&ocirc;ng&nbsp;t&aacute;c phối hợp cung cấp th&ocirc;ng tin viết tin, b&agrave;i; thực hiện c&aacute;c h&igrave;nh thức tuy&ecirc;n truyền phong ph&uacute;, ph&ugrave; hợp với điều kiện thực tế của từng địa phương, nhằm&nbsp;n&acirc;ng cao hiệu quả c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i&hellip;</p>\r\n\r\n<p style="text-align:center"><img alt="" src="/data/images/tin-tuc/31-copy.jpg" style="height:400px; width:600px" /><br />\r\nL&atilde;nh đạo c&aacute;c đơn vị tiến h&agrave;nh K&yacute; kết Chương tr&igrave;nh phối hợp.</p>\r\n\r\n<p>C&aacute;c đơn vị đ&atilde;&nbsp;thống nhất&nbsp;v&agrave; k&yacute; kết Chương tr&igrave;nh&nbsp;phối hợp hoạt động giai đoạn 2022 -&nbsp;2030, tập trung&nbsp;th&ocirc;ng tin tuy&ecirc;n truyền rộng r&atilde;i trong x&atilde; hội về c&aacute;c văn bản l&atilde;nh đạo, chỉ đạo của Trung ương, của cấp ủy, ch&iacute;nh quyền c&aacute;c cấp về phong tr&agrave;o x&acirc;y dựng x&atilde; hội học tập, m&ocirc; h&igrave;nh học tập, điển h&igrave;nh ti&ecirc;u biểu. Tuy&ecirc;n truyền&nbsp;Chỉ thị số 14/CT-TTg ng&agrave;y 25/5/2021 của Thủ tướng Ch&iacute;nh phủ về đẩy mạnh c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập giai đoạn 2021-2030; Quyết định số 387/QĐ-TTg của Thủ tướng Ch&iacute;nh phủ về ph&ecirc; duyệt Chương tr&igrave;nh &ldquo;đẩy mạnh phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị giai đoạn 2021-2030&rdquo;; Quyết định số 2547/QĐ-UBND của UBND tỉnh ban h&agrave;nh Kế hoạch thực hiện Đề &aacute;n &ldquo;X&acirc;y dựng x&atilde; hội học tập&rdquo; giai đoạn 2021-2030 tr&ecirc;n địa b&agrave;n tỉnh Sơn La&hellip;</p>\r\n', '2022-11-30 07:26:10', 66, '/data/images/tin-tuc/31-copy.jpg', '', 1, 33, 0, 0, '', 0, '', 'https://baosonla.org.vn/', 18, 'xuat_ban', '0000-00-00 00:00:00', 1, 0, 1);
INSERT INTO `tin_tuc` VALUES (1374, 'Giao ban công tác khuyến học Cụm thi đua số I các tỉnh Tây Bắc năm 2022', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; Ng&agrave;y 29.11, tại huyện Y&ecirc;n Minh, tỉnh H&agrave; Giang, cụm thi đua khuyến học c&aacute;c tỉnh T&acirc;y Bắc gồm 9 tỉnh: H&ograve;a B&igrave;nh, Ph&uacute; Thọ, Y&ecirc;n B&aacute;i, Sơn La, Điện Bi&ecirc;n, Lai Ch&acirc;u, L&agrave;o Cai, Tuy&ecirc;n Quang, H&agrave; Giang tổ chức hội nghị giao ban - trao đổi kinh nghiệm c&ocirc;ng t&aacute;c khuyến học, khyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập năm 2022.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp;Tới dự động vi&ecirc;n v&agrave; chỉ đạo Hội nghị c&oacute;: Đ/c Nguyễn Mạnh H&ugrave;ng, Ph&oacute; Chủ tịch ki&ecirc;m Tổng thư k&yacute; T.Ư HKH Việt Nam; đ/c Trần Đức Q&uacute;y, tỉnh ủy vi&ecirc;n, Ph&oacute; Chủ tịch UBND tỉnh H&agrave; Giang; l&atilde;nh đạo huyện Y&ecirc;n Minh v&agrave; Thường trực HKH c&aacute;c tỉnh trong cụm I.</span></span></span></p>\r\n\r\n<table align="center" cellspacing="0" class="Table" style="border-collapse:collapse; border:undefined">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img src="/data/images/tin-tuc/Capture12.JPG" style="border-bottom-style:none; border-bottom-width:0px; border-left-style:none; border-left-width:0px; border-right-style:none; border-right-width:0px; border-top-style:none; border-top-width:0px; box-sizing:border-box; height:309px; list-style-type:none; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; max-width:100%; outline:none; padding-bottom:0px; padding-left:0px; padding-right:0px; padding-top:0px; vertical-align:middle; width:586px" /></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="background-color:#efefef">\r\n			<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="color:#1f497d">C&aacute;c đồng ch&iacute; tham dự Hội nghị giao ban</span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Năm 2022, Hội Khuyến học c&aacute;c tỉnh T&acirc;y Bắc đ&atilde; nỗ lực ho&agrave;n th&agrave;nh c&aacute;c chỉ ti&ecirc;u, nhiệm vụ đề ra. C&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập đạt nhiều kết quả nổi bật: Hoạt động tuy&ecirc;n truyền được đẩy mạnh, nội dung, phương thức hoạt động c&oacute; nhiều đổi mới, s&aacute;t thực tế, g&oacute;p phần n&acirc;ng cao nhận thức của c&aacute;c tầng lớp nh&acirc;n d&acirc;n về c&ocirc;ng t&aacute;c khuyến học. Tăng cường củng cố tổ chức hội v&agrave; ph&aacute;t triển hội vi&ecirc;n; trong cụm hiện c&oacute; gần 2 triệu hội vi&ecirc;n. Phong tr&agrave;o xuất hiện nhiều m&ocirc; h&igrave;nh khuyến học ti&ecirc;u biểu, s&aacute;ng tạo; C&aacute;c tỉnh đều đẩy mạnh c&ocirc;ng t&aacute;c vận động, x&acirc;y dựng Quỹ KH c&aacute;c cấp, huy động sức mạnh tổng hợp của to&agrave;n x&atilde; hội tham gia hỗ trợ cho sự nghiệp gi&aacute;o dục&hellip;</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Hội Khuyến học tỉnh Sơn La tham luận tập trung về vấn đề &ldquo;Những kết quả bước đầu trong việc triển khai thực hiện chuyển đổi số trong c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập v&agrave; học tập suốt đời. Những thuận lợi v&agrave; kh&oacute; khăn trong việc tổ chức chỉ đạo thực hiện, kiến nghị v&agrave; đề xuất&rdquo;.</span></span></span></p>\r\n\r\n<table align="center" cellspacing="0" class="Table" style="border-collapse:collapse; border:undefined">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img src="/data/images/tin-tuc/Capture17.JPG" style="height:509px; width:535px" /></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="background-color:#efefef">\r\n			<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="color:#002060">Đ/c Mai Thu Hương - Chủ tịch Hội Khuyến học tỉnh Sơn La ph&aacute;t biểu tham luận</span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; Tại hội nghị, Trung ương Hội Khuyến học Việt Nam đ&atilde; tặng 100 triệu đồng cho Hội Khuyến học tỉnh H&agrave; Giang; 50 triệu đồng cho Hội Khuyến học huyện Y&ecirc;n Minh; tặng 10 suất học bổng trị gi&aacute; 30 triệu đồng cho 10 c&aacute; nh&acirc;n học tập ti&ecirc;u biểu tr&ecirc;n địa b&agrave;n huyện Y&ecirc;n Minh; Hội Khuyến học tỉnh Ph&uacute; Thọ tặng 40 suất qu&agrave; trị gi&aacute; 20 triệu cho học sinh ngh&egrave;o c&oacute; ho&agrave;n cảnh kh&oacute; khăn tr&ecirc;n địa b&agrave;n H&agrave; Giang.&nbsp;</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="background-color:white"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; Hội Khuyến học H&agrave; Giang trao cờ lu&acirc;n lưu đăng cai Cụm thi đua T&acirc;y Bắc năm 2023 cho Hội Khuyến học tỉnh Ho&agrave; B&igrave;nh.</span></span>/.</span></span></p>\r\n', '2022-12-02 04:24:36', 55, '/data/files/hkh/Capture21.JPG', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1375, 'Một số hoạt động kỷ niệm 40 năm ngày Nhà giáo Việt Nam 20/11 của  Hội Khuyến học tỉnh Sơn La ', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Ngay từ đầu th&aacute;ng 11 năm 2022; Hội Khuyến học tỉnh đ&atilde; đồng h&agrave;nh c&ugrave;ng một số đơn vị tổ chức c&aacute;c hoạt động khen thưởng để động vi&ecirc;n, kh&iacute;ch lệ phong tr&agrave;o dạy v&agrave; học cho c&aacute;c thầy c&ocirc;, học sinh v&agrave; nh&agrave; trường nh&acirc;n kỷ niệm 40 năm ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam 20/11, cụ thể như sau: </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; Ng&agrave;y 01/11/2022 trong buổi Tuy&ecirc;n dương &ldquo;Học sinh 3 tốt&rdquo;, &ldquo;Học sinh 3 r&egrave;n luyện&rdquo; do Đo&agrave;n thanh ni&ecirc;n cộng sản Hồ Ch&iacute; Minh tỉnh Sơn La tổ chức; Hội Khuyến học tỉnh đ&atilde; trao giấy khen v&agrave; tiền thưởng cho 15 nh&agrave; gi&aacute;o trẻ ti&ecirc;u biểu, 15 học sinh 3 tốt, học sinh 3 r&egrave;n luyện với tổng số tiền l&agrave; 15 triệu đồng.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:10pt"><span style="font-size:14.0pt"><img src="/data/images/tin-tuc/Capture15.JPG" style="height:382px; width:548px" /></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:12px"><em><span style="color:#0070c0">Đ/c Mai Thu Hương -&nbsp; Chủ tịch Hội Khuyến học tỉnh t</span></em><em><span style="color:#0070c0">rao </span></em><em><span style="color:#0070c0">giấy khen v&agrave; tiền thưởng cho học sinh 3 tốt, học sinh 3 r&egrave;n luyện</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:10pt"><span style="font-size:13.0pt"><img src="/data/images/tin-tuc/Capture16.JPG" style="height:309px; width:596px" /></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:12px"><em><span style="color:#0070c0">Đ/c Mai Thu Hương -&nbsp; Chủ tịch Hội Khuyến học tỉnh t</span></em><em><span style="color:#0070c0">rao </span></em><em><span style="color:#0070c0">Giấy khen v&agrave; tiền thưởng cho nh&agrave; gi&aacute;o trẻ ti&ecirc;u biểu</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;S&aacute;ng ng&agrave;y 6/11, Hội khuyến tỉnh phối hợp Đo&agrave;n Thiện nguyện &ldquo;Hội bảo tồn lan rừng Việt Nam&rdquo; , Bộ Chỉ huy Bộ đội Bi&ecirc;n ph&ograve;ng tỉnh v&agrave; C&acirc;u lạc bộ &ldquo;Hoa lan Y&ecirc;n Ch&acirc;u&rdquo; tổ chức trao tặng qu&agrave; cho một số trường tại tuyến huyện Y&ecirc;n Ch&acirc;u, tỉnh Sơn La gồm c&aacute;c trường: <span style="color:black">Trường Tiểu học x&atilde; Chiềng Tương; điểm Trường Tiểu học bản Lao Kh&ocirc;; x&atilde; L&oacute;ng Phi&ecirc;ng; Mường Lựm; Chiềng S&agrave;ng; Chiềng Đ&ocirc;ng A; Chiềng Đồng B; Viềng L&aacute;n.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:10pt"><span style="font-size:14.0pt"><span style="color:red"><img src="/data/images/tin-tuc/z3859070716886_407c2f44dda4c4f0afe9a0f2f79e17f4.jpg" style="height:414px; width:590px" /></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:12px"><em><span style="color:#00b0f0">Đ/c Nguyễn Văn Bao - Ph&oacute; Chủ tịch Thường trực Hội Khuyến học tỉnh trao qu&agrave; động vi&ecirc;n nh&agrave; trường nh&acirc;n kỷ niệm 40 năm Ng&agrave;y Nh&agrave; gi&aacute;o Vi&ecirc;t Nam 20/11</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:10pt"><span style="font-size:14.0pt"><img src="/data/images/tin-tuc/IMGP2899.JPG" style="height:395px; width:592px" /></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:12px"><em><span style="color:#00b0f0">Đ/c Nguyễn Văn Bao - Ph&oacute; Chủ tịch Hội Khuyến học tỉnh c&ugrave;ng đo&agrave;n thiện nguyện trao qu&agrave; tại điểm Trường Tiểu học bản Lao Kh&ocirc;, x&atilde; L&oacute;ng Phi&ecirc;ng, huyện Y&ecirc;n Ch&acirc;u</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;Ng&agrave;y 10/11/2022 trong Lễ tổng kết Hội thi gi&aacute;o vi&ecirc;n giỏi cấp THCS tỉnh Sơn La lần thứ XI, năm 2022 do Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo tổ chức; Hội Khuyến học tỉnh đ&atilde; trao Giấy khen v&agrave; tiền thưởng cho 30 <span style="background-color:white"><span style="color:black">gi&aacute;o vi&ecirc;n đạt danh hiệu gi&aacute;o vi&ecirc;n giỏi c&oacute; th&agrave;nh t&iacute;ch cao tại Hội thi</span></span> với tổng số tiền l&agrave; 15 triệu đồng.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:10pt"><em><span style="font-size:14.0pt"><span style="color:#0070c0"><img src="/data/images/tin-tuc/Capture31.JPG" style="height:305px; width:567px" /></span></span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:12px"><em><span style="color:#0070c0">Đồng ch&iacute; Nguyễn Văn Bao - Ph&oacute; Chủ tịch Thường trực Hội Khuyến học tỉnh trao Giấy khen v&agrave; tiền thưởng cho 30 gi&aacute;o vi&ecirc;n</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;Ng&agrave;y 12/11/2022 trong buổi Gặp mặt truyền thống kỷ niệm 60 năm ng&agrave;y th&agrave;nh lập ng&agrave;nh Gi&aacute;o dục v&agrave; Đ&agrave;o tạo tỉnh Sơn La v&agrave; 40 năm ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam 20/11 do Ban li&ecirc;n lạc Hội Cựu gi&aacute;o chức Sơn La tại H&agrave; Nội tổ chức, Hội Khuyến học tỉnh đ&atilde; c&oacute; l&atilde;ng hoa đến ch&uacute;c mừng</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:10pt"><span style="font-size:14.0pt"><img src="/data/images/tin-tuc/Capture18.JPG" style="height:431px; width:588px" /></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:10pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="font-size:12px"> <em><span style="color:#0070c0">Đồng ch&iacute; Mai Thu Hương - Chủ tịch Hội Khuyến học tỉnh tặng hoa </span></em></span></span><span style="font-size:10pt"><span style="font-size:12px"><em><span style="color:#0070c0">tại buổi gặp mặt</span></em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;Ng&agrave;y 15/11/2022 trường Tiểu học Chiềng Hặc, huyện Y&ecirc;n Ch&acirc;u tổ chức Lễ kỷ niệm 40 ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam, 20 năm th&agrave;nh lập trường, đ&oacute;n Bằng c&ocirc;ng nhận trường đạt chuẩn Quốc gia; Hội Khuyến học tỉnh đ&atilde; xuống tham dự, tặng Giấy khen tiền thưởng cho nh&agrave; trường v&agrave; trao 30 xuất học bổng cho c&aacute;c em học sinh chăm ngoan, hiếu học với tổng số tiền l&agrave; 16.500.000 đồng.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:10pt"><span style="font-size:14.0pt"><img src="/data/images/tin-tuc/H%E1%BB%99i%20Khuy%E1%BA%BFn%20h%E1%BB%8Dc%20t%E1%BB%89nh%20t%E1%BA%B7ng%20gi%E1%BA%A5y%20khen%20t%E1%BA%ADp%20th%E1%BB%83%20Tr%C6%B0%E1%BB%9Dng%20Ti%E1%BB%83u%20h%E1%BB%8Dc%20Chi%E1%BB%81ng%20H%E1%BA%B7c%20%C4%91%E1%BA%A1t%20th%C3%A0nh%20t%C3%ADch%20x%C3%A2y%20d%E1%BB%B1ng%20tr%C6%B0%E1%BB%9Dng%20%C4%91%E1%BA%A1t%20chu%E1%BA%A9n%20Qu%E1%BB%91c%20gia.jpg" style="height:330px; width:589px" /></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:12px"><em><span style="color:#0070c0">Đồng ch&iacute; Mai Thu Hương - Chủ tịch Hội Khuyến học tỉnh tặng Giấy khen v&agrave; tiền thưởng trường đạt chuẩn Quốc gia</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:10pt"><span style="font-size:13.0pt"><img src="/data/images/tin-tuc/H%E1%BB%99i%20Khuy%E1%BA%BFn%20h%E1%BB%8Dc%20t%E1%BB%89nh%20trao%20h%E1%BB%8Dc%20b%E1%BB%95ng%20T%C3%B4%20Hi%E1%BB%87u%20cho%20c%C3%A1c%20em%20h%E1%BB%8Dc%20sinh%20ch%C4%83m%20ngoan%20h%E1%BB%8Dc%20gi%E1%BB%8Fi%20tr%C6%B0%E1%BB%9Dng%20Ti%E1%BB%83u%20h%E1%BB%8Dc%20Chi%E1%BB%81ng%20H%E1%BA%B7c.jpg" style="height:341px; width:611px" /></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:12px"><em><span style="color:#0070c0">Đồng ch&iacute; Mai Thu Hương - Chủ tịch Hội Khuyến học tỉnh trao 30 xuất học bổng cho học sinh chăm ngoan, hiếu học tại trường tiểu học Chiềng Hặc</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; Ng&agrave;y 18/11/2022 Hội Khuyến học tỉnh đ&atilde; c&oacute; l&atilde;ng hoa v&agrave; qu&agrave; đến ch&uacute;c mừng Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo tỉnh Sơn La nh&acirc;n kỷ niệm 40 năm ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam 20/11 </span><span style="font-size:10pt">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src="/data/images/tin-tuc/z3890310946372_bffea98d90818716994488671d961bac.jpg" style="height:355px; width:624px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp;Từ ng&agrave;y 16/11/2022 đến&nbsp; ng&agrave;y 19/11/2022; Hội Khuyến học tỉnh đ&atilde; tổ chức đến thăm v&agrave; nắm t&igrave;nh h&igrave;nh hoạt động khuyến học của c&aacute;c đơn vị như Li&ecirc;n đo&agrave;n Lao động tỉnh, Bộ chỉ huy bộ đội Bi&ecirc;n ph&ograve;ng, Trường Cao đẳng Y Sơn La tại c&aacute;c đơn vị n&agrave;y nh&igrave;n chung phong tr&agrave;o khuyến học vẫn được duy tr&igrave; v&agrave; ph&aacute;t triển rất tốt.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:10pt"><span style="color:#7030a0"><img src="/data/images/tin-tuc/z3935382379256_85086c7974c5226fa7ab7c2fb57c01b3.jpg" style="height:358px; width:636px" /></span></span></p>\r\n\r\n<p style="text-align:center"><span style="font-size:12px"><em><span style="color:#1f497d">Đo&agrave;n c&ocirc;ng t&aacute;c thăm v&agrave; l&agrave;m việc với Hội khuyến học trường Cao đẳng Y Sơn La</span></em></span></p>\r\n', '2022-12-05 08:41:15', 121, '/data/files/hkh/z3890310946372_bffea98d90818716994488671d961bac.jpg', '', 0, 52, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
