INSERT INTO `tin_tuc` VALUES (1378, 'Phương châm “Cần gì học nấy” và “học đi đôi với hành” của Trung tâm học tập cộng đồng xã Hát Lót, huyện Mai Sơn', 'Nguyễn Thị Nhật Linh', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white">&nbsp; &nbsp; &nbsp; &nbsp; Với phương ch&acirc;m &ldquo;Cần g&igrave; học nấy&rdquo; v&agrave; &ldquo;học đi đ&ocirc;i với h&agrave;nh&rdquo;&hellip;, Trung t&acirc;m học tập cộng đồng x&atilde; H&aacute;t L&oacute;t h&agrave;ng năm thường xuy&ecirc;n mở c&aacute;c lớp tập huấn về chăn nu&ocirc;i v&agrave; trồng trọt cụ thể mỗi năm mở 3 lớp tập huấn với khoảng hơn 200 lượt người tham gia, gi&uacute;p b&agrave; con c&oacute; c&aacute;c kiến thức khoa học kỹ thuật ph&ugrave; hợp với nhu cầu ph&aacute;t triển kinh tế, x&atilde; hội của địa phương. Điều n&agrave;y được thể hiện r&otilde; như: To&agrave;n x&atilde; c&oacute; 13 hộ đạt danh hiệu hộ sản xuất kinh doanh giỏi cấp Trung ương; 35 hộ cấp tỉnh; 84 hộ cấp huyện v&agrave; 125 hộ cấp x&atilde;. Xuất hiện ng&agrave;y c&agrave;ng nhiều c&aacute;c m&ocirc; h&igrave;nh chăn nu&ocirc;i theo hướng h&agrave;ng h&oacute;a; số hộ kh&aacute;, gi&agrave;u tăng l&ecirc;n qua c&aacute;c năm, số hộ ngh&egrave;o giảm xuống c&ograve;n 4,6 %. <span style="background-color:white">C&oacute; thể khẳng định, hiệu quả hoạt động của trung t&acirc;m học tập cộng đồng x&atilde; H&aacute;t L&oacute;t kh&ocirc;ng chỉ c&oacute; vai tr&ograve; gi&uacute;p mọi người d&acirc;n được học tập thường xuy&ecirc;n m&agrave; c&ograve;n trang bị kiến thức nhiều mặt, g&oacute;p phần x&oacute;a đ&oacute;i, giảm ngh&egrave;o, tăng năng suất lao động, giải quyết việc l&agrave;m, chuyển dịch cơ cấu kinh tế đ&uacute;ng hướng, n&acirc;ng cao chất lượng cuộc sống của từng người d&acirc;n v&agrave; cả cộng đồng. </span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="background-color:white">&nbsp; &nbsp; &nbsp; &nbsp; Tiếp tục thực hiện những mục ti&ecirc;u đề ra trong năm 2022, Trung t&acirc;m học tập cộng đồng tiếp tục kết hợp với Khuyến n&ocirc;ng x&atilde; mở lớp tập huấn chuyển giao kỹ thuật chăm s&oacute;c c&acirc;y ăn quả v&agrave; chăn nu&ocirc;i d&ecirc;, b&ograve; v&agrave;o ng&agrave;y 30/3/2022 tại bản Lọng Khoang với 60 học vi&ecirc;n tham gia.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img alt="Description: LK..jpg" src="/data/images/anh%20co%20so/Capture89.JPG" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="background-color:white"><em>Tập huấn chuyển giao kỹ thuật chăm s&oacute;c c&acirc;y ăn quả tại bản Lọng Khoang</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="background-color:white"><strong><em><img alt="Description: ảnh THLK.jpg" src="/data/images/anh%20co%20so/Capture78.JPG" style="height:292px; width:432px" /></em></strong></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="background-color:white"><em>Tập huấn chuyển giao khoa học kỹ thuật chăn nu&ocirc;i b&ograve; tại bản Lọng Khoang</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="background-color:white"><strong><em><img alt="Description: người dân thuqwj hành LK.jpg" src="/data/images/anh%20co%20so/Capture67.JPG" style="height:313px; width:383px" /></em></strong></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="background-color:white"><em>Người d&acirc;n trực tiếp thực h&agrave;nh những hướng dẫn m&agrave; giảng vi&ecirc;n trao đổi.</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp; Sau lớp tập huấn nhiều n&ocirc;ng d&acirc;n tỏ ra phấn khởi v&igrave; tiếp thu được nhiều kỹ thuật mới trong trồng trọt, chăn nu&ocirc;iđể &aacute;p dụng hiệu quả trong gia đ&igrave;nh.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white">Để ph&aacute;t huy hiệu quả v&igrave; mục ti&ecirc;u x&acirc;y dựng x&atilde; hội học tập trong thời gian tới Trung t&acirc;m đề ra những giải ph&aacute;p cụ thể: </span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white">&nbsp; &nbsp; &nbsp; &nbsp;Tăng cường c&ocirc;ng t&aacute;c tuy&ecirc;n truyền n&acirc;ng cao nhận thức của người d&acirc;n về tầm quan trọng của việc học tập thường xuy&ecirc;n v&agrave; học tập suốt đời.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white">&nbsp; &nbsp; &nbsp; X&acirc;y dựng kế hoạch học tập của Trung t&acirc;m ph&ugrave; hợp với điều kiện thực tế của địa phương tr&ecirc;n cơ sở t&igrave;m hiểu nhu cầu, mong muốn của người d&acirc;n để đ&aacute;p ứng kịp thời.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white">&nbsp; &nbsp; &nbsp; &nbsp;Tổ chức hoạt động học tập, lựa chọn c&aacute;c nội dung dạy học ph&ugrave; hợp với từng đối tượng học, ph&aacute;t huy t&iacute;nh t&iacute;ch cực, độc lập của người học, sử dụng phương ph&aacute;p v&agrave; h&igrave;nh thức tổ chức dạy học ph&ugrave; hợp cho người tham gia học tập./.</span></span></p>\r\n', '2022-12-27 02:04:08', 36, '/data/files/anh%20co%20so/Capture89.JPG', '', 0, 36, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 1, 18, 0);
INSERT INTO `tin_tuc` VALUES (1379, 'Hội Khuyến học Việt Nam: Xác định 10 nhiệm vụ trọng tâm năm 2023', 'Ngày 28/12, tại Thủ đô Hà Nội, Hội Khuyến học Việt Nam tổ chức Hội nghị Ban Chấp hành lần thứ ba, khóa VI, nhiệm kỳ 2021-2026. Nguyên Phó Chủ tịch nước, Chủ tịch Hội Khuyến học Việt Nam Nguyễn Thị Doan chủ trì Hội nghị.', '<p style="margin-left:0cm; margin-right:0cm"><img src="/data/images/tin-tuc/Capturen.JPG" style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;; font-size:14px; height:428px; width:595px" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:#fbfaf9"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Năm 2022 l&agrave; năm đầu ti&ecirc;n Trung ương Hội triển khai Nghị quyết Đại hội VI, c&ocirc;ng t&aacute;c khuyến học c&oacute; nhiều kh&oacute; khăn v&agrave; th&aacute;ch thức, đặc biệt vẫn chịu ảnh hưởng của đại dịch COVID-19. Song, với tinh thần t&acirc;m huyết, tr&aacute;ch nhiệm, c&aacute;ch l&agrave;m s&aacute;ng tạo, vượt kh&oacute; khăn, cầu thị, đổi mới trong chỉ đạo, tổ chức c&aacute;c hoạt động, phối hợp nhịp nh&agrave;ng n&ecirc;n c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i (KH, KT) ở c&aacute;c cấp trong cả nước vẫn đạt được nhiều kết quả đ&aacute;ng kh&iacute;ch lệ.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:#fbfaf9"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Từ Trung ương đến cơ sở, nhận thức của người d&acirc;n ng&agrave;y c&agrave;ng đầy đủ hơn, c&aacute;c m&ocirc; h&igrave;nh học tập ng&agrave;y c&agrave;ng ph&aacute;t triển, g&oacute;p phần tạo dựng phong tr&agrave;o KH, KT, x&acirc;y dựng x&atilde; hội học tập ng&agrave;y c&agrave;ng hiệu quả, lan tỏa trong x&atilde; hội, được cấp ủy, ch&iacute;nh quyền c&aacute;c cấp đồng t&igrave;nh ủng hộ, người d&acirc;n t&iacute;ch cực hưởng ứng.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:#fbfaf9"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; C&aacute;c cấp hội đ&atilde; t&iacute;ch cực, chủ động tham mưu cho cấp ủy, ch&iacute;nh quyền v&agrave; phối hợp với c&aacute;c cấp, c&aacute;c ng&agrave;nh trong hoạt động KH, KT; đổi mới v&agrave; n&acirc;ng cao chất lượng c&ocirc;ng t&aacute;c th&ocirc;ng tin tuy&ecirc;n truyền với nội dung, h&igrave;nh thức đa dạng, phong ph&uacute;, g&oacute;p phần n&acirc;ng cao nhận thức trong x&atilde; hội về c&ocirc;ng t&aacute;c KH, KT, x&acirc;y dựng x&atilde; hội học tập.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:#fbfaf9"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Hội Khuyến học c&aacute;c cấp đ&atilde; triển khai thực hiện 2 chương tr&igrave;nh của Ch&iacute;nh phủ: &quot;Đẩy mạnh phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị&quot; v&agrave; x&acirc;y dựng m&ocirc; h&igrave;nh &quot;C&ocirc;ng d&acirc;n học tập&quot; kịp thời, bước đầu đạt kết quả thiết thực.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:#fbfaf9"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;C&ocirc;ng t&aacute;c kiện to&agrave;n, ph&aacute;t triển tổ chức hội v&agrave; hội vi&ecirc;n ng&agrave;y c&agrave;ng được quan t&acirc;m, ph&aacute;t triển hội trong trường đại học, cao đẳng, c&aacute;c cơ quan, đơn vị, lực lượng vũ trang. Theo b&aacute;o c&aacute;o của hội khuyến học c&aacute;c tỉnh, th&agrave;nh phố, đến nay cả nước c&oacute; hơn 23 triệu hội vi&ecirc;n, tăng 2% so với năm 2021; tr&ecirc;n 130 ngh&igrave;n chi hội khuyến học; gần 130 ngh&igrave;n ban khuyến học, tăng 7% so với năm 2021. B&ecirc;n cạnh những kết quả đ&atilde; đạt được, c&ocirc;ng t&aacute;c KH, KT, x&acirc;y dựng x&atilde; hội học tập vẫn gặp phải những kh&oacute; khăn, bất cập cần th&aacute;o gỡ.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:#fbfaf9"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Gi&aacute;o sư, tiến sĩ Nguyễn Thị Doan đề nghị hệ thống hội khuyến học trong cả nước ph&aacute;t huy th&agrave;nh t&iacute;ch đạt được của năm 2022, thực hiện tốt nhiệm vụ năm 2023 tr&ecirc;n tinh thần s&aacute;ng tạo, nhiệt t&igrave;nh, vượt kh&oacute; vươn l&ecirc;n.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:#fbfaf9"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Cũng tại Hội nghị, c&aacute;c đại biểu đ&atilde; x&aacute;c định 10 nhiệm vụ trọng t&acirc;m của Hội Khuyến học c&aacute;c cấp năm 2023 để tập trung triển khai thực hiện.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:#fbfaf9"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp; &nbsp; &nbsp; &nbsp; Trước đ&oacute;, ng&agrave;y 27/12, Hội Khuyến học Việt Nam đ&atilde; phối hợp với Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo tổ chức Hội thảo về bộ ti&ecirc;u ch&iacute; đ&aacute;nh gi&aacute; việc thực hiện x&acirc;y dựng x&atilde; hội học tập tr&ecirc;n địa b&agrave;n h&agrave;nh ch&iacute;nh c&aacute;c cấp.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm">&nbsp;</p>\r\n', '2022-12-29 03:16:48', 36, '/data/files/hkh/Capturen.JPG', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1380, 'Hội nghị BCH lần thứ ba Hội Khuyến học Việt Nam: Thực hiện nhiệm vụ năm 2023 với tinh thần sáng tạo, vượt khó', '', '<p><a href="https://congdankhuyenhoc.vn/hoi-khuyen-hoc-viet-nam-to-chuc-hoi-nghi-ban-chap-hanh-lan-thu-ba-khoa-vi-179221228114720273.htm">https://congdankhuyenhoc.vn/hoi-khuyen-hoc-viet-nam-to-chuc-hoi-nghi-ban-chap-hanh-lan-thu-ba-khoa-vi-179221228114720273.htm</a></p>\r\n', '2022-12-29 03:23:11', 33, '/data/files/hkh/Capturenh.JPG', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1381, 'Triển khai nhiệm vụ công tác khuyến học năm 2023.', 'Ngày 5/1, Hội Khuyến học tỉnh đã tổ chức Hội nghị tổng kết công tác khuyến học năm 2022; triển khai nhiệm vụ năm 2023.', '<p><a href="https://baosonla.org.vn/khoa-giao/trien-khai-nhiem-vu-cong-tac-khuyen-hoc-nam-2023-QzEVCi24g.html">https://baosonla.org.vn/khoa-giao/trien-khai-nhiem-vu-cong-tac-khuyen-hoc-nam-2023-QzEVCi24g.html</a></p>\r\n', '2023-01-06 01:57:25', 72, '/data/files/hkh/Capturenhung.JPG', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1382, 'Đoàn công tác của Quỹ học bổng  ISSHIN - ASAHI Nhật Bản và Hội Khuyến học tỉnh Sơn La làm việc tại một số Trường THPT trên địa bàn thành phố Sơn La. ', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;Ng&agrave;y 06/01/2023 Hội Khuyến học tỉnh c&ugrave;ng Quỹ học bổng b&aacute;o ASAHI do &ocirc;ng Hiroshi Hikawa đại diện to&agrave;n quyền khu vực Đ&ocirc;ng Dương v&agrave; đại diện Trung t&acirc;m Nhật ngữ ISSHIN tại H&agrave; Nội đ&atilde; đến một số trường THPT tr&ecirc;n địa b&agrave;n th&agrave;nh phố Sơn La (Trường THPT Nguyễn Du, Trường THPT Chuy&ecirc;n, Trường THPT D&acirc;n tộc Nội tr&uacute; tỉnh Sơn La) để tuy&ecirc;n truyền cho c&aacute;c em học sinh lớp 12 hiểu r&otilde; hơn về &yacute; nghĩa v&agrave; lợi &iacute;ch của học bổng &nbsp;ISSHIN - ASAHI, nhằm đem tới cho c&aacute;c bạn &nbsp;học sinh cơ hội du học Nhật với 15 xuất học bổng mỗi xuất trị gi&aacute; 15.000 USD.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img src="/data/images/tin-tuc/Capture112.JPG" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img src="/data/images/tin-tuc/Capture113.JPG" style="height:346px; width:438px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><em><span style="font-size:14px">&Ocirc;ng Hiroshi Hikawa c&ugrave;ng đo&agrave;n c&ocirc;ng t&aacute;c l&agrave;m việc tại Trường THPT Chuy&ecirc;n Sơn La.</span></em></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img src="/data/images/tin-tuc/Capture114.JPG" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img src="/data/images/tin-tuc/z4017820829162_158e0e16feaea78ccee62d18a1ddc6be.jpg" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:10pt"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="font-size:14.0pt"><img src="/data/images/tin-tuc/Capture115.JPG" style="height:347px; width:645px" /></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><em><span style="font-size:14px">&Ocirc;ng Hiroshi Hikawa c&ugrave;ng đo&agrave;n c&ocirc;ng t&aacute;c l&agrave;m việc tại Trường THPT Nguyễn Du v&agrave; Trường THPT D&acirc;n tộc Nội Tr&uacute; tỉnh Sơn La.</span></em></p>\r\n', '2023-01-06 09:07:21', 60, '/data/files/hkh/Capture112.JPG', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1383, 'HỌC BỔNG ISSHIN – ASHAHI – CƠ HỘI LỚN CHO HỌC SINH SƠN LA DU HỌC', 'Nguyễn Văn Bao – PCt TT Hội KH tỉnh Sơn La', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Gi&aacute;o dục Sơn La gần đ&acirc;y đ&atilde; c&oacute; những bước ph&aacute;t triển mạnh mẽ. Nhiều học sinh Sơn La đ&atilde; đạt được th&agrave;nh t&iacute;ch cao như Huy chương v&agrave;ng Olimpic quốc tế, Chung kết cuộc thi Đường l&ecirc;n đỉnh Olimpia, nhiều học sinh đ&atilde; được v&agrave;o c&aacute;c trường đại học lớn như Trường Đại học Y H&agrave; Nội, Trường Đại học Ngoại thương, Kinh tế quốc d&acirc;n, B&aacute;ch khoa H&agrave; Nội... V&agrave; cũng đ&atilde; c&oacute; em đi du học Hoa K&igrave;, Nhật Bản, Phần Lan... Nhưng n&oacute;i chung số lượng học sinh du học c&ograve;n rất hạn chế. Sự hạn chế một phần do suy nghĩ chưa s&acirc;u, t&acirc;m l&iacute; chưa mạnh dạn vươn xa, một phần do c&ocirc;ng t&aacute;c tuy&ecirc;n truyền, c&ocirc;ng t&aacute;c hướng nghiệp chưa mạnh mẽ để n&acirc;ng cao hiểu biết, n&acirc;ng cao kh&aacute;t vọng cho c&aacute;c em học sinh miền n&uacute;i vốn c&ograve;n nh&uacute;t nh&aacute;t. Học bổng ISSHIN &ndash; ASHAHI lần n&agrave;y l&agrave; cơ hội rất lớn cho học sinh Sơn La đi du học Nhật Bản.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Học bổng ISSHIN &ndash; ASHAHI l&agrave; học bổng của B&aacute;o ASHAHI, cơ quan ng&ocirc;n luận của Đảng Cộng sản Nhật Bản. Học bổng c&oacute; mục đ&iacute;ch gi&uacute;p đỡ học sinh ngh&egrave;o vượt kh&oacute; của c&aacute;c nước đang ph&aacute;t triển để gi&uacute;p c&aacute;c em c&oacute; điều kiện học tập, r&egrave;n luyện ở Nhật Bản, một đất nước ph&aacute;t triển c&oacute; tr&igrave;nh độ quản l&iacute;, tr&igrave;nh độ c&ocirc;ng nghệ, nề nếp l&agrave;m việc khoa học&hellip; để c&aacute;c em n&acirc;ng cao kiến thức chuy&ecirc;n m&ocirc;n, n&acirc;ng cao kĩ năng lao động, quản l&iacute;...từ đ&oacute; về nước l&agrave;m việc phục vụ bản th&acirc;n, gia đ&igrave;nh, qu&ecirc; hương. Y&ecirc;u cầu của Học Bổng ISSHI &ndash; ASHAHI kh&ocirc;ng qu&aacute; cao về học lực. Chỉ cần học lực kh&aacute; l&agrave; đ&atilde; đủ điều kiện. Song y&ecirc;u cầu về &yacute; thức rất cao. Đ&oacute; l&agrave; phải nghi&ecirc;m t&uacute;c, kh&ocirc;ng ngại r&egrave;n luyện để vừa l&agrave;m vừa học. Y&ecirc;u cầu n&agrave;y sẽ gi&uacute;p người học c&oacute; th&ecirc;m bản lĩnh, th&ecirc;m kĩ năng sống v&agrave; lao động, những yếu tố sẽ tạo n&ecirc;n th&agrave;nh c&ocirc;ng trong tương lại</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<span style="color:black">&nbsp;Học bổng ISSHIN &ndash; ASHAHI </span><span style="color:black">trị gi&aacute; </span><span style="color:black">15.000 USD (hơn 350 triệu đồng Việt Nam) cho mỗi học sinh để đi du học Nhật Bản nếu tr&uacute;ng tuyển, nhưng kh&ocirc;ng cấp tiền trực tiếp m&agrave; chỉ trao cơ hội</span> <span style="color:black">như tạo điều kiện cho c&aacute;c em l&uacute;c ban đầu học tiếng ở Việt Nam, c&oacute; nh&agrave; để ở sinh hoạt v&agrave; học tập khi sang Nhật, tạo việc l&agrave;m th&ecirc;m trong thời gian học tập để c&oacute; th&ecirc;m thu nhập, tạo cơ điều kiện để người học học l&ecirc;n c&aacute;c bậc học cao hơn ở Nhật Bản, đảm bảo cho người học sau khi học xong sẽ c&oacute; việc l&agrave;m ở Việt Nam, Nhật Bản hay bất cứ nước n&agrave;o tr&ecirc;n thế giới. Học bổng c&ograve;n gi&uacute;p du học sinh c&oacute; bảo hiểm th&acirc;n thể, bảo hiểm y tế với chế độ b&igrave;nh đẳng như mọi c&ocirc;ng d&acirc;n Nhật. Theo học bổng n&agrave;y, người học c&oacute; điều kiện l&agrave;m th&ecirc;m để c&oacute; thu nhập gi&uacute;p trang trải cho c&aacute; nh&acirc;n v&agrave; c&oacute; thể gi&uacute;p gia đ&igrave;nh trong qu&aacute; tr&igrave;nh học tập. C&aacute;i kh&oacute; m&agrave; học bổng n&agrave;y y&ecirc;u cầu l&agrave; người học phải vượt qua t&acirc;m l&iacute; nhớ nh&agrave; v&igrave; y&ecirc;u cầu hai năm đầu ở Nhật phải học li&ecirc;n tục.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="color:black">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; GSTS Nguyễn Thị Doan, nguy&ecirc;n Ph&oacute; Chủ tịch nước Cộng h&ograve;a X&atilde; hội chủ nghĩa Việt Nam, Chủ tịch Hội Khuyến học Việt Nam đ&atilde; rất quan t&acirc;m tới việc đ&agrave;o tạo nguồn nh&acirc;n lực cho c&aacute;c tỉnh miền n&uacute;i, c&aacute;c tỉnh c&oacute; nhiều d&acirc;n tộc thiểu số, trong đ&oacute; c&oacute; Sơn La n&ecirc;n đ&atilde; giới thiệu Quỹ Học bổng ISSHIIN &ndash; ASHAHI v&agrave; y&ecirc;u cầu Hội Khuyến học tỉnh Sơn La kết nối thực hiện. Hội Khuyến học tỉnh đ&atilde; b&aacute;o c&aacute;o v&agrave; được UBND tỉnh Sơn La cho ph&eacute;p v&agrave; y&ecirc;u cầu c&aacute;c sở, ban, ng&agrave;nh phối hợp triển khai thực hiện. Ng&agrave;y 06 v&agrave; 07/01/2023 Đo&agrave;n của Quỹ Học bổng đ&atilde; phối hợp với Hội Khuyến học tỉnh tới Trường trung học phổ th&ocirc;ng Nguyễn Du, Trường THPT Chuy&ecirc;n Sơn La, Trường PTDT Nội tr&uacute; tỉnh. Tại đ&acirc;y, đại diện Quỹ Học bổng đ&atilde; n&oacute;i r&otilde; những mục đ&iacute;ch, y&ecirc;u cầu của Quỹ, những kh&oacute; khăn thuận lợi, những c&aacute;ch thức thực hiện học bổng, đồng thời giải đ&aacute;p những &yacute; kiến của gi&aacute;o vi&ecirc;n, học sinh c&aacute;c trường. Đại diện Quỹ học bổng cũng cho biết trong 10 năm thực hiện, Việt Nam đ&atilde; c&oacute; hơn 3000 người sang Nhật học tập. </span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="color:black">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trong gặp gỡ c&aacute;c học sinh, đại diện Hội Khuyến học tỉnh sơn La đ&atilde; khẳng định sẽ phối hợp với Quỹ Học bổng ISSHIN &ndash; ASHAHI để gi&uacute;p đỡ những học sinh c&oacute; nhu cầu, tạo những điều kiện để c&aacute;c em hiểu biết v&agrave; thực hiện những c&ocirc;ng việc cần thiết để đạt được học bổng.</span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14.0pt"><span style="font-size:14px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Đất nước ta đ&atilde; đề ra những mục ti&ecirc;u to lớn: Tới 2030 l&agrave; nước c&oacute; thu nhập trung b&igrave;nh cao, năm 2045 l&agrave; nước ph&aacute;t triển. Những mục ti&ecirc;u n&agrave;y muốn th&agrave;nh hiện thực phải c&oacute; một nguồn nh&acirc;n lực chất lượng rất lớn. Sơn la, một v&ugrave;ng đất đầy tiềm năng cũng đ&atilde; th&iacute;ch ứng v&agrave; đang ph&aacute;t triển, đang hội nhập rất nhanh. Nhưng ph&iacute;a trước, nguồn nh&acirc;n lực chất lượng cao đang l&agrave; đ&ograve;i hỏi, l&agrave; th&aacute;ch thức cho to&agrave;n tỉnh để ph&aacute;t triển. V&igrave; vậy, b&ecirc;n cạnh những nguồn nh&acirc;n lực đang được đ&agrave;o tạo theo truyền thống th&igrave; việc cử học sinh của Sơn La đi du học l&agrave; một c&aacute;ch thức tốt để nhanh ch&oacute;ng đ&aacute;p ứng y&ecirc;u cầu. Học bổng ISSHIN &ndash; ASHAHI sẽ l&agrave; một trong những cơ hội tốt để Sơn La c&oacute; th&ecirc;m điều kiện thực hiện.</span></span></p>\r\n', '2023-01-09 02:14:54', 105, '/data/files/hkh/Capture117.JPG', '', 1, 35, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1384, 'Trao học bổng cho học sinh nghèo trong Chương trình “Xuân Biên phòng ấm lòng dân bản” tại xã Chiềng On, Yên Châu năm 2023.', '          Ngày 08/01/2023 Hội Khuyến học tỉnh trao 30 xuất học bổng (mỗi xuất 500.000đ) cho học sinh nghèo với tổng số tiền là 15 triệu đồng trong chương trình “Xuân Biên phòng ấm lòng dân bản”. ', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:14px"><img alt="Description: C:UsersAdministratorPicturesCapture118.JPG" id="Picture_x0020_1" src="/data/images/tin-tuc/Capture118.JPG" /> </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:#002060">Đ/c Nguyễn Văn Bao - Ph&oacute; Chủ tịch Thường trực Hội Khuyến học tỉnh trao học bổng cho c&aacute;c em học sinh ngh&egrave;o vượt kh&oacute; x&atilde; Chiềng On, huyện Y&ecirc;n Ch&acirc;u.</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img alt="Description: C:UsersAdministratorPicturesCapture119.JPG" id="Picture_x0020_2" src="/data/images/tin-tuc/Capture119.JPG" style="height:515px; width:757px" /> </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:#002060">Đ/c Nguyễn Văn Bao - Ph&oacute; Chủ tịch Thường trực Hội Khuyến học tỉnh v&agrave; c&aacute;c nh&agrave; t&agrave;i trợ </span></em><em><span style="color:#002060">được Ban tổ chức trao Chứng nhận đ&atilde; tham gia đồng h&agrave;nh c&ugrave;ng Chương tr&igrave;nh &ldquo;Xu&acirc;n Bi&ecirc;n ph&ograve;ng ấm l&ograve;ng d&acirc;n bản&rdquo; năm 2023</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img alt="Description: C:UsersAdministratorPicturesCapture122.JPG" id="Picture_x0020_7" src="/data/images/tin-tuc/Capture122.JPG" style="height:373px; width:375px" /> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <img alt="Description: C:UsersAdministratorPicturesCapture121.JPG" id="Picture_x0020_8" src="/data/images/tin-tuc/Capture121.JPG" /> </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <img alt="Description: C:UsersAdministratorPicturesCapture120.JPG" id="Picture_x0020_6" src="/data/images/tin-tuc/Capture120.JPG" /> </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="background-color:white"><span style="color:#002060">C&aacute;c nh&agrave; t&agrave;i trợ trao qu&agrave; cho c&aacute;c đối tượng ch&iacute;nh s&aacute;ch x&atilde; Chiềng On, Y&ecirc;n Ch&acirc;u, Sơn La trong Chương tr&igrave;nh &ldquo;Xu&acirc;n Bi&ecirc;n ph&ograve;ng ấm l&ograve;ng d&acirc;n bản&rdquo;</span></span>.</em></span></p>\r\n\r\n<table border="1" cellspacing="0" class="MsoTableGrid" style="border-collapse:collapse; border:solid windowtext 1.0pt">\r\n	<tbody>\r\n		<tr>\r\n			<td style="vertical-align:top; width:246.35pt">\r\n			<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:#002060"><img alt="Description: D:KHUYẾN HỌC SL 2022BÀI PHÁT BIỂUz4022145183220_db77f01d29be51515f368f7e52fe3a2b.jpg" id="Picture_x0020_11" src="/data/images/tin-tuc/z4022145183220_db77f01d29be51515f368f7e52fe3a2b.jpg" /> </span></em></span></p>\r\n			</td>\r\n			<td style="vertical-align:top; width:246.4pt">\r\n			<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:#002060"><img alt="Description: D:KHUYẾN HỌC SL 2022BÀI PHÁT B