INSERT INTO `tin_tuc` VALUES (1385, 'Hội Khuyến học tỉnh Sơn La trao học bổng từ Quĩ Học bổng Tô Hiệu, Quĩ Học bổng Đinh Thiện Lý nhân dịp đón Xuân Quí Mão 2023.', 'Nhân dịp đón Xuân Quí Mão 2023, Hội Khuyến học tỉnh Sơn La trao học bổng từ Quĩ Học bổng Tô Hiệu, Quĩ Học bổng Đinh Thiện Lý cho 134 em học sinh có hoàn cảnh đặc biệt khó khăn, học sinh nghèo vượt khó học giỏi, học sinh dân tộc thiểu số vùng biên giới tại các Huyện Vân Hồ, Sốp Cộp, Yên Châu, Sông Mã trị giá 77 triệu đồng. Đây là món quà ý nghĩa nhằm động viên, khích lệ tinh thần học tập của các em đồng thời mang không khí Xuân yêu thương đến với những mảnh đời còn khó khăn.', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong> <img alt="Description: C:UsersAdministratorPicturesz4025363447353_504fcf789374aa77c9fe0cf6f1e44935.jpg" id="Picture_x0020_2" src="/data/images/tin-tuc/z4025363447353_504fcf789374aa77c9fe0cf6f1e44935.jpg" /> </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><em><span style="font-size:14px">Đ/c Mai Thu Hương &ndash; Chủ tịch Hội Khuyến học tỉnh trao qu&agrave; cho học sinh tại huyện V&acirc;n Hồ.</span></em></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:14px"><img alt="Description: C:UsersAdministratorPicturesz4025363451869_185243bced4696791f62ebb94ecc9de0.jpg" id="Picture_x0020_5" src="/data/images/tin-tuc/z4025363451869_185243bced4696791f62ebb94ecc9de0.jpg" /> </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img alt="Description: C:UsersAdministratorPicturesz4025363462184_52bbe16d3dc25a763dc63bd2150efae8.jpg" id="Picture_x0020_7" src="/data/images/tin-tuc/z4025363462184_52bbe16d3dc25a763dc63bd2150efae8.jpg" /> <em>Đ/c Mai Thu Hương &ndash; Chủ tịch Hội Khuyến học tỉnh trao qu&agrave; cho học sinh tại huyện Sốp Cộp.</em></span></p>\r\n\r\n<p style="margin-