INSERT INTO `tin_tuc` VALUES (1386, 'Hội Khuyến học Quỳnh Nhai tập huấn công tác khuyến học, khuyến tài,  xây đựng xã hội học tập năm 2022', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Trong 2 ng&agrave;y 20 - 21/12/2022, Tại Hội trường Nh&agrave; văn h&oacute;a Thị trấn Quỳnh Nhai. Hội Khuyến học huyện Quỳnh Nhai đ&atilde; tổ chức Hội nghị tập huấn, qu&aacute;n triệt c&aacute;c văn bản về c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập giai đoạn 2021 - 2030. Tham gia tập huấn c&oacute; 70 đại biểu l&agrave; c&aacute;c đồng ch&iacute; l&agrave; Ủy vi&ecirc;n BCH Hội Khuyến học huyện; Chủ tịch, Ph&oacute; Chủ tịch Hội Khuyến học c&aacute;c x&atilde;, l&atilde;nh đạo Ban Khuyến học c&aacute;c cơ quan, đơn vị, doanh nghiệp; Đại diện c&aacute;c chi hội, d&ograve;ng họ học tập tr&ecirc;n địa b&agrave;n.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Tại hội nghị tập huấn, c&aacute;c đại biểu đ&atilde; được qu&aacute;n triệt: Quyết định số 1373 QĐ/TTg ng&agrave;y 30/7/2021 của Thủ tướng Ch&iacute;nh Phủ ph&ecirc; duyệt Đề &aacute;n &quot;X&acirc;y dựng x&atilde; hội học tập giai đoạn 2021-2030&quot;; Quyết định số 387/QĐ-TTg ng&agrave;y 25/3/2022 của Thủ tướng Ch&iacute;nh phủ về Ph&ecirc; duyệt Chương tr&igrave;nh &ldquo;Đẩy mạnh phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị giai đoạn 2021-2030&rdquo;; Quyết định số 677 QĐ/TTg ng&agrave;y 03/6/2022 của Thủ tướng Ch&iacute;nh Phủ ph&ecirc; duyệt Chương tr&igrave;nh &quot;X&acirc;y dựng m&ocirc; h&igrave;nh c&ocirc;ng d&acirc;n học tập giai đoạn 2021-2030&quot;; c&aacute;c kế hoạch của UBND tỉnh, UBND huyện về thực hiện Đề &aacute;n, Chương tr&igrave;nh của Thủ tướng Ch&iacute;nh phủ về<strong> </strong>c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y đựng x&atilde; hội học tập v&agrave; c&aacute;c Quyết định của TW Hội khuyến học Việt Nam về ban h&agrave;nh bộ ti&ecirc;u ch&iacute; v&agrave; Hướng dẫn đ&aacute;nh gi&aacute;, c&ocirc;ng nhận c&aacute;c danh hiệu &ldquo;Gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị giai đoạn 2021-2030&rdquo;; Hướng dẫn đ&aacute;nh gi&aacute; c&ocirc;ng nhận danh hiệu &ldquo;C&ocirc;ng d&acirc;n học tập&rdquo; giai đoạn 2021-2030; c&aacute;c Th&ocirc;ng tư hướng dẫn của Bộ Gi&aacute;o dục &amp; Đ&agrave;o tạo quy định về đ&aacute;nh gi&aacute;, xếp loại &quot;Đơn vị học tập&quot;; đ&aacute;nh gi&aacute;, xếp loại &quot;Cộng đồng học tập&quot; cấp x&atilde;&hellip;</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Th&ocirc;ng qua Hội nghị tập huấn nhằm tuy&ecirc;n truyền, phổ biến c&aacute;c chủ trương của Đảng, ch&iacute;nh s&aacute;ch ph&aacute;p luật của Nh&agrave; nước, của tỉnh, của huyện v&agrave; n&acirc;ng cao nghiệp vụ, chuy&ecirc;n m&ocirc;n cho đội ngũ c&aacute;n bộ khuyến học c&aacute;c cấp về c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập. Từ đ&oacute; n&acirc;ng cao nhận thức của c&aacute;n bộ, đảng vi&ecirc;n v&agrave; Nh&acirc;n d&acirc;n về &yacute; thức phấn đấu tự học, học tập suốt đời, học kh&ocirc;ng bao giờ c&ugrave;ng&hellip;Tạo sự chuyển biến về nhận thức, &yacute; thức tự gi&aacute;c tham gia phong tr&agrave;o khuyến học, khuyến t&agrave;i, x&acirc;y đựng x&atilde; hội học tập rộng khắp trong to&agrave;n huyện. Th&uacute;c đẩy sự nghiệp gi&aacute;o dục v&agrave; đ&agrave;o tạo huyện nh&agrave;. Nhằm n&acirc;ng cao d&acirc;n tr&iacute;, bồi dưỡng nh&acirc;n t&agrave;i, n&acirc;ng cao chất lượng nguồn nh&acirc;n lực của huyện Quỳnh Nhai./.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&agrave; Văn Pống</strong></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Chủ tich Hội Khuyến học huyện Quỳnh Nhai</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm">&nbsp;</p>\r\n', '2023-01-11 08:27:03', 62, '', '', 0, 36, 0, 0, '', 45, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1387, 'Hội Khuyến học tỉnh đã trao thưởng Cuộc thi khoa học, kỹ thuật cấp tỉnh dành cho học sinh trung học năm học 2022 - 2023.', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp; &nbsp; &nbsp; &nbsp; Ng&agrave;y 11/01/2023 Hội Khuyến học tỉnh đ&atilde; dự Tổng kết, động vi&ecirc;n v&agrave; trao thưởng cho 24 học sinh đạt giải trong Cuộc thi khoa học, kỹ thuật cấp tỉnh d&agrave;nh cho học sinh trung học năm học 2022 - 2023 do Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo tổ chức. </span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><img src="/data/images/tin-tuc/Capture124.JPG" style="height:377px; width:605px" /></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><img src="/data/images/tin-tuc/Capture125.JPG" style="height:307px; width:637px" /></span></span></p>\r\n\r\n<p style="text-align:center"><span style="font-size:14px"><em><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="color:#0070c0">Đ/c Nguyễn Văn Bao - Ph&oacute; Chủ tịch Thường trực Hội Khuyến học tỉnh trao giấy khen tiền thưởng cho học sinh đạt giải</span></span></em></span></p>\r\n', '2023-01-12 02:19:41', 54, '/data/files/hkh/Capture125.JPG', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1388, 'HỘI KHUYẾN HỌC MƯỜNG LA TỔ CHỨC TỔNG KẾT CÔNG TÁC KHUYẾN HỌC NĂM 2022 – TRIỂN KHAI NHIỆM VỤ NĂM 2023.', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Ng&agrave;y 10/01/2023 Hội Khuyến học huyện Mường La tổ chức tổng kết c&ocirc;ng t&aacute;c khuyến học năm 2022; triển khai nhiệm vụ trọng t&acirc;m năm 2023. Tới dự v&agrave; chỉ đạo hội nghị c&oacute; đc M&ugrave;a Thị Sinh Ph&oacute; chủ tịch UBND huyện.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img alt="Description: Capture130" src="/data/images/anh%20co%20so/Capture130.JPG" style="height:347px; width:499px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:#002060">Hội nghị tổng kết </span></em><em><span style="background-color:white"><span style="color:#002060">phong tr&agrave;o KH năm 2022; nhiệm vụ trọng t&acirc;m năm 2023</span></span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Hiện nay, tr&ecirc;n địa b&agrave;n huyện c&oacute; 16 hội khuyến học x&atilde;, thị trấn; với &nbsp;267 chi hội: trong đ&oacute; 201 chi hội bản, tiểu khu; 44 chi hội trường học; 12 chi hội d&ograve;ng họ; 10 chi hội đồng hương; tổng số Ban khuyến học 26: trong đ&oacute; 5 ban khuyến học thuộc c&aacute;c đơn vị, 5 ban khuyến học khối doanh nghiệp; 16 ban khuyến học khối c&ocirc;ng chức x&atilde;; c&oacute; 24.457 hội vi&ecirc;n, tăng so với năm 2021 l&agrave; 1.300 hội vi&ecirc;n; tỷ lệ hội vi&ecirc;n/số d&acirc;n của huyện&nbsp; đạt 23,1% (24.457/105.756).</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Kết quả b&igrave;nh x&eacute;t, đ&aacute;nh gi&aacute;, c&ocirc;ng nhận năm 2022 đối với: Gia đ&igrave;nh học tập: được c&ocirc;ng nhận 13.785 đạt tỷ lệ 61%; D&ograve;ng họ học tập được c&ocirc;ng nhận 6/12 d&ograve;ng họ, tỷ lệ 50%; Cộng đồng học tập được c&ocirc;ng nhận 108/201 tỷ lệ 53,7%; Đơn vị học tập cấp x&atilde;: 46/56 tỷ lệ 82,1%.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img alt="Description: Capture131" src="/data/images/anh%20co%20so/Capture131.JPG" style="height:325px; width:501px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:#002060">L&atilde;nh đạo Hội Khuyến học trao giấy khen cho c&aacute;c c&aacute; nh&acirc;n, tập thể</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; Trong năm 2022 Hội khuyến học đ&atilde; tổ chức khen thưởng cho 35 lượt gi&aacute;o vi&ecirc;n v&agrave; 292 lượt học sinh c&oacute; th&agrave;nh t&iacute;ch xuất sắc trong giảng dạy, học tập; hỗ trợ học sinh c&oacute; ho&agrave;n cảnh kh&oacute; khăn vươn l&ecirc;n trong học tập 55 lượt. Tổng số tiền đ&atilde; chi: 84.550.000đ.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Hội Khuyến học huyện Mường La đề nghị c&aacute;c cấp khen thưởng: </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- TW Hội tặng: Bằng khen 01 tập thể, 01 c&aacute; nh&acirc;n; Kỷ niệm chương &ldquo;V&igrave; sự nghiệp khuyến học&rdquo; 01 c&aacute; nh&acirc;n.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - Tỉnh hội tặng giấy khen cho 16 gia đ&igrave;nh; 01 d&ograve;ng họ; 5 tập thể; 04 c&aacute; nh&acirc;n.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Nhằm ph&aacute;t huy th&agrave;nh t&iacute;ch đ&atilde; đạt được v&agrave; khắc phục những hạn chế, tồn tại, Hội Khuyến học Mường La đ&atilde; triển khai c&aacute;c nhiệm vụ trọng t&acirc;m, tiếp tục ph&aacute;t động thi đua v&agrave; k&yacute; kết giao ước với c&aacute;c ng&agrave;nh, đo&agrave;n thể trong huyện thể hiện sự quyết t&acirc;m phấn đấu vươn l&ecirc;n, ho&agrave;n th&agrave;nh tốt nhiệm vụ được giao./.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center">&nbsp; &nbsp; &nbsp; &nbsp;<img alt="Description: Capture132" src="/data/images/anh%20co%20so/Capture132.JPG" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:#002060">K&yacute; kết chương tr&igrave;nh phối hợp giữa HKH huyện v&agrave; c&aacute;c đơn vị, tổ chức ch&iacute;nh trị x&atilde; hội nhiệm kỳ 2021 - 2026</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm">&nbsp;</p>\r\n', '2023-01-12 02:29:21', 38, '/data/files/anh%20co%20so/Capture131.JPG', '', 0, 54, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1389, 'GS.TS Nguyễn Thị Doan - nguyên Phó Chủ tịch nước, Chủ tịch Hội Khuyến học Việt Nam, được tặng Huân chương Hồ Chí Minh', 'Ngày 12/1/2023, tại Hội nghị tổng kết công tác năm 2022, triển khai nhiệm vụ năm 2023 của Văn phòng Chủ tịch nước, Chủ tịch nước Nguyễn Xuân Phúc đã trao tặng Huân chương Hồ Chí Minh cho GS.TS Nguyễn Thị Doan -nguyên Phó Chủ tịch nước, Chủ tịch Hội Khuyến học Việt Nam.', '<p><a href="https://congdankhuyenhoc.vn/chu-tich-hoi-khuyen-hoc-viet-nam-nguyen-thi-doan-nhan-huan-chuong-ho-chi-minh-179230112215552273.htm">https://congdankhuyenhoc.vn/chu-tich-hoi-khuyen-hoc-viet-nam-nguyen-thi-doan-nhan-huan-chuong-ho-chi-minh-179230112215552273.htm</a></p>\r\n', '2023-01-13 10:28:08', 33, '/data/files/hkh/Capture126.JPG', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1390, 'Tiếp tục củng cố, kiện toàn bộ máy tổ chức Hội cơ sở  và phát triển hội viên, góp phần xây dựng Hội vững mạnh', 'Th.s.NGƯT  Lò Thị Liên\r\nPhó Chủ tịch Hội khuyến học Thành phố\r\n', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thực hiện Nghị quyết Đại hội Hội khuyến học th&agrave;nh phố Sơn La kh&oacute;a V, nhiệm kỳ 2021-2026; Kế hoạch số 278-KH/TU ng&agrave;y 15/11/2019 của Th&agrave;nh ủy Sơn La về Kế hoạch thực hiện K&ecirc;́t lu&acirc;̣n 49-KL/TW ngày 10/5/2019 của Ban B&iacute; thư v&ecirc;̀ ti&ecirc;́p tục thực hi&ecirc;̣n Chỉ thị 11/CT/TW của B&ocirc;̣ Chính trị khóa X v&ecirc;̀ tăng cường sự l&atilde;nh đạo của Đảng đối với c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập; <span style="background-color:white">Th&ocirc;ng b&aacute;o </span><span style="background-color:white">K</span><span style="background-color:white">ết luận số 665/TB-UBND ng&agrave;y 24/11/2021 của UBND th&agrave;nh phố Sơn La về Th&ocirc;ng b&aacute;o &yacute; kiến kết luận của đồng ch&iacute; Trần C&ocirc;ng Ch&iacute;nh - UVBTV th&agrave;nh uỷ, Ph&oacute; Chủ tịch UBND th&agrave;nh phố tại cuộc l&agrave;m việc với H&ocirc;i khuyến học th&agrave;nh phố</span><span style="background-color:white">.</span> Hội Khuyến học th&agrave;nh phố Sơn La đ&atilde; x&acirc;y dựng Kế hoạch tiếp tục củng cố x&acirc;y dựng tổ chức Hội, gắn với r&agrave; so&aacute;t, kiện to&agrave;n bộ m&aacute;y tổ chức cơ sở v&agrave; ph&aacute;t triển hội vi&ecirc;n, x&acirc;y dựng tổ chức Hội vững mạnh tr&ecirc;n địa b&agrave;n th&agrave;nh phố Sơn La. Cụ thể như sau:</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;1.Về c&ocirc;ng t&aacute;c tuy&ecirc;n truyền: Để triển khai thực hiện kế hoạch, Hội Khuyến học th&agrave;nh phố đ&atilde; <span style="background-color:white"><span style="color:black">chỉ đạo Hội khuyến học c&aacute;c x&atilde;, phường đẩy mạnh c&ocirc;ng t&aacute;c tuy&ecirc;n truyền đến c&aacute;c chi hội về c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i l&agrave; truyền thống tốt đẹp của d&acirc;n tộc Việt Nam; l&agrave; một kh&acirc;u quan trọng của hoạt động x&atilde; hội h&oacute;a gi&aacute;o dục được Đảng, Nh&agrave; nước đặc biệt quan t&acirc;m</span></span><span style="background-color:white"><span style="color:black"> g&oacute;p phần th&uacute;c đẩy sự nghiệp Gi&aacute;o dục v&agrave; Đ&agrave;o tạo, n&acirc;ng cao d&acirc;n tr&iacute;, đ&agrave;o tạo nh&acirc;n lực, bồi dưỡng nh&acirc;n t&agrave;i cho tỉnh v&agrave; th&agrave;nh phố Sơn La.</span></span>&nbsp;</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2.Về tổ chức: Đối với <span style="color:black">c</span><span style="color:black">&aacute;c trường</span><span style="color:black">, </span><span style="color:black">từ bậc Mầm non, cấp Tiểu học v&agrave; THCS tr&ecirc;n địa b&agrave;n; c&aacute;c tổ, bản, tiểu khu, d&ograve;ng họ, hội đồng hương</span> <span style="color:black">l</span><span style="color:black">&agrave; Chi hội khuyến học trực thuộc Hội khuyến học c&aacute;c x&atilde;, phường tr&ecirc;n địa b&agrave;n th&agrave;nh phố Sơn La.</span> Đối với c&aacute;c cơ quan, đơn vị, doanh nghiệp, đơn vị vũ trang&nbsp; th&agrave;nh lập Ban khuyến học trực thuộc Hội khuyến học th&agrave;nh phố Sơn La. </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;3.Về nh&acirc;n sự: Ban chấp h&agrave;nh C&ocirc;ng đo&agrave;n cơ quan, đơn vị, doanh nghiệp, tham mưu, chuẩn bị nh&acirc;n sự, kế hoạch hoạt động, lập tờ tr&igrave;nh đề nghị Cấp ủy (hoặc)<em> </em>Thủ trưởng cơ quan ra quyết định th&agrave;nh lập Ban khuyến học. Đối với c&aacute;c trường học, Ban chấp h&agrave;nh C&ocirc;ng đo&agrave;n cơ sở chuẩn bị nh&acirc;n sự, lập tờ tr&igrave;nh đề nghị Hội khuyến học c&aacute;c x&atilde;, phường ra quyết định th&agrave;nh lập Chi hội khuyến học. <span style="color:black">Sau khi c&oacute; quyết định th&agrave;nh lập, Ban Chấp h&agrave;nh C&ocirc;ng đo&agrave;n tham mưu tổ chức hội nghị để th&ocirc;ng qua kế hoạch v&agrave; triển khai c&aacute;c nội dung hoạt động khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập trong cơ quan, đơn vị, doanh nghiệp, trường học ph&ugrave; hợp với chức năng, nhiệm vụ của từng cơ quan, đơn vị.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style="color:black">4.Về nội dung hoạt động:</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp;4.1. Định hướng cho c&aacute;c </span>Chi hội, Ban khuyến học thường xuy&ecirc;n quan t&acirc;m, theo d&otilde;i v&agrave; lập danh s&aacute;ch con em trong cơ quan, đơn vị, doanh nghiệp, trường học c&oacute; th&agrave;nh t&iacute;ch học tập, r&egrave;n luyện để động vi&ecirc;n, khen thưởng kịp thời, hỗ trợ học bổng cho học sinh ngh&egrave;o, chăm ngoan, hiếu học.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white">&nbsp; &nbsp; &nbsp; &nbsp;4.2. Tham gia x&acirc;y dựng qui hoạch đ&agrave;o tạo bồi dưỡng c&aacute;n bộ, nh&agrave; gi&aacute;o; đề xuất với cơ quan, đơn vị quan t&acirc;m thực hiện ch&iacute;nh s&aacute;ch với c&aacute;n bộ được cử đi học; c&aacute;n bộ, nh&agrave; gi&aacute;o, c&oacute; th&agrave;nh t&iacute;ch trong c&ocirc;ng t&aacute;c. </span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white">&nbsp; &nbsp; &nbsp; &nbsp;4.3. Tham mưu c&ugrave;ng với tổ chức C&ocirc;ng đo&agrave;n cơ quan, đơn vị ph&aacute;t động thi đua học tập, cải tiến kỹ thuật, đưa việc học v&agrave;o chỉ ti&ecirc;u thi đua h&agrave;ng năm.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 4.4. Tham gia hoạt động khuyến học, khuyến t&agrave;i của địa phương.<span style="color:black"> Phối hợp với gia đ&igrave;nh quản l&yacute;, gi&aacute;o dục học sinh, sinh vi&ecirc;n; tổ chức học tập, vui chơi l&agrave;nh mạnh, khắc phục t&igrave;nh trạng đua đ&ograve;i mắc c&aacute;c tệ nạn x&atilde; hội trong học sinh, sinh vi&ecirc;n.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 4.5. Vận động, tổ chức cho c&aacute;n bộ, c&ocirc;ng chức của cơ quan, đơn vị, doanh nghiệp, trường học tham gia x&acirc;y dựng gia đ&igrave;nh học tập, d&ograve;ng họ học tập, cộng đồng d&acirc;n cư học tập.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;4.6. X&acirc;y dựng quỹ khuyến học v&agrave; quản l&yacute; thu - chi quỹ đ&uacute;ng quy định của ph&aacute;p luật. Vi&ecirc;̣c thu và sử dụng h&ocirc;̣i phí, sử dụng Quỹ khuy&ecirc;́n học cho hoạt đ&ocirc;̣ng, h&ocirc;̃ trợ phụ c&acirc;́p: thực hiện theo Quy định số 17/QĐ-HKH ng&agrave;y 04/03/2020 của Hội khuyến học tỉnh Sơn La Quy định việc thu, nộp,quản l&yacute;, sử dụng hội ph&iacute; Hội khuyến học để đ&ocirc;̣ng vi&ecirc;n cán b&ocirc;̣ sơ sở tham gia, tri&ecirc;̉n khai nhi&ecirc;̣m vụ khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Định kỳ 6 th&aacute;ng v&agrave; một năm, c&aacute;c Chi hội, Ban khuyến học thực hiện chế độ b&aacute;o c&aacute;o kết quả hoạt động với Hội cấp tr&ecirc;n trực tiếp.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14pt"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Với những nội dung triển khai thực hiện như tr&ecirc;n, c&aacute;c cấp Hội đ&atilde; nhận được sự quan t&acirc;m, l&atilde;nh đạo của Cấp ủy, Ch&iacute;nh quyền; sự gắn kết, phối hợp với c&aacute;c cơ quan, đơn vị, doanh nghiệp, trường học v&agrave; cộng đồng để củng cố, kiện to&agrave;n tổ chức Hội cơ sở. Đến hết qu&yacute; I năm 2022, đ&atilde; c&oacute; 100% chi hội khuyến học c&aacute;c đơn vị trường học hoạt động hiệu quả; đ&atilde; kiện to&agrave;n Ban khuyến học đạt 80% đối với cơ quan, đơn vị, 50% đối với Doanh nghiệp tr&ecirc;n địa b&agrave;n th&agrave;nh phố. Trong thời gian tới, Hội khuyến học th&agrave;nh phố sẽ ti&ecirc;́p tục tham mưu, phối hợp để kiện to&agrave;n c&aacute;c Ban khuyến học (đặc biệt quan t&acirc;m kiện to&agrave;n tổ chức Hội&nbsp;tại c&aacute;c doanh nghiệp) g&oacute;p phần đẩy mạnh c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập tr&ecirc;n địa b&agrave;n th&agrave;nh phố Sơn La ng&agrave;y c&agrave;ng ph&aacute;t triển./.</span></span></p>\r\n', '2023-01-30 08:38:42', 39, '/data/files/anh%20co%20so/z4070923697203_b90930fdb812f8052eab5f5c2048f25f.jpg', '', 1, 52, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1391, '“Tết khuyến học” xứ Thanh xuân Quý Mão 2023', 'Nhân dịp đầu xuân Quý Mão 2023, tối 26-1 (tức mùng 5 Tết), Hội Khuyến học (HKH) tỉnh Thanh Hoá phối hợp với Đài Phát thanh và Truyền hình Thanh Hóa tổ chức Chương trình “Tết khuyến học” xứ Thanh; trao học bổng cho học sinh, sinh viên (HS, SV) có hoàn cảnh đặc biệt khó khăn vươn lên học giỏi; trao thưởng cho người lao động sáng tạo tiêu biểu và ra mắt Quỹ học bổng Nguyễn Chích.', '<p><a href="https://baothanhhoa.vn/thoi-su/tet-khuyen-hoc-xu-thanh-xuan-quy-mao-2023/177897.htm">https://baothanhhoa.vn/thoi-su/tet-khuyen-hoc-xu-thanh-xuan-quy-mao-2023/177897.htm</a></p>\r\n', '2023-01-31 01:51:32', 26, '/data/files/anh%20tinh%20ban/Capture36.JPG', '', 0, 55, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1392, 'Hội Khuyến học Tiền Giang trao học bổng thường xuyên tặng học sinh, sinh viên hoàn cảnh đặc biệt khó khăn', 'Ngày 4/12, Hội Khuyến học tỉnh Tiền Giang phối hợp với Công ty Trách nhiệm hữu hạn một thành viên Xổ số kiến thiết Tiền Giang tổ chức Lễ trao học bổng thường xuyên cho học sinh, sinh viên có hoàn cảnh đặc biệt khó khăn vươn lên học tốt năm học 2022-2023.', '<p><a href="https://congdankhuyenhoc.vn/hoi-khuyen-hoc-tien-giang-trao-hoc-bong-thuong-xuyen-tang-hoc-sinh-sinh-vien-hoan-canh-dac-biet-kho-khan-179221204151603545.htm">https://congdankhuyenhoc.vn/hoi-khuyen-hoc-tien-giang-trao-hoc-bong-thuong-xuyen-tang-hoc-sinh-sinh-vien-hoan-canh-dac-biet-kho-khan-179221204151603545.htm</a></p>\r\n', '2023-01-31 02:01:23', 36, '/data/files/anh%20tinh%20ban/Capture000.JPG', '', 0, 55, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1393, 'Chùm thơ xuân Quý mão của tác giả Bảo Nguyên.', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><strong><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">KHUYẾN HỌC VINH QUANG C&Ugrave;NG TỔ QUỐC</span></span></strong></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>&nbsp;&nbsp;&nbsp;&nbsp;Bảo Nguy&ecirc;n</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Xu&acirc;n đ&atilde; về với mọi miền Tổ quốc</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em>Rực rỡ sắc m&agrave;u, hoa nở khắp mu&ocirc;n nơi</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em>Giữa m&ecirc;nh mang giao h&ograve;a đất v&agrave; trời</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em>Niềm vui đến với trẻ, gi&agrave;, trai, g&aacute;i.</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Nh&acirc;m Dần ra đi để niềm vui ở lại</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em>Qu&yacute; M&atilde;o trở về th&ecirc;m tr&agrave;n ngập niềm tin</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em>Khuyến học khuyến t&agrave;i s&oacute;ng bước ra qu&acirc;n</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em>Theo lời B&aacute;c &ldquo;Suốt đời học tập&rdquo;!</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em>&nbsp;&nbsp;&nbsp;&nbsp; Con đường vinh quang đ&atilde; hiện ra ph&iacute;a trước</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em>&Yacute; Đảng, l&ograve;ng d&acirc;n c&ugrave;ng hợp sức tiến l&ecirc;n</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em>X&atilde; hội học tập lan tỏa khắp mọi miền</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em>Cho đất nước vững v&agrave;ng tiến bước.</em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;