INSERT INTO `tin_tuc` VALUES (1394, 'Hội Golf tỉnh Lâm Đồng trao tặng 400 triệu đồng cho Hội Khuyến học', 'Hội Golf tỉnh Lâm Đồng vừa trao tặng 400 triệu đồng cho Hội Khuyến học tỉnh Lâm Đồng để chăm lo cho các em học sinh có hoàn cảnh khó khăn trong tỉnh và trao 50 xe đạp tình thương cho Hội Nhà Báo tỉnh để tặng thưởng học sinh nghèo hiếu học, học giỏi trên địa bàn tỉnh.', '<p><a href="http://baolamdong.vn/xa-hoi/202302/hoi-golf-tinh-lam-dong-trao-tang-400-trieu-dong-cho-hoi-khuyen-hoc-d330525/">http://baolamdong.vn/xa-hoi/202302/hoi-golf-tinh-lam-dong-trao-tang-400-trieu-dong-cho-hoi-khuyen-hoc-d330525/</a></p>\r\n', '2023-02-08 02:03:44', 36, '/data/files/anh%20tinh%20ban/Capture0000.JPG', '', 0, 55, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1395, 'Học bổng Nguyễn Sinh Sắc cho Sinh viên hiếu học.', 'Hội khuyến học Tỉnh Đồng Tháp vừa phối hợp Chi Hội khuyến học Trường Đại học Đồng Tháp và Ban liên lạc Đồng hương ĐT tại TP Hồ Chí Minh trao 50 suất học bổng Nguyễn Sinh Sắc cho Sinh viên hiếu học với tổng kinh phí 254 triệu đồng. Dịp này Chi Hội khuyến học Trường ĐH ĐT cũng trao thêm 50 học học bổng cho 50 sv số tiền 50tr đồng.', '', '2023-02-08 02:10:38', 32, '/data/files/anh%20tinh%20ban/Capture00000.JPG', '', 0, 55, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1396, 'KHUYẾN HỌC SÔNG MÃ TẬP TRUNG ĐẨY MẠNH HOẠT ĐỘNG KHUYẾN HỌC, KHUYẾN TÀI, XÂY DỰNG XÃ HỘI HỌC TẬP ', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; Những th&aacute;ng cuối năm 2022, Thường trực Hội Khuyến học S&ocirc;ng M&atilde; đ&atilde; tập trung đẩy mạnh c&aacute;c hoạt động Khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập, bằng nhiều h&igrave;nh thức phong ph&uacute;, linh hoạt: </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; Để đ&aacute;nh gi&aacute; đ&uacute;ng thực trạng hoạt động của to&agrave;n huyện trong một năm qua, Thường trực huyện Hội đ&atilde; th&agrave;nh lập c&aacute;c đo&agrave;n c&ocirc;ng t&aacute;c v&agrave; ph&acirc;n c&ocirc;ng c&aacute;c đ/c UVBCH đi cơ sở dự, hướng dẫn, kiểm tra t&igrave;nh h&igrave;nh hoạt động, đặc biệt l&agrave; việc việc đ&aacute;nh gi&aacute;, xếp loại c&aacute;c m&ocirc; h&igrave;nh học tập tại 1 số bản, tổ d&acirc;n phố; Tham gia đo&agrave;n c&ograve;n c&oacute; đại diện c&aacute;c ph&ograve;ng, ban chuy&ecirc;n m&ocirc;n của UBND huyện. </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Sau khi c&oacute; kết quả khảo s&aacute;t, kiểm tra, Ban Chấp h&agrave;nh đ&atilde;: Xếp loại 19 trung t&acirc;m học tập cộng đồng x&atilde;, thị trấn; R&agrave; so&aacute;t, đ&aacute;nh gi&aacute; xếp loại &ldquo;Cộng đồng học tập cấp x&atilde;&rdquo;, v&agrave; c&aacute;c m&ocirc; h&igrave;nh học tập, từ đ&oacute; lập danh s&aacute;ch tr&igrave;nh cấp c&oacute; thẩm quyền c&ocirc;ng nhận, khen thưởng. &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; Thường trực Hội Khuyến học huyện c&ograve;n phối hợp với Huyện Đo&agrave;n S&ocirc;ng M&atilde; tổ chức Lễ ra mắt chương tr&igrave;nh &ldquo;Nu&ocirc;i em&rdquo; được nh&acirc;n d&acirc;n rất đồng t&igrave;nh, ủng hộ.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img alt="Description: C:UsersAdministratorDownloads627b2ea1c70462d303c60863764429fb (1).jpg" src="/data/images/anh%20co%20so/Capture1(1).JPG" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img alt="Description: C:UsersAdministratorDownloadscfa62175e875604caa3e97cdffd53361.jpg" src="/data/images/anh%20co%20so/Capture35.JPG" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px">Dự Tổng kết Hội thi v&agrave; khen thưởng 86 Gi&aacute;o vi&ecirc;n Giỏi bậc học Mầm non năm học 2022- 2023</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Nhằm động vi&ecirc;n, khuyến kh&iacute;ch c&aacute;c vận động vi&ecirc;n c&oacute; th&agrave;nh t&iacute;ch cao, Thường trực Hội đ&atilde; tham dự hội nghị Tổng kết c&ocirc;ng t&aacute;c Văn ho&aacute; Thể thao năm 2022 do huyện tổ chức v&agrave; đ&atilde; khen thưởng 10 c&aacute; nh&acirc;n đạt giải cao qua c&aacute;c Hội thi do Tỉnh, TW tổ chức.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img alt="Description: C:UsersAdministratorDesktop1c17a449901cce4bcac08ca80c6bd58a.jpg" src="/data/images/Capture3(2).JPG" style="height:411px; width:548px" /></span>&nbsp; &nbsp; &nbsp; &nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp; Ban Chấp h&agrave;nh Hội đ&atilde; tổ chức Hội nghị để kiểm điểm c&ocirc;ng t&aacute;c đối với tập thể ban Thường vụ, Ban Chấp h&agrave;nh. C&aacute;c đ/c Thường trực hội được đ&aacute;nh gi&aacute; Ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ năm 2022. B&agrave;n v&agrave; thống nhất nội dung, chương tr&igrave;nh chuẩn bị cho lễ Tổng kết c&ocirc;ng t&aacute;c năm 2022, triển khai nhiệm vụ năm 2023.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; Mặc d&ugrave; c&ograve;n nhiều kh&oacute; khăn, nhưng những th&aacute;ng cuối năm, Hội Khuyến học S&ocirc;ng M&atilde; đ&atilde; t&iacute;ch cực tổ chức nhiều hoạt động s&ocirc;i nổi, thiết thực, g&oacute;p phần c&ugrave;ng Đảng bộ, nh&acirc;n d&acirc;n c&aacute;c d&acirc;n tộc trong huyện thi đua ho&agrave;n th&agrave;nh thắng lợi mọi nhiệm vụ, mục ti&ecirc;u ph&aacute;t triển Kinh tế - X&atilde; hội - ANQP năm 2022.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;TRẦN TRANG NHUNG</strong></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Uỷ vi&ecirc;n TT Hội Khuyến học S&ocirc;ng M&atilde;</strong></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n', '2023-02-08 02:38:46', 37, '/data/files/anh%20co%20so/Capture1.JPG', '', 1, 52, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1397, 'Thuận Châu đẩy mạnh công tác khuyến học, khuyến tài,  xây dựng xã hội học tập', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:right"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><strong><em>Trọng Đại</em></strong></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Để thực hiện c&oacute; hiệu quả c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập, Thường trực Hội Khuyến học huyện Thuận Ch&acirc;u đ&atilde; <span style="color:black">phối hợp </span>tốt với c&aacute;c cơ quan, đo&agrave;n thể trong huyệnvề việc tuy&ecirc;n truyền, vận động n&acirc;ng cao nhận thức của c&aacute;n bộ, đo&agrave;n vi&ecirc;n, hội vi&ecirc;n c&aacute;c tổ chức về vị tr&iacute;, vai tr&ograve;, tầm quan trọng của c&ocirc;ng t&aacute;c <span style="color:black">khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Hội Khuyến học huyện đ&atilde; tổ chức k&yacute; kết chương tr&igrave;nh phối hợp với Mặt trận tổ quốc v&agrave; c&aacute;c tổ chức đo&agrave;n, với ph&ograve;ng Gi&aacute;o dục &ndash; Đ&agrave;o tạo, với hội Cựu gi&aacute;o chức huyện,</span> tuy&ecirc;n truyền, vận động hội vi&ecirc;n thực hiện hiệu quả c&aacute;c chỉ thị, nghị quyết của Đảng, nh&agrave; nước về c&ocirc;ng t&aacute;c khuyến học khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập như: Quyết định số 387/QĐ-TTg ng&agrave;y 25/3/2022 của Thủ tướng Ch&iacute;nh phủ về đẩy mạnh phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị giai đoạn 2021 &ndash; 2030; Th&ocirc;ng tư 22/2020/TT-BGĐT ng&agrave;y 06/8/2020 của Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo quy định về đ&aacute;nh gi&aacute;, xếp loại &ldquo;Đơn vị học tập&rdquo;&hellip; C&ugrave;ng với đ&oacute;, c&ocirc;ng t&aacute;c x&acirc;y dựng quỹ hội được c&aacute;c cấp hội ch&uacute; trọng thực hiện, c&ocirc;ng t&aacute;c thi đua, khen thưởng được thực hiện đảm bảo kịp thời. Năm 2022, Hội khuyến học huyện đ&atilde; khen thưởng cho 230 học sinh, 179 gi&aacute;o vi&ecirc;n, 19 tập thể, với tổng&nbsp; số tiền tr&ecirc;n 35 triệu đồng&hellip;</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp; &nbsp; &nbsp; &nbsp; Thường xuy&ecirc;n chỉ đạo việc củng cố tổ chức Hội v&agrave; hội vi&ecirc;n, v&igrave; vậy tổ chức Hội tr&ecirc;n địa b&agrave;n huyện lu&ocirc;n ph&aacute;t triển về cả số lượng v&agrave; chất lượng. Hiện nay to&agrave;n huyện c&oacute; 68 tổ chức hội. Trong đ&oacute; c&oacute; 29 Hội Khuyến học x&atilde;, thị trấn; 6 Hội Khuyến học trường trực thuộc; 33 Ban Khuyến học cơ quan, đơn vị.Qu&yacute; I năm 2022, Hội đ&atilde; ph&aacute;t triển th&ecirc;m 7 chi hội d&ograve;ng họ ở c&aacute;c bản thuộc x&atilde; Chiềng &nbsp;Ng&agrave;m v&agrave; Mường Khi&ecirc;ng, 3 Ban Khuyến học cơ quan, đơn vị, n&acirc;ng tổng số chi hội l&ecirc;n 636 chi hội, trong đ&oacute; c&oacute; 43 chi hội d&ograve;ng họ; 51.564 hội vi&ecirc;n. Đến hết năm 2022, Hội Khuyến học huyện Thuận Ch&acirc;u phấn đấu 100% bản, tiểu khu, th&ocirc;n l&agrave; chi hội khuyến học; 100% gia đ&igrave;nh l&agrave; hội vi&ecirc;n khuyến học; x&acirc;y dựng th&ecirc;m từ 1 đến 2 chi hội d&ograve;ng họ học tập tại c&aacute;c x&atilde;, thị trấn; bổ sung kịp thời nh&acirc;n sự cho Ban Chấp h&agrave;nh của Hội khuyến học x&atilde;, thị trấn.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;Để ph&aacute;t huy những kết quả đ&atilde; đạt được, Hội khuyến học huyện Thuận Ch&acirc;u ph&aacute;t tiếp tục ph&aacute;t huy vai tr&ograve; n&ograve;ng cốt tham mưu c&oacute; hiệu quả với cấp ủy, ch&iacute;nh quyền c&aacute;c cấp thực hiện tốt c&ocirc;ng t&aacute;c khuyến học khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập ph&ugrave; hợp với thực tế của huyện; đẩy mạnh phong tr&agrave;o học tập; duy tr&igrave; c&ocirc;ng t&aacute;c phối hợp với c&aacute;c cơ quan, ban ng&agrave;nh, đo&agrave;n thể, tổ chức ch&iacute;nh trị x&atilde; hội x&acirc;y dựng, triển khai kế hoạch thực hiện; ch&uacute; trọng c&ocirc;ng t&aacute;c huy động ph&aacute;t triển nguồn Quỹ hội bằng nhiều h&igrave;nh thức, khen thưởng, n&ecirc;u gương kịp thời c&aacute;c m&ocirc; h&igrave;nh học tập ti&ecirc;u biểu, tập thể c&aacute; nh&acirc;n c&oacute; th&agrave;nh t&iacute;ch trong giảng dạy, học tập v&agrave; c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập. </span></span><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="background-color:white">&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt="Description: C:UsersAdministratorDownloadsBCH hội  triển khai nhiệm vụ quý II 2022 (5).JPG" src="/data/images/anh%20co%20so/Capture4.JPG" style="height:453px; width:605px" /></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n', '2023-02-10 08:43:04', 37, '/data/files/anh%20co%20so/Capture4.JPG', '', 1, 54, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1398, 'Hội Khuyến học &amp;KHLS huyện Tháp Mười tổ chức Tổng kết công tác Hội năm 2022 và đề ra nhiệm vụ năm 2023.', '', '<p style="text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp; Ng&agrave;y 10/02/2023, Hội Khuyến học &amp;KHLS huyện Th&aacute;p Mười tổ chức Tổng kết c&ocirc;ng t&aacute;c Hội năm 2022 v&agrave; đề ra nhiệm vụ năm 2023. Đến dự c&oacute; B&agrave; Nguyễn Thị Nh&agrave;n-Chủ tịch Hội Khuyến học Tỉnh, &Ocirc;ng Nguyễn Văn Hiệp-Ph&oacute; Chủ tịch UBND huyện. Hội nghị đ&atilde; B&aacute;o c&aacute;o kết quả hoạt động của 3 Hội: Khuyến học, Cựu gi&aacute;o chức v&agrave; Khoa học Lịch sử năm 2022; Triển khai phương hướng, nhiệm vụ của c&aacute;c Hội năm 2023; Hướng dẫn v&agrave; tổ chức, ph&acirc;n c&ocirc;ng c&aacute;c Cụm thi đua cấp cơ sở Hội năm 2023.&nbsp;</p>\r\n\r\n<p style="text-align:justify">&nbsp; &nbsp; &nbsp; Ph&aacute;t biểu tại Hội nghị, l&atilde;nh đạo Hội Khuyến học tỉnh, UBND huyện Th&aacute;p Mười biểu dương những kết quả Hội KH&amp;KHLS huyện đạt được trong năm qua v&agrave; chỉ đạo một số nhiệm vụ c&aacute;c cấp Hội cần tập trung triển khai thực hiện hiệu quả trong năm 2023. Dịp n&agrave;y, Hội KH&amp;KHLS huyện, Hội Khuyến học -Cựu gi&aacute;o chức x&atilde; Đốc Binh Kiều được nhận Cờ thi đua của Hội Khuyến học Việt Nam l&agrave; đơn vị xuất sắc trong phong tr&agrave;o thi đua khuyến học khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập năm 2022. Nhiều tập thể, c&aacute; nh&acirc;n c&oacute; th&agrave;nh t&iacute;ch xuất sắc cũng được TW Hội, Tỉnh Hội, Huyện Hội biểu dương, khen thưởng.</p>\r\n', '2023-02-13 01:55:47', 34, '/data/files/anh%20tinh%20ban/Capture5.JPG', '', 0, 55, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1399, 'Đồng tháp tuyên dương tập thể', 'Một cách đưa các chỉ đạo của Đảng, Chính Phủ, Trung ương Hội khuyến học Việt Nam và các chỉ đạo của cấp ủy, uỷ ban nhân dân và của Hội Khuyến học các cấp đến hội viên khuyến học nhanh nhất là thực hiện lồng ghép nội dung hoạt động khuyến học, khuyến tài, xây dựng XHHT, nhất là tuyên truyền các tiêu chí xây dựng các mô hình học tập đến các Tổ NDTQ. Là thành viên của MTTQ thời gian qua Hội khuyến học các cấp của Đồng Tháp luôn xem mỗi Tổ NDTQ là một tổ hội, là nền tảng, là địa chỉ tin cậy để vận động nhân dân thực hiện khuyến học, phát triển hội viên và xây dựng Hội khuyến học vững mạnh, góp phần xây dựng các Thành phố học tập và sắp tới là các Huyện học tập…', '<p><a href="https://youtu.be/nZfMqelNESI">https://youtu.be/nZfMqelNESI</a></p>\r\n', '2023-02-15 03:43:09', 32, '/data/files/anh%20tinh%20ban/Capture6.JPG', '', 0, 55, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1400, 'Nữ Chủ tịch Hội Khuyến học tâm huyết với công tác Hội', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:Calibri,&quot;sans-serif&quot;"><em><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Lời dẫn: Với t&acirc;m niệm &ldquo;đ&atilde; nhận nhiệm vụ th&igrave; phải l&agrave;m hết m&igrave;nh&rdquo;, b&agrave; Phạm Thị Cẩm, Chủ tịch Hội Khuyến học huyện Y&ecirc;n Ch&acirc;u đ&atilde; đem hết t&acirc;m huyết, tr&iacute; tuệ phục vụ c&ocirc;ng t&aacute;c khuyến học, g&oacute;p phần đưa phong tr&agrave;o khuyến học, khuyến t&agrave;i tr&ecirc;n địa b&agrave;n huyện ng&agrave;y c&agrave;ng ph&aacute;t triển.</span></span></em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:Calibri,&quot;sans-serif&quot;"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Trưởng th&agrave;nh trong m&ocirc;i trường nghề gi&aacute;o, từng l&agrave; c&ocirc; gi&aacute;o đứng tr&ecirc;n bục giảng, 30 năm đảm nhiệm chức vụ Hiệu trưởng c&aacute;c trường THCS,sau khi về nghỉ chế độ, năm 2014, b&agrave; Phạm Thị Cẩm tham gia c&ocirc;ng t&aacute;c Hội với cương vị l&agrave; Ph&oacute; Chủ tịch Hội khuyến học huyện Y&ecirc;n Ch&acirc;u, đến năm 2021 b&agrave; được t&iacute;n nhiệm bầu l&agrave;m Chủ tịch Hội Khuyến học huyện. Tuy đ&atilde; 63 năm tuổi đời, b&agrave; Cẩm vẫn miệt m&agrave;i với c&ocirc;ng t&aacute;c khuyến học tại cơ sở. Từ khi gắn b&oacute; với c&ocirc;ng t&aacute;c hội, b&agrave; lu&ocirc;n t&iacute;ch cực t&igrave;m t&ograve;i, nghi&ecirc;n cứu c&aacute;c t&agrave;i liệu về c&ocirc;ng t&aacute;c khuyến học, đồng thời thường xuy&ecirc;n tham khảo c&aacute;ch l&agrave;m hay của những địa phương c&oacute; phong tr&agrave;o khuyến học ti&ecirc;u biểu để học tập.</span></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:Calibri,&quot;sans-serif&quot;"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; To&agrave;n huyện c&oacute; 15 Hội cấp x&atilde;, thị trấn,182chi hội bản, tiểu khu với 22.642 hội vi&ecirc;n. Với cương vị l&agrave; Chủ tịch Hội Khuyến học huyện Y&ecirc;n Ch&acirc;u, b&agrave; đ&atilde; c&ugrave;ng với tập thể Ban Thường vụ Hội Khuyến học huyện chăm lo x&acirc;y dựng tổ chức Hội vững mạnh v&agrave; ph&aacute;t triển hội vi&ecirc;n đạt nhiều th&agrave;nh t&iacute;ch đ&atilde; được ghi nhận. Để phong tr&agrave;o khuyến học khuyến t&agrave;i của huyện thực sự đi v&agrave;o chiều s&acirc;u v&agrave; ng&agrave;y c&agrave;ng ph&aacute;t triển, b&agrave; đ&atilde; kh&ocirc;ng ngừng đổi mới t&igrave;m ra hướng đi ph&ugrave; hợp với từng địa phương v&agrave; từng tổ chức cơ sở hội thu h&uacute;t được sự quan t&acirc;m của l&atilde;nh đạo Đảng, ch&iacute;nh quyền v&agrave; c&aacute;c tổ ch<em>ứ</em>c x&atilde; hội ở địa phương.</span></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:Calibri,&quot;sans-serif&quot;"><strong><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; B&agrave; Phạm Thị Cẩm, Chủ tịch Hội khuyến học huyện Y&ecirc;n Ch&acirc;u cho biết</span></span></strong><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="color:black">: <em>Để n&acirc;ng cao chất lượng của phong tr&agrave;o khuyến học khuyến t&agrave;i v&agrave; x&acirc;y dựng XHHT th&igrave; theo t&ocirc;i trước hết l&agrave; phải ổn định được đội ngũ c&aacute;n bộ l&agrave;m c&ocirc;ng t&aacute;c khuyến học ở cơ sở, cần tiếp tục thực hiện tốt c&ocirc;ng t&aacute;c bồi dưỡng nghiệp vụ cho c&aacute;c đồng ch&iacute; chuy&ecirc;n tr&aacute;ch c&ocirc;ng t&aacute;c khuyến học để c&aacute;c đồng ch&iacute; đ&oacute; n&acirc;ng cao được chuy&ecirc;n m&ocirc;n của m&igrave;nh trong qu&aacute; tr&igrave;nh hoạt động.Cần phải n&acirc;ng cao hơn nữa sự quan t&acirc;m của c&aacute;c cấp ủy đảng ch&iacute;nh quyền đối với c&ocirc;ng t&aacute;c khuyến học khuyến t&agrave;i v&agrave; x&acirc;y dựng XHHT ở c&aacute;c cơ sở, l&agrave;m tốt c&ocirc;ng t&aacute;c x&atilde; hội h&oacute;a k&ecirc;u gọi sự ủng hộ từ nhiều nguồn để khen thưởng cho c&aacute;c gia đ&igrave;nh, d&ograve;ng họ hiếu học v&agrave; c&aacute;c em học sinh c&oacute; th&agrave;nh t&iacute;ch cao trong học tập như vậy mới động vi&ecirc;n được phong tr&agrave;o. </em></span></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:Calibri,&quot;sans-serif&quot;"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Nhờ đ&oacute;, đến nay to&agrave;n huyện c&oacute; 20.038 c&ocirc;ng d&acirc;n học tập đạt tỷ lệ 25,36%, 11.031 hộ gia đ&igrave;nh học tập đạt 72%, 32 d&ograve;ng họ học tập đạt 71%;118 đơn vị học tập đạt 89,39%; Cộng đồng học tập 15/15 x&atilde; thị trấn đạt tỷ lệ100%. B&ecirc;n cạnh đ&oacute;, c&ocirc;ng t&aacute;c t&aacute;c x&acirc;y dựng Quỹ Khuyến học được triển khai, trong năm 2022, Hội đ&atilde;vận động hội vi&ecirc;n tham gia đ&oacute;ng g&oacute;p hội ph&iacute;, quỹ hội được tr&ecirc;n 100 triệu đồng.Để c&oacute; th&ecirc;m nguồn quỹ phục vụ cho c&ocirc;ng t&aacute;c khuyến học, b&agrave; Cẩm c&ugrave;ng với Thường trực hội thực hiện tốt việc huy động v&agrave; tiếp nhận từ c&aacute;c cơ quan, đơn vị, tổ chức c&aacute; nh&acirc;n; tận tuỵ khảo s&aacute;t c&aacute;c gia đ&igrave;nh c&oacute; học sinh, sinh vi&ecirc;n kh&oacute; khăn để kịp thời gi&uacute;p đỡ hoặc đề xuất xin t&agrave;i trợ từ doanh nghiệp, hội khuyến học cấp tr&ecirc;n. Đồng thời, quản l&yacute; v&agrave; sử dụng c&oacute; hiệu quả c&aacute;c chương tr&igrave;nh t&agrave;i trợ, sử dụng nguồn quỹ đ&uacute;ng mục đ&iacute;ch, đ&uacute;ng đối tượng, c&ocirc;ng khai, minh bạch để c&aacute;c nh&agrave; hảo t&acirc;m biết, tin tưởng v&agrave; lu&ocirc;n đồng h&agrave;nh c&ugrave;ng Hội Khuyến học trong c&ocirc;ng t&aacute;c chăm lo cho c&aacute;c em học sinh. G&oacute;p phần đ&aacute;ng kể trong việc gi&uacute;p được nhiều học sinh, sinh vi&ecirc;n c&oacute; ho&agrave;n cảnh kh&oacute; khăn. Tuy nhi&ecirc;n c&ocirc;ng t&aacute;c x&acirc;y dựng quỹ hội vẫn c&ograve;n những kh&oacute; khăn nhất định.</span></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:Calibri,&quot;sans-serif&quot;"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Tr&ecirc;n cương vị l&agrave; Chủ tịch Hội Khuyến học huyện, b&agrave; Cẩm c&ugrave;ng Ban Thường vụ Hội Khuyến học c&aacute;c x&atilde; x&acirc;y dựng chương tr&igrave;nh, kế hoạch hoạt động s&aacute;t với thực tiễn của địa phương. L&agrave;m tốt c&ocirc;ng t&aacute;c x&acirc;y dựng c&aacute;c m&ocirc; h&igrave;nh học tập tại cơ sở. Ch&uacute; trọng x&acirc;y dựng tổ chức hội cơ sở vững mạnh.</span></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:Calibri,&quot;sans-serif&quot;"><strong><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &Ocirc;ng L&ograve; Văn Thắng, Ph&oacute; Chủ tịch UBND x&atilde;, chủ tịch Hội khuyến học x&atilde; Chiềng Pằn b&agrave;y tỏ: </span></span></strong><em><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="color:black">Trong những năm qua, Hội Khuyến học x&atilde; Chiềng Pằn lu&ocirc;n nhận được sự quan t&acirc;m của Hội Khuyến học huyện. Đặc biệt l&agrave; được sự quan t&acirc;m chỉ đạo s&aacute;t sao của b&agrave; Phạm Thị Cẩm, Chủ tịch HKH huyện, b&agrave; rất năng động, nhiệt t&igrave;nh quan t&acirc;m chỉ đạo s&aacute;t sao với cơ sở hội. Nhờ đ&oacute; m&agrave; c&ocirc;ng t&aacute;c khuyến học khuyến t&agrave;i tr&ecirc;n địa b&agrave;n x&atilde; Chiềng Pằn lu&ocirc;n đạt được nhiều th&agrave;nh t&iacute;ch, h&agrave;ng năm.</span></span></em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:Calibri,&quot;sans-serif&quot;"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Với những kết quả m&agrave; Hội khuyến học huyện đ&atilde; đạt được trong những năm qua, c&oacute; sự đ&oacute;ng g&oacute;p kh&ocirc;ng nhỏ của b&agrave; Phạm Thị Cẩm. Với những th&agrave;nh t&iacute;ch đ&oacute;ng g&oacute;p đ&oacute;, b&agrave; đ&atilde; vinh dự được Trung ương Hội khuyến học tặng Bằng khen; được Hội Khuyến học tỉnh tặng giấy khen,c&ugrave;ng nhiều phần thưởng cao qu&yacute;, b&agrave; <span style="background-color:#fcfcfc">được cấp ủy, ch&iacute;nh quyền địa phương v&agrave; nh&acirc;n d&acirc;n ghi nhận, đ&aacute;nh gi&aacute; cao.</span></span></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="font-family:Calibri,&quot;sans-serif&quot;"><strong><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="color:black">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(Nguồn từ TT &ndash;TTVH huyện Y&ecirc;n Ch&acirc;u)</span></span></strong></span></span></p>\r\n', '2023-02-15 03:57:31', 35, '', '', 0, 54, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1401, 'Huyện Sông Mã tổ chức hội nghị tổng kết công tác khuyến học năm 2022, triển khai nhiệm vụ trọng tâm năm 2023.', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; Chiều ng&agrave;y 17/2/2023, tại Hội trường Ph&ograve;ng Gi&aacute;o dục-Đ&agrave;o tạo, Hội Khuyến học huyện S&ocirc;ng M&atilde; đ&atilde; tổ chức hội nghị tổng kết c&ocirc;ng t&aacute;c năm 2022, triển khai nhiệm vụ trọng t&acirc;m năm 2023 v&agrave; đ&oacute;n nhận Cờ thi đua xuất sắc của Hội Khuyến học Việt Nam.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tới dự v&agrave; chỉ đạo hội nghị c&oacute; đồng ch&iacute; Mai Thu Hương UV BCH TW Hộị, Chủ tịch Hội Khuyến học tỉnh v&agrave; đo&agrave;n c&ocirc;ng t&aacute;c của tỉnh hội. Về ph&iacute;a huyện c&oacute; đồng ch&iacute; Nguyễn Ch&iacute; Chung UV BCH huyện ủy, Ph&oacute; Chủ tịch UBND huyện, l&atilde;nh đạo 1 số ph&ograve;ng ban, c&aacute;c UV BCH Hội Khuyến học huyện v&agrave; c&aacute;c đồng ch&iacute; Chủ tịch, Ph&oacute; Chủ tịch Hội c&aacute;c x&atilde;, thị trấn của huyện.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Được sự ủy quyền của TW hội Khuyến học Việt Nam, đồng ch&iacute; Mai Thu Hương đ&atilde; chuyển trao cờ v&agrave; Bằng khen của TW hội cho tập thể v&agrave; c&aacute; nh&acirc;n xuất sắc của huyện S&ocirc;ng M&atilde;.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px">&nbsp; &nbsp; &nbsp;&nbsp;<img src="/data/images/anh%20co%20so/Capture3.JPG" style="height:303px; width:625px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img src="/data/images/anh%20co%20so/z4114311850023_6cb55f987ca0d3b7a813617fcd1a3e1e.jpg" style="height:303px; width:625px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img src="/data/images/anh%20co%20so/z4114317103413_a5e61747bd14bbbc035efbbda11ddae4.jpg" style="height:303px; width:625px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Tại hội nghị, Hội Khuyến học đ&atilde; tổ chức k&yacute; cam kết thi đua năm 2023 cho Hội Khuyến học c&aacute;c x&atilde;, thị trấn v&agrave; c&aacute;c ban khuyến học của huyện, thể hiện quyết t&acirc;m ho&agrave;n th&agrave;nh xuất sắc nhiện vụ của Hội năm 2023.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n', '2023-02-17 09:28:12', 38, '/data/files/anh%20co%20so/Capture3.JPG', '', 1, 54, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1402, 'Hội Khuyến học tỉnh trao thưởng cho tập thể, cá nhân đạt thành tích xuất sắc trong Hội thao giáo dục quốc phòng và an ninh học sinh THPT lần thứ VI.', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Hội thao gi&aacute;o dục quốc ph&ograve;ng, an ninh đối với học sinh cấp THPT tỉnh Sơn La lần thứ VI, năm học 2022-2023 diễn ra từ ng&agrave;y 13 đến 17/2/2023.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:14px"><span style="background-color:white"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Tham dự Hội thi c&oacute; 43 trường THPT với 378 th&iacute; sinh ch&iacute;nh thức,100 th&iacute; sinh dự bị.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="color:black"><img alt="Description: C:UsersAdministratorPicturesCapture7.JPG" src="/data/images/tin-tuc/Capture7.JPG" style="height:208px; width:551px" /></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img alt="Description: C:UsersAdministratorPicturesCapture8.JPG" src="/data/images/tin-tuc/Capture8.JPG" style="color:black" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Sau 4 ng&agrave;y diễn ra s&ocirc;i nổi, hội thao đ&atilde; th&agrave;nh c&ocirc;ng tốt đẹp. Hội Khuyến học tỉnh đ&atilde; trao tặng giấy khen v&agrave; tiền thưởng cho 18 giải tập thể, 40 giải c&aacute; nh&acirc;n c&oacute; th&agrave;nh t&iacute;ch xuất sắc.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="color:black"><img src="/data/images/tin-tuc/Capture6.JPG" style="height:303px; width:606px" /></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:black">Đ/c Nguyễn Văn Bao &ndash; Ph&oacute; Chủ tịch Thường trực trao giấy khen v&agrave; tiền thưởng cho 18 tập thể đạt giải hội thi</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:black"><img alt="Description: C:UsersAdministratorPicturesCapture9.JPG" src="/data/images/tin-tuc/Capture5.JPG" style="height:348px; width:631px" /></span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:black">Đ/c Nguyễn Văn Bao &ndash; Ph&oacute; Chủ tịch Thường trực trao giấy khen v&agrave; tiền thưởng cho 40 học sinh đạt giải hội thi</span></em></span></p>\r\n', '2023-02-17 09:48:14', 32, '/data/files/hkh/Capture5(1).JPG', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1367, 'Dòng họ Cầm hiếu học tiêu biểu ở Sơn La', 'Dòng họ Cầm ở phường Tô Hiệu, thành phố Sơn La có 21 hộ, với 65 thành viên, có truyền thống hiếu học và có nhiều con, cháu thành đạt, là dòng họ tiểu biểu của phong trào khuyến học, khuyến tài.', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="color:black">Khơi dậy tinh thần hiếu học, d&ograve;ng họ Cầm đ&atilde; v&agrave; c&oacute; nhiều hoạt động khuyến học, khuyến t&agrave;i; khuyến kh&iacute;ch c&aacute;c th&agrave;nh vi&ecirc;n lu&ocirc;n đo&agrave;n kết, gi&uacute;p nhau vươn l&ecirc;n trong học tập, c&ocirc;ng t&aacute;c v&agrave; cuộc sống.</span></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="color:black"><a href="https://congdankhuyenhoc.qltns.mediacdn.vn/449484899827462144/2022/5/19/s2a0qhfw-1652949532353-16529495327921796120651.jpg" target="_blank" title="Một buổi sinh hoạt Ban Thường trực công tác khuyến học dòng họ Cầm."><span style="color:black"><img alt="Dòng họ Cầm hiếu học tiêu biểu ở Sơn La  - Ảnh 1." id="_x0000_i1025" src="/data/images/anh%20dong%20ho/Capture6.JPG" title="&quot;Một buổi sinh hoạt Ban Thường trực công tác khuyến học dòng họ Cầm.&quot;" />&nbsp;</span></a></span></span></span></span><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em><span style="color:black">Một buổi sinh hoạt Ban Thường trực c&ocirc;ng t&aacute;c khuyến học d&ograve;ng họ Cầm.</span></em></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="color:black">Lật cuốn sổ ghi ch&eacute;p th&agrave;nh t&iacute;ch học tập của d&ograve;ng họ, &ocirc;ng Cầm C&acirc;u, Trưởng d&ograve;ng họ, chia sẻ: D&ograve;ng họ x&acirc;y dựng quy chế hoạt động; th&agrave;nh lập Ban Thường trực c&ocirc;ng t&aacute;c khuyến học; thường xuy&ecirc;n tổ chức c&aacute;c buổi sinh hoạt để hội vi&ecirc;n chia sẻ cũng như trao đổi kinh nghiệm trong dạy bảo con, ch&aacute;u thi đua học tập.&nbsp;</span></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="color:black">Hằng năm, Ban Thường trực c&ocirc;ng t&aacute;c khuyến học d&ograve;ng họ tổ chức tổng kết, tuy&ecirc;n dương, khen thưởng học sinh, sinh vi&ecirc;n c&oacute; th&agrave;nh t&iacute;ch cao trong học tập; n&ecirc;u gương những người th&agrave;nh đạt, những tấm gương ti&ecirc;u biểu về tinh thần vượt kh&oacute;, vươn l&ecirc;n trong học tập để con ch&aacute;u học theo. Nhờ đ&oacute;, 90% con em trong d&ograve;ng họ học phổ th&ocirc;ng đạt học lực kh&aacute;, giỏi. Hiện nay, d&ograve;ng họ c&oacute; 43 người c&oacute; tr&igrave;nh độ đại học trở l&ecirc;n, nhiều người c&oacute; 2 văn bằng đại học; c&oacute; 25 đảng vi&ecirc;n. Nhiều gia đ&igrave;nh c&oacute; con em trưởng th&agrave;nh, tham gia c&ocirc;ng t&aacute;c, cống hiến trong lực lượng vũ trang v&agrave; c&aacute;c cơ quan, doanh nghiệp.&nbsp;</span></span></span></span></p>\r\n', '2022-10-25 02:29:05', 57, '/data/files/anh%20dong%20ho/Capture6.JPG', '', 0, 36, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1368, 'CHUYỂN ĐỔI SỐ TRONG HOẠT ĐỘNG KHUYẾN HỌC - CƠ HỘI VÀ THÁCH THỨC', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:right"><span style="font-size:14px"><strong><em>Th.sĩ: Mai Thu Hương &ndash; CT Hội KH tỉnh</em></strong></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ng&agrave;y nay, chuyển đổi số ng&agrave;y c&agrave;ng phổ biến trong đời sống x&atilde; hội v&agrave; trở th&agrave;nh xu thế tất yếu của to&agrave;n cầu, l&agrave; yếu tố quyết định cho sự ph&aacute;t triển của mỗi quốc gia, d&acirc;n tộc. Chuyển đổi số kh&ocirc;ng chỉ gi&uacute;p tăng năng suất trong qu&aacute; tr&igrave;nh l&agrave;m việc, giảm tối đa chi ph&iacute; m&agrave; c&ograve;n đem lại một kh&ocirc;ng gian l&agrave;m việc ph&aacute;t triển mới, tạo n&ecirc;n những gi&aacute; trị mới b&ecirc;n cạnh những gi&aacute; trị truyền thống.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Đại hội XIII của Đảng đặc biệt nhấn mạnh chủ trương chuyển đổi số, ph&aacute;t triển kinh tế số trong c&aacute;c văn kiện Đại hội. Nội dung về chuyển đổi số được đưa v&agrave;o B&aacute;o c&aacute;o ch&iacute;nh trị th&agrave;nh một trong những nhiệm vụ trọng t&acirc;m v&agrave; c&aacute;c đột ph&aacute; chiến lược trong nhiệm kỳ Đại hội. Nghị quyết số 52-NQ/TW ng&agrave;y 27/9/2019 của Bộ Ch&iacute;nh trị về một số chủ trương, ch&iacute;nh s&aacute;ch chủ động tham gia cuộc C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư đ&atilde; nhấn mạnh y&ecirc;u cầu cấp b&aacute;ch để đẩy nhanh qu&aacute; tr&igrave;nh chuyển đổi số; Ch&iacute;nh phủ cũng đ&atilde; ban h&agrave;nh Chương tr&igrave;nh h&agrave;nh động của Ch&iacute;nh phủ thực hiện Nghị quyết số 52-NQ/TW ng&agrave;y 27/9/2019 của Bộ Ch&iacute;nh trị. Tr&ecirc;n cơ sở đ&oacute;, ng&agrave;y 03/6/2020, Thủ tướng Ch&iacute;nh phủ đ&atilde; ban h&agrave;nh Quyết định số 749/QĐ-TTg ph&ecirc; duyệt &ldquo;Chương tr&igrave;nh Chuyển đổi số quốc gia đến năm 2025, định hướng đến năm 2030&rdquo; v&agrave; ng&agrave;y 22/4/2022, Thủ tướng Ch&iacute;nh phủ đ&atilde; ban h&agrave;nh Quyết định số 505/QĐ-TTg lấy ng&agrave;y 10 th&aacute;ng 10 hằng năm l&agrave; Ng&agrave;y Chuyển đổi số quốc gia. Đ&acirc;y l&agrave; một ng&agrave;y &yacute; nghĩa với c&ocirc;ng cuộc chuyển đổi số tại Việt Nam. </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; Năm 2021, Thủ tướng Ch&iacute;nh phủ ban h&agrave;nh Chỉ thị số 14 về &ldquo;Đẩy mạnh c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập giai đoạn 2021 &ndash; 2030&rdquo; trong đ&oacute; nhấn mạnh&nbsp; việc &ldquo;Đẩy mạnh v&agrave; thực hiện hiệu quả chuyển đổi số trong c&aacute;c cơ sở gi&aacute;o dục đ&agrave;o tạo&hellip;Tăng cường ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin, c&ocirc;ng nghệ mới trong c&aacute;c hoạt động dạy v&agrave; học&rdquo;. Sau đ&oacute;, Đề &aacute;n 1373 về &ldquo;X&acirc;y dựng x&atilde; hội học tập giai đoạn 2021 &ndash; 2030&rdquo; c&oacute; n&ecirc;u quan điểm chỉ đạo: &ldquo;Ứng dụng c&ocirc;ng nghệ số v&agrave; dạy học trực tuyến trong tổ chức c&aacute;c hoạt động giảng dạy v&agrave; học tập&hellip; Mọi c&ocirc;ng d&acirc;n c&oacute; tr&aacute;ch nhiệm v&agrave; quyền lợi được học tập thường xuy&ecirc;n, học suốt đời, tận dụng mọi cơ hội học tập để trở th&agrave;nh c&ocirc;ng d&acirc;n số, c&ocirc;ng d&acirc;n học tập to&agrave;n cầu&rdquo; đồng thời đưa chỉ ti&ecirc;u về năng lực cơ bản v&agrave; tr&igrave;nh độ của người d&acirc;n l&agrave; &ldquo;Đến năm 2025: 50% số người trong độ tuổi lao động được trang bị năng lực th&ocirc;ng tin, 70% c&aacute;c trung t&acirc;m học tập cộng đồng ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin trong quản l&yacute;, tổ chức hoạt động gi&aacute;o dục&rdquo; (chỉ ti&ecirc;u n&agrave;y giai đoạn đến 2030 phải đạt lần lượt l&agrave;: 70% v&agrave; 90%).</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Th&aacute;ng 3 năm 2022, Thủ tướng Ch&iacute;nh phủ ban h&agrave;nh Quyết định 387 ph&ecirc; duyệt Chương tr&igrave;nh &ldquo;Đẩy mạnh phong tr&agrave;o học tập suốt đời&nbsp; trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị giai đoạn 2021 &ndash; 2030&rdquo; trong đ&oacute; n&ecirc;u mục ti&ecirc;u chung l&agrave; &ldquo;&hellip;.Ứng dụng mạnh mẽ c&ocirc;ng nghệ th&ocirc;ng tin ph&ugrave; hợp với Chương tr&igrave;nh chuyển đổi số quốc gia giai đoạn 2021 &ndash; 2030&rdquo;; V&agrave; Quyết định 677 ph&ecirc; duyệt Chương tr&igrave;nh &ldquo;X&acirc;y dựng m&ocirc; h&igrave;nh C&ocirc;ng d&acirc;n học tập giai đoạn 2021 &ndash; 2030&rdquo; cũng n&ecirc;u mục ti&ecirc;u chung &hellip;&rdquo;Mọi c&ocirc;ng d&acirc;n trong x&atilde; hội được tham gia học tập, hướng tới c&ocirc;ng d&acirc;n số, đ&aacute;p ứng y&ecirc;u cầu của Chương tr&igrave;nh chuyển đổi số quốc gia v&agrave; g&oacute;p phần x&acirc;y dựng th&agrave;nh c&ocirc;ng một x&atilde; hội học tập&rdquo;. Trong c&aacute;c nhiệm vụ v&agrave; giải ph&aacute;p của c&aacute;c văn bản chỉ đạo n&agrave;y đều nhấn mạnh việc tăng cường ứng dụng c&ocirc;ng nghệ số, c&ocirc;ng nghệ th&ocirc;ng tin v&agrave;o hoạt động khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thực hiện chủ trương của Đảng, Nh&agrave; nước, Trung ương Hội Khuyến học Việt Nam đ&atilde; c&oacute; nhiều chỉ đạo kịp thời về chuyển đổi số, ứng dụng c&ocirc;ng nghệ số trong hoạt động của Hội Khuyến học c&aacute;c cấp.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Việc ứng dụng rộng r&atilde;i chuyển đổi số trong c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập sẽ tạo dựng một phương thức hoạt động mới trong thực hiện nhiệm vụ cả về c&ocirc;ng t&aacute;c quản l&yacute;, điều h&agrave;nh, c&ocirc;ng t&aacute;c phối hợp, c&ocirc;ng t&aacute;c tuy&ecirc;n truyền, vận động thuyết phục, c&ocirc;ng t&aacute;c x&acirc;y dựng v&agrave; ph&aacute;t triển tổ chức hội, c&ocirc;ng t&aacute;c x&acirc;y dựng v&agrave; n&acirc;ng cao chất lượng c&aacute;c m&ocirc; h&igrave;nh học tập, c&ocirc;ng t&aacute;c quản l&yacute; v&agrave; ph&aacute;t triển quĩ hội&hellip;.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Như vậy, về tổng thể, chuyển đổi số đ&atilde; trở th&agrave;nh một định hướng quan trọng trong ph&aacute;t triển đất nước. Với c&ocirc;ng t&aacute;c khuyến học khuyến t&agrave;i x&acirc;y dựng x&atilde; hội học tập, chuyển đổi số như l&agrave; một cơ hội l&agrave; nền tảng để thực hiện c&oacute; hiệu quả c&aacute;c mục ti&ecirc;u chiến lược.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; Tuy nhi&ecirc;n, qu&aacute; tr&igrave;nh chuyển đổi số trong c&ocirc;ng t&aacute;c khuyến học khuyến t&agrave;i x&acirc;y dựng x&atilde; hội học tập c&ograve;n rất nhiều th&aacute;ch thức đặt ra.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong><em>Thứ nhất</em>:</strong> Gắn với chuyển đổi số l&agrave; qu&aacute; tr&igrave;nh thay đổi tổng thể v&agrave; to&agrave;n diện của c&aacute; nh&acirc;n, tổ chức về c&aacute;ch sống, c&aacute;ch l&agrave;m việc dựa tr&ecirc;n c&aacute;c c&ocirc;ng nghệ số. Cần hiểu r&otilde;: Cuộc sống l&agrave; kh&ocirc;ng ngừng biến đổi v&agrave; vận động. Mỗi người, mỗi tổ chức cũng sẽ cần phải kh&ocirc;ng ngừng thay đổi ch&iacute;nh bản th&acirc;n m&igrave;nh, t&igrave;m c&aacute;ch th&iacute;ch nghi, nếu kh&ocirc;ng chắc chắn sẽ bị bỏ lại ở ph&iacute;a sau. Do đ&oacute;, để thực hiện quy tr&igrave;nh chuyển đổi số, cần chuyển đổi về nhận thức, tư duy, sau đ&oacute; sẽ thực hiện dần dần chuyển đổi phương thức sống, c&aacute;ch thức, quy tr&igrave;nh l&agrave;m việc dựa tr&ecirc;n những ứng dụng c&ocirc;ng nghệ số mới nhất.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>Thứ hai:</strong></em> Phải c&oacute; hạ tầng đủ mạnh. Hạ tầng đ&aacute;p ứng, cung cấp được mọi loại dịch vụ cả viễn th&ocirc;ng, Internet, c&ocirc;ng nghệ th&ocirc;ng tin v&agrave; c&aacute;c loại h&igrave;nh dịch vụ mới kh&aacute;c, đồng thời đảm bảo độ an to&agrave;n, b&iacute; mật&hellip; của đối tượng tham gia v&agrave;o qu&aacute; tr&igrave;nh chuyển đổi số. Với hệ thống khuyến học khuyến t&agrave;i, ngo&agrave;i hạ tầng l&agrave; c&ocirc;ng cụ, thiết bị cho đội ngũ chuy&ecirc;n tr&aacute;ch l&agrave;m khuyến học, c&ograve;n cần đ&aacute;p ứng hạ tầng viễn th&ocirc;ng đến người d&acirc;n (Như mạng Internet, m&aacute;y t&iacute;nh, điện thoại di động th&ocirc;ng minh&hellip;)</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><strong><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Thứ ba:</em></strong>&nbsp; Cần c&oacute; nguồn nh&acirc;n lực đ&aacute;p ứng được y&ecirc;u cầu, cả người sử dụng v&agrave; người l&agrave;m việc trong c&ocirc;ng cuộc chuyển đổi số. Cụ thể, to&agrave;n d&acirc;n d&ugrave;ng c&ocirc;ng nghệ th&ocirc;ng tin th&igrave; to&agrave;n d&acirc;n phải c&oacute; kiến thức tối thiểu về c&ocirc;ng nghệ th&ocirc;ng tin, phải biết sử dụng Internet. C&aacute;n bộ khuyến học c&aacute;c cấp muốn thực hiện chuyển đổi số cần c&oacute; tr&igrave;nh độ, khả năng s&aacute;ng tạo v&agrave; th&uacute;c đẩy ph&aacute;t triển c&ocirc;ng nghệ số, c&ocirc;ng nghệ th&ocirc;ng tin. Trong khi, hiện nay c&aacute;n bộ l&agrave;m c&ocirc;ng t&aacute;c khuyến học chuy&ecirc;n tr&aacute;ch c&aacute;c cấp đa số đ&atilde; nghỉ hưu, l&agrave;m ki&ecirc;n nhiệm, việc nắm bắt c&ocirc;ng nghệ th&ocirc;ng tin v&agrave; sử dụng c&aacute;c thiết bị c&ocirc;ng nghệ hiện đại gặp nhiều kh&oacute; khăn.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><strong><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Thứ tư: </em></strong>Nguồn lực về t&agrave;i ch&iacute;nh cho việc chuyển đổi số trong hệ thống khuyến học c&aacute;c cấp. Do tổ chức hội l&agrave; tổ chức x&atilde; hội, nguồn lực từ ng&acirc;n s&aacute;ch hạn hẹp do đ&oacute; việc vận động, k&ecirc;u gọi nguồn hỗ trợ để thực hiện nhiệm vụ l&agrave; rất kh&oacute; khăn.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><strong><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thứ năm: </em></strong>Trong c&aacute;c nhiệm vụ chuyển đổi số về khuyến học khuyến t&agrave;i x&acirc;y dựng x&atilde; hội học tập, nhiều nội dung li&ecirc;n quan đến ng&agrave;nh gi&aacute;o dục đ&agrave;o tạo v&agrave; Hội khuyến học. Tuy nhi&ecirc;n, c&ocirc;ng t&aacute;c phối hợp để triển khai thực hiện c&aacute;c chỉ ti&ecirc;u, nhiệm vụ n&agrave;y c&ograve;n mờ nhạt, c&oacute; l&uacute;c, c&oacute; nơi chưa quan t&acirc;m phối hợp chặt chẽ, do đ&oacute; chất lượng, hiệu quả chưa cao. </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;Năm 2020 được x&aacute;c định l&agrave; năm khởi động chuyển đổi số quốc gia, l&agrave; năm tuy&ecirc;n ng&ocirc;n về chuyển đổi số; năm 2021 l&agrave; năm tổng diễn tập chuyển đổi số; năm 2022 v&agrave; những năm tiếp theo sẽ l&agrave; giai đoạn th&uacute;c đẩy mạnh mẽ chuyển đổi số ở mọi ng&agrave;nh, mọi cấp, tr&ecirc;n phạm vi to&agrave;n quốc, to&agrave;n d&acirc;n v&agrave; to&agrave;n diện. Với những cơ hội v&agrave; th&aacute;ch thức đ&atilde; n&ecirc;u, để thực hiện chuyển đổi số th&agrave;nh c&ocirc;ng trong hệ thống khuyến học c&aacute;c cấp, cần rất nhiều t&acirc;m huyết, tr&iacute; tuệ, sự học hỏi kh&ocirc;ng ngừng, vượt qua kh&oacute; khăn, th&aacute;ch thức v&agrave; quyết t&acirc;m cao độ, sự đồng thuận, đồng l&ograve;ng của to&agrave;n d&acirc;n.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Trong bối cảnh to&agrave;n cầu h&oacute;a c&ocirc;ng nghệ số, c&ocirc;ng t&aacute;c khuyến học khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập sẽ h&ograve;a v&agrave;o d&ograve;ng chảy kh&ocirc;ng ngừng của sự ph&aacute;t triển v&agrave; chắc chắn, sẽ g&oacute;p phần đắc lực v&agrave;o mục ti&ecirc;u &ldquo;n&acirc;ng cao d&acirc;n tr&iacute;, đ&agrave;o tạo nh&acirc;n lực, bồi dưỡng nh&acirc;n t&agrave;i&rdquo;, x&acirc;y dựng đất nước ng&agrave;y c&agrave;ng cường thịnh, phồn vinh.</span></p>\r\n', '2022-10-27 01:26:10', 64, '/data/files/hkh/Capture.JPG', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1369, 'HỘI KHUYẾN HỌC THÀNH PHỐ GẶP MẶT ĐỘNG VIÊN GIÁO VIÊN DỰ THI GIÁO VIÊN DẠY GIỎI THCS CẤP TỈNH  LẦN THỨ XI, NĂM HỌC 2022-2023', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;Thực hiện Kế hoạch số 151/KH-SGDĐT ng&agrave;y 20/9/2022 của Sở GD&amp;ĐT Sơn La về tổ chức Hội thi gi&aacute;o vi&ecirc;n dạy giỏi trung học cơ sở cấp tỉnh lần thứ XI, năm học 2022-2023, Ph&ograve;ng Gi&aacute;o dục v&agrave; Đ&agrave;o tạo th&agrave;nh phố Sơn La đ&atilde; ban h&agrave;nh Quyết định số 83/QĐ-GD&amp;ĐT ng&agrave;y 05/10/2022 về th&agrave;nh lập Đo&agrave;n tham gia Hội thi gi&aacute;o vi&ecirc;n dạy giỏi THCS cấp tỉnh lần thứ XI, năm học 2022-2023.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Nhằm động vi&ecirc;n, kh&iacute;ch lệ đội ngũ gi&aacute;o vi&ecirc;n t&iacute;ch cực tham gia ho&agrave;n th&agrave;nh tốt nhiệm vụ của Hội thi, ng&agrave;y 25 th&aacute;ng 10 năm 2022, Ph&ograve;ng GD&amp;ĐT phối hợp Hội Khuyến học th&agrave;nh phố Sơn La tổ chức gặp mặt, động vi&ecirc;n Đo&agrave;n gi&aacute;o vi&ecirc;n gi&aacute;o vi&ecirc;n tham gia Hội thi gi&aacute;o vi&ecirc;n dạy giỏi THCS cấp tỉnh lần thứ XI, năm học 2022-2023. Dự buổi gặp mặt c&oacute; đồng ch&iacute; Nguyễn Thị Hương Giang- TUV, Trưởng ph&ograve;ng GD&amp;ĐT, đồng ch&iacute; H&agrave; Văn Qu&acirc;n- Chủ tịch Hội Khuyến học th&agrave;nh phố Sơn La; c&aacute;c đồng ch&iacute; trong tập thể L&atilde;nh đạo, chuy&ecirc;n vi&ecirc;n Ph&ograve;ng GD&amp;ĐT, c&aacute;c đồng ch&iacute; trong Thường trực Hội Khuyến học th&agrave;nh phố; Hiệu trưởng c&aacute;c đơn vị c&oacute; gi&aacute;o vi&ecirc;n dự thi v&agrave; 34 gi&aacute;o vi&ecirc;n tham gia Hội thi<a name="_Hlk117786308"> gi&aacute;o vi&ecirc;n dạy giỏi THCS cấp tỉnh lần thứ XI, </a>năm học 2022-2023. </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Tại buổi gặp mặt, đồng ch&iacute; Trưởng ph&ograve;ng GD&amp;ĐT th&agrave;nh phố đ&atilde; ghi nhận, biểu dương kết quả, th&agrave;nh t&iacute;ch m&agrave; c&aacute;c nh&agrave; trường, c&aacute;c thầy gi&aacute;o, c&ocirc; gi&aacute;o đ&atilde; cống hiến cho Ng&agrave;nh GD&amp;ĐT th&agrave;nh phố trong những năm qua, đồng thời động vi&ecirc;n kh&iacute;ch lệ tinh thần c&aacute;c gi&aacute;o vi&ecirc;n tham gia Hội thi gi&aacute;o vi&ecirc;n dạy giỏi THCS cấp tỉnh lần thứ XI được tổ chức từ ng&agrave;y 28/10/2022.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Trong buổi gặp mặt, Hội Khuyến học th&agrave;nh phố phối hợp với Ph&ograve;ng GD&amp;ĐT tặng qu&agrave; động vi&ecirc;n c&aacute;c thầy gi&aacute;o, c&ocirc; gi&aacute;o c&oacute; t&acirc;m thế tốt để đạt th&agrave;nh t&iacute;ch cao nhất trong Hội thi lần n&agrave;y./.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><strong>Một số h&igrave;nh ảnh trong buổi gặp mặt c&aacute;c đồng ch&iacute; gi&aacute;o vi&ecirc;n dự thi</strong></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:14px"><img src="/data/images/anh%20co%20so/Capture1.JPG" style="height:293px; width:600px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px">Đồng ch&iacute; Nguyễn Thị Hương Giang - Th&agrave;nh ủy vi&ecirc;n, Trưởng ph&ograve;ng GD&amp;ĐT th&agrave;nh phố ph&aacute;t biểu động vi&ecirc;n</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:14px"><img src="/data/images/anh%20co%20so/Capture.JPG" style="height:372px; width:617px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px">Đồng ch&iacute; H&agrave; Văn Qu&acirc;n &ndash; Chủ tịch Hội khuyến học th&agrave;nh phố ph&aacute;t biểu động vi&ecirc;n</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img src="/data/images/anh%20co%20so/Capture2.JPG" style="height:331px; width:618px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px">Đồng ch&iacute; Nguyễn Văn Cương- Ph&oacute; Trưởng ph&ograve;ng GD&amp;ĐT th&agrave;nh phố ph&aacute;t biểu động vi&ecirc;n</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm">&nbsp;</p>\r\n', '2022-10-28 07:50:13', 49, '/data/files/anh%20co%20so/Capture.JPG', '', 0, 54, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1370, 'Tặng quà cho các nhà trường và các học sinh nghèo vượt khó  biên giới Sơn La.', 'Nhằm hỗ trợ, động viên các cháu học sinh có hoàn cảnh khó khăn và thầy, cô giáo tại các nhà trường trên địa bàn khu vực biên giới có điều kiện học tập, đến trường tốt hơn, thúc đẩy công tác Khuyến học, khuyến tài của tỉnh Sơn La;Thiết thực thi đua lập thành tích hướng tới Kỷ niệm 40 năm Ngày Nhà giáo Viêt Nam 20/11.', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;Nhằm hỗ trợ, động vi&ecirc;n c&aacute;c ch&aacute;u học sinh c&oacute; ho&agrave;n cảnh kh&oacute; khăn v&agrave; thầy, c&ocirc; gi&aacute;o tại c&aacute;c nh&agrave; trường tr&ecirc;n địa b&agrave;n khu vực bi&ecirc;n giới c&oacute; điều kiện học tập, đến trường tốt hơn, th&uacute;c đẩy c&ocirc;ng t&aacute;c Khuyến học, khuyến t&agrave;i của tỉnh Sơn La;Thiết thực thi đua lập th&agrave;nh t&iacute;ch hướng tới Kỷ niệm 40 năm Ng&agrave;y Nh&agrave; gi&aacute;o Vi&ecirc;t Nam 20/11. </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;S&aacute;ng ng&agrave;y 6/11, Hội khuyến tỉnh phối hợp Đo&agrave;n Thiện nguyện &ldquo;Hội bảo tồn lan rừng Việt Nam&rdquo; , Bộ Chỉ huy Bộ đội Bi&ecirc;n ph&ograve;ng tỉnh v&agrave; C&acirc;u lạc bộ &ldquo;Hoa lan Y&ecirc;n Ch&acirc;u&rdquo; tổ chức trao tặng qu&agrave; cho một số trường tại tuyến huyện Y&ecirc;n Ch&acirc;u, tỉnh Sơn La gồm c&aacute;c trường: Trường Tiểu học x&atilde; Chiềng Tương; Trường Tiểu học bản Lao Kh&ocirc;; x&atilde; L&oacute;ng Phi&ecirc;ng; Mường Lựm; Chiềng S&agrave;ng; Chiềng Đ&ocirc;ng A; Chiềng Đồng B; Viềng L&aacute;n. </span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><img src="/data/images/tin-tuc/IMGP3083.JPG" style="height:414px; width:623px" /></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em><span style="color:red">Đo&agrave;n thiện nguyện trao qu&agrave; tại Trường Tiểu học x&atilde; Chiềng Tương, huyện Y&ecirc;n Ch&acirc;u</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp;Với c&aacute;c phần qu&agrave; gồm tặng 57 xe đạp, 125 đ&egrave;n học chống cận; 10 suất học bổng mỗi suất trị gi&aacute; 500 ngh&igrave;n đồng, 230 &aacute;o kho&aacute;c ấm với tổng trị gi&aacute; 140 triệu đồng. Ngo&agrave;i ra, Hội Khuyến học tỉnh Sơn La đ&atilde; tr&iacute;ch một phần kinh ph&iacute; khuyến học, khuyến t&agrave;i năm 2022 hỗ trợ cho 02 điểm trường Chiềng Tương v&agrave; Lao Kh&ocirc; mỗi phần qu&agrave; trị gi&aacute; 5 triệu đồng.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img alt="" src="/data/images/tin-tuc/IMGP2899.JPG" style="height:365px; width:547px" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:red">Đo&agrave;n thiện nguyện trao qu&agrave; tại Trường Tiểu học bản Lao Kh&ocirc;, x&atilde; L&oacute;ng Phi&ecirc;ng, huyện Y&ecirc;n Ch&acirc;u</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img src="/data/images/tin-tuc/z3859070721587_7eb999f7a58b0a403374a286bfae448e.jpg" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:red">Đ/c Nguyễn Văn Bao - Ph&oacute; Chủ tịch Hội Khuyến học tỉnh ph&aacute;t biểu động vi&ecirc;n thầy v&agrave; tr&ograve; Trường Tiểu học x&atilde; Chiềng Tương</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img src="/data/images/tin-tuc/z3859070716886_407c2f44dda4c4f0afe9a0f2f79e17f4.jpg" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><em><span style="color:red">Đ/c Nguyễn Văn Bao - Ph&oacute; Chủ tịch Hội Khuyến học tỉnh trao qu&agrave; động vi&ecirc;n nh&agrave; trường nh&acirc;n kỷ niệm 40 năm Ng&agrave;y Nh&agrave; gi&aacute;o Vi&ecirc;t Nam 20/11</span></em></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:14px">&nbsp; &nbsp; &nbsp; &nbsp;Những hoạt động n&agrave;y, rất thiết thực v&agrave; mang &yacute; nghĩa rất nh&acirc;n văn của c&aacute;c Nh&agrave; thiện nguyện nhằm chia sẻ những kh&oacute; khăn trong cuộc sống đối với nh&acirc;n d&acirc;n khu vực bi&ecirc;n giới, đặc biệt l&agrave; những học sinh ngh&egrave;o học giỏi, vượt kh&oacute;, gi&uacute;p c&aacute;c ch&aacute;u c&oacute; điều kiện học tập tốt nhất. G&oacute;p phần tăng cường khối đại đo&agrave;n kết d&acirc;n tộc, đo&agrave;n kết qu&acirc;n d&acirc;n tr&ecirc;n địa b&agrave;n khu vực Bi&ecirc;n giới./.</span></p>\r\n', '2022-11-07 04:08:07', 115, '/data/files/hkh/IMGP2899.JPG', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1371, 'Hội Khuyến học tỉnh Sơn La động viên, khen thưởng cho giáo viên dạy giỏi THCS cấp tỉnh năm học 2022-2023', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><strong>Hội Khuyến học tỉnh Sơn La </strong><strong>động vi&ecirc;n, khen thưởng cho gi&aacute;o vi&ecirc;n dạy giỏi THCS cấp tỉnh năm học 2022-2023</strong></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="background-color:white"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Ng&agrave;y 10/11, Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo đ&atilde; tổ chức tổng kết Hội thi gi&aacute;o vi&ecirc;n dạy giỏi cấp THCS tỉnh Sơn La lần thứ XI, năm 2022.</span></span> <span style="background-color:white"><span style="color:black">Căn cứ kết quả đạt được của c&aacute;c gi&aacute;o vi&ecirc;n tham dự Hội thi, Ban Tổ chức đ&atilde; c&ocirc;ng nhận 186 gi&aacute;o vi&ecirc;n (80,2%) đạt danh hiệu gi&aacute;o vi&ecirc;n dạy giỏi cấp THCS cấp tỉnh lần thứ XI, năm học 2022-2023.</span></span> </span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><span style="background-color:white"><span style="color:black">&nbsp; &nbsp; &nbsp; &nbsp;Nh&acirc;n dịp n&agrave;y, Hội Khuyến học tỉnh tặng Giấy khen v&agrave; tiền thưởng cho 30 gi&aacute;o vi&ecirc;n đạt danh hiệu gi&aacute;o vi&ecirc;n giỏi c&oacute; th&agrave;nh t&iacute;ch cao hơn tại Hội thi.</span></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em><span style="color:#0070c0"><img src="/data/images/tin-tuc/Capture31.JPG" style="height:305px; width:567px" /></span></em></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><em><span style="color:#0070c0">Đồng ch&iacute; Nguyễn Văn Bao - Ph&oacute; Chủ tịch Hội Khuyến học tỉnh trao Giấy khen v&agrave; tiền thưởng cho 30 gi&aacute;o vi&ecirc;n</span></em></span></span></p>\r\n', '2022-11-11 02:16:38', 93, '/data/files/hkh/z3868978978757_3c78cdac91ee38c39cfcdbdd825384af.jpg', '', 1, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1372, 'Sông Mã với công tác khuyến học, khuyến tài, xây dựng xã hội học tập', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; Tiếp tục triển khai thực hiện Quyết định số 1373 ng&agrave;y 30/7/2021 của Thủ tướng Ch&iacute;nh phủ về &ldquo;X&acirc;y dựng x&atilde; hội học tập, giai đoạn 2021- 2030&rdquo;; Kỷ niệm 19 năm Ng&agrave;y th&agrave;nh lập Hội Khuyến học S&ocirc;ng M&atilde;; 40 năm Ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam 20/11; 70 năm Ng&agrave;y th&agrave;nh lập huyện S&ocirc;ng M&atilde;. Từ th&aacute;ng 9 năm 2022 đến nay, Ban chấp h&agrave;nh Hội Khuyến học huyện đ&atilde; triển khai c&aacute;c hoạt động thiết thực ch&agrave;o mừng c&aacute;c ng&agrave;y Kỷ niệm, g&oacute;p phần n&acirc;ng cao chất lượng hoạt động Khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp;Thường trực Hội Khuyến học huyện đ&atilde; tham mưu cho Thường trực huyện ủy, UBND huyện c&oacute; văn bản chỉ đạo c&aacute;c cơ quan, ban ng&agrave;nh, x&atilde;, thị trấn tổ chức hoạt động khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập; phối hợp với Ph&ograve;ng GD-ĐT, c&aacute;c ban, ng&agrave;nh, đo&agrave;n thể huyện tổ chức c&aacute;c hoạt động:</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp; Ph&aacute;t động tuần lễ học tập suốt đời năm 2022 tại c&aacute;c x&atilde;, thị trấn.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img alt="Description: A picture containing text, building, outdoor, peopleDescription automatically generated" id="Picture_x0020_3" src="/data/images/anh%20co%20so/Capture32.JPG" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; Tuy&ecirc;n truyền vận động c&aacute;c tổ chức, doanh nghiệp, c&aacute; nh&acirc;n, nh&agrave; hảo t&acirc;m ủng hộ quỹ khuyến học.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp; Tổ chức hoạt động thể dục, thể thao, thi tiếng h&aacute;t Người Gi&aacute;o vi&ecirc;n Nh&acirc;n d&acirc;n.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img alt="Description: A group of people standing on a stageDescription automatically generated with medium confidence" id="Picture_x0020_4" src="/data/images/anh%20co%20so/Capture34.JPG" style="height:366px; width:540px" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp;Khai mạc v&agrave; tổng kết Hội thi gi&aacute;o vi&ecirc;n dạy Giỏi bậc học Mầm non huyện S&ocirc;ng M&atilde;. Hội Khuyến học huyện trao thưởng 27.800.000 đồng cho 86 gi&aacute;o vi&ecirc;n đạt danh hiệu Gi&aacute;o vi&ecirc;n dạy Giỏi.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img alt="Description: A group of people on a stageDescription automatically generated" id="Picture_x0020_6" src="/data/images/anh%20co%20so/Capture35.JPG" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp;Phối hợp với Viettent, Huyện Đo&agrave;n trao học bổng v&agrave; khảo s&aacute;t, x&eacute;t danh s&aacute;ch học sinh c&oacute; ho&agrave;n cảnh đặc biệt kh&oacute; khăn để thực hiện Chương tr&igrave;nh &ldquo;Nu&ocirc;i em&rdquo; theo kế hoạch đ&atilde; được k&yacute; kết giữa BCH hội Khuyến học Huyện v&agrave; Huyện Đo&agrave;n S&ocirc;ng M&atilde;.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp;Ban Chấp h&agrave;nh huyện Hội đ&atilde; ph&acirc;n c&ocirc;ng c&aacute;c đ/c UVBCH phụ tr&aacute;ch x&atilde; trực tiếp đến c&aacute;c x&atilde;, bản, hướng dẫn văn bản của Ch&iacute;nh phủ, của tỉnh, huyện chỉ đạo về khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập, nghiệp vụ l&agrave;m khuyến học theo phương ch&acirc;m &ldquo;Cầm tay, chỉ việc&rdquo;. Đến ng&agrave;y 30/10/2022 Huyện Hội đ&atilde; tổ chức kiểm tra, tập huấn v&agrave; hướng dẫn được 19/19 hội x&atilde;, thị trấn.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp;Thường trực huyện Hội đ&atilde; phối hợp với UBMTTQVN x&atilde;, thị trấn dự hội nghị x&eacute;t, c&ocirc;ng nhận c&aacute;c danh hiệu Học tập năm 2022 tại c&aacute;c tổ d&acirc;n phố v&agrave; 1 số bản, x&atilde; quanh khu vực huyện, kịp thời c&ocirc;ng bố v&agrave;o Ng&agrave;y Đại đo&agrave;n kết to&agrave;n d&acirc;n.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:center"><img alt="Description: A group of people sitting at tablesDescription automatically generated with medium confidence" id="Picture_x0020_8" src="/data/images/anh%20co%20so/Capture36.JPG" /></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; Hướng dẫn x&eacute;t khen thưởng năm 2022, th&aacute;o gỡ kh&oacute; khăn cho c&aacute;c tổ chức hội cơ sở nhất l&agrave; việc tuy&ecirc;n truyền vận động ủng hộ quỹ Hội v&agrave; thu Hội ph&iacute;.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp; &nbsp; &nbsp; Đ&oacute;n nhận sự quan t&acirc;m, chỉ đạo của Thường trực huyện ủy, UBND huyện v&agrave; Thường trực Hội Khuyến học Tỉnh Sơn La, sự phối hợp kịp thời, c&oacute; hiệu quả của UBMTTQVN huyện, c&aacute;c ban ng&agrave;nh, đo&agrave;n thể, Khuyến học S&ocirc;ng M&atilde; đ&atilde;, đang ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập năm 2022. G&oacute;p phần c&ugrave;ng Đảng bộ, nh&acirc;n d&acirc;n c&aacute;c d&acirc;n tộc S&ocirc;ng M&atilde; thực hiện thắng lợi mục ti&ecirc;u ph&aacute;t triển kinh tế, x&atilde; hội năm 2022 tr&ecirc;n địa b&agrave;n huyện v&ugrave;ng bi&ecirc;n.</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trần Trang Nhung</strong></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Ủy vi&ecirc;n TT Hội Khuyến học S&ocirc;ng M&atilde;</p>\r\n', '2022-11-11 02:29:15', 106, '/data/files/anh%20co%20so/Capture35.JPG', '', 1, 54, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1339, 'Chuyển đổi số trong xây dựng các mô hình học tập của Hội Khuyến học tỉnh Thái Nguyên', 'Nhận thức rõ vai trò trách nhiệm và sự cần thiết về chuyển đổi số giai đoạn 2021-2025, định hướng đến năm 2030, theo Nghị quyết 01 của Ban Chấp hành Tỉnh ủy Thái Nguyên, đặc biệt là triển khai xây dựng các mô hình học tập giai đoạn 2020-2030 của Thủ tướng Chính phủ Hội Khuyến học Thái Nguyên đã có những triển khai cụ thể thiết thực.', '<p>Chuyển đổi số dựa tr&ecirc;n ba trụ cột: Ch&iacute;nh quyền số, kinh tế số v&agrave; x&atilde; hội số. L&agrave; một Hội x&atilde; hội với số hội vi&ecirc;n gần 400 ng&agrave;n người tr&ecirc;n to&agrave;n tỉnh, Hội Khuyến học Th&aacute;i Nguy&ecirc;n đ&atilde; triển khai t&iacute;ch cực c&ocirc;ng t&aacute;c chuyển đổi số với những việc cụ thể ở tất cả c&aacute;c cấp Hội<strong>.</strong></p>\r\n\r\n<p>Hiểu được c&ocirc;ng t&aacute;c chuyển đổi số kh&oacute; khăn nhất l&agrave; thay đổi th&oacute;i quen, th&aacute;ch thức lớn nhất l&agrave; c&oacute; nhận thức đ&uacute;ng. Ch&iacute;nh v&igrave; vậy việc đầu ti&ecirc;n l&agrave; c&ocirc;ng t&aacute;c tuy&ecirc;n truyền n&acirc;ng cao nhận thưc, bắt đầu từ c&aacute;n bộ hội rồi đến &nbsp;hội vi&ecirc;n c&aacute;c cấp.</p>\r\n\r\n<p>Trong 2 năm 2021, 2022, Hội đ&atilde; tổ chức 11 buổi tập huấn từ tỉnh Hội đến c&aacute;c đơn vị cấp cơ sở cho h&agrave;ng ng&agrave;n hội vi&ecirc;n về c&ocirc;ng t&aacute;c chuyển đổi số. Nội dung tập huấn lựa chọn những vấn đề căn cốt, giản dị, dễ hiểu nhất về nhận thức v&agrave; h&agrave;nh động của mỗi hội vi&ecirc;n về chuyển đổi số. Việc chuyển đổi số l&agrave; qu&aacute; tr&igrave;nh thay đổi tổng thể v&agrave; to&agrave;n diện của c&aacute; nh&acirc;n, tổ chức về c&aacute;ch sống c&aacute;ch l&agrave;m việc v&agrave; phương thức sản xuất dựa tr&ecirc;n c&aacute;c c&ocirc;ng nghệ số, nhưng để hiểu đ&uacute;ng về nội dung n&agrave;y trong c&aacute;c hội vi&ecirc;n cũng kh&ocirc;ng phải đơn giản. Bắt đầu phải từ việc rất cụ thể, những tiếp x&uacute;c h&agrave;ng ng&agrave;y của mỗi người với thay&nbsp; đổi v&agrave; sự tiện dụng của c&aacute;c ứng dụng của chuyển đổi số tr&ecirc;n c&aacute;c nền tảng c&ocirc;ng nghệ v&agrave; c&aacute;c thiết bị th&ocirc;ng minh. Từ việc ph&acirc;n biệt sự kh&aacute;c biệt của điện thoại th&ocirc;ng minh so với điện thoại truyền thống v&agrave; tiện &iacute;ch của n&oacute; gi&uacute;p người d&acirc;n trong hoạt động, giao tiếp ứng xử, trong c&ocirc;ng việc v&agrave; nhu cầu bổ sung th&ocirc;ng tin h&agrave;ng ng&agrave;y .</p>\r\n\r\n<p>Năm 2022, với 2 Quyết định của Thủ tướng Ch&iacute;nh phủ &nbsp;về x&acirc;y dựng c&aacute;c m&ocirc; h&igrave;nh học tập trong giai đoạn n&agrave;y đều thấm đẫm việc cụ thể h&oacute;a việc c&aacute;c năng lực về nhận thức số v&agrave; chuyển đổi số trong c&aacute;c m&ocirc; h&igrave;nh.</p>\r\n\r\n<p>Để hiểu r&otilde; từng nội dung để giải th&iacute;ch cho người d&acirc;n về c&aacute;c ti&ecirc;u ch&iacute; c&oacute; nội dung số, hội đ&atilde; bồi dưỡng tập trung l&agrave;m &nbsp;r&otilde; cho tất cả c&aacute;c c&aacute;n bộ hội vi&ecirc;n về c&aacute;c thuật ngữ v&agrave; nội h&agrave;m như: Thế n&agrave;o l&agrave; kỹ năng số, hay c&aacute;c yếu tố cấu th&agrave;nh của c&ocirc;ng d&acirc;n số như: khả năng truy cập, khả năng giao tiếp, kỹ năng số cơ bản, chuẩn mực đạo đức, bảo vệ thể chất v&agrave; t&acirc;m l&yacute;, quyền v&agrave; tr&aacute;ch nhiệm, định danh v&agrave; x&aacute;c thực, dữ liệu c&aacute; nh&acirc;n v&agrave; quyền ri&ecirc;ng tư tr&ecirc;n m&ocirc;i trường số.</p>\r\n\r\n<p>Tham gia v&agrave;o qu&aacute; tr&igrave;nh chuyển đổi số, Hội tập trung v&aacute;o tập huấn theo dạng cầm tay chỉ việc c&aacute;c nội dung từ việc nhỏ: Biết c&agrave;i đặt c&aacute;c phần mềm ứng dụng tr&ecirc;n điện thoại theo từng loại hệ điều h&agrave;nh tương ứng với c&aacute;c điện thoại th&ocirc;ng minh th&ocirc;ng dụng, ph&acirc;n biệt hệ điều h&agrave;nh, phần mềm hệ thống, phần mềm ứng dụng, phần mềm miễn ph&iacute;, phần mềm trả ph&iacute;..</p>\r\n\r\n<p>Hội đ&atilde; hướng dẫn cụ thể tập trung v&agrave;o hướng dẫn c&agrave;i đặt sử dụng c&aacute;c ứng dụng của mỗi c&aacute; nh&acirc;n như:</p>\r\n\r\n<ol>\r\n	<li>C&agrave;i đặt sử dụng dịch vụ H&agrave;nh ch&iacute;nh c&ocirc;ng.</li>\r\n	<li>C&agrave;i đặt sử dụng thanh to&aacute;n kh&ocirc;ng d&ugrave;ng tiền mặt.</li>\r\n	<li>C&agrave;i đặt để tham gia mua b&aacute;n tr&ecirc;n s&agrave;n điện tử.</li>\r\n	<li>C&agrave;i đặt sử dụng nền tảng của tỉnh Th&aacute;i Nguy&ecirc;n C-Th&aacute;i Nguy&ecirc;n (c&ocirc;ng d&acirc;n Th&aacute;i Nguy&ecirc;n, ID-Th&aacute;i Nguy&ecirc;n (định danh c&aacute; nh&acirc;n -Th&aacute;i Nguy&ecirc;n). Sổ tay điện tử &nbsp;đối với đảng vi&ecirc;n)</li>\r\n	<li>C&agrave;i đặt phần mềm an to&agrave;n th&ocirc;ng tin mạng.</li>\r\n	<li>C&agrave;i đặt những phần mềm thiết dụng ph&ugrave; hợp với nhu cầu học tập, l&agrave;m việc, n&acirc;ng cao tri thức t&ugrave;y theo của mỗi c&aacute; nh&acirc;n,</li>\r\n</ol>\r\n\r\n<p>Với c&aacute;c phần mềm kh&aacute;c, tỉnh Hội cũng đ&atilde; phối hợp động vi&ecirc;n, hướng dẫn hội vi&ecirc;n c&aacute;c cấp hội tham gia. Cụ thể như: Ph&ograve;ng&nbsp;chống dịch Covid-19; phần mềm Sổ sức khoẻ điện tử với c&aacute;c tiện &iacute;ch như: Khai b&aacute;o y tế, hồ sơ sức khoẻ, đăng k&yacute; ti&ecirc;m chủng, x&aacute;c nhận ti&ecirc;m chủng, m&atilde; số sức khoẻ, đặt kh&aacute;m bệnh &hellip; rất hữu &iacute;ch với từng c&aacute; nh&acirc;n.&nbsp;Phần mền an to&agrave;n an ninh th&ocirc;ng t&iacute;n, cảnh gi&aacute;c cảnh b&aacute;o lừa đảo khi tham gia tr&ecirc;n kh&ocirc;ng gian mạng.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="/data/images/HOI_-_DAP_jpg.jpg" style="height:279px; width:259px" /></p>\r\n\r\n<p>Ngo&agrave;i việc sử dụng c&aacute;c&nbsp;ứng dụng&nbsp;thực hiện nhiệm vụ quản l&yacute;, điều h&agrave;nh c&ocirc;ng t&aacute;c, tỉnh Hội c&ograve;n hướng dẫn, tuy&ecirc;n truyền đến hội vi&ecirc;n t&igrave;m hiểu c&aacute;c ứng dụng phục vụ cho học trực tuyến để hiểu biết th&ecirc;m v&agrave; hỗ trợ gi&uacute;p đỡ con, ch&aacute;u học trực tuyến, họp trực tuyến.&nbsp;&nbsp;Đồng thời, động vi&ecirc;n c&aacute;n bộ, hội vi&ecirc;n ủng hộ v&agrave; t&iacute;ch cực sử dụng giao dịch thương mại tr&ecirc;n nền tảng điện tử (thanh to&aacute;n th&ocirc;ng qua t&agrave;i khoản, v&iacute; điện tử, mua b&aacute;n online &hellip;) đảm bảo an to&agrave;n, tiện lợi.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="/data/images/Anh_1(1)(1).jpg" style="height:450px; width:600px" /><br />\r\n<em>Bồi dưỡng tập huấn dưới dạng cầm tay chỉ việc</em></p>\r\n\r\n<p>Đối với c&ocirc;ng t&aacute;c quản l&yacute; ở c&aacute;c cấp Hội ứng dụng triệt để trong điều kiện c&oacute; thề như:</p>\r\n\r\n<p>Tham&nbsp;gia t&iacute;ch cực v&agrave;o x&acirc;y dựng&nbsp;trang th&ocirc;ng tin điện tử của Hội khuyenhocthainguyen.edu.vn. Trang th&ocirc;ng tin được thiết kế với giao diện th&acirc;n thiện, hiện đại với c&aacute;c c&ocirc;ng cụ để giới thiệu, trao đổi, tuy&ecirc;n truyền, lưu trữ c&aacute;c tư liệu cũng như hoạt động từ cơ sở đến tỉnh Hội. Trang th&ocirc;ng tin c&ograve;n li&ecirc;n kết với c&aacute;c trang th&ocirc;ng tin của Trung ương v&agrave; địa phương như: Hội Khuyến học Việt Nam, Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo, Cổng dịch c&ocirc;ng Quốc gia, Cơ sở dữ liệu Quốc gia, C&ocirc;ng b&aacute;o Th&aacute;i Nguy&ecirc;n, Sở Gi&aacute;o dục Đ&agrave;o tạo, VNPT Th&aacute;i Nguy&ecirc;n&hellip;. Trang th&ocirc;ng tin của Hội được giới thiệu đến tất cả c&aacute;c cấp hội để c&aacute;c hội vi&ecirc;n biết v&agrave; quan t&acirc;m chia sẻ. Từ ng&agrave;y ra mắt trang th&ocirc;ng tin, c&aacute;c hoạt động của Hội từ tỉnh đến cơ sở được cập nhật thường xuy&ecirc;n, tạo sự quan t&acirc;m ch&uacute; &yacute; của hội vi&ecirc;n v&agrave; n&acirc;ng cao nhận thức cũng như hiệu quả c&ocirc;ng t&aacute;c tuy&ecirc;n truyền của hội khuyến học c&aacute;c cấp trong tỉnh.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="/data/images/Anh_3.jpg" style="height:450px; width:600px" /><br />\r\n<em>Khai trương trang th&ocirc;ng tin điện tử của Hội.</em></p>\r\n\r\n<p>Lập nh&oacute;m điều h&agrave;nh c&ocirc;ng t&aacute;c h&agrave;ng ng&agrave;y th&ocirc;ng qua mạng x&atilde; hội Zalo gồm: Nh&oacute;m Thường trực Hội; nh&oacute;m Ban Chấp h&agrave;nh; nh&oacute;m Chủ tịch Hội c&aacute;c huyện, th&agrave;nh phố, thị x&atilde;; nh&oacute;m c&aacute;c huyện th&agrave;nh thực hiện th&iacute; điểm x&acirc;y dựng m&ocirc; h&igrave;nh &ldquo;C&ocirc;ng d&acirc;n học tập&rdquo;. Chủ tịch v&agrave; Ph&oacute; Chủ tịch Thường trực tỉnh Hội tham gia nh&oacute;m Zalo&nbsp;của&nbsp;Hội&nbsp;Khuyến học Việt Nam lập. Mọi th&ocirc;ng tin điều h&agrave;nh về c&ocirc;ng t&aacute;c khuyến học được phản &aacute;nh tức th&igrave; hai chiều qua hệ thống tin nhắn tiện dụng của Zalo. Thường trực tỉnh Hội đ&atilde; thống nhất với c&aacute;n bộ, hội vi&ecirc;n quy định về sử dụng th&ocirc;ng tin tr&ecirc;n c&aacute;c nh&oacute;m Zalo để phục vụ c&ocirc;ng t&aacute;c chuy&ecirc;n m&ocirc;n đảm bảo nhanh nhạy, lại tiết kiệm về thời gian cũng như kinh ph&iacute; gửi nhận văn bản qua Bưu điện (trừ c&aacute;c văn bản cần sử dụng dấu đỏ v&agrave; c&aacute;c văn bản mật).</p>\r\n\r\n<p>Tỉnh Hội đ&atilde; chủ động đăng k&yacute; v&agrave; sử dụng phần mềm li&ecirc;n th&ocirc;ng văn bản th&ocirc;ng qua hệ thống mailthainguyen.gov.vn v&agrave; chữ k&yacute; số khi giao dịch với c&aacute;c cơ quan trong tỉnh th&ocirc;ng qua xử l&yacute; tr&ecirc;n m&ocirc;i trường mạng.</p>\r\n\r\n<p>C&oacute; thể khẳng định, Hội Khuyến học tỉnh đ&atilde; v&agrave; đang t&iacute;ch cực việc&nbsp;chuyển đối số&nbsp;trong hoạt động của Hội bằng những việc l&agrave;m rất&nbsp;cụ thể. Đ&acirc;y cũng l&agrave; h&agrave;nh động thiết thực của tỉnh Hội triển khai thực hiện chủ trương chuyến đổi số của tỉnh. Kết quả thu được bước đầu&nbsp;theo b&aacute;o c&aacute;o của c&aacute;c huyện th&agrave;nh, hiện nay đ&atilde; c&oacute; 1/3 số hội vi&ecirc;n đ&atilde; c&agrave;i đặt C-Th&aacute;i Nguy&ecirc;n v&agrave; đang tiếp tục c&agrave;i đặt ID-Th&aacute;i Nguy&ecirc;n bước đầu hiệu quả,&nbsp;song tỉnh Hội nhận thấy vẫn c&ograve;n c&oacute; nhiều kh&oacute; khăn phải phấn đấu, quyết t&acirc;m vượt qua, nhất l&agrave; t&acirc;m l&yacute; ngại thay đổi phương ph&aacute;p l&agrave;m việc do nhiều hội vi&ecirc;n cao về tuổi t&aacute;c, tr&igrave;nh độ tin học chưa theo kịp; cơ sở vật chất của c&aacute;c cấp hội c&ograve;n c&oacute; nhiều kh&oacute; khăn. Tỉnh Hội rất mong được l&atilde;nh đạo tỉnh, c&aacute;c cấp, c&aacute;c ng&agrave;nh, c&aacute;c địa phương trong tỉnh quan t&acirc;m, gi&uacute;p đỡ, ủng hộ để Hội Khuyến học c&aacute;c cấp trong tỉnh hội nhập về c&ocirc;ng t&aacute;c chuyển đổi số, thực sự v&agrave;o cuộc từ những c&ocirc;ng việc cụ thể nhỏ nhất của ch&iacute;nh tổ chức Hội Khuyến học v&agrave; bản th&acirc;n mỗi hội vi&ecirc;n&nbsp;g&oacute;p phần thực hiện tốt x&acirc;y dựng c&aacute;c m&ocirc; h&igrave;nh học tập từ cơ sở theo y&ecirc;u cầu hiện nay.</p>\r\n\r\n<p style="text-align:right"><em>NGƯT Nguyễn Quang Thuấn, Ph&oacute; Chủ tịch Thường trực Hội Khuyến học Th&aacute;i Nguy&ecirc;n</em></p>\r\n\r\n<p>&nbsp;</p>\r\n', '2022-09-28 07:33:18', 50, '/data/images/Anh_3.jpg', '', 1, 55, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1340, 'Đẩy mạnh công tác xây dựng xã hội học tập ở địa bàn biên giới tỉnh Sơn La', 'Trong những năm qua, Bộ Chỉ huy Bộ đội Biên phòng tỉnh và Sở Giáo dục &amp; Đào tạo tỉnh Sơn La đã triển khai có hiệu quả Chương trình phối hợp đẩy mạnh công tác xây dựng xã hội học tập ở địa bàn biên giới tỉnh Sơn La, mở được nhiều lớp xóa mù chữ, vận động được nhiều học sinh ra lớp, giúp đỡ được nhiều học sinh có hoàn cảnh khó khăn được đến trường; tổ chức được nhiều hoạt động giáo dục có ý nghĩa, góp phần nâng cao dân trí cho nhân dân các xã thuộc khu vực biên giới.', '<p style="text-align:center"><em><img alt="" src="/data/images/l51.jpg" style="height:500px; width:667px" /><br />\r\nC&ocirc;ng t&aacute;c gi&aacute;o dục lu&ocirc;n được c&aacute;c cấp, c&aacute;ch ng&agrave;nh quan t&acirc;m</em></p>\r\n\r\n<p>Hai ng&agrave;nh đ&atilde; chỉ đạo c&aacute;c đồn bi&ecirc;n ph&ograve;ng phối hợp với ph&ograve;ng Gi&aacute;o dục &amp; Đ&agrave;o tạo c&aacute;c huyện bi&ecirc;n giới tham mưu cho cấp ủy, ch&iacute;nh quyền địa phương, c&aacute;c ng&agrave;nh v&agrave; đo&agrave;n thể đẩy mạnh c&ocirc;ng t&aacute;c tuy&ecirc;n truyền s&acirc;u rộng trong nh&acirc;n d&acirc;n về &yacute; nghĩa thiết thực của chương tr&igrave;nh, mục ti&ecirc;u l&agrave; n&acirc;ng cao tr&igrave;nh độ d&acirc;n tr&iacute;, trước hết l&agrave; x&oacute;a m&ugrave; chữ, phổ cập gi&aacute;o dục; giảm khoảng c&aacute;ch về chất lượng gi&aacute;o dục giữa c&aacute;c v&ugrave;ng miền. Duy tr&igrave; v&agrave; n&acirc;ng cao hiệu quả c&ocirc;ng t&aacute;c&nbsp;x&oacute;a m&ugrave; chữ, gi&aacute;o dục tiếp tục sau biết chữ gắn với thực hiện nhiệm vụ ph&aacute;t&nbsp;triển kinh tế, văn h&oacute;a, x&atilde; hội&nbsp;của địa phương, g&oacute;p phần quản l&yacute;, bảo vệ vững chắc chủ quyền, l&atilde;nh thổ an ninh bi&ecirc;n giới quốc gia.</p>\r\n\r\n<p>C&aacute;c đồn bi&ecirc;n ph&ograve;ng phối hợp với ph&ograve;ng Gi&aacute;o dục &amp; Đ&agrave;o tạo c&aacute;c huyện bi&ecirc;n giới, c&aacute;c x&atilde;, c&aacute;c đơn vị trường học tr&ecirc;n địa b&agrave;n tuy&ecirc;n truyền, vận động được hơn 100 học sinh bỏ học tiếp tục đến trường, gi&uacute;p đỡ học sinh c&oacute; ho&agrave;n cảnh đặc biệt kh&oacute; khăn được đi học; tuy&ecirc;n truyền đến c&aacute;c bậc phụ huynh đưa con em đi học đ&uacute;ng độ tuổi v&agrave; cam kết kh&ocirc;ng bỏ học; hỗ trợ học sinh ngh&egrave;o c&oacute; ho&agrave;n cảnh kh&oacute; khăn vươn l&ecirc;n trong học tập, r&egrave;n luyện.</p>\r\n\r\n<p>Thực hiện Chương tr&igrave;nh &ldquo;N&acirc;ng bước em tới trường v&agrave; con nu&ocirc;i đồn Bi&ecirc;n ph&ograve;ng&rdquo;, từ năm 2016 đến nay,&nbsp;Bộ đội bi&ecirc;n ph&ograve;ng&nbsp;tỉnh đ&atilde; đỡ đầu cho 81 ch&aacute;u học sinh (c&oacute; thời điểm l&agrave; 90 ch&aacute;u) từ cấp Tiểu học đến hết THPT ở khu vực bi&ecirc;n giới, c&oacute; ho&agrave;n cảnh kh&oacute; khăn, kh&ocirc;ng nơi nương tựa, mồ c&ocirc;i cha, mẹ, con, ch&aacute;u c&aacute;c gia đ&igrave;nh ch&iacute;nh s&aacute;ch; c&aacute;c ch&aacute;u học sinh nước bạn L&agrave;o ở địa b&agrave;n gi&aacute;p bi&ecirc;n c&oacute; điều kiện sinh sống c&ograve;n gặp nhiều kh&oacute; khăn, với mức hỗ trợ 500.000đ/l th&aacute;ng học; trong đ&oacute; c&oacute; 10 ch&aacute;u được đưa về c&aacute;c đồn Bi&ecirc;n ph&ograve;ng trực tiếp nu&ocirc;i dạy; tổ chức thường xuy&ecirc;n bữa ăn s&aacute;ng cho 75 ch&aacute;u ở điểm trường Buốc P&aacute;t v&agrave; Trường PTDT b&aacute;n tr&uacute; Tiểu học - THCS L&oacute;ng Sập.</p>\r\n\r\n<p>Đặc biệt, năm 2018, Bộ Chỉ huy&nbsp;Bộ đội bi&ecirc;n ph&ograve;ng&nbsp;tỉnh v&agrave; Tỉnh Đo&agrave;n Sơn La đ&atilde; phối hợp tổ chức Chương tr&igrave;nh &ldquo;Học kỳ trong qu&acirc;n đội - Ươm mầm bảo vệ bi&ecirc;n cương&rdquo;.&nbsp;Đ&acirc;y l&agrave; lớp &ldquo;Học kỳ trong Qu&acirc;n đội&rdquo; đầu ti&ecirc;n trong cả nước trong Chương tr&igrave;nh &ldquo;N&acirc;ng bước em tới trường&rdquo; của&nbsp;Bộ đội bi&ecirc;n ph&ograve;ng. Đối tượng tham gia gồm 57 học sinh người d&acirc;n tộc thiểu số, c&oacute; ho&agrave;n cảnh đặc biệt kh&oacute; khăn (10 ch&aacute;u mồ c&ocirc;i cả cha lẫn mẹ, 11 ch&aacute;u mồ c&ocirc;i cha, 01 ch&aacute;u con liệt sĩ, c&oacute; ch&aacute;u bố hoặc mẹ đang chấp h&agrave;nh h&igrave;nh phạt t&ugrave;, 03 ch&aacute;u được nu&ocirc;i tại đồn Bi&ecirc;n ph&ograve;ng). Lớp học lu&ocirc;n nhận được sự quan t&acirc;m l&atilde;nh đạo, chỉ đạo s&aacute;t sao của Đảng ủy, Bộ Tư lệnh&nbsp;Bộ đội bi&ecirc;n ph&ograve;ng, của Trung ương Đo&agrave;n TNCS Hồ Ch&iacute; Minh, của Thường trực Tỉnh ủy, UBND tỉnh Sơn La, l&atilde;nh đạo Sở&nbsp;Gi&aacute;o dục v&agrave; Đ&agrave;o tạo&nbsp;v&agrave; c&aacute;c sở, ban, ng&agrave;nh, đo&agrave;n thể trong tỉnh.</p>\r\n\r\n<p>Trong 02 năm 2020 v&agrave; 2021,&nbsp;Bộ đội bi&ecirc;n ph&ograve;ng&nbsp;tỉnh đ&atilde; k&ecirc;u gọi c&aacute;c doanh nghiệp, c&aacute;c tổ chức v&agrave; c&aacute; nh&acirc;n đồng h&agrave;nh ủng hộ x&acirc;y dựng 14 ph&ograve;ng học v&agrave; nh&agrave; c&ocirc;ng vụ cho gi&aacute;o vi&ecirc;n ở khu vực bi&ecirc;n giới, trị gi&aacute;&nbsp;tr&ecirc;n 3,3 tỷ. Hằng năm, nh&acirc;n dịp chuẩn bị khai giảng năm học mới, hai ng&agrave;nh đ&atilde; chỉ đạo c&aacute;c đồn Bi&ecirc;n ph&ograve;ng, ph&ograve;ng&nbsp;Gi&aacute;o dục v&agrave; Đ&agrave;o tạo&nbsp;c&aacute;c huyện bi&ecirc;n giới triển khai c&aacute;c hoạt động chia sẻ v&agrave; đồng h&agrave;nh c&ugrave;ng thầy, c&ocirc; gi&aacute;o khu vực bi&ecirc;n giới tu sửa ph&ograve;ng học, cảnh quan khu&ocirc;n vi&ecirc;n nh&agrave; trường, đ&oacute;ng g&oacute;p h&agrave;ng trăm ng&agrave;y c&ocirc;ng lao động; phối hợp với c&aacute;c tổ chức, c&aacute; nh&acirc;n quy&ecirc;n g&oacute;p, ủng hộ vật chất, chăn, &aacute;o ấm, s&aacute;ch vở, xe đạp cho hơn 4.860 lượt học sinh ở khu vực bi&ecirc;n giới.</p>\r\n\r\n<p>Lực lượng đo&agrave;n vi&ecirc;n, thanh ni&ecirc;n&nbsp;Bộ đội bi&ecirc;n ph&ograve;ng&nbsp;tỉnh đ&atilde; chủ động phối hợp với lực lượng đo&agrave;n vi&ecirc;n, thanh ni&ecirc;n địa phương v&agrave; c&aacute;c nh&agrave; trường tổ chức c&aacute;c buổi ngoại kho&aacute; để tuy&ecirc;n truyền về an to&agrave;n giao th&ocirc;ng, ph&ograve;ng chống đuối nước, ph&ograve;ng chống tai nạn thương t&iacute;ch, bồi dưỡng, gi&aacute;o dục kỹ năng sống cho c&aacute;c em học sinh; tổ chức tiếp sức m&ugrave;a thi hỗ trợ bữa ăn trưa cho c&aacute;c th&iacute; sinh trong&nbsp;những ng&agrave;y thi tốt nghiệp THPT, phối hợp tổ chức &ldquo;Đ&ecirc;m hội Trăng rằm&rdquo; cho c&aacute;c ch&aacute;u...</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, Bộ Chỉ huy&nbsp;Bộ đội bi&ecirc;n ph&ograve;ng&nbsp;tỉnh v&agrave; Sở&nbsp;Gi&aacute;o dục v&agrave; Đ&agrave;o tạo&nbsp;đ&atilde; chỉ đạo c&aacute;c đồn Bi&ecirc;n ph&ograve;ng phối hợp với Ph&ograve;ng&nbsp;Gi&aacute;o dục v&agrave; Đ&agrave;o tạo&nbsp;c&aacute;c huyện bi&ecirc;n giới tổ chức khảo s&aacute;t số lượng người m&ugrave; chữ tại địa b&agrave;n bi&ecirc;n giới, chuẩn bị c&aacute;c điều kiện mở c&aacute;c lớp dạy x&oacute;a m&ugrave; chữ. T&iacute;nh đến nay, c&aacute;c Đồn Bi&ecirc;n ph&ograve;ng Mường Lạn, Mường L&egrave;o, Nậm Lạnh đ&atilde; phối hợp với Ph&ograve;ng&nbsp;Gi&aacute;o dục v&agrave; Đ&agrave;o tạo&nbsp;huyện&nbsp;Sốp Cộp v&agrave; c&aacute;c trường tiểu học khu vực bi&ecirc;n giới huyện Sốp Cộp tuy&ecirc;n truyền, vận động, mở 24 lớp&nbsp;x&oacute;a m&ugrave; chữ&nbsp;với 422 học vi&ecirc;n. Kết th&uacute;c kh&oacute;a học, 100% học vi&ecirc;n đ&atilde; được Ph&ograve;ng&nbsp;Gi&aacute;o dục v&agrave; Đ&agrave;o tạo Sốp Cộp tổ chức kiểm tra, cấp chứng chỉ chứng nhận ho&agrave;n th&agrave;nh chương tr&igrave;nh x&oacute;a m&ugrave; chữ. Hiện tại, Đồn Bi&ecirc;n ph&ograve;ng Mường L&egrave;o, Nậm Lạnh v&agrave; Mường Lạn đang tiếp tục giảng dạy 07 lớp x&oacute;a m&ugrave; chữ với 230 học vi&ecirc;n.&nbsp;Bộ Chỉ huy&nbsp;Bộ đội bi&ecirc;n ph&ograve;ng&nbsp;tỉnh đ&atilde; chọn cử 11 c&aacute;n bộ Sĩ quan, Qu&acirc;n nh&acirc;n chuy&ecirc;n nghiệp c&oacute; phẩm chất, năng lực, kiến thức sư phạm tham gia dạy x&oacute;a m&ugrave; chữ. C&aacute;c &quot;Thầy gi&aacute;o mang qu&acirc;n h&agrave;m xanh&quot; đ&atilde; c&ugrave;ng với gi&aacute;o vi&ecirc;n c&aacute;c nh&agrave; trường thường xuy&ecirc;n trao đổi về chuy&ecirc;n m&ocirc;n, nghiệp vụ, phương ph&aacute;p, kỹ năng dạy học, trực tiếp tham gia dạy c&aacute;c lớp x&oacute;a m&ugrave; chữ.</p>\r\n\r\n<p>Chương tr&igrave;nh phối hợp&nbsp;giữa hai ng&agrave;nh&nbsp;đ&atilde; ph&aacute;t huy sức mạnh tổng hợp của hệ thống ch&iacute;nh trị, c&aacute;c cấp, c&aacute;c ng&agrave;nh, c&aacute;c lực lượng trong sự nghiệp x&acirc;y dựng v&agrave; bảo vệ bi&ecirc;n giới; nhằm n&acirc;ng cao đời sống vật chất, tinh thần của đồng b&agrave;o v&ugrave;ng d&acirc;n tộc tuyến bi&ecirc;n giới của tỉnh, nhằm tạo điều kiện cho đồng b&agrave;o ph&aacute;t triển to&agrave;n diện, n&acirc;ng cao chất lượng cuộc sống sớm vươn l&ecirc;n ho&agrave; nhập cộng đồng, từng bước khắc phục sự ch&ecirc;nh lệch giữa d&acirc;n tộc thiểu số v&agrave; d&acirc;n tộc kh&aacute;c, r&uacute;t ngắn khoảng c&aacute;ch giữa c&aacute;c v&ugrave;ng. G&oacute;p phần thực hiện thắng lợi nhiệm vụ ph&aacute;t triển kinh tế - x&atilde; hội, củng cố quốc ph&ograve;ng, an ninh tại địa phương... x&acirc;y dựng tiềm lực ch&iacute;nh trị tinh thần, tiềm lực quốc ph&ograve;ng, củng cố l&ograve;ng tin của nh&acirc;n d&acirc;n với Đảng, Nh&agrave; nước, Qu&acirc;n đội v&agrave; cấp ủy Đảng, ch&iacute;nh quyền c&aacute;c cấp; x&acirc;y dựng &ldquo;Bi&ecirc;n giới l&ograve;ng d&acirc;n&rdquo; vững mạnh.</p>\r\n\r\n<p style="text-align:right"><strong>Như Thủy</strong></p>\r\n', '2022-09-28 07:35:13', 49, '/data/images/l51.jpg', '', 1, 52, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1341, 'Xây dựng xã hội học tập vùng biên giới Sơn La', 'Trong ba năm qua, ngành giáo dục và đào tạo tỉnh Sơn La phối hợp cùng Bộ Chỉ huy Bộ đội Biên phòng tỉnh triển khai thực hiện Chương trình xây dựng xã hội học tập vùng biên giới. Nhờ đó đã góp phần giảm tỷ lệ học sinh bỏ học và nâng cao được chất lượng công tác xóa mù chữ, nhất là trong cộng đồng các dân tộc thiểu số tại địa phương.', '<p style="text-align:center"><em><img alt="" src="/data/images/4_01-1637104157176.jpg" style="height:500px; width:800px" /><br />\r\nC&aacute;n bộ, chiến sĩ bi&ecirc;n ph&ograve;ng tỉnh Sơn La trực tiếp l&ecirc;n lớp dạy học cho đồng b&agrave;o d&acirc;n tộc thiểu số.</em></p>\r\n\r\n<p style="text-align:justify">Sau bữa cơm tối, nhờ &ocirc;ng b&agrave; sang tr&ocirc;ng con, vợ chồng anh H&agrave;ng A Th&aacute;i v&agrave; chị M&ugrave;a Thị Dợ, d&acirc;n tộc H&rsquo;M&ocirc;ng, bản Ph&aacute; Th&oacute;ng, x&atilde; Mường V&agrave;, huyện Sốp Cộp&nbsp; vội v&agrave;ng đến lớp học. Đ&acirc;y l&agrave; một trong nhiều lớp x&oacute;a m&ugrave; chữ cho đồng b&agrave;o d&acirc;n tộc thiểu số v&ugrave;ng bi&ecirc;n nằm trong Chương tr&igrave;nh x&acirc;y dựng x&atilde; hội học tập v&ugrave;ng bi&ecirc;n giới do những người l&iacute;nh mang qu&acirc;n h&agrave;m xanh đảm nhiệm đứng lớp. L&agrave; ng&agrave;y nghỉ cuối tuần, nhưng chưa đến 19 giờ, tại Trường tiểu học v&agrave; THCS b&aacute;n tr&uacute; N&agrave; Khoang, x&atilde; Mường V&agrave;, huyện Sốp Cộp đ&atilde; đ&ocirc;ng vui, nhộn nhịp. Tiếng cười, tiếng n&oacute;i của hơn 60 người d&acirc;n trong bản Ph&aacute; Th&oacute;ng từ trẻ cho đến gi&agrave;, từ nam, nữ chưa x&acirc;y dựng gia đ&igrave;nh cho đến những người đ&atilde; c&oacute; gia đ&igrave;nh hay đang nu&ocirc;i con nhỏ&hellip; thật r&ocirc;m rả. &Ocirc;ng Mường A Lồng, bản Ph&aacute; Th&oacute;ng, x&atilde; Mường V&agrave;, huyện Sốp Cộp, người cao tuổi nhất của lớp x&oacute;a m&ugrave; chữ bản Ph&aacute; Th&oacute;ng chia sẻ: &ldquo;Thế hệ ch&uacute;ng t&ocirc;i phần lớn đều kh&ocirc;ng được đi học. C&aacute;n bộ l&ecirc;n ph&aacute;t tờ rơi tuy&ecirc;n truyền cũng chỉ biết nh&igrave;n ảnh, c&ograve;n chữ th&igrave; kh&ocirc;ng biết. Thấy nhiều x&atilde; c&oacute; lớp học như vậy, b&agrave; con trong bản đ&atilde; họp v&agrave; thống nhất l&agrave;m đơn đề nghị bộ đội mở lớp dạy chữ cho những người chưa biết chữ. Gần 60 tuổi rồi nhưng t&ocirc;i cũng phải cố gắng học để c&ograve;n biết đọc, biết viết&rdquo;.</p>\r\n\r\n<p style="text-align:justify">PGS, TS Nguyễn Huy Ho&agrave;ng, Gi&aacute;m đốc Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo tỉnh Sơn La cho biết, qua ba năm triển khai thực hiện Chương tr&igrave;nh x&acirc;y dựng x&atilde; hội học tập v&ugrave;ng bi&ecirc;n giới, đến nay đ&atilde; mở được 31 lớp x&oacute;a m&ugrave; chữ tại c&aacute;c x&atilde; v&ugrave;ng bi&ecirc;n cho gần 1.000 học vi&ecirc;n với 100% l&agrave; đồng b&agrave;o d&acirc;n tộc thiểu số. Để c&oacute; được những lớp học như vậy, Thượng t&aacute; Đ&agrave;o Mạnh Tưởng, Ph&oacute; Chủ nhiệm Ch&iacute;nh trị Bộ đội Bi&ecirc;n ph&ograve;ng tỉnh Sơn La cho biết th&ecirc;m: Với&nbsp; mong muốn duy tr&igrave; v&agrave; vận động được b&agrave; con tham gia đầy đủ c&aacute;c lớp học x&oacute;a m&ugrave; chữ, lực lượng bi&ecirc;n ph&ograve;ng phải ng&agrave;y đ&ecirc;m b&aacute;m bản để tuy&ecirc;n truyền, giải th&iacute;ch. C&oacute; những địa b&agrave;n, anh em phải tranh thủ buổi tối sau giờ b&agrave; con đi nương, hay phối hợp c&aacute;c gi&agrave; l&agrave;ng, người c&oacute; uy t&iacute;n đến từng nh&agrave; để tuy&ecirc;n truyền, vận động người d&acirc;n. Nhiều hộ c&aacute;n bộ, chiến sĩ phải đi lại ba, bốn lần mới vận động th&agrave;nh c&ocirc;ng v&agrave; sau đ&oacute; kh&ocirc;ng chỉ một người tham gia m&agrave; cả vợ chồng c&ugrave;ng xin tham gia lớp x&oacute;a m&ugrave; chữ.</p>\r\n\r\n<p style="text-align:justify">L&agrave; một trong những tỉnh T&acirc;y Bắc c&oacute; đường bi&ecirc;n giới gi&aacute;p với nước bạn L&agrave;o, khu vực bi&ecirc;n giới của Sơn La c&oacute; gần 27.000 người thuộc 286 bản của s&aacute;u huyện, trong đ&oacute; chủ yếu l&agrave; đồng b&agrave;o c&aacute;c d&acirc;n tộc Th&aacute;i, Mường, H&rsquo;M&ocirc;ng, Khơ M&uacute;, Sinh Mun, T&agrave;y v&agrave; L&agrave;o c&ugrave;ng sinh sống. Cũng do điều kiện kh&oacute; khăn, cho n&ecirc;n trẻ em tại c&aacute;c bản &iacute;t c&oacute; điều kiện được quan t&acirc;m, chăm s&oacute;c. Đ&aacute;ng ch&uacute; &yacute; l&agrave; phụ nữ thuộc một số d&acirc;n tộc thiểu số do phong tục cũ cho n&ecirc;n từ nhỏ đ&atilde; kh&ocirc;ng được đi học, đến tuổi lấy chồng cũng kh&ocirc;ng biết tiếng phổ th&ocirc;ng. B&agrave; Sồng Thị D&ocirc;ng, d&acirc;n tộc H&rsquo;M&ocirc;ng, bản Huối P&aacute;, x&atilde; Mường Lạn, huyện Sốp Cộp bảo: &ldquo;Ng&agrave;y xưa, do nh&agrave; ở xa trung t&acirc;m, đường đi lại qu&aacute; kh&oacute; khăn v&agrave; phong tục kh&ocirc;ng cho trẻ em nữ đi học, n&ecirc;n t&ocirc;i kh&ocirc;ng được đến lớp. Sau khi tham gia lớp x&oacute;a m&ugrave; chữ mở tại bản năm 2020, giờ t&ocirc;i đ&atilde; biết đọc, biết t&iacute;nh. Đến lớp, t&ocirc;i c&ograve;n học được nhiều kinh nghiệm trong sản xuất v&agrave; x&acirc;y dựng gia đ&igrave;nh&rdquo;.</p>\r\n\r\n<p style="text-align:justify">Theo Thượng t&aacute; Đ&agrave;o Anh Tuấn, Ch&iacute;nh trị vi&ecirc;n Đồn Bi&ecirc;n ph&ograve;ng Mường L&egrave;o, huyện Sốp Cộp, kh&oacute; khăn bước đầu để tổ chức c&aacute;c lớp học l&agrave; c&ocirc;ng t&aacute;c tuy&ecirc;n truyền, vận động người chưa biết chữ đến lớp. Đơn vị đ&atilde; trực tiếp b&aacute;m trường, b&aacute;m bản. V&agrave;o ban ng&agrave;y, một số m&ocirc; h&igrave;nh trồng rau, trồng c&acirc;y ăn quả, chăn nu&ocirc;i gia cầm được thực hiện để người d&acirc;n thấy được thực tế l&agrave; phải học mới biết l&agrave;m. C&ograve;n v&agrave;o buổi tối th&igrave; l&ecirc;n lớp dạy chữ. Ng&agrave;y đầu c&oacute; số học vi&ecirc;n &iacute;t, nhưng dần dần lớp học đ&ocirc;ng l&ecirc;n, trong bản ai chưa biết chữ đều mạnh dạn đăng k&yacute; đi học. Ngo&agrave;i việc trực tiếp tham gia dạy x&oacute;a m&ugrave; chữ, c&aacute;n bộ, chiến sĩ thuộc c&aacute;c đồn bi&ecirc;n ph&ograve;ng khi xuống địa b&agrave;n c&ocirc;ng t&aacute;c c&ograve;n nắm t&igrave;nh h&igrave;nh, bảo vệ bi&ecirc;n giới, tuy&ecirc;n truyền đường lối, chủ trương của Đảng, ch&iacute;nh s&aacute;ch, ph&aacute;p luật Nh&agrave; nước; lồng gh&eacute;p tuy&ecirc;n truyền, vận động c&aacute;c hộ gia đ&igrave;nh cho con em đi học đ&uacute;ng độ tuổi.</p>\r\n\r\n<p style="text-align:justify">Thực tế cho thấy, Chương tr&igrave;nh phối hợp đẩy mạnh c&ocirc;ng t&aacute;c x&acirc;y dựng x&atilde; hội học tập của Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo tỉnh Sơn La c&ugrave;ng Bộ đội Bi&ecirc;n ph&ograve;ng tỉnh đ&atilde; v&agrave; đang ph&aacute;t huy rất hiệu quả, được đ&aacute;nh gi&aacute; cao. Th&ocirc;ng qua lớp học, b&agrave; con c&ograve;n được học hỏi v&agrave; chia sẻ th&ecirc;m c&aacute;c kiến thức, kinh nghiệm ph&aacute;t triển kinh tế, x&oacute;a ngh&egrave;o v&agrave; l&agrave;m gi&agrave;u tr&ecirc;n qu&ecirc; hương. Chương tr&igrave;nh g&oacute;p phần củng cố th&ecirc;m niềm tin v&agrave;o sự l&atilde;nh đạo của Đảng, Nh&agrave; nước v&agrave; cấp ủy, ch&iacute;nh quyền c&aacute;c cấp.</p>\r\n', '2022-09-28 07:40:06', 59, '/data/images/4_01-1637104157176.jpg', '', 1, 52, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1342, 'Thành phố với phong trào khuyến học, khuyến tài', 'Nhằm khơi dậy và phát huy truyền thống hiếu học, Hội Khuyến học thành phố Sơn La thường xuyên phối hợp với các cấp, ngành, đơn vị trường học đẩy mạnh phong trào khuyến học, khuyến tài, hướng đến mục tiêu xây dựng xã hội học tập gắn với chủ trương xã hội hóa giáo dục.', '<p style="text-align:center"><em><img alt="" src="/data/images/1yb3qgpf(1).jpg" style="height:420px; width:630px" /><br />\r\nHội Khuyến học Th&agrave;nh phố trao Giấy khen cho c&aacute;c c&aacute; nh&acirc;n, tập thể c&oacute; th&agrave;nh t&iacute;ch xuất sắc năm học 2020-2021.</em></p>\r\n\r\n<p>Hội Khuyến học Th&agrave;nh phố c&oacute; 17 hội cơ sở; trong đ&oacute; 12 hội x&atilde;, phường; 3 hội trường THPT, 1 hội doanh nghiệp, 1 hội đồng hương, với 275 chi hội, 36.323 hội vi&ecirc;n. &Ocirc;ng H&agrave; Văn Qu&acirc;n, Chủ tịch Hội Khuyến học Th&agrave;nh phố, cho biết: Với vai tr&ograve; th&uacute;c đẩy phong tr&agrave;o khuyến học, khuyến t&agrave;i, g&oacute;p phần x&acirc;y dựng x&atilde; hội học tập, nhiều năm qua, Hội đ&atilde; tham mưu cho cấp ủy, ch&iacute;nh quyền địa phương ban h&agrave;nh kế hoạch thực hiện c&aacute;c chương tr&igrave;nh hoạt động x&acirc;y dựng x&atilde; hội học tập. C&ugrave;ng với đ&oacute;, kh&ocirc;ng ngừng củng cố mạng lưới hoạt động của Hội ph&aacute;t triển cả bề rộng v&agrave; chiều s&acirc;u; triển khai nhiều giải ph&aacute;p kh&aacute;c nhau để c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i đi v&agrave;o thực tế cuộc sống, g&oacute;p phần n&acirc;ng cao chất lượng gi&aacute;o dục. Đặc biệt l&agrave; thực hiện chủ trương của Ch&iacute;nh phủ về đẩy mạnh phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, qua đ&oacute;, huy động được sự v&agrave;o cuộc của cả hệ thống ch&iacute;nh trị.</p>\r\n\r\n<p>Ngo&agrave;i ra, Hội chủ động x&acirc;y dựng chương tr&igrave;nh phối hợp với c&aacute;c tổ chức ch&iacute;nh trị, x&atilde; hội đẩy mạnh phong tr&agrave;o x&acirc;y dựng x&atilde; hội học tập gắn với phong tr&agrave;o &ldquo;To&agrave;n d&acirc;n đo&agrave;n kết x&acirc;y dựng đời sống văn h&oacute;a&rdquo;, đưa nhiệm vụ khuyến học th&agrave;nh ti&ecirc;u chuẩn để b&igrave;nh x&eacute;t c&aacute;c phong tr&agrave;o thi đua y&ecirc;u nước của mỗi đơn vị, địa phương. Trong nhiệm kỳ 2016-2021, Th&agrave;nh phố c&oacute; 23.842 hộ gia đ&igrave;nh học tập; 15 d&ograve;ng họ học tập; 12/12 x&atilde;, phường đạt cộng đồng học tập; 100% tổ, bản, tiểu khu đạt cộng đồng học tập; 53 cơ quan, đơn vị đạt đơn vị học tập. Kết quả đ&oacute; đ&atilde; g&oacute;p sức lan tỏa v&agrave; khuyến kh&iacute;ch tinh thần học tập rất cao trong cộng đồng d&acirc;n cư; nhiều m&ocirc; h&igrave;nh c&ocirc;ng d&acirc;n học tập, gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị học tập ti&ecirc;u biểu đ&atilde; tỏa s&aacute;ng, th&uacute;c đẩy phong tr&agrave;o học tập suốt đời tr&ecirc;n địa b&agrave;n.</p>\r\n\r\n<p>D&ograve;ng họ Cầm ở tổ 7, phường T&ocirc; Hiệu, Th&agrave;nh phố, l&agrave; một trong những d&ograve;ng họ ti&ecirc;u biểu trong phong tr&agrave;o khuyến học, khuyến t&agrave;i, đ&atilde; vinh dự được nhận nhiều bằng khen, giấy khen của UBND tỉnh, Trung ương Hội khuyến học Việt Nam, Hội Khuyến học tỉnh. &Ocirc;ng Cầm C&acirc;u, Trưởng d&ograve;ng họ, chia sẻ: D&ograve;ng họ Cầm c&oacute; 100% gia đ&igrave;nh trong d&ograve;ng họ t&iacute;ch cực đăng k&yacute; phấn đấu x&acirc;y dựng gia đ&igrave;nh học tập, đ&atilde; c&oacute; 5 gia đ&igrave;nh đạt danh hiệu &ldquo;Gia đ&igrave;nh học tập&rdquo; xuất sắc ti&ecirc;u biểu 5 năm liền.</p>\r\n\r\n<p>Để tạo kinh ph&iacute; hoạt động, Hội tổ chức nhiều cuộc vận động, ủng hộ trực tiếp, huy động x&acirc;y dựng quỹ từ nhiều nguồn, như quy&ecirc;n g&oacute;p ủng hộ của c&aacute;c cơ quan, đơn vị, doanh nghiệp, c&aacute;c trường học, c&aacute;n bộ, nh&acirc;n d&acirc;n... Số quỹ h&agrave;ng năm của Hội đạt tr&ecirc;n 1,5 tỷ đồng, Hội đ&atilde; khen thưởng tr&ecirc;n 15 ngh&igrave;n người, hỗ trợ tr&ecirc;n 300 lượt học sinh ngh&egrave;o, thầy c&ocirc; c&oacute; ho&agrave;n cảnh kh&oacute; khăn; phối hợp với Ph&ograve;ng Gi&aacute;o dục v&agrave; Đ&agrave;o tạo Th&agrave;nh phố đỡ đầu 50 học sinh c&oacute; ho&agrave;n cảnh đặc biệt kh&oacute; khăn tr&ecirc;n địa b&agrave;n...</p>\r\n\r\n<p>Chia sẻ kinh nghiệm x&acirc;y dựng quỹ khuyến học, b&agrave; Ng&ocirc; T&uacute; Mỹ, Chi hội trưởng Chi hội khuyến học tổ 6, phường Chiềng Sinh, cho biết: Hiện, quỹ của tổ đạt tr&ecirc;n 124 triệu đồng, đ&atilde; vinh danh, khen thưởng học sinh, gi&aacute;o vi&ecirc;n c&oacute; th&agrave;nh t&iacute;ch cao trong học tập, giảng dạy, hỗ trợ học sinh ngh&egrave;o vượt kh&oacute; gần 92 triệu đồng...</p>\r\n\r\n<p>Năm 2022, Hội Khuyến học Th&agrave;nh phố tiếp tục đẩy mạnh phong tr&agrave;o khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập đi v&agrave;o chiều s&acirc;u, chất lượng, g&oacute;p phần n&acirc;ng cao d&acirc;n tr&iacute;, đ&agrave;o tạo nh&acirc;n lực, bồi dưỡng nh&acirc;n t&agrave;i cho địa phương.</p>\r\n', '2022-09-29 13:09:58', 55, '/data/images/1yb3qgpf(1).jpg', '', 1, 52, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1343, 'Sự học ở vùng cao Chiềng Muôn', 'Đứng chân trên địa bàn xã vùng cao của huyện Mường La, thầy và trò Trường Tiểu học và THCS Chiềng Muôn luôn nỗ lực thực hiện phong trào thi đua “Dạy tốt, học tốt”, từng bước nâng cao chất lượng dạy và học, giúp những học sinh dân tộc Mông, La Ha nơi vùng cao này trau dồi tri thức trong môi trường giáo dục tốt nhất.', '<p style="text-align:center"><em><img alt="" src="/data/images/1.jpg" style="height:420px; width:630px" /><br />\r\nTrường Tiểu học - THCS Chiềng Mu&ocirc;n</em></p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i đến Trường Tiểu học v&agrave; THCS Chiềng Mu&ocirc;n v&agrave;o ng&agrave;y cuối thu.&nbsp;Chiềng Mu&ocirc;n&nbsp;trời đ&atilde; chuyển lạnh, s&aacute;ng sớm, ng&ocirc;i trường nằm ở lưng chừng đồi&nbsp;thấp tho&aacute;ng trong l&agrave;n sương m&ugrave;. Tr&ograve; chuyện với ch&uacute;ng t&ocirc;i, thầy gi&aacute;o Nguyễn Ngọc Sơn, Ph&oacute; hiệu trưởng&nbsp;nh&agrave; trường, th&ocirc;ng tin: Năm học 2022 - 2023,&nbsp;trường c&oacute;&nbsp;12 lớp bậc tiểu học v&agrave; 5 lớp bậc THCS với&nbsp;466 học sinh, trong đ&oacute; c&oacute; 398 học sinh được hưởng&nbsp;chế độ b&aacute;n tr&uacute;.&thinsp;Cơ sở vật chất cơ bản&nbsp;đ&aacute;p ứng c&ocirc;ng t&aacute;c dạy v&agrave; học. Hiện,&nbsp;trường c&oacute; 17 ph&ograve;ng học ki&ecirc;n cố, b&aacute;n ki&ecirc;n cố, 15 ph&ograve;ng b&aacute;n tr&uacute; v&agrave; một nh&agrave; ăn cho học sinh. Học sinh&nbsp;đa số thuộc diện hộ ngh&egrave;o, cận ngh&egrave;o, nhưng c&aacute;c em vẫn vượt kh&oacute; đến trường, c&oacute; &yacute; thức học tập, r&egrave;n luyện.</p>\r\n\r\n<p style="text-align:center"><em><img alt="" src="/data/images/2.jpg" style="height:420px; width:630px" /><br />\r\nMột tiết học của c&ocirc; v&agrave; tr&ograve; Trường Tiểu học - THCS Chiềng Mu&ocirc;n</em></p>\r\n\r\n<p>Nh&agrave; trường hiện c&oacute; 27 c&aacute;n bộ, gi&aacute;o vi&ecirc;n, nh&acirc;n vi&ecirc;n, trong đ&oacute; 100% gi&aacute;o vi&ecirc;n đạt tr&igrave;nh độ chuẩn v&agrave; tr&ecirc;n chuẩn nghề nghiệp. Trong c&aacute;c năm học,&nbsp;Ban Gi&aacute;m hiệu nh&agrave; trường x&acirc;y dựng kế hoạch chỉ đạo chuy&ecirc;n m&ocirc;n đối với từng cấp học; hướng dẫn&nbsp;gi&aacute;o vi&ecirc;n soạn gi&aacute;o &aacute;n b&aacute;m s&aacute;t chuẩn kiến thức, giảm tải, t&iacute;ch hợp nội dung lồng gh&eacute;p trong c&aacute;c b&agrave;i soạn trước ng&agrave;y giảng;&nbsp;sử dụng đồ d&ugrave;ng dạy học, c&ocirc;ng nghệ th&ocirc;ng tin trong c&aacute;c tiết dạy. Ph&acirc;n c&ocirc;ng gi&aacute;o vi&ecirc;n cốt c&aacute;n, gi&aacute;o vi&ecirc;n c&oacute; chuy&ecirc;n m&ocirc;n vững gi&uacute;p đỡ gi&aacute;o vi&ecirc;n mới c&ograve;n hạn chế về phương ph&aacute;p dạy học, phương ph&aacute;p quản l&yacute; học sinh. Đồng thời,&nbsp;c&ugrave;ng c&aacute;c tổ chuy&ecirc;n m&ocirc;n tăng cường dự giờ, thăm lớp; khảo s&aacute;t, ph&acirc;n loại tr&igrave;nh độ gi&aacute;o vi&ecirc;n... Ngo&agrave;i c&aacute;c m&ocirc;n học ch&iacute;nh kh&oacute;a, nh&agrave; trường c&ograve;n&nbsp;tăng cường m&ocirc;n Tiếng Việt, To&aacute;n cho học sinh bậc tiểu học v&agrave;&nbsp;H&oacute;a, Vật l&yacute;, Văn học cho học sinh THCS;&nbsp;tổ chức &ocirc;n tập, bổ trợ kiến thức, phụ đạo học sinh yếu k&eacute;m, bồi dưỡng học sinh c&oacute; năng khiếu. Hằng năm, tỷ lệ huy động trẻ 6 tuổi v&agrave;o lớp 1 v&agrave; học sinh v&agrave;o lớp 6 đạt 100%;&nbsp;học sinh chuy&ecirc;n cần đạt tr&ecirc;n 98%; hơn 94% học sinh ho&agrave;n th&agrave;nh chương tr&igrave;nh bậc tiểu học; tr&ecirc;n&nbsp;98% học sinh lớp 9 đủ điều kiện thi chuyển cấp...</p>\r\n\r\n<p>C&ocirc; gi&aacute;o Trần Thị Th&ugrave;y Dung, chủ nhiệm lớp 4B, cho biết: Hơn 3 năm gắn b&oacute; với học sinh&nbsp;của trường,&nbsp;t&ocirc;i quản l&yacute; lớp theo c&aacute;ch chia học sinh th&agrave;nh c&aacute;c nh&oacute;m học tập v&agrave; nh&oacute;m &ldquo;quen&rdquo; (c&ugrave;ng bản, l&agrave; người th&acirc;n trong gia đ&igrave;nh, họ h&agrave;ng). Qua đ&oacute;,&nbsp;gi&uacute;p học tr&ograve; r&egrave;n luyện t&iacute;nh tự gi&aacute;c, tự quản, chủ động gi&uacute;p đỡ lẫn nhau trong học tập, trong cuộc sống.</p>\r\n\r\n<p style="text-align:center"><em><img alt="" src="/data/images/3.jpeg" style="height:420px; width:630px" /><br />\r\nGi&aacute;o vi&ecirc;n Trường Tiểu học THCS Chiềng Mu&ocirc;n thăm, động vi&ecirc;n gia đ&igrave;nh em&nbsp;Gi&agrave;ng Thị Nu v&agrave; Gi&agrave;ng A Hiện</em></p>\r\n\r\n<p>Hai chị&nbsp;em Gi&agrave;ng Thị Nu, học sinh lớp 7 v&agrave; Gi&agrave;ng A Hiện, học sinh&nbsp;lớp 6, bản Hua Chiến, c&oacute; mẹ bị bệnh, bố mất sức lao động, gia đ&igrave;nh em&nbsp;sống c&ugrave;ng b&agrave; nội đ&atilde; cao tuổi. Em Nu&nbsp;chia sẻ: Sau những buổi học, em c&ugrave;ng em trai&nbsp; gi&uacute;p đỡ bố mẹ&nbsp;việc nh&agrave; v&agrave;&nbsp;d&agrave;nh thời gian buổi tối để học b&agrave;i. Ch&uacute;ng em lu&ocirc;n được c&aacute;c thầy, c&ocirc; gi&aacute;o động vi&ecirc;n,&nbsp;n&ecirc;n d&ugrave; ho&agrave;n cảnh&nbsp;kh&oacute; khăn, ch&uacute;ng em&nbsp;sẽ&nbsp;cố gắng đi học đầy đủ v&agrave; học tập tốt để kh&ocirc;ng phụ c&ocirc;ng của c&aacute;c thầy, c&ocirc;.</p>\r\n\r\n<p style="text-align:center"><em><img alt="" src="/data/images/4.jpg" style="height:420px; width:630px" /><br />\r\nGi&aacute;o vi&ecirc;n&nbsp;hướng dẫn th&ecirc;m những b&agrave;i tập kh&oacute; ngo&agrave;i giờ l&ecirc;n lớp</em></p>\r\n\r\n<p>C&ugrave;ng với việc ho&agrave;n th&agrave;nh&nbsp;tốt c&ocirc;ng t&aacute;c giảng dạy, nh&agrave; trường c&ograve;n thực hiện tốt chế độ b&aacute;n tr&uacute; cho học sinh;&nbsp;tổ chức nấu ăn đảm bảo dinh dưỡng, vệ sinh an to&agrave;n thực phẩm.&nbsp;Ph&acirc;n c&ocirc;ng gi&aacute;o vi&ecirc;n trực, đảm bảo an ninh, trật tự khu b&aacute;n tr&uacute;... Đồng thời, tổ chức c&aacute;c buổi hoạt động ngoại kh&oacute;a, sinh hoạt đầu tuần dưới cờ, với nhiều chủ đề, như:&nbsp;Tr&aacute;ch nhiệm của học sinh&nbsp;với việc giữ g&igrave;n v&agrave; ph&aacute;t huy bản sắc văn h&oacute;a d&acirc;n tộc; t&igrave;m hiểu về b&igrave;nh đẳng giới v&agrave; ph&ograve;ng, chống bạo lực gia đ&igrave;nh; Luật Giao th&ocirc;ng đường bộ;&nbsp;giao lưu văn nghệ, thể thao, với c&aacute;c tr&ograve; chơi d&acirc;n gian... thu h&uacute;t đ&ocirc;ng đảo học sinh tham gia, gi&uacute;p c&aacute;c em th&ecirc;m y&ecirc;u trường mến lớp.</p>\r\n\r\n<p>Em Ho&agrave;ng Thị Kim Thạch, lớp 6B, cho biết: Nh&agrave; ở xa, n&ecirc;n em được ăn, ở b&aacute;n tr&uacute; tại trường rất&nbsp;thuận lợi cho việc học tập v&agrave; sinh hoạt. Ngo&agrave;i ra, em c&ograve;n được c&aacute;c thầy, c&ocirc; gi&aacute;o hướng dẫn phương ph&aacute;p tự học trong thời gian học buổi tối, tham gia c&aacute;c hoạt động ngoại kh&oacute;a để c&oacute; th&ecirc;m kiến thức tr&ecirc;n c&aacute;c lĩnh vực của cuộc sống.</p>\r\n\r\n<p style="text-align:center"><em><img alt="" src="/data/images/5.jpg" style="height:420px; width:630px" /><br />\r\nHọc sinh Trường tiểu học - THCS Chiềng Mu&ocirc;n ăn cơm b&aacute;n tr&uacute;</em></p>\r\n\r\n<p>Chia tay thầy v&agrave; tr&ograve; Trường tiểu học - THCS Chiềng Mu&ocirc;n, ch&uacute;ng t&ocirc;i hiểu th&ecirc;m phần n&agrave;o về&nbsp;sự nghiệp &ldquo;trồng người&rdquo; của c&aacute;c thầy gi&aacute;o, c&ocirc; gi&aacute;o nơi đ&acirc;y,&nbsp;họ đang hằng ng&agrave;y&nbsp;gi&uacute;p học sinh vươn l&ecirc;n trong học tập, trau dồi kiến&nbsp;thức để c&oacute; tương lai tốt đẹp hơn.</p>\r\n', '2022-09-29 13:15:07', 90, '/data/images/tin-tuc/2.jpg', '', 1, 35, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1344, 'Đẩy mạnh khuyến học, khuyến tài, xây dựng xã hội học tập', 'Ngày 23/8, Hội Khuyến học tỉnh đã tổ chức quán triệt, triển khai các văn bản về “Xây dựng xã hội học tập giai đoạn 2021 - 2030” cho 88 thành viên Ban chỉ đạo Xây dựng xã hội học tập tỉnh; ban khuyến học các cơ quan, đơn vị; hội khuyến học, phòng giáo dục và đào tạo các huyện, thành phố.', '<p style="text-align:center"><em><img alt="" src="/data/images/tin-tuc/z3664436299270_e9c9b858323eb8e12d5cf49827737d58.jpg" style="height:420px; width:630px" /><br />\r\nC&aacute;c đại biểu dự Hội nghị qu&aacute;n triệt, triển khai c&aacute;c văn bản về &ldquo;X&acirc;y dựng x&atilde; hội học tập giai đoạn 2021 - 2030&rdquo;.</em></p>\r\n\r\n<p style="text-align:justify">Hội nghị đ&atilde;&nbsp;qu&aacute;n triệt nội dung&nbsp;Chỉ thị số 14/CT-TTg ng&agrave;y 25/5/2021 của Thủ tướng Ch&iacute;nh phủ về đẩy mạnh c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập giai đoạn 2021 -&nbsp;2030; Quyết định số 1373/QĐ-TTg ng&agrave;y 30/7/2021 của Thủ tướng Ch&iacute;nh phủ ph&ecirc; duyệt Đề &aacute;n &quot;X&acirc;y dựng x&atilde; hội học tập giai đoạn 2021 - 2030&quot;; Quyết định số 387/QĐ-TTg ng&agrave;y 25/3/2022 của Thủ tướng Ch&iacute;nh phủ ph&ecirc; duyệt Chương tr&igrave;nh &quot;Đẩy mạnh phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị giai đoạn 2021 - 2030&quot;; Quyết định số 677/QĐ-TTg, ng&agrave;y 3/6/2022 của Thủ tướng Ch&iacute;nh phủ ph&ecirc; duyệt Chương tr&igrave;nh &quot;X&acirc;y dựng m&ocirc; h&igrave;nh C&ocirc;ng d&acirc;n học tập giai đoạn 2021 - 2030&quot;; Th&ocirc;ng tư số 22/2020/TT-BGDĐT ng&agrave;y 6/8/2020 của Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo quy định về đ&aacute;nh gi&aacute;, xếp loại Đơn vị học tập; Th&ocirc;ng tư số 44/2014/TT-BGDĐT, ng&agrave;y 12/12/2014 của Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo quy định về đ&aacute;nh gi&aacute;, xếp loại &quot;cộng đồng học tập&rdquo; cấp x&atilde;; Kế hoạch số 123/KH-KHVN, ng&agrave;y 14/4/2022 của Hội Khuyến học Việt Nam về thực hiện Chương tr&igrave;nh &quot;Đẩy mạnh phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị giai đoạn 2021-2030&quot;&hellip;.</p>\r\n\r\n<p style="text-align:justify">Hội Khuyến học tỉnh đề nghị ban khuyến học c&aacute;c&nbsp;cơ quan, đơn vị, hội khuyến học v&agrave;&nbsp;c&aacute;c ph&ograve;ng gi&aacute;o dục - đ&agrave;o tạo huyện, th&agrave;nh phố&nbsp;tổ chức qu&aacute;n triệt c&aacute;c nội dung tr&ecirc;n đến&nbsp;c&aacute;n bộ, hội vi&ecirc;n, nhằm tạo sự chuyển biến t&iacute;ch cực về nhận thức v&agrave;&nbsp;hiệu quả trong c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i.</p>\r\n', '2022-09-29 13:17:52', 96, '/data/files/hkh/z3664436299270_e9c9b858323eb8e12d5cf49827737d58.jpg', '', 1, 57, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1345, 'Chùm truyện cười ngắn về thể thao', 'Chuyện 1. Một cậu bé khoe với bạn: - Bố tớ có hơn 100 huy chương vàng cúp bóng đá - Tuyệt nhỉ, thế bố cậu đá ở vị trí nào? Trung phong hả? - Không, bố tớ là chủ tiệm cầm đồ.', '<p><strong>Chuyện 2.<br />\r\nMột nh&agrave; qu&aacute;n qu&acirc;n thể thao bị ốm, b&aacute;c sĩ bảo nhiệt độ anh ta rất cao<br />\r\n- Bao nhi&ecirc;u thưa b&aacute;c sĩ? - Anh ta hỏi<br />\r\n- 41 độ.<br />\r\n- Thế kỉ lục thế giới l&agrave; bao nhi&ecirc;u?<br />\r\n<br />\r\nChuyện 3.<br />\r\nNgười ta hỏi gi&aacute;m đốc 1 trung t&acirc;m thể dục nhịp điệu:<br />\r\n- Phần lớn phụ nữ trả 1 khoản tiền lớn tới đ&acirc;y để được gầy bớt đi, họ n&oacute;i những g&igrave; với &ocirc;ng?<br />\r\n- Ồ, hầu như c&aacute;c b&agrave; c&aacute;c c&ocirc; ấy đều than phiền: &quot;Tại sao &ocirc;ng lại đặt ph&ograve;ng tập ở tầng 2 m&agrave; ko c&oacute; thang m&aacute;y? Ch&uacute;ng t&ocirc;i phải leo 18 bậc thang, cao qu&aacute; &quot;<br />\r\n<br />\r\nChuyện 4.<br />\r\nGi&aacute;o vi&ecirc;n thể dục n&oacute;i với 1 học vi&ecirc;n:<br />\r\n- Anh ko được phạm quy tắc. H&ocirc;m nay ch&uacute;ng ta học m&ocirc;n nhảy 3 bước. Cho n&ecirc;n khoảng c&aacute;ch 17m n&agrave;y, anh ko được nhảy 1 bước như anh vừa nhảy!<br />\r\n<br />\r\nChuyện 5.<br />\r\nCon: -Bố ui, sao mọi người la h&eacute;t chửi bới ch&uacute; kia thế?<br />\r\nBố: -Tại v&igrave; ch&uacute; ấy n&eacute;m đ&aacute; v&agrave;o &ocirc;ng trọng t&agrave;i!<br />\r\nCon: -Nhưng mừ ch&uacute; ấy n&eacute;m hok tr&uacute;ng m&agrave;?<br />\r\nBố: -Th&igrave; tại v&igrave; n&eacute;m kh&ocirc;ng tr&uacute;ng n&ecirc;n ch&uacute; ấy mới bị chửi đ&oacute; con ạ!<br />\r\n<br />\r\nChuyện 6.<br />\r\n- Ai cũng n&oacute;i l&agrave; đội b&oacute;ng nữ n&agrave;y, em chẳng k&eacute;m g&igrave; cầu thủ nam từ kĩ thuật đến thể lực. Vậy tại sao em ko tham gia thi đấu nữa?<br />\r\n- Ch&iacute;nh v&igrave; em sợ nếu m&igrave;nh chẳng k&eacute;m g&igrave; cầu thủ nam th&igrave; sau l&uacute;c ghi b&agrave;n sẽ... cởi &aacute;o ra v&agrave; chạy v&ograve;ng quanh s&acirc;n(!!!)</strong></p>\r\n', '2022-09-29 13:19:38', 76, '', '', 0, 59, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1346, 'Lễ gặp mặt nhân kỷ niệm Ngày Khuyến học Việt Nam và phát động giải thưởng Nhân tài Đất Việt năm 2023', 'Ngày 27/9 tại Hà Nội, Hội Khuyến học Việt Nam tổ chức lễ gặp mặt nhân kỷ niệm ngày khuyến học Việt Nam 2/10 và phát động giải thưởng nhân tài Đất Việt năm 2023 lần thứ 17.', '<p style="text-align:center"><iframe frameborder="0" height="500" scrolling="no" src="/data/files/ky-niem-ngay-khuyen-hoc.mp4" width="700"></iframe></p>\r\n', '2022-09-29 17:03:59', 101, '/data/images/tothimai.jpg', '', 1, 52, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1274, 'Hội nghị trực tuyến quán triệt các nghị quyết của Đảng và của Hội', 'Ngày 7/4, Hội Người cao tuổi Việt Nam đã tổ chức Hội nghị quán triệt, triển khai Nghị quyết Đại hội XIII của Đảng; bài phát biểu của đồng chí Tổng Bí thư Nguyễn Phú Trọng tại buổi gặp mặt các đại biểu về dự Đại hội VI Hội Người cao tuổi Việt Nam; Nghị quyết Đại hội VI Hội Người cao tuổi Việt Nam.', '<p><img alt="" src="/data/images/tr0bbtn1.jpg" style="height:427px; width:640px" /></p>\r\n\r\n<p style="text-align:center"><em>C&aacute;c đại biểu dự Hội nghị qu&aacute;n triệt nghị quyết tại điểm cầu tỉnh Sơn La.</em></p>\r\n\r\n<p style="text-align:justify">Dự&nbsp;Hội nghị, c&oacute; đồng ch&iacute; Nguyễn Trọng Nghĩa, B&iacute; thư Trung ương Đảng, Trưởng Ban Tuy&ecirc;n gi&aacute;o Trung ương. Hội nghị được tổ chức trực tuyến tới 63 tỉnh, th&agrave;nh trong cả nước. Dự Hội nghị điểm cầu tỉnh Sơn La, c&oacute; đại diện l&atilde;nh đạo Ban Tuy&ecirc;n gi&aacute;o Tỉnh ủy, Ban D&acirc;n vận Tỉnh ủy, Ban đại diện Hội Người cao tuổi tỉnh v&agrave; c&aacute;c huyện, th&agrave;nh phố.</p>\r\n\r\n<p style="text-align:justify">Hội nghị đ&atilde; qu&aacute;n triệt&nbsp;Nghị quyết Đại hội VI Hội Người cao tuổi&nbsp;Việt Nam nhiệm kỳ 2021-2026&nbsp;với&nbsp;c&aacute;c chuy&ecirc;n đề: X&acirc;y dựng tổ chức Hội vững mạnh; n&acirc;ng cao hiệu quả c&ocirc;ng t&aacute;c chăm s&oacute;c người cao tuổi (NCT); ph&aacute;t huy vai tr&ograve; NCT tham gia x&acirc;y dựng v&agrave; bảo vệ Tổ quốc; c&ocirc;ng t&aacute;c đối ngoại nh&acirc;n d&acirc;n v&agrave; thực hiện Đề &aacute;n nh&acirc;n rộng m&ocirc; h&igrave;nh c&acirc;u lạc bộ li&ecirc;n thế hệ tự gi&uacute;p nhau giai đoạn đến năm 2025. Với những chỉ ti&ecirc;u chủ yếu: Tập hợp tr&ecirc;n 90% người cao tuổi&nbsp;v&agrave;o tổ chức Hội;&nbsp;100% c&aacute;n bộ Hội NCT&nbsp;cấp tỉnh, cấp huyện,&nbsp;tr&ecirc;n 90% c&aacute;n bộ Hội NCT cấp cơ sở được tập huấn, bồi dưỡng về kỹ năng c&ocirc;ng t&aacute;c Hội; 95% số x&atilde;, phường, thị trấn trở l&ecirc;n th&agrave;nh lập v&agrave; duy tr&igrave; hoạt động thường xuy&ecirc;n Quỹ chăm s&oacute;c v&agrave; ph&aacute;t huy vai tr&ograve; NCT; 90% Hội NCT cơ sở trở l&ecirc;n c&oacute; c&aacute;c loại h&igrave;nh c&acirc;u lạc bộ NCT...</p>\r\n', '2022-04-08 12:31:17', 267, '/data/files/tr0bbtn1.jpg', '', 1, 55, 0, 0, '', 0, '', 'baosonla.org.vn/', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1301, 'Thêm động lực, tạo đà cho Sơn La phát triển', 'Nằm trong chuỗi sự kiện Hội nghị Thủ tướng Chính phủ đối thoại với nông dân Việt Nam và Festival trái cây và sản phẩm OCOP Việt Nam năm 2022, đồng chí Phạm Minh Chính, Ủy viên Bộ Chính trị, Thủ tướng Chính phủ cùng đoàn công tác của Trung ương đã dành nhiều thời gian đi kiểm tra, khảo sát một số sự án trọng điểm của tỉnh Sơn La và làm việc với Ban Thường vụ Tỉnh ủy Sơn La. Đây là tình cảm đặc biệt của Thủ tướng Chính phủ dành cho Đảng bộ, chính quyền và nhân dân các dân tộc Sơn La, tiếp thêm động lực cho tỉnh trong quá trình xây dựng và phát triển.', '<p style="text-align:center"><strong>Đổi thay Sơn La v&agrave; kh&aacute;t vọng ph&aacute;t triển&nbsp;</strong></p>\r\n\r\n<p style="text-align:justify">Sơn La v&ugrave;ng đất mang n&eacute;t đẹp đặc trưng của thi&ecirc;n nhi&ecirc;n, lịch sử, văn h&oacute;a v&ugrave;ng T&acirc;y Bắc. Những di t&iacute;ch lịch sử vang m&atilde;i kh&uacute;c tr&aacute;ng ca, những con đường uốn lượn điệp tr&ugrave;ng ngập sắc hoa, những đồi ch&egrave; của sắc xanh b&aacute;t ng&aacute;t, thảo nguy&ecirc;n bao la, những bản l&agrave;ng của những điệu kh&egrave;n, điệu m&uacute;a x&ograve;e l&agrave;m say đắm l&ograve;ng người. L&agrave; mảnh đất c&oacute; truyền thống lịch sử c&aacute;ch mạng h&agrave;o h&ugrave;ng.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="/data/images/q2bs5zwm.jpg" style="height:420px; width:630px" /></p>\r\n\r\n<p style="text-align:center"><em>Thủ tướng Ch&iacute;nh phủ l&agrave;m việc với Ban Thường vụ Tỉnh ủy Sơn La.</em></p>\r\n\r\n<p style="text-align:justify">Với kh&iacute; hậu nhiệt đới, đất đai m&agrave;u mở, Sơn La c&oacute; lợi thế để ph&aacute;t triển c&aacute;c loại n&ocirc;ng, l&acirc;m sản, thủy sản v&agrave; đa dạng h&oacute;a c&aacute;c sản phẩm n&ocirc;ng nghiệp chất lượng cao. Đến nay, to&agrave;n tỉnh đ&atilde; c&oacute; 82.800 ha c&acirc;y ăn quả với c&aacute;c loại, với sản lượng tr&ecirc;n 450.000 tấn/năm. Trong đ&oacute; nổi tiếng với sản phẩm c&acirc;y ăn quả c&oacute; gi&aacute; trị kinh tế cao như: xo&agrave;i tr&ograve;n Y&ecirc;n Ch&acirc;u, cam Ph&ugrave; Y&ecirc;n, nh&atilde;n S&ocirc;ng M&atilde;, t&aacute;o sơn tra Sơn La, bơ, mận hậu Mộc Ch&acirc;u, na Mai Sơn... Hiện to&agrave;n tỉnh hiện c&oacute; 24 sản phẩm n&ocirc;ng saản được Cục Sở hữu tr&iacute; tuệ cấp văn bằng bảo hộ; x&acirc;y dựng, duy tr&igrave; v&agrave; ph&aacute;t triển 235 chuỗi cung ứng thực phẩm n&ocirc;ng sản, thủy sản an to&agrave;n; 83 sản phẩm OCOP. Nhiều sản phẩm n&ocirc;ng sản của tỉnh đ&atilde; tham gia v&agrave;o chuỗi cung ứng của c&aacute;c hệ thống ph&acirc;n phối lớn trong nước như: Big C, Winmart, Hapro Mart; ti&ecirc;u thụ tr&ecirc;n nhiều s&agrave;n giao dịch thương mại điện tử; đ&atilde; c&oacute; 17 sản phẩm n&ocirc;ng sản xuất khẩu sang thị trường 21 nước tr&ecirc;n thế giới.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="/data/images/dznpyfbl.jpg" style="height:420px; width:630px" /></p>\r\n\r\n<p style="text-align:center"><em>Thủ tướng Ch&iacute;nh phủ xem sơ đồ vị tr&iacute; Dự &aacute;n đường cao tốc H&ograve;a B&igrave;nh - Mộc Ch&acirc;u.</em></p>\r\n\r\n<p style="text-align:justify">Những năm gần đ&acirc;y, Sơn La đ&atilde; vươn l&ecirc;n trở th&agrave;nh trung t&acirc;m sản xuất, chế biến rau, quả lớn nhất v&ugrave;ng T&acirc;y Bắc, trở th&agrave;nh &ldquo;hiện tượng&rdquo; của n&ocirc;ng nghiệp Việt Nam. Hoạt động sản xuất n&ocirc;ng nghiệp khởi sắc kh&ocirc;ng chỉ g&oacute;p phần n&acirc;ng cao đời sống người n&ocirc;ng d&acirc;n m&agrave; c&ograve;n khẳng định việc ch&uacute; trọng ph&aacute;t triển n&ocirc;ng nghiệp đang l&agrave; hướng đi đ&uacute;ng đắn, hiệu quả của địa phương. Sơn La h&ocirc;m nay đang nỗ lực vươn l&ecirc;n, với những dự định, kh&aacute;t vọng vươn l&ecirc;n, với mục ti&ecirc;u phấn đấu: X&acirc;y dựng tỉnh Sơn La trở th&agrave;nh trung t&acirc;m ph&aacute;t triển n&ocirc;ng nghiệp ứng dụng c&ocirc;ng nghệ cao của v&ugrave;ng T&acirc;y Bắc; trở th&agrave;nh trung t&acirc;m chế biến n&ocirc;ng sản của v&ugrave;ng T&acirc;y Bắc v&agrave;o năm 2025.</p>\r\n\r\n<p style="text-align:justify">Sơn La đ&atilde; đổi thay rất nhiều, nhưng vẫn l&agrave; tỉnh miền n&uacute;i c&ograve;n nhiều kh&oacute; khăn, cần được quan t&acirc;m hỗ trợ. Trong cuộc l&agrave;m việc của Thủ tướng Ch&iacute;nh phủ với Ban Thường vụ Tỉnh ủy Sơn La, đồng ch&iacute; Nguyễn Hữu Đ&ocirc;ng, Ủy vi&ecirc;n Ban Chấp h&agrave;nh Trung ương Đảng, B&iacute; thư Tỉnh ủy, Trưởng Đo&agrave;n đại biểu Quốc hội tỉnh Sơn La đề xuất, kiến nghị Thủ tướng Ch&iacute;nh phủ xem x&eacute;t đầu tư theo h&igrave;nh thức đầu tư c&ocirc;ng v&agrave; giao UBND tỉnh Sơn La l&agrave; cơ quan chủ quản thực hiện đầu tư đoạn tuyến đường cao tốc H&ograve;a B&igrave;nh - Mộc Ch&acirc;u. Cho ph&eacute;p đầu tư n&acirc;ng cấp Cảng h&agrave;ng kh&ocirc;ng N&agrave; Sản l&agrave; s&acirc;n bay lưỡng dụng theo phương thức đối t&aacute;c c&ocirc;ng tư, loại hợp đồng BOT; giao UBND tỉnh Sơn La l&agrave; cơ quan nh&agrave; nước c&oacute; thẩm quyền triển khai dự &aacute;n. Chỉ đạo c&aacute;c bộ, ng&agrave;nh Trung ương phối hợp với Tập đo&agrave;n Điện lực Việt Nam nghi&ecirc;n cứu, xem x&eacute;t đề xuất mở rộng, n&acirc;ng c&ocirc;ng suất Nh&agrave; m&aacute;y Thủy điện Sơn La từ 2400 MW l&ecirc;n 3200MW. Cho ph&eacute;p tỉnh Sơn La được x&acirc;y dựng v&agrave; thực hiện Đề &aacute;n th&iacute; điểm kinh doanh t&iacute;n chỉ c&aacute;c-bon rừng để hỗ trợ tổ chức (cộng đồng, doanh nghiệp, hợp t&aacute;c x&atilde;&hellip;), c&aacute; nh&acirc;n v&agrave; người d&acirc;n trong bảo vệ vốn rừng, trồng rừng, chăm s&oacute;c bảo vệ diện t&iacute;ch rừng hiện c&oacute;. Chỉ đạo c&aacute;c Bộ, ng&agrave;nh Trung ương hỗ trợ tỉnh Sơn La ho&agrave;n thiện c&aacute;c ti&ecirc;u ch&iacute; của Khu du lịch quốc gia Mộc Ch&acirc;u, phấn đấu đến năm 2025 được c&ocirc;ng nhận l&agrave; Khu du lịch quốc gia; đề nghị Thủ tướng Ch&iacute;nh phủ ban h&agrave;nh cơ chế, ch&iacute;nh s&aacute;ch đặc th&ugrave; cho Khu du lịch Quốc gia Mộc Ch&acirc;u...</p>\r\n\r\n<p style="text-align:center"><strong>Cảm nhận, sẻ chia v&agrave; đồng h&agrave;nh</strong></p>\r\n\r\n<p style="text-align:justify">Tại cuộc l&agrave;m việc của Thủ tướng Ch&iacute;nh phủ với Ban Thường vụ Tỉnh ủy Sơn La thật ấm &aacute;p. C&aacute;c đồng ch&iacute; l&atilde;nh đạo c&aacute;c bộ, ng&agrave;nh Trung ương đ&atilde; thực sự quan t&acirc;m, cảm nhận về những n&eacute;t đổi mới m&agrave; Đảng bộ, ch&iacute;nh quyền v&agrave; nh&acirc;n d&acirc;n c&aacute;c d&acirc;n tộc Sơn La đ&atilde; bứt ph&aacute; trong những năm qua. Đồng thời, chia sẻ những kh&oacute; khăn của tỉnh Sơn La v&agrave; d&agrave;nh t&igrave;nh cảm đặc biệt, đồng thuận cao với những đề xuất của tỉnh trong lộ tr&igrave;nh ph&aacute;t triển.</p>\r\n\r\n<p style="text-align:justify">Bộ trưởng Bộ Lao động, Thương binh v&agrave; X&atilde; hội, Đ&agrave;o Ngọc Dung đ&aacute;nh gi&aacute;, thời gian qua tỉnh đ&atilde; &ldquo;thay da đổi thịt&rdquo; rất nhanh, tuy nhi&ecirc;n, tỷ lệ hộ ngh&egrave;o của tỉnh vẫn c&ograve;n đứng thứ 5 cả nước, trong đ&oacute; c&oacute; 95% l&agrave; đồng b&agrave;o d&acirc;n tộc thiểu số. Bởi vậy, c&ugrave;ng với ph&aacute;t triển kinh tế tỉnh Sơn La cần đặc biệt quan t&acirc;m c&ocirc;ng t&aacute;c giảm ngh&egrave;o với đồng b&agrave;o d&acirc;n tộc thiểu số. Bộ Lao động, TB&amp;XH sẽ phối hợp với tỉnh triển khai thực hiện tốt c&aacute;c chủ trương, ch&iacute;nh s&aacute;ch về lao động, việc l&agrave;m để n&acirc;ng cao đời sống của nh&acirc;n d&acirc;n.</p>\r\n\r\n<p style="text-align:justify">Bộ trưởng Bộ Giao th&ocirc;ng - Vận tải Nguyễn Văn Thể cho rằng: Việc x&acirc;y dựng tuyến cao tốc tuyến đường cao tốc H&ograve;a B&igrave;nh - Mộc Ch&acirc;u; đầu tư n&acirc;ng cấp Cảng h&agrave;ng kh&ocirc;ng N&agrave; Sản c&oacute; &yacute; nghĩa rất quan trọng thu h&uacute;t đầu tư, ph&aacute;t triển kinh tế - x&atilde; hội tr&ecirc;n địa b&agrave;n, nhất l&agrave; tỉnh miền n&uacute;i như Sơn La. Khi ho&agrave;n th&agrave;nh c&aacute;c dự &aacute;n Sơn La sẽ l&agrave; điểm đến hấp dẫn thu h&uacute;t với c&aacute;c nh&agrave; đầu tư. Bộ sẽ phối hợp chặt chẽ với tỉnh Sơn La v&agrave; c&aacute;c bộ, ng&agrave;nh li&ecirc;n quan nghi&ecirc;n cứu, tr&igrave;nh Thủ tướng xem x&eacute;t, để c&aacute;c dự &aacute;n sớm được triển khai...</p>\r\n\r\n<p><img alt="" src="/data/images/4scqfy1y(1).jpg" style="height:420px; width:630px" /></p>\r\n\r\n<p style="text-align:center"><em>Bộ trưởng Bộ C&ocirc;ng Thương ph&aacute;t biểu tại cuộc l&agrave;m việc&nbsp;<em>với Ban Thường vụ Tỉnh ủy Sơn La.</em></em></p>\r\n\r\n<p style="text-align:justify">C&ograve;n Bộ trưởng Bộ C&ocirc;ng Thương Nguyễn Hồng Di&ecirc;n, nhấn mạnh: Để ph&aacute;t huy tiềm năng, lợi thế v&agrave; ph&aacute;t triển hơn nữa, Sơn La cần b&aacute;m s&aacute;t c&aacute;c quy hoạch ng&agrave;nh, nhất l&agrave; quy hoạch ph&aacute;t triển c&ocirc;ng nghiệp v&agrave; thương mại của Bộ C&ocirc;ng Thương, t&iacute;ch hợp c&aacute;c quy hoạch đ&oacute; trong c&aacute;c quy hoạch ph&aacute;t triển của địa phương. Trong đ&oacute;, ch&uacute; &yacute; khai th&aacute;c kh&ocirc;ng gian ph&aacute;t triển từ c&aacute;c tuyến giao th&ocirc;ng mới mở, đặc biệt l&agrave; s&acirc;n bay lưỡng dụng N&agrave; Sản v&agrave; h&igrave;nh th&agrave;nh c&aacute;c khu c&ocirc;ng nghiệp, cụm c&ocirc;ng nghiệp, khu du lịch, khu đ&ocirc; thị v&agrave; trung t&acirc;m logistics...</p>\r\n\r\n<p style="text-align:center"><strong>Sớm đưa Sơn La th&agrave;nh động lực tăng trưởng của T&acirc;y Bắc</strong></p>\r\n\r\n<p style="text-align:justify">L&agrave;m việc với l&atilde;nh đạo tỉnh, Thủ tướng Ch&iacute;nh phủ Phạm Minh Ch&iacute;nh ấn tượng v&agrave; khẳng định: Sơn La l&agrave; tỉnh c&oacute; điều kiện thi&ecirc;n nhi&ecirc;n, con người, văn h&oacute;a - lịch sử tương đối thuận lợi cho ph&aacute;t triển kinh tế-x&atilde; hội, c&oacute; vị tr&iacute; quan trọng về quốc ph&ograve;ng, an ninh. Sơn La c&oacute; thi&ecirc;n nhi&ecirc;n h&ugrave;ng vĩ, xứ sở của n&uacute;i rừng, thảo nguy&ecirc;n; c&oacute; truyền thống lịch sử, văn h&oacute;a l&acirc;u đời, truyền thống c&aacute;ch mạng h&agrave;o h&ugrave;ng, nền văn h&oacute;a đậm đ&agrave; bản sắc T&acirc;y Bắc, truyền thống đo&agrave;n kết giữa c&aacute;c d&acirc;n tộc, người d&acirc;n y&ecirc;u nước, một l&ograve;ng đi theo Đảng, tin tưởng v&agrave;o đường lối, ch&iacute;nh s&aacute;ch, ph&aacute;p luật của Đảng v&agrave; Nh&agrave; nước. Sơn La t&iacute;ch cực triển khai Nghị quyết Đại hội đại biểu to&agrave;n quốc lần thứ XIII của Đảng, Nghị quyết Đại hội Đảng bộ tỉnh lần thứ XV v&agrave; cụ thể h&oacute;a bằng c&aacute;c đề &aacute;n, nghị quyết tr&ecirc;n c&aacute;c lĩnh vực. Mặc d&ugrave; chịu t&aacute;c động của dịch bệnh bệnh Covid-19, kinh tế Sơn La vẫn tăng trưởng; an ninh ch&iacute;nh trị, quốc ph&ograve;ng an - an ninh được giữ vững; thực hiện tốt c&ocirc;ng t&aacute;c đối ngoại...</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="" src="/data/images/i3dwxz14.jpg" style="height:420px; width:630px" /></p>\r\n\r\n<p style="text-align:center"><em>Thủ tướng Ch&iacute;nh phủ v&agrave; Đo&agrave;n c&ocirc;ng t&aacute;c kiểm tra thực địa tại đường băng Cảng h&agrave;ng kh&ocirc;ng N&agrave; Sản.</em></p>\r\n\r\n<p style="text-align:justify">Thủ tướng Ch&iacute;nh phủ cũng b&agrave;y tỏ trăn trở đối với Sơn La khi hạ tầng cơ sở c&ograve;n kh&oacute; khăn, nhất l&agrave; hệ thống giao th&ocirc;ng việc li&ecirc;n kết giữa c&aacute;c v&ugrave;ng; ph&aacute;t triển chưa đồng bộ, tương xứng với tiềm năng lợi thế của tỉnh. Việc huy động c&aacute;c nguồn lực đầu tư ph&aacute;t triển kinh tế - x&atilde; hội c&ograve;n hạn chế; đời sống một bộ phận đồng b&agrave;o c&ograve;n kh&oacute; khăn, thu nhập b&igrave;nh qu&acirc;n đầu người c&ograve;n chưa cao...</p>\r\n\r\n<p style="text-align:justify">Thủ tướng đề nghị tỉnh Sơn La ph&acirc;n t&iacute;ch r&otilde; nguy&ecirc;n nh&acirc;n để c&oacute; những giải ph&aacute;p khắc phục hiệu quả, bền vững; tỉnh cần mạnh mẽ hơn, vững v&agrave;ng hơn; &ldquo;Tư tưởng phải th&ocirc;ng, suy nghĩ phải kỹ, quyết t&acirc;m phải cao, nỗ lực phải lớn, h&agrave;nh động quyết liệt, hiệu quả, c&oacute; trọng t&acirc;m trọng điểm, l&agrave;m việc n&agrave;o dứt việc đ&oacute;&rdquo;; t&iacute;ch cực huy động sức mạnh của nh&acirc;n d&acirc;n, doanh nghiệp tăng nguồn lực đầu tư cho ph&aacute;t triển; phối hợp với c&aacute;c bộ, ng&agrave;nh Trung ương th&aacute;o gỡ những kh&oacute; khăn, vướng mắc...</p>\r\n\r\n<p style="text-align:center"><img alt="" src="/data/images/q31sv4fg.jpg" style="height:420px; width:630px" /></p>\r\n\r\n<p style="text-align:center"><em>Thủ tướng Ch&iacute;nh phủ l&agrave;m việc tại Cảng h&agrave;ng kh&ocirc;ng N&agrave; Sản, huyện Mai Sơn</em></p>\r\n\r\n<p style="text-align:justify">Về quan điểm chỉ đạo thời gian tới, Thủ tướng y&ecirc;u cầu tỉnh Sơn La tiếp tục qu&aacute;n triệt, thực hiện quyết liệt đồng bộ, hiệu quả Nghị quyết Đại hội đại biểu to&agrave;n quốc lần thứ XIII của Đảng, Nghị quyết Đại hội Đảng bộ tỉnh lần thứ XV, c&aacute;c nghị quyết chuy&ecirc;n đề của Trung ương, trong đ&oacute; c&oacute; c&aacute;c nghị quyết sắp được ban h&agrave;nh về đất đai, về n&ocirc;ng nghiệp, n&ocirc;ng d&acirc;n, n&ocirc;ng th&ocirc;n v&agrave; về kinh tế tập thể, Nghị quyết số 11-NQ/TW của Bộ Ch&iacute;nh trị về phương hướng ph&aacute;t triển kinh tế - x&atilde; hội v&agrave; bảo đảm quốc ph&ograve;ng, an ninh v&ugrave;ng trung du v&agrave; miền n&uacute;i Bắc bộ đến năm 2030, tầm nh&igrave;n đến năm 2045.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong c&ocirc;ng việc, tỉnh Sơn La cần b&aacute;m s&aacute;t thực tiễn, xuất ph&aacute;t từ thực tiễn, t&ocirc;n trọng thực tiễn; ki&ecirc;n định mục ti&ecirc;u xuy&ecirc;n suốt nhưng phải linh hoạt, s&aacute;ng tạo, bảo đảm hiệu quả. Thực hiện c&ocirc;ng việc c&oacute; trọng t&acirc;m, trọng điểm, chọn những việc c&oacute; t&aacute;c động lan tỏa, l&agrave;m đến đ&acirc;u dứt điểm đến đ&oacute;, việc n&agrave;o xong việc đ&oacute;. Trong ph&aacute;t triển hạ tầng, Thủ tướng gợi &yacute;, tỉnh phải tập trung cho x&acirc;y dựng tuyến cao tốc H&ograve;a B&igrave;nh - Mộc Ch&acirc;u v&agrave; n&acirc;ng cấp, cải tạo s&acirc;n bay N&agrave; Sản.</p>\r\n\r\n<p style="text-align:justify">Thủ tướng lưu &yacute; tỉnh ch&uacute; trọng tổng kết, đ&aacute;nh gi&aacute; b&agrave;i học kinh nghiệm, tồn tại, hạn chế, bất cập, ph&aacute;t huy kết quả đạt được, m&ocirc; h&igrave;nh hay, c&aacute;ch l&agrave;m hiệu quả. Ph&aacute;t huy mạnh mẽ tinh thần đo&agrave;n kết, tự lực, tự cường, vươn l&ecirc;n mạnh mẽ, kh&ocirc;ng tr&ocirc;ng chờ, kh&ocirc;ng ỷ lại, n&eacute; tr&aacute;nh; h&agrave;nh động quyết liệt hơn, nỗ lực hơn, chủ động hơn, s&aacute;ng tạo hơn, phấn đấu đạt kết quả cao nhất.</p>\r\n\r\n<p style="text-align:justify">Về c&aacute;c đề xuất, kiến nghị của Sơn La, Thủ tướng cơ bản đồng &yacute; đối với c&aacute;c đề xuất, kiến nghị của tỉnh; đồng thời y&ecirc;u cầu c&aacute;c bộ, ng&agrave;nh, phối hợp, b&agrave;n bạc với Sơn La giải quyết theo thầm quyền. Những vấn đề vượt thẩm quyền th&igrave; tổng hợp, đề xuất, tr&igrave;nh Ch&iacute;nh phủ v&agrave; cấp c&oacute; thẩm quyền xem x&eacute;t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="" src="/data/images/n5oenn1u.jpg" style="height:420px; width:630px" /></p>\r\n\r\n<p style="text-align:center"><em>Phối cảnh Dự &aacute;n đường cao tốc H&ograve;a B&igrave;nh - Mộc Ch&acirc;u.</em></p>\r\n\r\n<p style="text-align:right">Ảnh: PV</p>\r\n\r\n<p style="text-align:justify">Theo đ&oacute;, Thủ tướng đồng &yacute; chuyển h&igrave;nh thức đầu tư đoạn tuyến cao tốc Ho&agrave; B&igrave;nh - Mộc Ch&acirc;u từ PPP sang h&igrave;nh thức đầu tư c&ocirc;ng, thống nhất giao UBND tỉnh l&agrave; cơ quan chủ quản triển khai đoạn tuyến cao tốc Mộc Ch&acirc;u &ndash; Sơn La. Về n&acirc;ng cấp, cải tạo cảng h&agrave;ng kh&ocirc;ng N&agrave; Sản, Thủ tướng giao tỉnh Sơn La phối hợp c&aacute;c bộ, ng&agrave;nh triển khai c&aacute;c thủ tục, b&aacute;o c&aacute;o, đề xuất c&aacute;c cấp c&oacute; thẩm quyền với tinh thần l&agrave;m nhanh, quyết liệt...</p>\r\n\r\n<p style="text-align:justify">Thủ tướng mong muốn tỉnh Sơn La giữ vững bản lĩnh, đo&agrave;n kết, thống nhất vượt qua kh&oacute; khăn, th&aacute;ch thức, tập trung nguồn lực giải quyết c&aacute;c điểm nghẽn, c&aacute;c vấn đề được thảo luận tại cuộc l&agrave;m việc. Thủ tướng tin tưởng tỉnh Sơn La sẽ l&agrave;m được, l&agrave;m tốt trong những năm tới, tự lực, tự cường, tự tin để đi l&ecirc;n từ b&agrave;n tay, khối &oacute;c, khung trời, mảnh đất của m&igrave;nh. C&aacute;c Bộ, ng&agrave;nh &quot;xắn tay&quot; đồng h&agrave;nh, hỗ trợ, phối hợp chặt chẽ với tỉnh Sơn La v&agrave; c&aacute;c tỉnh T&acirc;y Bắc, coi việc của tỉnh như việc của m&igrave;nh để th&aacute;o gỡ c&aacute;c kh&oacute; khăn, vướng mắc, tạo nguồn lực, động lực cho c&aacute;c địa phương ph&aacute;t triển.</p>\r\n', '2022-05-31 14:22:28', 64, '/data/files/n5oenn1u.jpg', '', 1, 33, 0, 0, '', 0, '', 'baosonla.org.vn', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1302, 'Đại biểu Quốc hội tỉnh Sơn La tham gia ý kiến vào Dự án Luật sửa đổi, bổ sung một số điều của Luật Sở hữu trí tuệ', 'Tiếp tục chương trình Kỳ họp thứ ba, sáng 31/5, Quốc hội thảo luận ở hội trường về một số nội dung còn ý kiến khác nhau của Dự án Luật sửa đổi, bổ sung một số điều của Luật Sở hữu trí tuệ.', '<p style="text-align:center"><img alt="" src="/data/images/zmfe24ie.jpg" style="height:420px; width:630px" /></p>\r\n\r\n<p style="text-align:center"><em>Đồng ch&iacute; Vi Đức Thọ, đại biểu Quốc hội tỉnh Sơn La tham gia &yacute; kiến</em></p>\r\n\r\n<p style="text-align:justify">Theo c&aacute;c đại biểu Quốc hội, việc kh&ocirc;ng thu hẹp đối tượng xử phạt vi phạm h&agrave;nh ch&iacute;nh trong lĩnh vực sở hữu tr&iacute; tuệ l&agrave; hợp l&yacute;, bởi, ưu điểm của việc xử l&yacute; vi phạm bằng biện ph&aacute;p h&agrave;nh ch&iacute;nh l&agrave; hồ sơ, thủ tục đơn giản, nhanh ch&oacute;ng, chi ph&iacute; thấp nhằm kịp thời ngăn chặn, chấm dứt h&agrave;nh vi x&acirc;m phạm quyền sở hữu tr&iacute; tuệ.</p>\r\n\r\n<p style="text-align:justify">Trong điều kiện hiện nay, nếu loại bỏ biện ph&aacute;p xử phạt h&agrave;nh ch&iacute;nh đối với h&agrave;nh vi x&acirc;m phạm quyền li&ecirc;n quan đến s&aacute;ng chế, kiểu d&aacute;ng c&ocirc;ng nghiệp, thiết kế bố tr&iacute; sẽ dẫn đến khoảng trống ph&aacute;p luật trong việc kịp thời ph&aacute;t hiện, ngăn chặn, xử l&yacute; h&agrave;nh vi vi phạm ph&aacute;p luật trong lĩnh vực sở hữu c&ocirc;ng nghiệp, tăng th&ecirc;m g&aacute;nh nặng cho hệ thống T&ograve;a &aacute;n hiện đang qu&aacute; tải, cũng như tạo th&ecirc;m th&aacute;ch thức cho đương sự khi sử dụng biện ph&aacute;p tố tụng d&acirc;n sự.</p>\r\n\r\n<p style="text-align:justify">Hiệp định Đối t&aacute;c to&agrave;n diện v&agrave; tiến bộ xuy&ecirc;n Th&aacute;i B&igrave;nh Dương (CPTPP), Hiệp định Thương mại tự do Việt Nam - Li&ecirc;n minh ch&acirc;u &Acirc;u (EVFTA) cũng kh&ocirc;ng y&ecirc;u cầu hạn chế việc &aacute;p dụng biện ph&aacute;p xử phạt h&agrave;nh ch&iacute;nh đối với h&agrave;nh vi x&acirc;m phạm quyền sở hữu c&ocirc;ng nghiệp trong luật của quốc gia th&agrave;nh vi&ecirc;n m&agrave; thậm ch&iacute; c&ograve;n y&ecirc;u cầu tăng cường c&aacute;c biện ph&aacute;p xử l&yacute; bằng chế t&agrave;i h&igrave;nh sự đối với một số h&agrave;nh vi vi phạm trong lĩnh vực sở hữu tr&iacute; tuệ.</p>\r\n\r\n<p style="text-align:justify">Nhất tr&iacute; với sự cần thiết ban h&agrave;nh Luật sửa đổi, bổ sung một số điều của Luật Sở hữu tr&iacute; tuệ, đại biểu Quốc hội tỉnh Sơn La Vi Đức Thọ tham gia &yacute; kiến v&agrave;o Dự &aacute;n luật:</p>\r\n\r\n<p style="text-align:justify"><strong>Tại&nbsp;</strong><strong>Điều 1, Khoản 1, điểm e, sửa đổi bổ sung khoản 20,</strong>&nbsp;đề nghị xem x&eacute;t quy định r&otilde; ti&ecirc;u ch&iacute; về nh&atilde;n hiệu nổi tiếng, quy định đang chung chung, kh&ocirc;ng c&oacute; ti&ecirc;u ch&iacute;, kh&ocirc;ng lượng h&oacute;a đ&aacute;nh gi&aacute; được. Thậm ch&iacute; nội h&agrave;m &quot;nổi tiếng&quot; đ&atilde; phản &aacute;nh sự tương đối, kh&oacute; đ&aacute;nh gi&aacute; về mặt ph&aacute;p luật v&agrave; quản l&yacute; nh&agrave; nước. Mặt kh&aacute;c, nh&atilde;n hiệu gắn với h&agrave;ng h&oacute;a, sản phẩm v&agrave; người ti&ecirc;u d&ugrave;ng ở nghĩa rộng hay hẹp đối với bất kể loại h&agrave;ng h&oacute;a được x&aacute;c định bằng gi&aacute; trị kinh tế, do đ&oacute; sử dụng &quot;c&ocirc;ng ch&uacute;ng&quot; tại nội dung n&agrave;y l&agrave; chưa ph&ugrave; hợp.</p>\r\n\r\n<p style="text-align:justify">Đề nghị Quốc hội xem x&eacute;t r&agrave; so&aacute;t, l&agrave;m r&otilde; nội h&agrave;m v&agrave; &yacute; nghĩa về mặt ph&aacute;p luật, t&aacute;c động trong thực tiễn khi luật c&oacute; hiệu lực của một số thuật ngữ được sử dụng nhiều lần tại c&aacute;c điều khoản sửa đổi bổ sung lần n&agrave;y. Cụ thể l&agrave;: Cụm từ &quot;Sử dụng hợp l&yacute;&quot;&#39;; &quot;Sao ch&eacute;p hợp l&yacute;&quot; quy định tại khoản 9 Điều 1 của dự thảo; quy định về sửa đổi, bổ sung Điều 25. Tại điểm b, c, e khoản 1, Điều 25; điểm c Khoản 1, Điều 32. Đề nghị l&agrave;m r&otilde; nội h&agrave;m &quot;hợp l&yacute;&quot; l&agrave; ở mức độ n&agrave;o để kh&ocirc;ng x&acirc;m phạm quyền t&aacute;c giả? Đồng thời đề nghị gh&eacute;p 2 điểm a, b th&agrave;nh 1 điểm, do nội dung tương tự nhau. Cụm từ &quot;thiệt hại một c&aacute;ch bất hơp l&yacute;&quot; tại khoản 2, Điều 25 v&agrave; khoản 2, Điều 26, l&agrave;m r&otilde; nội h&agrave;m của thiệt hại bất hợp l&yacute;. Nếu quy định như tr&ecirc;n mặc nhi&ecirc;n thừa nhận c&oacute; sự thiệt hại khi sử dụng t&aacute;c phẩm, chỉ l&agrave; mức độ kh&aacute;c nhau để đ&aacute;nh gi&aacute; c&oacute; hợp l&yacute; hay kh&ocirc;ng. Điều n&agrave;y m&acirc;u thuẫn với nguy&ecirc;n tắc bảo hộ, bảo vệ t&aacute;c giả t&aacute;c phẩm được quy định trong luật. Đề nghị xem x&eacute;t l&agrave;m r&otilde; nội dung, nội h&agrave;m của mục n&agrave;y.</p>\r\n\r\n<p style="text-align:justify"><strong>Tại khoản 4: Bổ sung Điều 12a v&agrave;o trước Điều 13 tại Mục 1 Chương I&nbsp; Phần thứ 2,</strong>&nbsp;đề nghị quy định r&otilde; cụm từ &quot;thống nhất &yacute; ch&iacute;&quot; tại Khoản 3, đề nghị quy định r&otilde; nội dung n&agrave;y cần thống nhất bằng văn bản nhằm x&aacute;c định r&otilde; thỏa thuận, giữa c&aacute;c đồng t&aacute;c giả, l&agrave;m cơ sở thực hiện c&aacute;c quyền lợi kh&aacute;c, tr&aacute;nh tranh chấp, kh&ocirc;ng c&oacute; căn cứ giải quyết tranh chấp ph&aacute;t siinh nếu &quot;thỏa thuận, thống nhất bằng &yacute; ch&iacute;&quot;.</p>\r\n\r\n<p style="text-align:justify">Khoản 4, Điều 19, đề nghị xem x&eacute;t ph&acirc;n biệt, t&aacute;ch r&otilde; quyền bảo vệ sự to&agrave;n vẹn của t&aacute;c phẩm của t&aacute;c giả v&agrave; hậu quả của sự kh&ocirc;ng to&agrave;n vẹn g&acirc;y ra. Tại Khoản 6, 7, Điều 28, đề nghị xem x&eacute;t quy định r&otilde; nội h&agrave;m cụm từ &quot;<strong>khi biết hoặc c&oacute; cơ sở để biết</strong>&quot;, để l&agrave;m r&otilde; việc&nbsp;<strong>v&ocirc; &yacute;</strong>&nbsp;hay&nbsp;<strong>cố &yacute;&nbsp;</strong>vi x&acirc;m phạm quyền t&aacute;c giả.</p>\r\n\r\n<p style="text-align:justify">Chiều 31/5, c&aacute;c đại biểu Quốc hội thảo luận ở tổ về c&aacute;c nội dung: Dự &aacute;n Luật Ph&ograve;ng, chống bạo lực gia đ&igrave;nh (sửa đổi); Dự &aacute;n Luật Thực hiện d&acirc;n chủ ở cơ sở.</p>\r\n', '2022-06-01 08:05:09', 83, '/data/files/zmfe24ie.jpg', '', 1, 33, 0, 0, '', 0, '', 'baosonla.org.vn', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1305, 'THÔNG CÁO BÁO CHÍ SỐ 19, KỲ HỌP THỨ 3, QUỐC HỘI KHÓA XV', 'Thứ Năm, ngày 16/6, Quốc hội tiếp tục ngày làm việc thứ 19, cũng là ngày làm việc cuối cùng của Kỳ họp thứ 3, Quốc hội khóa XV tại Nhà Quốc hội dưới sự chủ trì của Chủ tịch Quốc hội Vương Đình Huệ.', '<p style="text-align:center"><img alt="" src="/data/images/ctqh-thumb-1655377604171_vdnw.jpg" style="height:363px; width:648px" /></p>\r\n\r\n<p style="text-align:center"><em>Quang cảnh phi&ecirc;n họp&nbsp;</em></p>\r\n\r\n<p style="text-align:justify">Buổi s&aacute;ng, dưới sự điều h&agrave;nh của Ph&oacute; Chủ tịch Quốc hội Nguyễn Khắc Định, Quốc hội biểu quyết th&ocirc;ng qua những nội dung sau:</p>\r\n\r\n<p style="text-align:justify">Quốc hội nghe Chủ nhiệm Ủy ban Tư ph&aacute;p của Quốc hội L&ecirc; Thị Nga tr&igrave;nh b&agrave;y B&aacute;o c&aacute;o giải tr&igrave;nh, tiếp thu, chỉnh l&yacute; dự thảo Nghị quyết th&iacute; điểm m&ocirc; h&igrave;nh tổ chức hoạt động lao động, hướng nghiệp, dạy nghề cho phạm nh&acirc;n ngo&agrave;i trại giam; sau đ&oacute;, Quốc hội biểu quyết th&ocirc;ng qua Nghị quyết bằng h&igrave;nh thức biểu quyết điện tử, kết quả như sau: c&oacute; 480 đại biểu tham gia biểu quyết (bằng 96,39% tổng số đại biểu Quốc hội), trong đ&oacute; c&oacute; 467 đại biểu t&aacute;n th&agrave;nh (bằng 93,78% tổng số đại biểu Quốc hội); c&oacute; 10 đại biểu kh&ocirc;ng t&aacute;n th&agrave;nh (bằng 2,01% tổng số đại biểu Quốc hội); c&oacute; 3 đại biểu kh&ocirc;ng biểu quyết (bằng 0,60% tổng số đại biểu Quốc hội).</p>\r\n\r\n<p style="text-align:justify">Quốc hội nghe Tổng Thư k&yacute; Quốc hội B&ugrave;i Văn Cường tr&igrave;nh b&agrave;y B&aacute;o c&aacute;o giải tr&igrave;nh, tiếp thu, chỉnh l&yacute; dự thảo Luật sửa đổi, bổ sung một số điều của Luật Sở hữu tr&iacute; tuệ; sau đ&oacute;, Quốc hội biểu quyết th&ocirc;ng qua Điều 25, Điều 86a v&agrave; to&agrave;n bộ Luật bằng h&igrave;nh thức biểu quyết điện tử, kết quả như sau:</p>\r\n\r\n<p style="text-align:justify">Về Điều 25 quy định về: C&aacute;c trường hợp ngoại lệ kh&ocirc;ng x&acirc;m phạm quyền t&aacute;c giả: c&oacute; 480 đại biểu tham gia biểu quyết (bằng 96,39% tổng số đại biểu Quốc hội), trong đ&oacute; c&oacute; 478 đại biểu t&aacute;n th&agrave;nh (bằng 95,98% tổng số đại biểu Quốc hội); c&oacute; 2 đại biểu kh&ocirc;ng t&aacute;n th&agrave;nh (bằng 0,40% tổng số đại biểu Quốc hội).</p>\r\n\r\n<p style="text-align:justify">Về Điều 86a quy định về: Quyền đăng k&yacute; s&aacute;ng chế, kiểu d&aacute;ng c&ocirc;ng nghiệp, thiết kế bố tr&iacute; l&agrave; kết quả của nhiệm vụ khoa học v&agrave; c&ocirc;ng nghệ sử dụng ng&acirc;n s&aacute;ch nh&agrave; nước: c&oacute; 477 đại biểu tham gia biểu quyết (bằng 95,78% tổng số đại biểu Quốc hội), trong đ&oacute; c&oacute; 476 đại biểu t&aacute;n th&agrave;nh (bằng 95,58% tổng số đại biểu Quốc hội); c&oacute; 1 đại biểu kh&ocirc;ng biểu quyết (bằng 0,20% tổng số đại biểu Quốc hội).</p>\r\n\r\n<p style="text-align:justify">Về to&agrave;n bộ Luật: c&oacute; 477 đại biểu tham gia biểu quyết (bằng 95,78% tổng số đại biểu Quốc hội), trong đ&oacute; c&oacute; 476 đại biểu t&aacute;n th&agrave;nh (bằng 95,58% tổng số đại biểu Quốc hội); c&oacute; 1 đại biểu kh&ocirc;ng biểu quyết (bằng 0,20% tổng số đại biểu Quốc hội).</p>\r\n\r\n<p style="text-align:justify">Dưới sự điều h&agrave;nh của Ph&oacute; Chủ tịch Quốc hội Nguyễn Đức Hải, Quốc hội biểu quyết th&ocirc;ng qua những nội dung sau:</p>\r\n\r\n<p style="text-align:justify">Quốc hội nghe Chủ nhiệm Ủy ban Kinh tế của Quốc hội Vũ Hồng Thanh tr&igrave;nh b&agrave;y B&aacute;o c&aacute;o giải tr&igrave;nh, tiếp thu, chỉnh l&yacute; dự thảo Luật Kinh doanh bảo hiểm (sửa đổi); sau đ&oacute; Quốc hội biểu quyết th&ocirc;ng qua Luật bằng h&igrave;nh thức biểu quyết điện tử, kết quả như sau: c&oacute; 474 đại biểu tham gia biểu quyết (bằng 95,18% tổng số đại biểu Quốc hội), trong đ&oacute; c&oacute; 469 đại biểu t&aacute;n th&agrave;nh (bằng 94,18% tổng số đại biểu Quốc hội); c&oacute; 5 đại biểu kh&ocirc;ng t&aacute;n th&agrave;nh (bằng 1% tổng số đại biểu Quốc hội).</p>\r\n\r\n<p style="text-align:justify">Quốc hội nghe Chủ nhiệm Ủy ban T&agrave;i ch&iacute;nh, Ng&acirc;n s&aacute;ch của Quốc hội Nguyễn Ph&uacute; Cường tr&igrave;nh b&agrave;y B&aacute;o c&aacute;o giải tr&igrave;nh, tiếp thu, chỉnh l&yacute; dự thảo Nghị quyết về th&iacute; điểm một số cơ chế, ch&iacute;nh s&aacute;ch đặc th&ugrave; ph&aacute;t triển tỉnh Kh&aacute;nh H&ograve;a; sau đ&oacute;, Quốc hội biểu quyết th&ocirc;ng qua Nghị quyết bằng h&igrave;nh thức biểu quyết điện tử, kết quả như sau: c&oacute; 480 đại biểu tham gia biểu quyết (bằng 96,39% tổng số đại biểu Quốc hội), trong đ&oacute; c&oacute; 477 đại biểu t&aacute;n th&agrave;nh (bằng 95,78% tổng số đại biểu Quốc hội); c&oacute; 3 đại biểu kh&ocirc;ng biểu quyết (bằng 0,60% tổng số đại biểu Quốc hội).</p>\r\n\r\n<p style="text-align:justify">Quốc hội nghe Chủ nhiệm Ủy ban Kinh tế của Quốc hội Vũ Hồng Thanh tr&igrave;nh b&agrave;y B&aacute;o c&aacute;o giải tr&igrave;nh, tiếp thu, chỉnh l&yacute; dự thảo Nghị quyết về chủ trương đầu tư Dự &aacute;n x&acirc;y dựng đường V&agrave;nh đai 4 - V&ugrave;ng Thủ đ&ocirc; H&agrave; Nội; sau đ&oacute;, Quốc hội biểu quyết th&ocirc;ng qua Nghị quyết bằng h&igrave;nh thức biểu quyết điện tử, kết quả như sau: c&oacute; 475 đại biểu tham gia biểu quyết (bằng 95,38% tổng số đại biểu Quốc hội), trong đ&oacute; c&oacute; 474 đại biểu t&aacute;n th&agrave;nh (bằng 95,18% tổng số đại biểu Quốc hội); c&oacute; 1 đại biểu kh&ocirc;ng biểu quyết (bằng 0,20% tổng số đại biểu Quốc hội).</p>\r\n\r\n<p style="text-align:justify">Quốc hội nghe Chủ nhiệm Ủy ban Kinh tế của Quốc hội Vũ Hồng Thanh tr&igrave;nh b&agrave;y B&aacute;o c&aacute;o giải tr&igrave;nh, tiếp thu, chỉnh l&yacute; dự thảo Nghị quyết về chủ trương đầu tư Dự &aacute;n x&acirc;y dựng đường V&agrave;nh đai 3 Th&agrave;nh phố Hồ Ch&iacute; Minh; sau đ&oacute;, Quốc hội biểu quyết th&ocirc;ng qua Nghị quyết bằng h&igrave;nh thức biểu quyết điện tử, kết quả như sau: c&oacute; 478 đại biểu tham gia biểu quyết (bằng 95,98% tổng số đại biểu Quốc hội), trong đ&oacute; c&oacute; 475 đại biểu t&aacute;n th&agrave;nh (bằng 95,38% tổng số đại biểu Quốc hội); c&oacute; 2 đại biểu kh&ocirc;ng t&aacute;n th&agrave;nh (bằng 0,40% tổng số đại biểu Quốc hội); c&oacute; 1 đại biểu kh&ocirc;ng biểu quyết (bằng 0,20% tổng số đại biểu Quốc hội).</p>\r\n\r\n<p style="text-align:justify">Quốc hội nghe Chủ nhiệm Ủy ban Kinh tế của Quốc hội Vũ Hồng Thanh tr&igrave;nh b&agrave;y B&aacute;o c&aacute;o giải tr&igrave;nh, tiếp thu, chỉnh l&yacute; dự thảo Nghị quyết về chủ trương đầu tư Dự &aacute;n đầu tư x&acirc;y dựng đường bộ cao tốc Kh&aacute;nh H&ograve;a - Bu&ocirc;n Ma Thuột giai đoạn 1; sau đ&oacute;, Quốc hội biểu quyết th&ocirc;ng qua Nghị quyết bằng h&igrave;nh thức biểu quyết điện tử, kết quả như sau: c&oacute; 468 đại biểu tham gia biểu quyết (bằng 93,98% tổng số đại biểu Quốc hội), trong đ&oacute; c&oacute; 467 đại biểu t&aacute;n th&agrave;nh (bằng 93,78% tổng số đại biểu Quốc hội); c&oacute; 1 đại biểu kh&ocirc;ng t&aacute;n th&agrave;nh (bằng 0,20% tổng số đại biểu Quốc hội).</p>\r\n\r\n<p style="text-align:justify">Quốc hội nghe Chủ nhiệm Ủy ban Kinh tế của Quốc hội Vũ Hồng Thanh tr&igrave;nh b&agrave;y B&aacute;o c&aacute;o giải tr&igrave;nh, tiếp thu, chỉnh l&yacute; dự thảo Nghị quyết về chủ trương đầu tư Dự &aacute;n đầu tư x&acirc;y dựng đường bộ cao tốc Bi&ecirc;n H&ograve;a - Vũng T&agrave;u giai đoạn 1; sau đ&oacute;, Quốc hội biểu quyết th&ocirc;ng qua Nghị quyết bằng h&igrave;nh thức biểu quyết điện tử, kết quả như sau: c&oacute; 478 đại biểu tham gia biểu quyết (bằng 95,98% tổng số đại biểu Quốc hội), trong đ&oacute; c&oacute; 475 đại biểu t&aacute;n th&agrave;nh (bằng 95,38% tổng số đại biểu Quốc hội); c&oacute; 2 đại biểu kh&ocirc;ng t&aacute;n th&agrave;nh (bằng 0,40% tổng số đại biểu Quốc hội); c&oacute; 1 đại biểu kh&ocirc;ng biểu quyết (bằng 0,20% tổng số đại biểu Quốc hội).</p>\r\n\r\n<p style="text-align:justify">Quốc hội nghe Chủ nhiệm Ủy ban Kinh tế của Quốc hội Vũ Hồng Thanh tr&igrave;nh b&agrave;y B&aacute;o c&aacute;o giải tr&igrave;nh, tiếp thu, chỉnh l&yacute; dự thảo Nghị quyết về chủ trương đầu tư Dự &aacute;n đầu tư x&acirc;y dựng đường bộ cao tốc Ch&acirc;u Đốc - Cần Thơ - S&oacute;c Trăng giai đoạn 1; sau đ&oacute;, Quốc hội biểu quyết th&ocirc;ng qua Nghị quyết bằng h&igrave;nh thức biểu quyết điện tử, kết quả như sau: c&oacute; 479 đại biểu tham gia biểu quyết (bằng 96,18% tổng số đại biểu Quốc hội), trong đ&oacute; c&oacute; 478 đại biểu t&aacute;n th&agrave;nh (bằng 95,98% tổng số đại biểu Quốc hội); c&oacute; 1 đại biểu kh&ocirc;ng t&aacute;n th&agrave;nh (bằng 0,20% tổng số đại biểu Quốc hội).</p>\r\n\r\n<p style="text-align:justify">Buổi chiều, Quốc hội họp phi&ecirc;n bế mạc. Phi&ecirc;n họp được truyền h&igrave;nh, ph&aacute;t thanh trực tiếp tr&ecirc;n s&oacute;ng của Đ&agrave;i Truyền h&igrave;nh Việt Nam, Đ&agrave;i Tiếng n&oacute;i Việt Nam, Truyền h&igrave;nh Quốc hội Việt Nam.</p>\r\n\r\n<p style="text-align:justify">Dưới sự điều h&agrave;nh của Ph&oacute; Chủ tịch Thường trực Quốc hội Trần Thanh Mẫn, Quốc hội biểu quyết th&ocirc;ng qua những nội dung sau:</p>\r\n\r\n<p style="text-align:justify">Quốc hội nghe Chủ nhiệm Ủy ban Kinh tế của Quốc hội Vũ Hồng Thanh tr&igrave;nh b&agrave;y B&aacute;o c&aacute;o giải tr&igrave;nh, tiếp thu, chỉnh l&yacute; v&agrave; Dự thảo Nghị quyết về tiếp tục tăng cường hiệu lực, hiệu quả thực hiện ch&iacute;nh s&aacute;ch, ph&aacute;p luật về quy hoạch v&agrave; một số giải ph&aacute;p th&aacute;o gỡ kh&oacute; khăn, vướng mắc, đẩy nhanh tiến độ lập v&agrave; n&acirc;ng cao chất lượng quy hoạch thời kỳ 2021-2030; sau đ&oacute;, Quốc hội biểu quyết th&ocirc;ng qua Nghị quyết bằng h&igrave;nh thức biểu quyết điện tử, kết quả như sau: c&oacute; 482 đại biểu tham gia biểu quyết (bằng 96,79% tổng số đại biểu Quốc hội), trong đ&oacute; c&oacute; 475 đại biểu t&aacute;n th&agrave;nh (bằng 95,38% tổng số đại biểu Quốc hội); c&oacute; 3 đại biểu kh&ocirc;ng t&aacute;n th&agrave;nh (bằng 0,60% tổng số đại biểu Quốc hội); c&oacute; 4 đại biểu kh&ocirc;ng biểu quyết (bằng 0,80% tổng số đại biểu Quốc hội).</p>\r\n\r\n<p style="text-align:justify">Quốc hội nghe Trưởng Ban C&ocirc;ng t&aacute;c đại biểu Nguyễn Thị Thanh tr&igrave;nh b&agrave;y B&aacute;o c&aacute;o giải tr&igrave;nh, tiếp thu, chỉnh l&yacute; v&agrave; Dự thảo Nghị quyết về hoạt động chất vấn tại Kỳ họp thứ 3, Quốc hội kh&oacute;a XV; sau đ&oacute;, Quốc hội biểu quyết th&ocirc;ng qua Nghị quyết bằng h&igrave;nh thức biểu quyết điện tử, kết quả như sau: c&oacute; 480 đại biểu tham gia biểu quyết (bằng 96,39% tổng số đại biểu Quốc hội), trong đ&oacute; c&oacute; 478 đại biểu t&aacute;n th&agrave;nh (bằng 95,98% tổng số đại biểu Quốc hội); c&oacute; 2 đại biểu kh&ocirc;ng biểu quyết (bằng 0,40% tổng số đại biểu Quốc hội).</p>\r\n\r\n<p style="text-align:justify">Quốc hội nghe Tổng Thư k&yacute; Quốc hội B&ugrave;i Văn Cường tr&igrave;nh b&agrave;y B&aacute;o c&aacute;o giải tr&igrave;nh, tiếp thu, chỉnh l&yacute; v&agrave; Dự thảo Nghị quyết Kỳ họp thứ 3, Quốc hội kh&oacute;a XV; sau đ&oacute;, Quốc hội biểu quyết th&ocirc;ng qua Nghị quyết bằng h&igrave;nh thức biểu quyết điện tử, kết quả như sau: c&oacute; 479 đại biểu tham gia biểu quyết (bằng 96,18% tổng số đại biểu Quốc hội), trong đ&oacute; c&oacute; 476 đại biểu t&aacute;n th&agrave;nh (bằng 95,58% tổng số đại biểu Quốc hội); c&oacute; 3 đại biểu kh&ocirc;ng biểu quyết (bằng 0,60% tổng số đại biểu Quốc hội).</p>\r\n\r\n<p style="text-align:justify">Tiếp đ&oacute;, Quốc hội nghe Chủ tịch Quốc hội Vương Đ&igrave;nh Huệ ph&aacute;t biểu bế mạc Kỳ họp thứ 3, Quốc hội kh&oacute;a XV.</p>\r\n', '2022-06-17 14:40:28', 48, '/data/files/ctqh-thumb-1655377604171_vdnw.jpg', '', 1, 33, 0, 0, '', 0, '', 'http://mattran.org.vn', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
INSERT INTO `tin_tuc` VALUES (1356, 'Mai Sơn tổ chức Hội nghị tập huấn quán triệt, triển khai các văn bản chỉ đạo về &quot;Xây dựng xã hội học tập giai đoạn 2021-2030”. ', '', '<p style="text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp;Thực hiện Kế hoạch số 112/KH-HKH ng&agrave;y 15/8/2022 của HKH tỉnh Sơn La, Kế hoạch số 52/KH_HKH ng&agrave;y 03/10/2022 của Hội Khuyến học huyện Mai Sơn, ng&agrave;y 13, 14/10/2022 Hội Khuyến học huyện Mai Sơn tổ chức Hội nghị tập huấn qu&aacute;n triệt, triển khai c&aacute;c văn bản chỉ đạo về &quot;X&acirc;y dựng x&atilde; hội học tập giai đoạn 2021-2030&rdquo;. ​</p>\r\n\r\n<p style="text-align:justify">&nbsp; &nbsp; &nbsp; Ng&agrave;y 13/10/2022 Hội nghị mời giảng vi&ecirc;n l&agrave; l&atilde;nh đạo HKH tỉnh qu&aacute;n triệt triển khai c&aacute;c Quyết định của Thủ tướng Ch&iacute;nh phủ.</p>\r\n\r\n<p style="text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp;Ng&agrave;y 14/10/2022 Hội nghị tiếp tục triển khai c&aacute;c Th&ocirc;ng tư 44/TT-BGD&amp;ĐT ng&agrave;y 22/12/2014 v&agrave; Th&ocirc;ng tư 22/TT-BGD&amp;ĐT ng&agrave;y 06/8/2022 của Bộ GD&amp;ĐT, hướng dẫn c&aacute;c biểu mẫu khảo s&aacute;t tổng hợp, triển khai c&aacute;c nhiệm vụ của HKH tỉnh, huyện từ nay đến cuối năm 2022.</p>\r\n\r\n<p style="text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp;Mặc d&ugrave; tổ chức v&agrave;o thời điểm c&aacute;c x&atilde;, thị trấn, bản, tiểu khu đang tập trung chuẩn bị v&agrave; tiến h&agrave;nh đại hội chi bộ, song Hội nghị vẫn c&oacute; sự tham gia của 90 c&aacute;n bộ khuyến học của 22 Hội Khuyến học x&atilde;, thị trấn v&agrave; 5 HKH trực thuộc HKH huyện. Hội nghị tập huấn lần n&agrave;y sẽ bổ sung những kiến thức v&agrave; kỹ năng cơ bản để c&aacute;n bộ l&agrave;m c&ocirc;ng t&aacute;c khuyến học của huyện triển khai c&oacute; hiệu quả c&aacute;c chỉ ti&ecirc;u nhiệm vụ đề ra năm 2022 v&agrave; những năm tiếp theo.</p>\r\n', '2022-10-13 09:55:27', 49, '/data/files/hkh/Anh%20HN%20S%C6%A1%20k%E1%BA%BFt.JPG', '', 0, 52, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 18, 0);
INSERT INTO `tin_tuc` VALUES (1357, 'CUỘC THI VIẾT VỀ “KHUYẾN HỌC, KHUYẾN TÀI, XÂY DỰNG XÃ HỘI HỌC TẬP NĂM 2022”  LÀ CUỘC TẬP HUẤN VỀ KHUYẾN HỌC THIẾT THỰC, HIỆU QUẢ.', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:center"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><strong><span style="font-family:&quot;TitilliumText22LBold&quot;,&quot;serif&quot;">CUỘC THI VIẾT VỀ &ldquo;KHUYẾN HỌC, KHUYẾN T&Agrave;I, X&Acirc;Y DỰNG X&Atilde; HỘI HỌC TẬP NĂM 2022&rdquo;&nbsp; L&Agrave; CUỘC TẬP HUẤN VỀ KHUYẾN HỌC THIẾT THỰC, HIỆU QUẢ.</span></strong></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><strong><em><span style="font-family:&quot;TitilliumText22LBold&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Nguyễn Văn Bao &ndash; Phạm Xu&acirc;n Thịnh, Hội KH Sơn La.</span></em></strong></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<table align="left" style="border:undefined">\r\n	<tbody>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px; text-align:justify"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Trong những năm qua, Hội Khuyến học tỉnh đ&atilde; tổ chức, triển khai nhiều loại h&igrave;nh của c&aacute;c cuộc thi về &ldquo;<strong>khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập</strong>&rdquo; nhằm tuy&ecirc;n truyền, n&acirc;ng cao nhận thức cho c&aacute;n bộ, đảng vi&ecirc;n v&agrave; nh&acirc;n d&acirc;n c&aacute;c d&acirc;n tộc, đồng thời cũng l&agrave; dịp bồi dưỡng về kiến thức chuy&ecirc;n m&ocirc;n, nghiệp vụ cho đội ngũ l&agrave;m c&ocirc;ng t&aacute;c khuyến học trong to&agrave;n tỉnh.&nbsp;&nbsp;</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thực hiện Kế hoạch số: 07/KH-HKH ng&agrave;y 15 th&aacute;ng 12 năm 2021, Hội Khuyến học tỉnh đ&atilde; ban h&agrave;nh Thể lệ v&agrave; Quyết định số 121/QĐ-HKH ng&agrave;y 07/9/2022 th&agrave;nh lập Ban Tổ chức, Quyết định số 122/QĐ-HKH th&agrave;nh lập Ban Gi&aacute;m khảo v&agrave; Tổ Thư k&yacute; cuộc thi. &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Để cuộc thi đảm bảo y&ecirc;u cầu về chất lượng v&agrave; tiến độ đề ra, Hội Khuyến học tỉnh đ&atilde; thường xuy&ecirc;n chỉ đạo Hội Khuyến học c&aacute;c huyện, th&agrave;nh phố, c&aacute;c ban khuyến học cơ quan tổ chức ph&aacute;t động, triển khai cuộc thi v&agrave; động vi&ecirc;n tất cả c&aacute;n bộ, đảng vi&ecirc;n, c&ocirc;ng chức, vi&ecirc;n chức, người lao động, chiến sỹ lực lượng vũ trang, học sinh, sinh vi&ecirc;n v&agrave; nh&acirc;n d&acirc;n t&iacute;ch cực hưởng ứng tham gia cuộc thi; Hội Khuyến học tỉnh đ&atilde; chỉ đạo c&aacute;c đơn vị cung cấp tư liệu hoặc hướng dẫn nguồn khai th&aacute;c t&agrave;i liệu để mọi người c&ugrave;ng biết v&agrave; vận dụng l&agrave;m b&agrave;i. Ban Tổ chức đ&atilde; y&ecirc;u cầu Hội Khuyến học c&aacute;c huyện, th&agrave;nh phố tiếp nhận, sơ loại v&agrave; lựa chọn c&aacute;c b&agrave;i thi c&oacute; chất lượng gửi về Ban Tổ chức cuộc thi của tỉnh.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thực hiện chỉ đạo của Hội Khuyến học tỉnh, Hội Khuyến học c&aacute;c huyện, th&agrave;nh phố đ&atilde; ban h&agrave;nh Kế hoạch v&agrave; bằng nhiều h&igrave;nh thức triển khai, ph&aacute;t động, động vi&ecirc;n c&aacute;n bộ, c&ocirc;ng chức, vi&ecirc;n chức, nh&acirc;n d&acirc;n tham gia cuộc thi. C&aacute;c đơn vị, địa phương đ&atilde; tổ chức triển khai cuộc thi nghi&ecirc;m t&uacute;c, thường xuy&ecirc;n đ&ocirc;n đốc tiến độ, giao chỉ ti&ecirc;u v&agrave; qu&aacute;n triệt về số lượng, y&ecirc;u cầu về chất lượng của cuộc thi v&agrave; th&agrave;nh lập Ban Tổ chức, Ban Gi&aacute;m khảo để đảm bảo sự th&agrave;nh c&ocirc;ng chung của cuộc thi tr&ecirc;n địa b&agrave;n, ti&ecirc;u biểu như: Th&agrave;nh phố Sơn La, huyện Mai Sơn, Thuận Ch&acirc;u, S&ocirc;ng M&atilde;&hellip; </span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; B&ecirc;n cạnh đ&oacute;, c&aacute;c cấp Hội đ&atilde; tập trung thực hiện kh&aacute; tốt c&ocirc;ng t&aacute;c tuy&ecirc;n truyền, phổ biến về cuộc thi v&agrave; đ&atilde; được c&aacute;c cơ quan, đơn vị, địa phương hưởng ứng, x&acirc;y dựng Kế hoạch tham gia dự thi kịp thời v&agrave; rộng khắp.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Hội Khuyến học c&aacute;c huyện, th&agrave;nh phố đ&atilde; t&iacute;ch cực triển khai, ph&aacute;t động cuộc thi v&agrave; c&oacute; nhiều biện ph&aacute;p t&aacute;c động, đ&ocirc;n đốc, cử c&aacute;n bộ trực tiếp đến tận cơ sở để tuy&ecirc;n truyền, hướng dẫn v&agrave; th&aacute;o gỡ kh&oacute; khăn. Coi đ&acirc;y l&agrave; nhiệm vụ trọng t&acirc;m của năm 2022, n&ecirc;n c&aacute;c huyện, th&agrave;nh phố đ&atilde; đầu tư c&ocirc;ng sức, tr&iacute; tuệ, kinh ph&iacute;, thời gian để sưu tầm, chọn lọc, cung cấp t&agrave;i liệu, động vi&ecirc;n mọi người c&ugrave;ng tham gia. Ti&ecirc;u biểu như huyện S&ocirc;ng M&atilde;, th&agrave;nh phố Sơn La, huyện Thuận Ch&acirc;u, huyện Mộc Ch&acirc;u&hellip;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trong suốt qu&aacute; tr&igrave;nh tổ chức ph&aacute;t động cuộc thi, bộ phận gi&uacute;p việc của thường trực Hội v&agrave; Ban Tổ chức cuộc thi đ&atilde; nhận được sự phản hồi t&iacute;ch cực từ những tập thể, c&aacute; nh&acirc;n quan t&acirc;m đến cuộc thi th&ocirc;ng qua c&aacute;c k&ecirc;nh th&ocirc;ng tin như điện thoại, mạng Zalo hoặc trực tiếp gặp gỡ để phản &aacute;nh những vướng mắc trong qu&aacute; tr&igrave;nh tổ chức, tham gia cuộc thi, đề nghị hướng dẫn, gợi &yacute; về c&aacute;c nội dung như nguồn t&agrave;i liệu tham khảo cũng như th&ocirc;ng tin về tiến tr&igrave;nh cuộc thi. Điều đ&oacute; cho thấy, cuộc thi đ&atilde; nhận được sự quan t&acirc;m, hưởng ứng nhiệt t&igrave;nh của đ&ocirc;ng đảo c&aacute;n bộ, hội vi&ecirc;n nhiều tầng lớp nh&acirc;n d&acirc;n trong tỉnh.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Sau gần 10 th&aacute;ng triển khai, t&iacute;nh đến 15/8/2022, to&agrave;n tỉnh đ&atilde; c&oacute; tổng số 2.207 b&agrave;i dự thi tham gia (đ&acirc;y l&agrave; cuộc thi thu h&uacute;t nhiều người tham gia nhất từ trước đến nay). Nhiều đơn vị, địa phương c&oacute; số lượng/tỷ lệ c&aacute;n bộ, c&ocirc;ng chức, vi&ecirc;n chức, người lao động, tham gia cuộc thi cao như: Th&agrave;nh phố Sơn La (378 b&agrave;i), Huyện Thuận Ch&acirc;u (350 b&agrave;i), huyện S&ocirc;ng M&atilde; (306 b&agrave;i), Mai Sơn (201b&agrave;i), V&acirc;n Hồ (200 b&agrave;i) ... Tại c&aacute;c huyện, th&agrave;nh phố đ&atilde; tổ chức tiếp nhận, sơ loại v&agrave; gửi những b&agrave;i c&oacute; chất lượng về Ban Tổ chức cuộc thi của tỉnh. Cơ quan thường trực của Ban Tổ chức cuộc thi tỉnh đ&atilde; nhận được 1.078 b&agrave;i dự thi. Trong đ&oacute; huyện Thuận Ch&acirc;u c&oacute; 185 b&agrave;i, huyện S&ocirc;ng M&atilde; 179 b&agrave;i, huyện Mai Sơn 129 b&agrave;i, Y&ecirc;n Ch&acirc;u 99 b&agrave;i, th&agrave;nh phố Sơn La 85 b&agrave;i, Mộc Ch&acirc;u 84 b&agrave;i, Quỳnh Nhai 83 b&agrave;i &hellip;</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Sau 2 v&ograve;ng sơ loại tại tỉnh, c&oacute; 89 b&agrave;i được lựa chọn đưa v&agrave;o chấm ch&iacute;nh thức, trong số những người c&oacute; b&agrave;i dự thi được lựa chọn, về c&aacute; nh&acirc;n, c&oacute; 24 nam, 59 nữ, c&ograve;n lại l&agrave; b&agrave;i của tập thể, với đầy đủ th&agrave;nh phần c&aacute;c d&acirc;n tộc tham gia như: Th&aacute;i, M&ocirc;ng, Mường, Kinh, T&agrave;y&hellip;c&oacute; c&aacute;c lứa tuổi (người cao tuổi nhất 81, thấp nhất 10 tuổi), v&agrave; đủ c&aacute;c th&agrave;nh phần x&atilde; hội: C&ocirc;ng chức, vi&ecirc;n chức, c&aacute;n bộ l&atilde;nh đạo, người lao động, hội vi&ecirc;n, học sinh&hellip;</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trong số 1.078 b&agrave;i dự thi, c&oacute; h&agrave;ng chục b&agrave;i viết được đầu tư c&ocirc;ng phu, thể hiện qua việc t&igrave;m t&ograve;i, nghi&ecirc;n cứu, sưu tầm về tư liệu, nội dung, h&igrave;nh ảnh, giấy in, đ&oacute;ng quyển, c&oacute; h&igrave;nh ảnh đẹp minh họa. Nhiều b&agrave;i dự thi c&oacute; từ 300-500 trang, thể hiện sự nhiệt t&igrave;nh, t&acirc;m huyết, tr&aacute;ch nhiệm với khuyến học. Đ&oacute; l&agrave; c&aacute;c b&agrave;i của &ocirc;ng Ho&agrave;ng Huy Long đơn vị th&agrave;nh phố, b&agrave; T&ograve;ng Thị Thanh trường Cao đẳng Y, &ocirc;ng L&ecirc; Cao Thi&ecirc;n C&ocirc;ng an tỉnh, thầy gi&aacute;o Nguyễn Thanh B&igrave;nh trường THPT Chu Văn Thịnh Mai Sơn&hellip;B&ecirc;n cạnh đ&oacute; cũng c&oacute; những b&agrave;i viết c&ograve;n rất sơ lược do chưa đọc kỹ đầu b&agrave;i, kh&ocirc;ng x&aacute;c định đ&uacute;ng những vấn đề cốt l&otilde;i, cơ bản của văn bản m&agrave; đề b&agrave;i y&ecirc;u cầu, c&oacute; những b&agrave;i n&ecirc;u được &iacute;t t&agrave;i liệu v&agrave; vẫn c&ograve;n c&oacute; hiện tượng sao ch&eacute;p lẫn nhau. C&oacute; những b&agrave;i đưa thừa t&agrave;i liệu kh&ocirc;ng cần thiết thậm ch&iacute; đưa cả văn bản của tỉnh bạn như Ph&uacute; Thọ, Hưng Y&ecirc;n, Thanh H&oacute;a&hellip; v&agrave;o b&agrave;i l&agrave;m&hellip;.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Sau khi chấm b&agrave;i, Ban Gi&aacute;m khảo, Ban Tổ chức cuộc thi nhận thấy: </span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Nhiều b&agrave;i viết đ&atilde; b&aacute;m s&aacute;t đề b&agrave;i, thể hiện r&otilde; tầm hiểu biết s&acirc;u sắc về khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập, nghi&ecirc;n cứu c&aacute;c văn bản kỹ lưỡng, đầu tư c&ocirc;ng phu, chọn lọc th&ocirc;ng qua việc ph&acirc;n t&iacute;ch, c&acirc;n nhắc, lựa chọn để n&ecirc;u được ch&iacute;nh x&aacute;c những nội dung cơ bản, cốt l&otilde;i của từng văn bản theo đ&uacute;ng y&ecirc;u cầu của đề b&agrave;i. Nhiều b&agrave;i viết ngắn gọn, đ&atilde; đi thẳng v&agrave;o vấn đề ch&iacute;nh, trọng t&acirc;m, kh&ocirc;ng lan man, viết kh&ocirc;ng qu&aacute; d&agrave;i nhưng vẫn đủ &yacute;, s&aacute;t y&ecirc;u cầu n&ecirc;n chất lượng b&agrave;i l&agrave;m rất tốt như b&agrave;i của b&agrave; L&ograve; Thị Li&ecirc;n (Th&agrave;nh phố SL), &ocirc;ng H&ograve;a Quang Khang (S&ocirc;ng M&atilde;), b&agrave; L&ecirc; Thị Ch&acirc;u (Mai Sơn). Nhiều b&agrave;i viết về tấm gương ti&ecirc;u biểu rất x&uacute;c động n&ecirc;u nhiều tấm gương s&aacute;ng l&agrave;m khuyến học đ&atilde; được biểu dương, n&ecirc;u gương v&agrave; lan tỏa, tỏ r&otilde; t&aacute;c dụng tốt, đ&atilde; được kiểm chứng ở cơ sở&hellip; Ti&ecirc;u biểu như b&agrave;i thi của &ocirc;ng L&ecirc; Cao Thi&ecirc;n c&ocirc;ng an tỉnh,,,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ban Tổ chức c&ograve;n nhận được b&agrave;i dự thi của c&aacute;c cụ cao tuổi, điển h&igrave;nh như: &ocirc;ng Nguyễn Quốc Hồng 81 tuổi, c&aacute;n bộ hưu tr&iacute; Thị trấn S&ocirc;ng M&atilde;, &ocirc;ng Ng&ocirc; Trọng Qu&yacute; 81 tuổi, c&aacute;n bộ hưu tr&iacute; Thị trấn Mộc Ch&acirc;u v&agrave; ch&aacute;u học sinh &iacute;t tuổi nhất l&agrave; Trần Thị Ngọc &Aacute;nh 10 tuổi ở Thị trấn H&aacute;t L&oacute;t, Mai Sơn.&nbsp;&nbsp; </span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ban Gi&aacute;m khảo cuộc thi đ&atilde; triển khai tổ chức chấm thi với nguy&ecirc;n tắc c&ocirc;ng t&acirc;m, kh&aacute;ch quan, trung thực, đ&uacute;ng thể lệ, đ&uacute;ng đ&aacute;p &aacute;n do Ban Tổ chức cuộc thi của tỉnh c&ocirc;ng bố, đặc biệt d&agrave;nh thời gian để nghi&ecirc;n cứu v&agrave; trao đổi, thảo luận giữa c&aacute;c th&agrave;nh vi&ecirc;n trong việc đ&aacute;nh gi&aacute;, chấm điểm đối với những b&agrave;i dự thi c&oacute; nội dung chất lượng cao, s&acirc;u sắc, h&igrave;nh thức đẹp, độc đ&aacute;o, tư liệu minh họa hợp l&yacute;, h&agrave;i h&ograve;a, s&aacute;ng tạo v&agrave; thuyết phục.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tr&ecirc;n cơ sở kết quả chấm điểm của Ban Gi&aacute;o khảo, Ban Tổ chức cuộc thi của tỉnh đ&atilde; quyết định: Đối với giải tập thể:</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trao 01 giải Nhất, trị gi&aacute;: 5 (năm) triệu đồng cho tập thể Hội Khuyến học Th&agrave;nh phố Sơn La.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trao 02 giải Nh&igrave;, trị gi&aacute; 3 (ba) triệu đồng/ giải cho 2 tập thể: Hội Khuyến học Huyện S&ocirc;ng M&atilde; v&agrave; Hội Khuyến học Huyện Thuận Ch&acirc;u.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trao 02 giải Ba, trị gi&aacute; 2 (hai) triệu đồng/giải cho 2 tập thể: Hội Khuyến học Mai Sơn v&agrave; Hội Khuyến học Bắc Y&ecirc;n.</span></span></span></p>\r\n\r\n<p style="margin-left:36pt; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Trao 07 giải Khuyến kh&iacute;ch, trị gi&aacute; 1(một) triệu /giải cho c&aacute;c tập thể:</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">Hội Khuyến học Quỳnh Nhai , Hội Khuyến học Mường La, Hội Khuyến học Y&ecirc;n Ch&acirc;u, Hội Khuyến học Ph&ugrave; Y&ecirc;n, Hội Khuyến học V&acirc;n Hồ, Hội Khuyến học Sốp Cộp v&agrave; Hội khuyến học Mộc Ch&acirc;u.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đối với c&aacute; nh&acirc;n:</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trao 01 giải nhất, trị gi&aacute; 2(hai) triệu đồng cho B&agrave; L&ograve; Thị Li&ecirc;n Hội KH Th&agrave;nh Phố Sơn La.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trao 02 giải nh&igrave; trị gi&aacute; 1,5 triệu đồng cho 2c&aacute; nh&acirc;n: &Ocirc;ng H&ograve;a Quang Khang HKH S&ocirc;ng M&atilde; v&agrave; b&agrave;: L&ecirc; Thị Ch&acirc;u, HKH Mai Sơn.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trao 03 giải Ba trị gi&aacute; 1,0 triệu đồng cho 2c&aacute; nh&acirc;n: Phạm Thị Thanh Hường, Huyện Thuận Ch&acirc;u v&agrave; &ocirc;ng Ho&agrave;ng Huy Long CTCP M&ocirc;i trường v&agrave; Dịch vụ đ&ocirc; thị Sơn La, &ocirc;ng: Đinh Thanh H&ugrave;ng, Huyện Bắc Y&ecirc;n.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trao 6 giải khuyến kh&iacute;ch trị gi&aacute; 500,000 đồng/ giải&nbsp; cho 5t&aacute;c giả v&agrave; nh&oacute;m t&aacute;c giả l&agrave;: B&agrave; Vũ Thị Thanh Huyền (Thuận Ch&acirc;u), L&ograve; Thị Phượng (Th&agrave;nh phố ), Trần Quốc B&igrave;nh (Th&agrave;nh phố), Nh&oacute;m t&aacute;c giả T&ocirc; Như Quy&ecirc;n (Th&agrave;nh phố), T&ograve;ng Thị Thanh (trường CĐ Y Sơn La), Nguyễn Thị Th&uacute;y H&agrave; (trường CĐ Sơn La).&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trao giải B&agrave;i viết về tấm gương khuyến học xuất sắc nhất, b&agrave;i viết của người cao tuổi nhất, b&agrave;i viết của người &iacute;t tuổi nhất&hellip;mỗi giải k&egrave;m theo tiền thưởng 500.000 đồng cho &ocirc;ng L&ecirc; Cao Thi&ecirc;n, C&ocirc;ng an tỉnh Sơn La; &ocirc;ng Nguyễn Quốc Hồng 81 tuổi (S&ocirc;ng M&atilde;); &ocirc;ng Ng&ocirc; Trọng Qu&yacute; 81 tuổi (Mộc Ch&acirc;u) v&agrave; em Trần Thị Ngọc &Aacute;nh</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;10 tuổi (Mai Sơn). </span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong>C&oacute; thể n&oacute;i Cuộc thi viết về khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập năm 2022 của Hội Khuyến học tỉnh lần n&agrave;y được tổ chức triển khai nghi&ecirc;m t&uacute;c, b&agrave;i bản v&agrave; đảm bảo đ&uacute;ng tiến độ, y&ecirc;u cầu. C&ocirc;ng t&aacute;c th&ocirc;ng tin, tuy&ecirc;n truyền đ&atilde; được c&aacute;c cơ quan, đơn vị, địa phương thực hiện tương đối kịp thời v&agrave; rộng khắp đ&atilde; thu h&uacute;t được đ&ocirc;ng đảo c&aacute;n bộ, hội vi&ecirc;n v&agrave; c&aacute;c tầng lớp nh&acirc;n d&acirc;n tr&ecirc;n địa b&agrave;n tỉnh tham gia, thể hiện qua số lượng b&agrave;i dự thi lớn. Đối tượng tham dự cuộc thi đa dạng, c&oacute; nhiều lứa tuổi, tr&igrave;nh độ văn h&oacute;a, nghề nghiệp, d&acirc;n tộc; cư tr&uacute; ở khắp c&aacute;c địa b&agrave;n, v&ugrave;ng miền tr&ecirc;n địa b&agrave;n tỉnh. Trong số hơn ngh&igrave;n b&agrave;i dự thi m&agrave; Ban Tổ chức cuộc thi của tỉnh nhận được, c&oacute; h&agrave;ng chục b&agrave;i dự thi được đầu tư c&ocirc;ng phu, nội dung s&acirc;u sắc, to&agrave;n diện, h&igrave;nh thức in, tr&igrave;nh b&agrave;y đẹp, thể hiện ch&iacute;nh kiến của c&aacute; nh&acirc;n v&agrave;&nbsp;đ&atilde;<em>&rdquo;hiến kế&rdquo;</em>&nbsp;về c&aacute;c giải ph&aacute;p thực hiện c&ocirc;ng t&aacute;c KHKT, XDXHHT tr&ecirc;n địa b&agrave;n.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Cuộc thi l&agrave; một trong những nhiệm vụ trọng t&acirc;m v&agrave; c&oacute; &yacute; nghĩa rất quan trọng đối với c&ocirc;ng t&aacute;c KHKT, XDXHHT trong năm 2022, l&agrave; đợt tập huấn, sinh hoạt chuy&ecirc;n m&ocirc;n, nghiệp vụ s&acirc;u sắc v&agrave; rộng khắp tr&ecirc;n địa b&agrave;n to&agrave;n tỉnh, g&oacute;p phần thiết thực trong việc đẩy mạnh c&ocirc;ng t&aacute;c tuy&ecirc;n truyền s&acirc;u rộng nội dung c&ocirc;ng t&aacute;c KHKT, XDXHHT đến to&agrave;n thể c&aacute;n bộ v&agrave; nh&acirc;n d&acirc;n, cũng l&agrave; diễn đ&agrave;n để người tham gia dự thi trao đổi, luận b&agrave;n, nghi&ecirc;n cứu, t&igrave;m hiểu về khuyến học, qua đ&oacute;, người dự thi c&oacute; c&aacute;i nh&igrave;n s&acirc;u sắc, to&agrave;n diện về nhiệm vụ v&agrave; &yacute; nghĩa to lớn, thiết thực của c&ocirc;ng t&aacute;c KHKT, XD XHHT giai đoạn 2021-2030 theo Kết luận số 49 của Ban B&iacute; thư v&agrave; c&aacute;c Quyết định của Thủ tướng Ch&iacute;nh phủ thời kỳ tiếp tục đổi mới đất nước trong sự nghiệp x&acirc;y dựng, bảo vệ Tổ quốc v&agrave; hội nhập quốc tế.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; B&ecirc;n cạnh những kết quả đ&atilde; đạt được, c&ocirc;ng t&aacute;c tổ chức triển khai v&agrave; tham gia hưởng ứng cuộc thi tr&ecirc;n địa b&agrave;n tỉnh vẫn c&ograve;n một số tồn tại, hạn chế như:</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Việc triển khai, ph&aacute;t động Cuộc thi ở một số Ban Khuyến học cơ quan, đơn vị c&ograve;n chiếu lệ, thậm ch&iacute; một số cơ quan, đơn vị ở cấp tỉnh kh&ocirc;ng c&oacute; c&aacute;n bộ, Hội vi&ecirc;n n&agrave;o tham dự cuộc thi.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&ocirc;ng t&aacute;c tổ chức triển khai cuộc thi ở một số nơi c&ograve;n chậm, l&uacute;ng t&uacute;ng, thiếu sự đ&ocirc;n đốc, nhắc nhở, chưa lường hết được những kh&oacute; khăn trong triển khai. C&ocirc;ng t&aacute;c chỉ đạo, hướng dẫn việc chấm sơ loại v&agrave; gửi b&agrave;i dự thi về Ban Tổ chức cuộc thi của tỉnh c&ograve;n chậm, thậm ch&iacute; c&oacute; nơi chỉ sơ loại bằng cảm quang, chưa lựa chọn ch&iacute;nh x&aacute;c những b&agrave;i c&oacute; chất lượng cao gửi về dự thi ở tỉnh.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Số lượng b&agrave;i dự thi mang t&iacute;nh &ldquo;phong tr&agrave;o&rdquo; chiếm tỷ lệ cao so với tổng số b&agrave;i dự thi; nhiều b&agrave;i dự thi sao ch&eacute;p lẫn nhau, đưa cả văn bản ngo&agrave;i ng&agrave;nh, của tỉnh bạn v&agrave;o b&agrave;i, kh&ocirc;ng c&oacute; sự đầu tư cao khi tham gia dự thi. Điều n&agrave;y kh&ocirc;ng đ&uacute;ng với mục đ&iacute;ch, &yacute; nghĩa của việc tổ chức cuộc thi, dẫn đến sự l&atilde;ng ph&iacute; cho mỗi c&aacute; nh&acirc;n v&agrave; x&atilde; hội.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&oacute; b&agrave;i dự thi được đầu tư c&ocirc;ng phu nhưng phương ph&aacute;p tiếp cận chưa tốt n&ecirc;n chất lượng b&agrave;i chưa cao, hoặc c&oacute; những b&agrave;i thi với số lượng trang rất lớn nhưng chưa thực sự nắm chắc y&ecirc;u cầu của đề b&agrave;i, chưa trả lời, x&aacute;c định ch&iacute;nh x&aacute;c chỗ n&agrave;o l&agrave; nội dung cơ bản, cốt l&otilde;i của vấn đề?&nbsp; C&oacute; những b&agrave;i chỉ n&ecirc;u t&ecirc;n văn bản m&agrave; kh&ocirc;ng x&aacute;c định được nội dung ch&iacute;nh cần n&ecirc;u hoặc đưa tất những văn bản biết được kh&ocirc;ng c&oacute; sự lựa chọn, c&acirc;n nhắc&hellip; Viết về gương điển h&igrave;nh trong phong tr&agrave;o KHKT, XDXHHT ở cơ quan, đơn vị, địa phương m&agrave; m&igrave;nh t&acirc;m đắc nhất nhưng c&oacute; b&agrave;i kh&ocirc;ng viết ở địa phương, đơn vị m&agrave; lại viết ở nơi kh&aacute;c, địa phương kh&aacute;c nơi m&agrave; m&igrave;nh kh&ocirc;ng sinh sống, kh&ocirc;ng chứng kiến, kh&ocirc;ng c&ocirc;ng t&aacute;c c&ugrave;ng&hellip;&nbsp; nhiều b&agrave;i viết lan man, kh&ocirc;ng tập trung, chưa n&ecirc;u bật được việc l&agrave;m c&oacute; &yacute; nghĩa thiết thực v&agrave; sự lan tỏa t&aacute;c dụng của tấm gương&hellip;</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&ocirc;ng t&aacute;c r&agrave; so&aacute;t, ph&acirc;n loại sơ tuyển b&agrave;i dự thi chưa tốt, chưa đ&uacute;ng quy định n&ecirc;n số lượng b&agrave;i dự thi gửi l&ecirc;n Ban Tổ chức cuộc thi của tỉnh nhiều nhưng chất lượng nhiều b&agrave;i c&ograve;n hạn chế.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Việc ra đề thi, đ&aacute;p &aacute;n chấm cũng c&oacute; điểm chưa thật khoa học như tỉ lệ điểm c&aacute;c c&acirc;u, c&aacute;c &yacute;&hellip;</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Nguy&ecirc;n nh&acirc;n của những hạn chế l&agrave;:</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; L&atilde;nh đạo một số Ban Khuyến học cơ quan, đơn vị, địa phương kinh nghiệm tổ chức cuộc thi c&ograve;n hạn chế, chưa quan t&acirc;m, s&acirc;u s&aacute;t, quyết liệt trong chỉ đạo tổ chức triển khai, dẫn đến t&igrave;nh trạng tổ chức cuộc thi chiếu lệ, thiếu đ&ocirc;n đốc, kiểm tra, do vậy số lượng c&aacute;c b&agrave;i dự thi so với số c&aacute;n bộ, hội vi&ecirc;n q&uacute;a &iacute;t hoặc chất lượng kh&ocirc;ng cao.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đội ngũ c&aacute;n bộ tham mưu ở c&aacute;c cấp c&ograve;n mỏng, chưa c&oacute; nhiều kinh nghiệm trong tổ chức cuộc thi với quy m&ocirc; lớn, thời gian d&agrave;i&hellip;</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Kinh ph&iacute; phục vụ cuộc thi ở nhiều đơn vị, địa phương kh&ocirc;ng c&oacute; hoặc được bố tr&iacute; nhưng chưa đ&aacute;p ứng được y&ecirc;u cầu, quy m&ocirc; tổ chức cuộc thi.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tr&ecirc;n cơ sở những kết quả đạt được v&agrave; hạn chế, tồn tại của cuộc thi, để c&aacute;c cuộc thi về c&ocirc;ng t&aacute;c KHKT, XDXHHT trong thời gian tới đạt kết quả cao, Hội Khuyến học tỉnh r&uacute;t ra một số b&agrave;i học kinh nghiệm như sau:</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&ocirc;ng t&aacute;c th&ocirc;ng tin, tuy&ecirc;n truyền cần phối hợp chặt chẽ với c&aacute;c ng&agrave;nh, c&aacute;c cấp, nhất l&agrave; c&aacute;c cơ quan th&ocirc;ng tin đại ch&uacute;ng. Tuy&ecirc;n truyền phải kịp thời, li&ecirc;n tục, s&acirc;u rộng, kết hợp với động vi&ecirc;n, khuyến kh&iacute;ch trong suốt thời gian diễn ra cuộc thi để thu h&uacute;t sự quan t&acirc;m, ch&uacute; &yacute; của đ&ocirc;ng đảo c&aacute;c tầng lớp nh&acirc;n d&acirc;n. Tăng cường thời lượng, linh hoạt về phương ph&aacute;p tuy&ecirc;n truyền của cuộc thi, cung cấp hoặc hướng dẫn c&aacute;ch khai th&aacute;c c&aacute;c t&agrave;i liệu phục vụ cuộc thi, gợi &yacute; trả lời c&acirc;u hỏi bằng nhiều h&igrave;nh thức, phương tiện th&ocirc;ng tin đại ch&uacute;ng ở cơ sở.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Chỉ đạo, hướng dẫn c&aacute;c đơn vị, địa phương triển khai tổ chức cuộc thi kịp thời; cần giao chỉ ti&ecirc;u về số lượng, y&ecirc;u cầu về chất lượng khi tham gia; thường xuy&ecirc;n theo d&otilde;i, nắm bắt t&igrave;nh h&igrave;nh, hướng dẫn, kiểm tra, đ&ocirc;n đốc việc triển khai ở c&aacute;c đơn vị trực thuộc, phải tổ chức triển khai tốt ngay từ địa phương, cơ sở. Ngo&agrave;i việc ph&aacute;t động cuộc thi rộng, c&aacute;c cơ quan, đơn vị cần nắm bắt, c&oacute; h&igrave;nh thức động vi&ecirc;n, khuyến kh&iacute;ch kịp thời những người c&oacute; t&acirc;m huyết với cuộc thi để hỗ trợ họ l&agrave;m b&agrave;i thi c&oacute; chất lượng, ch&iacute;nh những c&aacute; nh&acirc;n tr&ecirc;n sẽ x&acirc;y dựng n&ecirc;n những b&agrave;i dự thi s&aacute;ng tạo, tr&iacute; tuệ, g&oacute;p phần quan trọng tạo n&ecirc;n th&agrave;nh c&ocirc;ng của cuộc thi.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bố tr&iacute; kinh ph&iacute; hợp l&yacute; để đảm bảo tương xứng với quy m&ocirc; tổ chức cuộc thi, đ&aacute;p ứng mục đ&iacute;ch, &yacute; nghĩa, y&ecirc;u cầu của cuộc thi.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tổ chức tổng kết, khen thưởng kịp thời, t&ocirc;n vinh những tập thể, c&aacute; nh&acirc;n c&oacute; th&agrave;nh t&iacute;ch trong tổ chức v&agrave; tham gia hưởng ứng cuộc thi để tạo th&agrave;nh nề nếp c&aacute;c hoạt động x&acirc;y dựng XHHT để c&oacute; sức lan tỏa mạnh mẽ trong cộng đồng.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Với mục ti&ecirc;u tạo điều kiện cho c&aacute;n bộ, hội vi&ecirc;n, nh&acirc;n d&acirc;n t&igrave;m hiểu v&agrave; hiểu đ&uacute;ng chủ trương, đường lối của Đảng về c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, về x&acirc;y dựng x&atilde; hội học tập, t&igrave;m hiểu v&agrave; hiểu kĩ c&aacute;c chủ trương, kế hoạch, c&aacute;c giải ph&aacute;p của Ch&iacute;nh phủ, c&aacute;c bộ ng&agrave;nh, của Tỉnh ủy, HĐND, UBND, của c&aacute;c cấp Hội Khuyến học&hellip;từ đ&oacute; hiểu đủ, hiểu đ&uacute;ng để tham gia g&oacute;p phần x&acirc;y dựng x&atilde; hội học tập tr&ecirc;n địa b&agrave;n tỉnh ta, g&oacute;p phần ho&agrave;n th&agrave;nh c&aacute;c chương tr&igrave;nh của Ch&iacute;nh phủ về x&acirc;y dựng x&atilde; hội học tập tới năm 2030 m&agrave; trước mắt l&agrave; giai đoạn từ nay tới 2025. </span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,&quot;serif&quot;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; X&eacute;t về t&iacute;nh chất như vậy, cuộc thi lần n&agrave;y đ&atilde; th&agrave;nh c&ocirc;ng tốt đẹp. Đ&acirc;y c&oacute; thể coi l&agrave; cuộc tập huấn quy m&ocirc;, bền bỉ về c&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội của Hội Khuyến học Sơn La. Qua cuộc thi, nhiều c&aacute;n bộ, hội vi&ecirc;n cũng như c&aacute;c tầng lớp nh&acirc;n d&acirc;n c&aacute;c d&acirc;n tộc Sơn La đ&atilde; hiểu đ&uacute;ng, biết nhiều v&agrave;&nbsp; c&oacute; nhiều giải ph&aacute;p thiết thực phục vụ cho hoạt động khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập tr&ecirc;n địa b&agrave;n Sơn La!</span></span></span></p>\r\n', '2022-10-14 07:57:58', 73, '', '', 0, 33, 0, 0, '', 0, '', '', 18, 'xuat_ban', '0000-00-00 00:00:00', 0, 0, 0);
