INSERT INTO `van_ban` VALUES (323, '677/QĐ-TTg', '03/6/2022', 'QUYẾT ĐỊNH 677/QĐ-TTg PHÊ DUYỆT CHƯƠNG TRÌNH “XÂY DỰNG MÔ HÌNH CÔNG DÂN HỌC TẬP GIAI ĐOẠN 2021 - 2030”', 8, '/data/files/QD-677%20%20PH%C3%8A%20DUY%E1%BB%86T%20CH%C6%AF%C6%A0NG%20TR%C3%8CNH%20%E2%80%9CX%C3%A2y%20d%E1%BB%B1ng%20m%C3%B4%20h%C3%ACnh%20c%C3%B4ng%20d%C3%A2n%20h%E1%BB%8Dc%20t%E1%BA%ADp%E2%80%9D%20.pdf', '', '<p>QUYẾT ĐỊNH PH&Ecirc; DUYỆT CHƯƠNG TR&Igrave;NH &ldquo;X&Acirc;Y DỰNG M&Ocirc; H&Igrave;NH C&Ocirc;NG D&Acirc;N HỌC TẬP GIAI ĐOẠN 2021 - 2030&rdquo;</p>\r\n', 10, 57, 0, 0, '', 0, '2022-10-20 09:18:17', '', '', '');
INSERT INTO `van_ban` VALUES (328, '210/KH-UBND', '19/8/2022', 'KH SỐ 210/KH-UBND Ban hành Chương trình “Xây dựng mô hình công dân học tập giai đoạn 2021 - 2030” trên địa bàn tỉnh Sơn La', 13, '/data/files/KH%20210.pdf', '', '<p><img alt="" src="/data/images/Capture.JPG" style="height:545px; width:389px" /><img alt="" src="/data/images/Capture1.JPG" style="height:522px; width:359px" /><img alt="" src="/data/images/Capture3.JPG" style="height:535px; width:353px" /><img alt="" src="/data/images/Capture4.JPG" style="height:526px; width:346px" /><img alt="" src="/data/images/Capture5.JPG" style="height:526px; width:349px" /><img alt="" src="/data/images/Capture6.JPG" style="height:524px; width:337px" /><img alt="" src="/data/images/Capture7.JPG" style="height:344px; width:341px" /></p>\r\n', 15, 77, 0, 0, '', 0, '2022-10-20 08:52:05', '', '', '');
INSERT INTO `van_ban` VALUES (329, '206/KH-UBND', '17/8/2022', 'KH SỐ 206/KH-UBND Ban hành Chương trình ', 13, '/data/files/Q%C4%90-206-%20UBND%20t%E1%BB%89nh.pdf', '', '<p>ỦY BAN NH&Acirc;N D&Acirc;N&nbsp;<br />\r\nTỈNH SƠN LA&nbsp;<br />\r\n&nbsp;&nbsp;<br />\r\nSố: &nbsp; &nbsp; &nbsp; &nbsp; /KH-UBND&nbsp;<br />\r\nCỘNG H&Ograve;A X&Atilde; HỘI CHỦ NGHĨA VIỆT NAM&nbsp;<br />\r\n&nbsp; &nbsp;Độc lập - Tự do - Hạnh ph&uacute;c&nbsp;<br />\r\n&nbsp;&nbsp;<br />\r\nSơn La, ng&agrave;y &nbsp; &nbsp; &nbsp; th&aacute;ng 8 năm 2022&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<br />\r\nKẾ HOẠCH&nbsp;<br />\r\nThực hiện Chương tr&igrave;nh &ldquo;Đẩy mạnh phong tr&agrave;o học tập suốt đời trong&nbsp;<br />\r\n&nbsp;gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị giai đoạn 2021 - 2030&rdquo; &nbsp;<br />\r\ntr&ecirc;n địa b&agrave;n tỉnh Sơn La&nbsp;<br />\r\n&nbsp; &nbsp;<br />\r\n&nbsp; Thực hiện Quyết định số &nbsp;387/QĐ-TTg ng&agrave;y 25/3/2022 của Thủ &nbsp;tướng&nbsp;<br />\r\nCh&iacute;nh phủ ph&ecirc; duyệt Chương tr&igrave;nh &ldquo;Đẩy mạnh phong tr&agrave;o học tập suốt đời trong&nbsp;<br />\r\ngia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị giai đoạn 2021- 2030&rdquo;.&nbsp;<br />\r\n&nbsp; Theo đ&ecirc;̀ nghị &nbsp;của Hội Khuyến học tỉnh tại Tờ &nbsp;tr&igrave;nh số 60/TTr-HKH ng̀y&nbsp;<br />\r\n25/5/2022. Chủ &nbsp;tịch Ủy Ban nh&acirc;n d&acirc;n &nbsp;tỉnh Sơn La ban h̀nh Kế hoạch &nbsp;thực hiện&nbsp;<br />\r\nChương tr&igrave;nh &nbsp;&ldquo;Đẩy mạnh phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ,&nbsp;<br />\r\ncộng đồng, đơn vị giai đoạn 2021- 2030&rdquo; tr&ecirc;n địa b&agrave;n tỉnh như sau: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />\r\nI. MỤC Đ&Iacute;CH, Y&Ecirc;U CẦU&nbsp;<br />\r\n1. Mục đ&iacute;ch&nbsp;<br />\r\n- N&acirc;ng cao nhận thức v̀ h̀nh động của hệ thống ch&iacute;nh trị, c&aacute;c tầng lớp nh&acirc;n&nbsp;<br />\r\nd&acirc;n trong tỉnh v&ecirc;̀ mục đ&iacute;ch, &yacute; nghĩa việc học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ,&nbsp;<br />\r\ncộng đồng, đơn vị. Từ đ&oacute; t&iacute;ch cực tham gia x&acirc;y dựng x&atilde; hội học tập bằng nhi&ecirc;̀u&nbsp;<br />\r\nh&igrave;nh thức th&iacute;ch hợp, g&oacute;p phần ph&aacute;t triển kinh tế - x&atilde; hội, đảm bảo an ninh - quốc&nbsp;<br />\r\nph&ograve;ng, n&acirc;ng cao đời sống vật chất, tinh thần của nh&acirc;n d&acirc;n trong thời kỳ mới.&nbsp;<br />\r\n- Tiếp tục thực hiện c&oacute; hiệu quả c&aacute;c Kế hoạch của Ủy ban nh&acirc;n d&acirc;n tỉnh v&ecirc;̀&nbsp;<br />\r\nc&ocirc;ng t&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập tr&ecirc;n địa b&agrave;n tỉnh. &nbsp;<br />\r\n- Tăng cường c&ocirc;ng t&aacute;c kiểm tra, gi&aacute;m s&aacute;t của cấp ủy, ch&iacute;nh quy&ecirc;̀n qua đ&oacute; tạo&nbsp;<br />\r\nđi&ecirc;̀u kiện v̀ th&uacute;c đẩy người lớn học tập, học thường xuy&ecirc;n, học suốt đời, theo&nbsp;<br />\r\nhướng lấy tự học l&agrave;m cốt. Huy động c&aacute;c nguồn lực, x&acirc;y dựng c&aacute;c kỹ năng số cho&nbsp;<br />\r\nngười học, đẩy mạnh phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng&nbsp;<br />\r\nđồng, đơn vị giai đoạn 2021 - 2030.&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2. Y&ecirc;u cầu&nbsp;<br />\r\nC&aacute;c cấp, c&aacute;c ng&agrave;nh, Mặt trận Tổ quốc, c&aacute;c tổ chức ch&iacute;nh trị - x&atilde; hội, x&atilde; hội&nbsp;<br />\r\nngh&ecirc;̀ nghiệp v&agrave; to&agrave;n x&atilde; hội tham gia t&iacute;ch cực v&agrave; tạo mọi đi&ecirc;̀u kiện, cơ hội tham gia&nbsp;<br />\r\nthực hiện Chương tr&igrave;nh &ldquo;Đẩy mạnh phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh,&nbsp;<br />\r\nd&ograve;ng họ, cộng đồng, đơn vị&rdquo; đ&aacute;p ứng y&ecirc;u cầu c&ocirc;ng nghiệp h&oacute;a, hiện đại h&oacute;a v&agrave; hội&nbsp;<br />\r\nnhập quốc tế trong t&igrave;nh h&igrave;nh mới.&nbsp;<br />\r\nTổ chức, triển khai đồng bộ, thực hiện c&oacute; hiệu quả c&aacute;c m&ocirc; h&igrave;nh &ldquo;Gia đ&igrave;nh học&nbsp;<br />\r\ntập&rdquo;, &ldquo;D&ograve;ng họ học tập&rdquo;, &ldquo;Cộng đồng học tập&rdquo; v̀ &ldquo;Đơn vị học tập&rdquo;, &ldquo; C&ocirc;ng d&acirc;n học&nbsp;<br />\r\ntập&rdquo; theo ti&ecirc;u ch&iacute; giai đoạn 2021 - 2030 của Trung ương Hội Khuyến học Việt Nam,&nbsp;<br />\r\ntạo n&ecirc;̀n tảng vững chắc cho việc x&acirc;y dựng m&ocirc; h&igrave;nh &ldquo;Huyện,th̀nh phố học tập&rdquo;. 2&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\nTriển khai Chương tr&igrave;nh &ldquo;Đẩy mạnh phong tr&agrave;o học tập suốt đời trong gia&nbsp;<br />\r\nđ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị&rdquo; lồng gh&eacute;p với c&aacute;c kế hoạch, đ&ecirc;̀ &aacute;n, chương tr&igrave;nh&nbsp;<br />\r\nc&oacute; li&ecirc;n quan, gắn với c&aacute;c mục ti&ecirc;u ph&aacute;t triển kinh tế - x&atilde; hội của tỉnh.&nbsp;<br />\r\nII. MỤC TI&Ecirc;U&nbsp;<br />\r\n1. Mục ti&ecirc;u chung&nbsp;<br />\r\nĐẩy mạnh học tập thường xuy&ecirc;n, học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ,&nbsp;<br />\r\ncộng đồng, đơn vị; n&acirc;ng cao chất lượng học tập th&ocirc;ng qua việc x&acirc;y dựng v&agrave; triển&nbsp;<br />\r\nkhai, nh&acirc;n rộng c&aacute;c m&ocirc; h&igrave;nh học tập, ph&aacute;t huy khả năng s&aacute;ng tạo của mỗi c&aacute; nh&acirc;n,&nbsp;<br />\r\ny&ecirc;u gia đ&igrave;nh, y&ecirc;u Tổ quốc, sống tốt v&agrave; l&agrave;m việc hiệu quả, để mọi người d&acirc;n phấn&nbsp;<br />\r\nđấu đạt danh hiệu học tập, tạo yếu tố cơ bản cho việc ph&aacute;t triển m&ocirc; h&igrave;nh học tập&nbsp;<br />\r\ntrong to&agrave;n tỉnh, g&oacute;p phần x&acirc;y dựng nguồn nh&acirc;n lực chất lượng cao, đ&aacute;p ứng y&ecirc;u&nbsp;<br />\r\ncầu ph&aacute;t triển kinh &nbsp;tế &nbsp;- x&atilde; hội trong chương tr&igrave;nh &ldquo;Chuyển đổi số &nbsp;to&agrave;n cầu&rdquo;, x&acirc;y&nbsp;<br />\r\ndựng Sơn La th̀nh một x&atilde; hội học tập. &nbsp;<br />\r\nTiếp tục đ&aacute;nh gi&aacute; việc học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng,&nbsp;<br />\r\nđơn vị theo bộ ti&ecirc;u ch&iacute; mới của Trung ương Hội Khuyến học Việt Nam; vận dụng&nbsp;<br />\r\nlinh hoạt, s&aacute;ng tạo, ph&ugrave; hợp với đi&ecirc;̀u kiện thực tiễn địa phương.&nbsp;<br />\r\nDuy tr&igrave; v&agrave; ph&aacute;t huy tinh thần học tập, học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng&nbsp;<br />\r\nhọ, cộng đồng, đơn vị; x&acirc;y dựng m&ocirc;i trường học tập để c&oacute; thể sống v&agrave; l&agrave;m việc&nbsp;<br />\r\ntrong đi&ecirc;̀u kiện của n&ecirc;̀n kinh tế số, x&atilde; hội số v&agrave; Ch&iacute;nh phủ số.&nbsp;<br />\r\n2. Mục ti&ecirc;u cụ thể&nbsp;<br />\r\n2.1. Phấn đấu đến năm 2025&nbsp;<br />\r\n- 100% c&aacute;n bộ, hội vi&ecirc;n Hội Khuyến học c&aacute;c cấp được học tập, qu&aacute;n triệt&nbsp;<br />\r\nc&aacute;c chủ &nbsp;trương của Trung ương Đảng, Ch&iacute;nh phủ, Hội Khuyến học Việt Nam v&agrave;&nbsp;<br />\r\nc&aacute;c nghị quyết, chỉ &nbsp;thị, chương tr&igrave;nh, kế hoạch&hellip; của Tỉnh ủy, Ủy ban nh&acirc;n d&acirc;n&nbsp;<br />\r\ntỉnh v&ecirc;̀ đẩy mạnh khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập.&nbsp;<br />\r\n- 70% gia đ&igrave;nh được c&ocirc;ng nhận đạt &ldquo;Gia đ&igrave;nh học tập&rdquo;; 65% d&ograve;ng họ được&nbsp;<br />\r\nc&ocirc;ng nhận đạt &ldquo;D&ograve;ng họ &nbsp;học tập&rdquo;; &nbsp;65% cộng đồng được c&ocirc;ng nhận đạt &ldquo;Cộng&nbsp;<br />\r\nđồng học tập&rdquo;; 95% đơn vị (gồm c&aacute;c tổ chức, cơ quan, trường học, doanh nghiệp,&nbsp;<br />\r\ncơ sở sản suất&hellip; kể cả cấp x&atilde; quản l&yacute;) được c&ocirc;ng nhận đạt &ldquo;Đơn vị học tập&rdquo;. &nbsp; &nbsp;<br />\r\n- 40% người lao động trong &ldquo;Gia đ&igrave;nh học tập&rdquo;, &ldquo;D&ograve;ng họ học tập&rdquo;, &ldquo;Cộng&nbsp;<br />\r\nđồng học tập&rdquo; (th&ocirc;n, bản, tổ d&acirc;n phố v̀ tương đương); 60% c&aacute;n bộ, c&ocirc;ng chức,&nbsp;<br />\r\nvi&ecirc;n chức, người lao động trong &ldquo;Đơn vị học tập&rdquo; cấp x&atilde;, cấp huyện, cấp tỉnh đạt&nbsp;<br />\r\ndanh hiệu &ldquo;C&ocirc;ng d&acirc;n học tập&rdquo;.&nbsp;<br />\r\n- Phấn đấu 100% Chủ tịch hoặc Ph&oacute; Chủ tịch Hội Khuyến học cấp x&atilde; tham&nbsp;<br />\r\ngia quản l&yacute; trung t&acirc;m học tập cộng đồng; 40% c&aacute;n bộ l&agrave;m c&ocirc;ng t&aacute;c khuyến học cấp&nbsp;<br />\r\nx&atilde; tham gia tổ chức c&aacute;c hoạt động của trung t&acirc;m học tập cộng đồng v̀ được bồi&nbsp;<br />\r\ndưỡng, tập huấn v&ecirc;̀ ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin để khai th&aacute;c, bi&ecirc;n soạn t&agrave;i liệu,&nbsp;<br />\r\nlưu trữ th&ocirc;ng tin, quản l&yacute; v&agrave; vận h&agrave;nh thiết bị kỹ thuật.&nbsp;<br />\r\n&nbsp;3&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n2.2. Phấn đấu đến năm 2030&nbsp;<br />\r\n- 100% c&aacute;n bộ, hội vi&ecirc;n Hội Khuyến học c&aacute;c cấp được học tập, qu&aacute;n triệt&nbsp;<br />\r\nc&aacute;c chủ trương, ch&iacute;nh s&aacute;ch mới của Đảng, Nh̀ nước, của tỉnh v&ecirc;̀ đẩy mạnh c&ocirc;ng&nbsp;<br />\r\nt&aacute;c khuyến học, khuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập, nhất l&agrave; những vấn đ&ecirc;̀ v&ecirc;̀ x&atilde;&nbsp;<br />\r\nhội học tập b&ecirc;̀n vững v&agrave; những su thế ph&aacute;t triển c&aacute;c m&ocirc; h&igrave;nh học tập giai đoạn&nbsp;<br />\r\n2026 - 2030 trong nước v&agrave; thế giới.&nbsp;<br />\r\n- 80% gia đ&igrave;nh được c&ocirc;ng nhận đạt &ldquo;Gia đ&igrave;nh học tập&rdquo;; 75% d&ograve;ng họ được&nbsp;<br />\r\nc&ocirc;ng nhận đạt &ldquo;D&ograve;ng họ &nbsp;học tập&rdquo;; 75% cộng đồng được c&ocirc;ng nhận đạt &ldquo;Cộng&nbsp;<br />\r\nđồng học tập&rdquo;; 100% đơn vị (gồm c&aacute;c tổ chức, cơ quan, trường học, doanh nghiệp,&nbsp;<br />\r\ncơ sở sản suất&hellip; kể cả cấp x&atilde; quản l&yacute;) được c&ocirc;ng nhận đạt &ldquo;Đơn vị học tập&rdquo;. &nbsp; &nbsp;<br />\r\n- 60% người lao động trong &ldquo;Gia đ&igrave;nh học tập&rdquo;, &ldquo;D&ograve;ng họ học tập&rdquo;, &ldquo;Cộng&nbsp;<br />\r\nđồng học tập&rdquo; (th&ocirc;n, bản, tổ d&acirc;n phố v̀ tương đương); 80% c&aacute;n bộ, c&ocirc;ng chức,&nbsp;<br />\r\nvi&ecirc;n chức, người lao động trong &ldquo;Đơn vị học tập&rdquo; cấp x&atilde;, cấp huyện, cấp tỉnh đạt&nbsp;<br />\r\ndanh hiệu &ldquo;C&ocirc;ng d&acirc;n học tập&rdquo;.&nbsp;<br />\r\n- 70% c&aacute;n bộ &nbsp;l&agrave;m c&ocirc;ng t&aacute;c khuyến học cấp x&atilde; tham gia tổ &nbsp;chức c&aacute;c hoạt&nbsp;<br />\r\nđộng của trung t&acirc;m học tập cộng đồng v̀ được bồi dưỡng, tập huấn v&ecirc;̀ ứng dụng&nbsp;<br />\r\nc&ocirc;ng nghệ &nbsp;th&ocirc;ng tin để khai th&aacute;c, bi&ecirc;n soạn t&agrave;i liệu, lưu trữ &nbsp;th&ocirc;ng tin, quản l&yacute; v&agrave;&nbsp;<br />\r\nvận h&agrave;nh thiết bị kỹ thuật&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;III. NHIỆM VỤ V&Agrave; GIẢI PH&Aacute;P &nbsp;<br />\r\n1. Đẩy mạnh c&ocirc;ng t&aacute;c tuy&ecirc;n truyền, vận động, n&acirc;ng cao nhận thức v&agrave;&nbsp;<br />\r\nnh&acirc;n rộng c&aacute;c m&ocirc; h&igrave;nh học tập v&agrave; học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ,&nbsp;<br />\r\ncộng đồng, đơn vị tr&ecirc;n địa b&agrave;n tỉnh&nbsp;<br />\r\nCấp ủy, ch&iacute;nh quy&ecirc;̀n c&aacute;c cấp, c&aacute;c ng̀nh, c&aacute;c cơ quan, đơn vị, lực lượng vũ&nbsp;<br />\r\ntrang, doanh nghiệp, tập trung tuy&ecirc;n truy&ecirc;̀n, phổ biến, vận động, n&acirc;ng cao nhận&nbsp;<br />\r\nthức của c&aacute;c c&aacute;n bộ, đảng vi&ecirc;n, c&ocirc;ng chức, vi&ecirc;n chức, hội vi&ecirc;n v&agrave; to&agrave;n x&atilde; hội v&ecirc;̀&nbsp;<br />\r\nx&acirc;y dựng v&agrave; ph&aacute;t triển m&ocirc; h&igrave;nh học tập, học tập thường xuy&ecirc;n, học suốt đời trong&nbsp;<br />\r\ngia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị; học tập ở người lớn; x&acirc;y dựng x&atilde; hội học tập&nbsp;<br />\r\ntrong n&ecirc;̀n kinh tế &nbsp;số, x&atilde; hội số &nbsp;th&ocirc;ng qua c&aacute;c phương tiện th&ocirc;ng tin đại ch&uacute;ng,&nbsp;<br />\r\ntruy&ecirc;̀n th&ocirc;ng x&atilde; hội, hệ thống th&ocirc;ng tin cơ sở, c&aacute;c trung t&acirc;m học tập cộng đồng, c&aacute;c&nbsp;<br />\r\ncơ sở gi&aacute;o dục v̀ c&aacute;c phương tiện kh&aacute;c.&nbsp;<br />\r\nTổ chức tuy&ecirc;n truy&ecirc;̀n &nbsp;th&ocirc;ng qua sự phối hợp của B&aacute;o, Đ̀i, cơ quan &nbsp;truy&ecirc;̀n&nbsp;<br />\r\nth&ocirc;ng, trang th&ocirc;ng tin điện tử, website, bản tin, sự phối hợp, v&agrave;o cuộc của c&aacute;c sở&nbsp;<br />\r\nban ng&agrave;nh, c&aacute;c tổ chức ch&iacute;nh trị - &nbsp;x&atilde; hội, v&agrave; c&aacute;c lực lượng x&atilde; hội &hellip; &nbsp;<br />\r\nKịp thời biểu dương, t&ocirc;n vinh c&aacute;c m&ocirc; h&igrave;nh điển h&igrave;nh ti&ecirc;n tiến, nh&acirc;n rộng m&ocirc;&nbsp;<br />\r\nh&igrave;nh c&oacute; c&aacute;ch l&agrave;m hay, s&aacute;ng kiến cải tiến đạt hiệu quả trong đi&ecirc;̀u kiện của n&ecirc;̀n kinh&nbsp;<br />\r\ntế số, x&atilde; hội số v&agrave; Ch&iacute;nh phủ số.&nbsp;<br />\r\nPh&aacute;t động phong tr̀o thi đua tổ chức cuộc vận động để th&uacute;c đẩy học tập suốt&nbsp;<br />\r\nđời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị; gắn kết với phong tr&agrave;o x&acirc;y dựng&nbsp;<br />\r\ncộng đồng học tập cấp x&atilde;, cấp huyện, cấp tỉnh. 4&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n2. Thực hiện cơ chế ch&iacute;nh s&aacute;ch về đẩy mạnh phong tr&agrave;o học tập suốt đời&nbsp;<br />\r\ntrong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị&nbsp;<br />\r\n- &nbsp;Triển khai s&acirc;u rộng bộ &nbsp;ti&ecirc;u ch&iacute; đ&aacute;nh gi&aacute;, c&ocirc;ng nhận m&ocirc; h&igrave;nh học tập,&nbsp;<br />\r\nhuyện, th&agrave;nh phố, tỉnh học tập.&nbsp;<br />\r\n- Triển khai thực hiện đầy đủ c&aacute;c cơ chế, ch&iacute;nh s&aacute;ch tạo đi&ecirc;̀u kiện thuận lợi đưa&nbsp;<br />\r\nphong tr&agrave;o học tập ở người lớn, học thường xuy&ecirc;n, li&ecirc;n tục; n&acirc;ng cao chất lượng, hiệu&nbsp;<br />\r\nquả học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị của tỉnh.&nbsp;<br />\r\n- Tăng cường huy động mọi nguồn nh&acirc;n lực, vật lực x&atilde; hội tham gia thực&nbsp;<br />\r\nhiện phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị&hellip;&nbsp;<br />\r\n3. Tiếp tục đổi mới, đẩy mạnh ứng dụng c&ocirc;ng nghệ &nbsp;th&ocirc;ng tin, chuyển&nbsp;<br />\r\nđổi số trong việc x&acirc;y dựng v&agrave; ph&aacute;t triển c&aacute;c m&ocirc; h&igrave;nh gia đ&igrave;nh, d&ograve;ng họ, cộng&nbsp;<br />\r\nđồng, đơn vị học tập trong c&aacute;c ng&agrave;nh, c&aacute;c đơn vị, địa phương&nbsp;<br />\r\n- C&aacute;c cấp, &nbsp;c&aacute;c ng&agrave;nh tập trung l&atilde;nh đạo, chỉ &nbsp;đạo đổi mới, đẩy mạnh ứng&nbsp;<br />\r\ndụng c&ocirc;ng nghệ &nbsp;th&ocirc;ng tin trong &nbsp;học tập, sản xuất, kinh doanh, đời sống&hellip;, g&oacute;p&nbsp;<br />\r\nphần th&uacute;c đẩy phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn&nbsp;<br />\r\nvị đạt chất lượng, hiệu quả.&nbsp;<br />\r\n- Hội Khuyến học c&aacute;c cấp x&acirc;y dựng, n&acirc;ng cao chất lượng hoạt động của bản&nbsp;<br />\r\ntin khuyến học, trang th&ocirc;ng tin điện tử, mở c&aacute;c chuy&ecirc;n trang, chuy&ecirc;n mục... để phổ&nbsp;<br />\r\nbiến, hướng dẫn n&acirc;ng cao hiểu biết cho c&aacute;n bộ hội, hội vi&ecirc;n khuyến học v̀ người&nbsp;<br />\r\nd&acirc;n v&ecirc;̀ nhiệm vụ khuyến học, khuyến t&agrave;i, x&acirc;y dựng c&aacute;c m&ocirc; h&igrave;nh học tập; tạo sự&nbsp;<br />\r\nchuyển biến v&ecirc;̀ ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin trong triển khai thực hiện c&aacute;c mục&nbsp;<br />\r\nti&ecirc;u của chương tr&igrave;nh &ldquo;Đẩy mạnh phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng&nbsp;<br />\r\nhọ, cộng đồng, đơn vị&rdquo;.&nbsp;<br />\r\n- Tăng cường c&ocirc;ng t&aacute;c phối hợp, li&ecirc;n kết giữa c&aacute;c ng&agrave;nh, tổ chức, đơn vị,&nbsp;<br />\r\ntrung t&acirc;m học tập cộng đồng trong việc tổ chức tập huấn, mở c&aacute;c lớp bồi dưỡng&nbsp;<br />\r\nứng dụng c&ocirc;ng nghệ th&ocirc;ng tin cho người d&acirc;n trong thời kỳ c&ocirc;ng nghiệp h&oacute;a, hiện&nbsp;<br />\r\nđại h&oacute;a; x&acirc;y dựng m&ocirc;i trường học tập để c&oacute; thể sống v&agrave; l&agrave;m việc trong đi&ecirc;̀u kiện&nbsp;<br />\r\ncủa n&ecirc;̀n kinh tế số, x&atilde; hội số v&agrave; Ch&iacute;nh phủ số.&nbsp;<br />\r\n&nbsp;4. Tăng cường c&ocirc;ng t&aacute;c phối hợp với c&aacute;c sở, ng&agrave;nh, c&aacute;c tổ chức ch&iacute;nh&nbsp;<br />\r\ntrị - x&atilde; hội tổ chức c&aacute;c phong tr&agrave;o, cuộc vận động đẩy mạnh phong tr&agrave;o học&nbsp;<br />\r\ntập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị giai đoạn 2021 - 2030&nbsp;<br />\r\n- Ph&aacute;t động phong tr̀o thi đua s&acirc;u rộng đẩy mạnh học tập suốt đời trong gia&nbsp;<br />\r\nđ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị, n&acirc;ng cao chất lượng c&ocirc;ng t&aacute;c khuyến học,&nbsp;<br />\r\nkhuyến t&agrave;i, x&acirc;y dựng x&atilde; hội học tập h̀ng năm v̀ giai đoạn gắn kết chặt chẽ v&agrave;&nbsp;<br />\r\nhiệu quả với c&aacute;c cuộc vận động, phong tr̀o thi đua kh&aacute;c nhằm th&uacute;c đẩy việc học&nbsp;<br />\r\ntập ở người lớn v&agrave; to&agrave;n x&atilde; hội.&nbsp;<br />\r\n- Huy động sự tham gia, phối hợp của to&agrave;n x&atilde; hội để triển khai chất lượng,&nbsp;<br />\r\nhiệu quả phong tr&agrave;o tự học, học thường xuy&ecirc;n, học suốt đời tr&ecirc;n m&ocirc;i trường c&ocirc;ng&nbsp;<br />\r\nnghệ cho mọi gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị. 5&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n- Đẩy mạnh phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng,&nbsp;<br />\r\nđơn vị, g&oacute;p phần đ&aacute;p ứng y&ecirc;u cầu ph&aacute;t triển kinh tế &nbsp;- x&atilde; hội trong chương tr&igrave;nh&nbsp;<br />\r\n&ldquo;Chuyển đổi số to&agrave;n cầu&rdquo;, x&acirc;y dựng tỉnh Sơn La th̀nh một x&atilde; hội học tập.&nbsp;<br />\r\n5. Đẩy mạnh sơ kết, tổng kết, nh&acirc;n rộng m&ocirc; h&igrave;nh v&agrave; tổ chức đại hội t&ocirc;n&nbsp;<br />\r\nvinh c&aacute;c m&ocirc; h&igrave;nh ti&ecirc;u biểu trong phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh,&nbsp;<br />\r\nd&ograve;ng họ, cộng đồng, đơn vị&hellip;&nbsp;<br />\r\n- Hằng năm c&aacute;c cấp uỷ, ch&iacute;nh quy&ecirc;̀n tập trung l&atilde;nh đạo, chỉ đạo triển khai&nbsp;<br />\r\nx&acirc;y dựng v&agrave; nh&acirc;n rộng c&aacute;c m&ocirc; h&igrave;nh học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng&nbsp;<br />\r\nđồng, đơn vị&hellip;&nbsp;<br />\r\n- Tăng cường c&ocirc;ng t&aacute;c kiểm tra, gi&aacute;m s&aacute;t, đ&aacute;nh gi&aacute; chất lượng, hiệu quả c&aacute;c&nbsp;<br />\r\ndanh hiệu &ldquo;Gia đ&igrave;nh học tập&rdquo;, &ldquo;D&ograve;ng họ học tập&rdquo;, &ldquo;Cộng đồng học tập&rdquo;, &ldquo;Đơn vị&nbsp;<br />\r\nhọc tập&rdquo; ở từng địa phương, đơn vị.&nbsp;<br />\r\n- Định kỳ tổ chức sơ kết việc triển khai thực hiện chương tr&igrave;nh h̀ng năm; tổ&nbsp;<br />\r\nchức sơ kết 5 năm gắn với hội nghị biểu dương c&aacute;c m&ocirc; h&igrave;nh học tập ti&ecirc;u biểu to&agrave;n&nbsp;<br />\r\ntỉnh giai đoạn 2021 &nbsp;- 2025 v&agrave;o qu&yacute; &nbsp;IV năm 2025; Tổ chức Hội nghị &nbsp;tổng kết 10&nbsp;<br />\r\nnăm v̀ đại hội biểu dương c&aacute;c m&ocirc; h&igrave;nh học tập ti&ecirc;u biểu to&agrave;n tỉnh giai đoạn 2021 -&nbsp;<br />\r\n2030 v̀o qu&yacute; IV năm 2030. &nbsp;<br />\r\n6. Duy tr&igrave;, củng cố v&agrave; n&acirc;ng cao chất lượng, hiệu quả &nbsp;tổ &nbsp;chức v&agrave; hoạt&nbsp;<br />\r\nđộng của Trung t&acirc;m học tập cộng đồng, c&aacute;c thiết chế văn h&oacute;a cơ sở g&oacute;p phần&nbsp;<br />\r\nđẩy mạnh phong tr&agrave;o học tập suốt đời &nbsp;trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng,&nbsp;<br />\r\nđơn vị giai đoạn 2021 - 2030&nbsp;<br />\r\n- Ng&agrave;nh Gi&aacute;o dục &amp; Đ̀o tạo phối hợp với Hội Khuyến học v&agrave; c&aacute;c ng&agrave;nh&nbsp;<br />\r\nli&ecirc;n quan tiếp tục &nbsp;tổ chức tập huấn n&acirc;ng cao năng lực quản l&yacute;, đi&ecirc;̀u h&agrave;nh v&agrave; hoạt&nbsp;<br />\r\nđộng của trung t&acirc;m học tập cộng đồng cấp x&atilde;. Đồng thời quan t&acirc;m tập huấn, n&acirc;ng&nbsp;<br />\r\ncao tr&igrave;nh độ, năng lực cho đội ngũ c&aacute;n bộ, hội vi&ecirc;n Hội Khuyến học c&aacute;c cấp trong&nbsp;<br />\r\nviệc &nbsp;tham gia quản l&yacute;, giảng dạy &nbsp;tại c&aacute;c &nbsp;trung t&acirc;m học tập cộng đồng, g&oacute;p phần&nbsp;<br />\r\nn&acirc;ng cao chất lượng, hiệu quả hoạt động của c&aacute;c trung t&acirc;m.&nbsp;<br />\r\n- Ng̀nh Văn ho&aacute;, Thể thao v&agrave; Du lịch tập trung đầu tư, n&acirc;ng cao chất lượng,&nbsp;<br />\r\nhiệu quả hoạt động của c&aacute;c thiết chế văn ho&aacute; cơ sở g&oacute;p phần th&uacute;c đẩy phong tr&agrave;o&nbsp;<br />\r\nhọc tập trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị ở c&aacute;c địa phương, cơ sở.&nbsp;<br />\r\n&nbsp;7. Tổ chức đ&aacute;nh gi&aacute; v&agrave; cấp giấy chứng nhận danh hiệu &ldquo;Gia đ&igrave;nh học&nbsp;<br />\r\ntập&rdquo;, &ldquo;D&ograve;ng họ học tập&rdquo;, &ldquo;Cộng đồng học tập&rdquo;, &ldquo;Đơn vị học tập&rdquo;.&nbsp;<br />\r\nH̀ng năm, Hội Khuyến học c&aacute;c cấp chủ &nbsp;tr&igrave;, phối hợp với c&aacute;c cơ quan c&oacute;&nbsp;<br />\r\nli&ecirc;n quan, tổ &nbsp;chức tập huấn, &nbsp;đ&aacute;nh gi&aacute;, &nbsp;tr&igrave;nh cấp c&oacute; thẩm quy&ecirc;̀n cấp giấy chứng&nbsp;<br />\r\nnhận c&aacute;c danh hiệu &ldquo;Gia đ&igrave;nh học tập&rdquo;, &ldquo;D&ograve;ng họ học tập&rdquo;, &ldquo;Cộng đồng học tập&rdquo;&nbsp;<br />\r\n&ldquo;Đơn vị học tập&rdquo; gắn với &ldquo;Gia đ&igrave;nh văn h&oacute;a&rdquo; theo quy định hiện h&agrave;nh.&nbsp;<br />\r\n&nbsp;8. Huy động nguồn lực x&atilde; hội h&oacute;a nhằm tăng cường sự đ&oacute;ng g&oacute;p của&nbsp;<br />\r\ncộng đồng&nbsp;<br />\r\nCấp ủy, ch&iacute;nh quy&ecirc;̀n c&aacute;c địa phương, c&aacute;c ng̀nh, đơn vị tập trung huy động&nbsp;<br />\r\nnguồn lực x&atilde; hội ho&aacute; từ sự đ&oacute;ng g&oacute;p của c&aacute;c tổ chức, doanh nghiệp, c&aacute;c nh&agrave; hảo 6&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\nt&acirc;m&hellip;trong v̀ ngòi tỉnh để c&oacute; nguồn kinh ph&iacute; triển khai thực hiện c&aacute;c mục ti&ecirc;u,&nbsp;<br />\r\nnhiệm vụ x&acirc;y dựng x&atilde; hội học tập tr&ecirc;n địa b&agrave;n.&nbsp;<br />\r\nIV. KINH PH&Iacute; THỰC HIỆN &nbsp;<br />\r\n1. Kinh ph&iacute; thực hiện kế hoạch gồm: Nguồn ng&acirc;n s&aacute;ch nh̀ nước cấp &nbsp;theo&nbsp;<br />\r\nquản l&yacute; hiện h&agrave;nh, nguồn x&atilde; hội h&oacute;a v&agrave; c&aacute;c nguồn t&agrave;i ch&iacute;nh hợp ph&aacute;p kh&aacute;c. &nbsp;<br />\r\n2. Căn cứ nhiệm vụ được giao, Hội Khuyến học c&aacute;c cấp lập dự to&aacute;n kinh ph&iacute;&nbsp;<br />\r\nthực hiện nhiệm vụ gửi cơ quan t̀i ch&iacute;nh c&ugrave;ng cấp, tổng hợp, tr&igrave;nh cấp c&oacute; thẩm&nbsp;<br />\r\nquy&ecirc;̀n ph&acirc;n bổ trong dự to&aacute;n ng&acirc;n s&aacute;ch nh̀ nước giao h̀ng năm, từ nguồn x&atilde; hội&nbsp;<br />\r\nh&oacute;a v&agrave; c&aacute;c nguồn t&agrave;i ch&iacute;nh hợp ph&aacute;p kh&aacute;c.&nbsp;<br />\r\nV. TỔ CHỨC THỰC HIỆN KẾ HOẠCH&nbsp;<br />\r\n1. Tr&aacute;ch nhiệm chung&nbsp;<br />\r\nCăn cứ nội dung kế hoạch v&agrave; chức năng, nhiệm vụ được giao, c&aacute;c Sở, ban,&nbsp;<br />\r\nng&agrave;nh, Hội Khuyến học tỉnh, UBND c&aacute;c huyện, th&agrave;nh phố, c&aacute;c đơn vị li&ecirc;n quan c&oacute;&nbsp;<br />\r\ntr&aacute;ch nhiệm:&nbsp;<br />\r\n- Qu&aacute;n triệt, triển &nbsp;khai, thực hiện nghi&ecirc;m t&uacute;c Quyết định số &nbsp;387/QĐ-TTg&nbsp;<br />\r\nng&agrave;y 25/3/2022 của Thủ &nbsp;tướng Ch&iacute;nh phủ &nbsp;ph&ecirc; duyệt Chương tr&igrave;nh &nbsp;&ldquo;Đẩy mạnh&nbsp;<br />\r\nphong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị giai đoạn&nbsp;<br />\r\n2021 - 2030&rdquo; .&nbsp;<br />\r\n- &nbsp;X&acirc;y dựng kế hoạch triển khai thực hiện chương tr&igrave;nh ph&ugrave; hợp với đi&ecirc;̀u kiện&nbsp;<br />\r\nthưc tế của cơ quan, đơn vị, địa phương m&igrave;nh.&nbsp;<br />\r\n- Tạo đi&ecirc;̀u kiện, cơ hội để c&aacute;n bộ, c&ocirc;ng chức, vi&ecirc;n chức, hội vi&ecirc;n, người lao&nbsp;<br />\r\nđộng v&agrave; nh&acirc;n d&acirc;n tham gia t&iacute;ch cực phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh,&nbsp;<br />\r\nd&ograve;ng họ, cộng đồng, đơn vị.&nbsp;<br />\r\n- &nbsp;Đưa nội dung, kết quả thực hiện chương tr&igrave;nh &ldquo;Đẩy mạnh phong tr&agrave;o học&nbsp;<br />\r\ntập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị&quot; v&agrave;o ti&ecirc;u ch&iacute; b&igrave;nh x&eacute;t c&aacute;c&nbsp;<br />\r\ndanh hiệu thi đua hằng năm của cơ quan, đơn vị.&nbsp;<br />\r\n- B&aacute;o c&aacute;o định kỳ 6 th&aacute;ng, hằng năm v̀ đột xuất v&ecirc;̀ Hội Khuyến học tỉnh, để&nbsp;<br />\r\ntổng hợp b&aacute;o c&aacute;o Ủy ban nh&acirc;n d&acirc;n tỉnh.&nbsp;<br />\r\n2. Hội Khuyến học tỉnh&nbsp;<br />\r\n- Chủ tr&igrave;, phối hợp với c&aacute;c Sở, ban, ng&agrave;nh, tổ chức ch&iacute;nh trị - x&atilde; hội, Ủy ban&nbsp;<br />\r\nnh&acirc;n d&acirc;n c&aacute;c huyện, th&agrave;nh phố triển khai, tổ chức thực hiện chương tr&igrave;nh.&nbsp;<br />\r\n- Chỉ đạo, hướng dẫn Hội Khuyến học c&aacute;c cấp triển khai thực hiện kế hoạch&nbsp;<br />\r\ntại địa phương, đơn vị theo hướng n&acirc;ng cao chất lượng c&aacute;c m&ocirc; h&igrave;nh &ldquo;Gia đ&igrave;nh học&nbsp;<br />\r\ntập&rdquo;, &ldquo;D&ograve;ng họ học tập&rdquo;, &ldquo;Cộng đồng học tập&rdquo;, &ldquo;Đơn vị học tập&rdquo;. &nbsp;<br />\r\n- Chủ &nbsp;tr&igrave;, phối hợp c&aacute;c cơ quan, đơn vị &nbsp;li&ecirc;n quan bi&ecirc;n soạn, in &nbsp;ấn t&agrave;i liệu&nbsp;<br />\r\nhướng dẫn đ&aacute;nh gi&aacute;, c&ocirc;ng nhận danh hiệu gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị&nbsp;<br />\r\nhọc tập; Tổ chức tập huấn v&agrave; triển khai ti&ecirc;u ch&iacute; đ&aacute;nh gi&aacute;, c&ocirc;ng nhận c&aacute;c m&ocirc; h&igrave;nh&nbsp;<br />\r\nhọc tập theo bộ ti&ecirc;u ch&iacute; mới của Trung ương Hội Khuyến học Việt nam. 7&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n- Kiểm tra, gi&aacute;m s&aacute;t, đ&aacute;nh gi&aacute; t&aacute;c động v&agrave; hiệu quả &nbsp;thực hiện &nbsp;kế &nbsp;hoạch&nbsp;<br />\r\nchương tr&igrave;nh. Hướng dẫn tr&igrave;nh tự, thủ tục cấp giấy chứng nhận c&aacute;c danh hiệu &ldquo;Gia&nbsp;<br />\r\nđ&igrave;nh học tập&rdquo;, &ldquo;D&ograve;ng họ học tập&rdquo;, &ldquo;Cộng đồng học tập&rdquo;, &nbsp;&ldquo;Đơn vị học tập&rdquo; , &ldquo;C&ocirc;ng&nbsp;<br />\r\nd&acirc;n học tập&rdquo; tr&ecirc;n địa b&agrave;n tỉnh theo quy định.&nbsp;<br />\r\n- Định kỳ, tổng hợp b&aacute;o c&aacute;o sơ kết, tổng kết việc thực hiện kế hoạch; đ&ecirc;̀ xuất&nbsp;<br />\r\nvới tỉnh, trung ương v&ecirc;̀ khen thưởng c&aacute;c tập thể, c&aacute; nh&acirc;n c&oacute; th&agrave;nh t&iacute;ch xuất sắc&nbsp;<br />\r\ntrong triển khai phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng,&nbsp;<br />\r\nđơn vị giai đoạn 2021 - 2030.&nbsp;<br />\r\n3. Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo&nbsp;<br />\r\n- Chủ tr&igrave;, phối hợp với Hội Khuyến học tỉnh đ&aacute;nh gi&aacute; t&aacute;c động v&agrave; hiệu quả hoạt&nbsp;<br />\r\nđộng của c&aacute;c trung t&acirc;m học tập cộng đồng trong việc x&acirc;y dựng v&agrave; nh&acirc;n rộng c&aacute;c m&ocirc;&nbsp;<br />\r\nh&igrave;nh &ldquo;Gia đ&igrave;nh học tập&rdquo;, &ldquo;D&ograve;ng họ học tập&rdquo;, &ldquo;Cộng đồng học tập&rdquo;, &ldquo;Đơn vị học tập&rdquo;.&nbsp;<br />\r\n- L&agrave;m n&ograve;ng cốt trong việc li&ecirc;n kết, phối hợp với c&aacute;c tổ chức, c&aacute;c lực lượng&nbsp;<br />\r\nx&atilde; hội tham gia tuy&ecirc;n truy&ecirc;̀n, vận động to&agrave;n d&acirc;n tham gia phong tr&agrave;o học tập suốt&nbsp;<br />\r\nđời, học tập ở người lớn, ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin trong đời sống.&nbsp;<br />\r\n- Chỉ đạo c&aacute;c cơ sở gi&aacute;o dục phối hợp với Hội Khuyến học c&ugrave;ng cấp tổ chức&nbsp;<br />\r\ntriển khai, gi&aacute;m s&aacute;t, đ&aacute;nh gi&aacute;, c&ocirc;ng nhận c&aacute;c m&ocirc; h&igrave;nh học tập tr&ecirc;n địa b&agrave;n.&nbsp;<br />\r\n&nbsp; 4. Sở Lao động - Thương binh v&agrave; X&atilde; hội&nbsp;<br />\r\nChủ &nbsp;tr&igrave;, phối hợp với c&aacute;c Sở, ban, ng&agrave;nh c&oacute; li&ecirc;n quan, UBND c&aacute;c huyện,&nbsp;<br />\r\nth&agrave;nh phố cụ thể h&oacute;a Kế hoạch n&agrave;y, chỉ đạo, tổ chức triển khai thực hiện c&oacute; hiệu&nbsp;<br />\r\nquả việc học tập suốt đời ở người lớn; &nbsp;<br />\r\nTổ &nbsp;chức lồng gh&eacute;p việc thực hiện kế &nbsp;hoạch v&agrave;o kế &nbsp;hoạch gi&aacute;o dục ngh&ecirc;̀&nbsp;<br />\r\nnghiệp hằng năm để thực hiện; đẩy mạnh ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin trong quản&nbsp;<br />\r\nl&yacute;, giảng dạy v&agrave; học tập; đổi mới, s&aacute;ng tạo, n&acirc;ng cao tr&igrave;nh độ kỹ năng ngh&ecirc;̀, kỹ&nbsp;<br />\r\nnăng m&ecirc;̀m cho người học.&nbsp;<br />\r\n&nbsp;5. Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng&nbsp;<br />\r\nPhối hợp với Hội Khuyến học tỉnh v̀ c&aacute;c cơ quan li&ecirc;n quan triển khai kế&nbsp;<br />\r\nhoạch truy&ecirc;̀n th&ocirc;ng v&ecirc;̀ phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng&nbsp;<br />\r\nđồng, đơn vị giai đoạn 2021- 2030 &nbsp;tr&ecirc;n địa b&agrave;n tỉnh. &nbsp;<br />\r\nChỉ đạo c&aacute;c cơ quan b&aacute;o ch&iacute; v̀ hệ &nbsp;thống th&ocirc;ng tin cơ sở &nbsp;th&ocirc;ng tin, tuy&ecirc;n&nbsp;<br />\r\ntruy&ecirc;̀n v&ecirc;̀ &nbsp;quan điểm, mục ti&ecirc;u, nhiệm vụ &nbsp;v&agrave; giải ph&aacute;p của Chương tr&igrave;nh &nbsp;&ldquo;Đẩy&nbsp;<br />\r\nmạnh phong tr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị&rdquo; tr&ecirc;n&nbsp;<br />\r\nđịa b&agrave;n tỉnh gắn với Chương tr&igrave;nh chuyển đổi số quốc gia.&nbsp;<br />\r\n&nbsp; 6. Sở Nội vụ&nbsp;<br />\r\nHằng năm, tham mưu cho UBND tỉnh x&acirc;y dựng kế &nbsp;hoạch đ̀o tạo, bồi&nbsp;<br />\r\ndưỡng c&aacute;n bộ, c&ocirc;ng chức, vi&ecirc;n chức đảm bảo lồng gh&eacute;p một c&aacute;ch khoa học g&oacute;p&nbsp;<br />\r\nphần đạt được ti&ecirc;u ch&iacute; c&aacute;c m&ocirc; h&igrave;nh học tập, n&acirc;ng cao chất lượng học tập suốt đời&nbsp;<br />\r\ntrong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị v̀ đạt ti&ecirc;u ch&iacute; &ldquo;C&ocirc;ng d&acirc;n học tập&rdquo;. &nbsp;&nbsp;<br />\r\n&nbsp; 8&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n7. Sở T&agrave;i ch&iacute;nh&nbsp;<br />\r\nHằng năm, căn cứ khả năng c&acirc;n đối ng&acirc;n s&aacute;ch, tổng &nbsp;hợp tham mưu cho &nbsp;ủy&nbsp;<br />\r\nban nh&acirc;n d&acirc;n tỉnh bố &nbsp;tr&iacute; kinh ph&iacute; ng&acirc;n s&aacute;ch Nh̀ nước để &nbsp;thực hiện chương tr&igrave;nh&nbsp;<br />\r\ntheo quy định của ph&aacute;p luật&nbsp;<br />\r\nChủ &nbsp;tr&igrave;, phối hợp với c&aacute;c cơ quan &nbsp;li&ecirc;n quan hướng dẫn cơ chế quản l&yacute; t&agrave;i&nbsp;<br />\r\nch&iacute;nh, đầu tư đối với c&aacute;c hoạt động của chương tr&igrave;nh; phối hợp kiểm tra, thanh tra&nbsp;<br />\r\nviệc thực hiện chương tr&igrave;nh.&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;8. Sở Văn h&oacute;a, Thể thao v&agrave; Du lịch&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Phối hợp với Hội Khuyến học trong việc đ&aacute;nh gi&aacute;, c&ocirc;ng nhận danh hiệu &ldquo;Gia&nbsp;<br />\r\nđ&igrave;nh học tập&rdquo;, &ldquo;D&ograve;ng họ học tập&rdquo;, &ldquo;Cộng đồng học tập&rdquo;, &quot;Đơn vị học tập&quot;, gắn với&nbsp;<br />\r\nc&ocirc;ng nhận danh hiệu &ldquo;Gia đ&igrave;nh văn h&oacute;a&rdquo;, &ldquo;Th&ocirc;n văn h&oacute;a&rdquo;, &ldquo;L̀ng văn h&oacute;a&rdquo;, &ldquo;Đơn&nbsp;<br />\r\nvị văn h&oacute;a&rdquo;, &ldquo;Bản văn h&oacute;a&rdquo;, &ldquo;Tổ d&acirc;n phố văn h&oacute;a&rdquo; hằng năm.&nbsp;<br />\r\n&nbsp;9. Đề nghị Ủy ban Mặt trận Tổ quốc Việt Nam tỉnh Sơn La, c&aacute;c tổ chức&nbsp;<br />\r\nch&iacute;nh trị - x&atilde; hội, x&atilde; hội nghề nghiệp trong tỉnh&nbsp;<br />\r\nĐẩy mạnh tuy&ecirc;n truy&ecirc;̀n, vận động, tạo đi&ecirc;̀u kiện cho c&aacute;n bộ, c&ocirc;ng chức, vi&ecirc;n&nbsp;<br />\r\nchức, đòn vi&ecirc;n, hội vi&ecirc;n, người lao động&hellip; của tổ chức m&igrave;nh được thường xuy&ecirc;n&nbsp;<br />\r\nhọc tập, học &nbsp;tập suốt đời, cần g&igrave; học nấy; &nbsp;t&iacute;ch cực tuy&ecirc;n truy&ecirc;̀n, vận động c&aacute;c tổ&nbsp;<br />\r\nchức th̀nh vi&ecirc;n ph&aacute;t động, tham gia c&aacute;c phong tr̀o thi đua v&ecirc;̀ c&ocirc;ng t&aacute;c khuyến&nbsp;<br />\r\nhọc, khuyến t&agrave;i; lồng gh&eacute;p c&aacute;c ti&ecirc;u ch&iacute; x&acirc;y dựng c&aacute;c m&ocirc; h&igrave;nh học tập v&agrave;o ti&ecirc;u ch&iacute;&nbsp;<br />\r\nc&ocirc;ng nhận &ldquo;N&ocirc;ng th&ocirc;n mới&rdquo;, &ldquo;đ&ocirc; thị văn minh&rdquo;, gắn kết chặt chẽ v&agrave; hiệu quả với&nbsp;<br />\r\nc&aacute;c cuộc vận động, phong tr̀o thi đua kh&aacute;c.&nbsp;<br />\r\n&nbsp;Phối hợp lồng gh&eacute;p c&aacute;c hoạt động tuy&ecirc;n truy&ecirc;̀n v&ecirc;̀ phong tr&agrave;o học tập suốt&nbsp;<br />\r\nđời trong gia đ&igrave;nh, d&ograve;ng họ, cộng đồng, đơn vị &nbsp;thuộc phạm vi, đối tượng, thẩm&nbsp;<br />\r\nquy&ecirc;̀n của tổ chức m&igrave;nh g&oacute;p phần thực hiện mục ti&ecirc;u x&acirc;y dựng x&atilde; hội học tập của&nbsp;<br />\r\ntỉnh đạt hiệu quả cao.&nbsp;<br />\r\nVận động đòn vi&ecirc;n, hội vi&ecirc;n v&agrave; nh&acirc;n d&acirc;n t&iacute;ch cực học tập v&agrave; tham gia x&acirc;y&nbsp;<br />\r\ndựng c&aacute;c m&ocirc; h&igrave;nh &ldquo;Gia đ&igrave;nh học &nbsp;tập&rdquo;, &ldquo;D&ograve;ng họ học tập&rdquo;, &ldquo;Cộng đồng học tập&rdquo;,&nbsp;<br />\r\n&ldquo;Đơn vị học tập&rdquo; tại nơi cư tr&uacute; v̀ cơ quan, đơn vị.&nbsp;<br />\r\n&nbsp;10. Ủy ban nh&acirc;n d&acirc;n c&aacute;c huyện, th&agrave;nh phố&nbsp;<br />\r\n- C&oacute; tr&aacute;ch nhiệm x&acirc;y dựng kế hoạch v&agrave; bố tr&iacute; ng&acirc;n s&aacute;ch địa phương để thực&nbsp;<br />\r\nhiện c&aacute;c nhiệm vụ, mục ti&ecirc;u của kế hoạch tại địa phương; theo d&otilde;i, đ&ocirc;n đốc, kiểm&nbsp;<br />\r\ntra, gi&aacute;m s&aacute;t, &nbsp;chỉ &nbsp;đạo c&aacute;c &nbsp;ph&ograve;ng, &nbsp;ban, đòn thể, tổ &nbsp;chức, doanh nghiệp &nbsp;ở &nbsp;địa&nbsp;<br />\r\nphương phối hợp với Hội Khuyến học c&aacute;c cấp triển khai thực hiện kế hoạch n&agrave;y;&nbsp;<br />\r\n- Chỉ &nbsp;đạo cơ quan &nbsp;th&ocirc;ng tin, &nbsp;truy&ecirc;̀n th&ocirc;ng &nbsp;của địa phương tổ &nbsp;chức tuy&ecirc;n&nbsp;<br />\r\ntruy&ecirc;̀n v&ecirc;̀ x&atilde; hội học tập, học tập suốt đời, v&ecirc;̀ gi&aacute;o dục người lớn v&agrave; v&ecirc;̀ c&aacute;c m&ocirc; h&igrave;nh&nbsp;<br />\r\nhọc tập trong chương tr&igrave;nh.&nbsp;<br />\r\n- Chỉ đạo Ph&ograve;ng Gi&aacute;o dục v̀ Đ&agrave;o tạo phối hợp với Hội Khuyến học c&ugrave;ng&nbsp;<br />\r\ncấp kiểm tra, gi&aacute;m s&aacute;t, sơ kết, tổng kết việc triển khai c&aacute;c m&ocirc; h&igrave;nh học tập v&agrave; quyết&nbsp;<br />\r\nđịnh c&ocirc;ng nhận c&aacute;c m&ocirc; h&igrave;nh đạt danh hiệu &ldquo;Gia đ&igrave;nh học tập&rdquo;, &ldquo;D&ograve;ng họ học tập&rdquo;,&nbsp;<br />\r\n&ldquo;Cộng đồng học tập&rdquo;, &ldquo;Đơn vị học tập&rdquo;, &ldquo;C&ocirc;ng d&acirc;n học tập&rdquo;. 9&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\nTr&ecirc;n đ&acirc;y l̀ Kế hoạch triển khai, thực hiện Chương tr&igrave;nh &ldquo;Đẩy mạnh phong&nbsp;<br />\r\ntr&agrave;o học tập suốt đời trong gia đ&igrave;nh, d&ograve;ng học, cộng đồng, đơn vị giai đoạn 2021-<br />\r\n2030&rdquo; &nbsp;tr&ecirc;n địa b&agrave;n tỉnh Sơn La. Y&ecirc;u cầu c&aacute;c Sở, ban, ng̀nh, UBND c&aacute;c huyện,&nbsp;<br />\r\nth̀nh phố phối hợp triển khai thực hiện./.&nbsp;<br />\r\n&nbsp;<br />\r\nNơi nhận:&nbsp;<br />\r\n- Bộ Gi&aacute;o dục v̀ Đ̀o tạo;&nbsp;<br />\r\n- Hội Khuyến học Việt Nam;&nbsp;<br />\r\n- Thường trực Tỉnh ủy; &nbsp;<br />\r\n- Thường trực HĐND tỉnh;&nbsp;<br />\r\n- Chủ tịch UBND tỉnh;&nbsp;<br />\r\n- C&aacute;c Ph&oacute; Chủ tịch UBND tỉnh;&nbsp;<br />\r\n- Ủy ban Mặt trận Tổ quốc VN tỉnh v&agrave; c&aacute;c tổ chức&nbsp;<br />\r\nth&agrave;nh vi&ecirc;n; &nbsp;<br />\r\n- Ban Tuy&ecirc;n gi&aacute;o Tỉnh ủy;&nbsp;<br />\r\n- Ban VHXH HĐND tỉnh;&nbsp;<br />\r\n- C&aacute;c Sở, ban, ng&agrave;nh của tỉnh;&nbsp;<br />\r\n- Hội Khuyến học tỉnh;&nbsp;<br />\r\n- UBND c&aacute;c huyện,th&agrave;nh phố;&nbsp;<br />\r\n- LĐVP UBND tỉnh;&nbsp;<br />\r\n- Lưu: VT, KGVX, Đại.&nbsp;<br />\r\nKT. CHỦ TỊCH&nbsp;<br />\r\nPHÓ CHỦ TỊCH THỪNG TRỰC&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\nTr&aacute;ng Thị Xu&acirc;n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;</p>\r\n', 15, 107, 0, 0, '', 0, '2022-10-20 07:49:30', '', '', '');
